// [FRONTEND] src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();

  // role = 'user' | 'admin' (‡∏≠‡πà‡∏≤‡∏ô‡∏à‡∏≤‡∏Å query string)
  const [role, setRole] = useState('user');
  const [form, setForm] = useState({ username: '', password: '', showPw: false });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // ‡∏≠‡πà‡∏≤‡∏ô ?role=admin|user
  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const r = params.get('role');
    if (r) setRole(r);
  }, [location.search]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    if (!form.username.trim() || !form.password.trim()) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');
      return;
    }

    try {
      setLoading(true);

      // ‚ùó ‡πÉ‡∏ä‡πâ axios ‡πÇ‡∏î‡∏¢‡∏≠‡∏≤‡∏®‡∏±‡∏¢ baseURL ‡∏ó‡∏µ‡πà‡∏ï‡∏±‡πâ‡∏á‡πÑ‡∏ß‡πâ‡πÉ‡∏ô src/index.js = http://localhost:3001/api
      //    ‡∏î‡∏±‡∏á‡∏ô‡∏±‡πâ‡∏ô endpoint ‡∏ï‡∏£‡∏á‡∏ô‡∏µ‡πâ "‡πÑ‡∏°‡πà‡∏ï‡πâ‡∏≠‡∏á" ‡πÉ‡∏™‡πà /api ‡∏ã‡πâ‡∏≥ ‡πÉ‡∏´‡πâ‡πÉ‡∏ä‡πâ /auth/login ‡πÑ‡∏î‡πâ‡πÄ‡∏•‡∏¢
      const payload = {
        username: form.username.trim(),
        email:    form.username.trim(),   // ‡πÄ‡∏ú‡∏∑‡πà‡∏≠ backend ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö email ‡∏î‡πâ‡∏ß‡∏¢
        password: form.password.trim(),
      };

      const res = await axios.post('/auth/login', payload);

      // ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡∏´‡∏•‡∏≤‡∏¢‡∏ä‡∏∑‡πà‡∏≠‡∏Ñ‡∏µ‡∏¢‡πå
      const token =
        res.data?.token ||
        res.data?.accessToken ||
        res.data?.jwt;

      const serverRole =
        res.data?.role ||
        res.data?.user?.role ||
        role;

      if (!token) throw new Error('‡πÑ‡∏°‡πà‡∏û‡∏ö token ‡∏à‡∏≤‡∏Å‡πÄ‡∏ã‡∏¥‡∏£‡πå‡∏ü‡πÄ‡∏ß‡∏≠‡∏£‡πå');

      // ‡πÄ‡∏Å‡πá‡∏ö token ‡πÅ‡∏•‡∏∞‡πÅ‡∏ô‡∏ö‡∏Å‡∏±‡∏ö axios ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö‡∏£‡∏µ‡πÄ‡∏Ñ‡∏ß‡∏™‡∏ï‡πå‡∏ñ‡∏±‡∏î‡πÑ‡∏õ
      localStorage.setItem('token', token);
      localStorage.setItem('role', serverRole);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // ‡∏ô‡∏≥‡∏ó‡∏≤‡∏á: ‡πÅ‡∏≠‡∏î‡∏°‡∏¥‡∏ô‡πÑ‡∏õ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤, ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏ó‡∏±‡πà‡∏ß‡πÑ‡∏õ‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö‡∏´‡∏ô‡πâ‡∏≤‡πÅ‡∏£‡∏Å (‡∏°‡∏µ‡∏≠‡∏¢‡∏π‡πà‡∏à‡∏£‡∏¥‡∏á)
      if (serverRole === 'admin') {
        navigate('/admin/products', { replace: true });
      } else {
        navigate('/', { replace: true }); // ‚úÖ ‡∏Å‡∏±‡∏ô‡∏´‡∏ô‡πâ‡∏≤‡∏Ç‡∏≤‡∏ß‡∏à‡∏≤‡∏Å route ‡∏ó‡∏µ‡πà‡πÑ‡∏°‡πà‡∏°‡∏µ
      }
    } catch (e) {
      // ‡∏™‡∏£‡∏∏‡∏õ error ‡πÉ‡∏´‡πâ‡∏≠‡πà‡∏≤‡∏ô‡∏á‡πà‡∏≤‡∏¢
      const status = e.response?.status;
      const url = e.config?.baseURL
        ? `${e.config.baseURL}${e.config.url}`
        : e.config?.url;

      const msg =
        status === 401 ? '‡∏≠‡∏µ‡πÄ‡∏°‡∏•/‡∏ä‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ ‡∏´‡∏£‡∏∑‡∏≠‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á'
        : e.code === 'ERR_NETWORK' ? '‡πÄ‡∏ä‡∏∑‡πà‡∏≠‡∏°‡∏ï‡πà‡∏≠‡πÄ‡∏ã‡∏¥‡∏£‡πå‡∏ü‡πÄ‡∏ß‡∏≠‡∏£‡πå‡πÑ‡∏°‡πà‡πÑ‡∏î‡πâ (‡∏ï‡∏£‡∏ß‡∏à‡∏™‡∏≠‡∏ö backend ‡∏û‡∏≠‡∏£‡πå‡∏ï 3001)'
        : e.response?.data?.error || '‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß';
      setError(`${msg}${status ? ` [${status}]` : ''}${url ? ` @ ${url}` : ''}`);
    } finally {
      setLoading(false);
    }
  }

