// src/pages/HomeUser.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import './HomeUser.css';
import { addItem as addToCart } from '../lib/cart';

/* -------------------- Fallback -------------------- */
const FALLBACK_CATEGORIES = [
  { id: 'PLT', slug: 'plants', name: '‡∏ï‡πâ‡∏ô‡πÑ‡∏°‡πâ',            image: process.env.PUBLIC_URL + '/tonmai.jpg' },
  { id: 'TLS', slug: 'tools',  name: '‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå‡πÄ‡∏û‡∏≤‡∏∞‡∏õ‡∏•‡∏π‡∏Å', image: process.env.PUBLIC_URL + '/opakorn.jpg' },
];

const FALLBACK_BEST = [
  { id: 1, name: '‡∏ï‡πâ‡∏ô‡∏Å‡∏•‡πâ‡∏≤‡∏•‡∏≥‡πÉ‡∏¢‡∏Ñ‡∏£‡∏¥‡∏™‡∏ï‡∏±‡∏•', price: 250, img: process.env.PUBLIC_URL + '/lamyai.jpg' },
  { id: 2, name: '‡∏ï‡πâ‡∏ô‡∏û‡∏ç‡∏≤‡∏ô‡∏≤‡∏Ñ‡∏£‡∏≤‡∏ä',       price: 150, img: process.env.PUBLIC_URL + '/payanaka1.jpg' },
  { id: 3, name: '‡∏Å‡∏£‡∏∞‡∏ñ‡∏≤‡∏á‡∏°‡∏¥‡∏ô‡∏¥‡∏°‡∏≠‡∏•',      price: 300, img: process.env.PUBLIC_URL + '/katar.jpeg' },
  { id: 4, name: '‡∏î‡∏¥‡∏ô‡∏™‡∏π‡∏ï‡∏£‡∏ó‡∏≤‡∏á‡∏£‡πâ‡∏≤‡∏ô',      price:  99, img: process.env.PUBLIC_URL + '/din.jpg' },
  { id: 5, name: '‡∏´‡∏¥‡∏ô‡∏†‡∏π‡πÄ‡∏Ç‡∏≤‡πÑ‡∏ü‡∏õ‡∏•‡∏π‡∏Å‡πÑ‡∏°‡πâ',   price: 129, img: process.env.PUBLIC_URL + '/logo.png' },
];

export default function HomeUser() {
  const apiPath = (p) =>
    (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api') ? p : '/api' + p;

  const [categories, setCategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState({ cat: true, best: true, all: true });
  const [errMsg, setErrMsg] = useState('');

  /* -------------------- Helpers -------------------- */
  const normalizeProduct = (p, idx) => ({
    id: p.product_id ?? p.id ?? p._id ?? `p-${idx}`,
    name: p.product_name ?? p.name_th ?? p.name ?? p.title ?? '‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤',
    price: Number(p.selling_price ?? p.price ?? p.sale_price ?? p.amount ?? p.product_price ?? 0),
    img:
      p.image_url ||
      p.cover_url ||
      p.main_image_url ||
      p.image ||
      p.thumbnail ||
      (Array.isArray(p.images) ? p.images[0] : '') ||
      process.env.PUBLIC_URL + '/logo.png',
  });
  const toArray = (data) => {
    if (Array.isArray(data)) return data;
    if (Array.isArray(data?.data)) return data.data;
    if (Array.isArray(data?.rows)) return data.rows;
    return [];
  };

  /* -------------------- ‡πÇ‡∏´‡∏•‡∏î‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà (2 ‡∏õ‡∏∏‡πà‡∏°‡∏ö‡∏ô) -------------------- */
  useEffect(() => {
    (async () => {
      const normalizeCat = (c, idx) => {
        const cid = c.category_id ?? c.id ?? c.code ?? c.slug ?? `cat-${idx}`;
        const rawName =
          (c.category_name ?? c.name_th ?? c.name ?? c.title ?? '').toString().trim() || '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏‡∏ä‡∏∑‡πà‡∏≠';
        const guessSlug =
          c.slug ??
          (rawName.includes('‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå') ? 'tools' :
           rawName.includes('‡∏ï‡πâ‡∏ô‡πÑ‡∏°‡πâ')   ? 'plants' : undefined) ??
          String(cid).toLowerCase();

        return {
          id: String(cid),
          code: (c.code ?? '').toString().toUpperCase(),
          slug: guessSlug,
          name: rawName,
          image:
            c.image_url ||
            c.image ||
            c.cover ||
            c.thumbnail ||
            (guessSlug === 'tools'
              ? process.env.PUBLIC_URL + '/opakorn.jpg'
              : process.env.PUBLIC_URL + '/tonmai.jpg'),
        };
      };

      async function loadFromLookups() {
        try {
          const r = await axios.get(apiPath('/lookups?published=1'));
          const data = r.data || {};
          const list =
            Array.isArray(data.product_categories)
              ? data.product_categories
              : Array.isArray(data.categories)
              ? data.categories
              : [];
          if (!list.length) return null;
          return list.map(normalizeCat);
        } catch { return null; }
      }
      async function loadFromCategoriesApi() {
        try {
          const r = await axios.get(apiPath('/categories?status=active'));
          const arr = (Array.isArray(r.data?.data) ? r.data.data : r.data) || [];
          if (!arr.length) return null;
          return arr.map(normalizeCat);
        } catch { return null; }
      }

      try {
        setLoading((s) => ({ ...s, cat: true }));
        let cats = (await loadFromLookups()) || (await loadFromCategoriesApi()) || [];

        const order = ['PLT', 'PLANTS', 'plants', 'TLS', 'TOOLS', 'tools'];
        cats.sort((a, b) => {
          const ai = order.findIndex((k) =>
            [a.code?.toUpperCase(), a.slug?.toLowerCase()].includes(String(k).toLowerCase())
          );
          const bi = order.findIndex((k) =>
            [b.code?.toUpperCase(), b.slug?.toLowerCase()].includes(String(k).toLowerCase())
          );
          return (ai === -1 ? 999 : ai) - (bi === -1 ? 999 : bi);
        });

        if (!cats.length) cats = FALLBACK_CATEGORIES;
        setCategories(cats.slice(0, 2));
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* -------------------- Best Sellers (5 ‡∏ä‡∏¥‡πâ‡∏ô) -------------------- */
  useEffect(() => {
    (async () => {
      async function tryFetch(url) {
        try {
          const r = await axios.get(url, {
            headers: { 'Cache-Control': 'no-cache' },
            params: { _: Date.now() },
          });
        const arr = toArray(r.data);
          const mapped = arr.map((p, i) => normalizeProduct(p, i)).filter((x) => x.id && x.name);
          if (mapped.length) return mapped;
        } catch {}
        return null;
      }

      try {
        setLoading((s) => ({ ...s, best: true }));
        const urls = [
          apiPath('/products/best-sellers?limit=5'),
          apiPath('/products?sort=popular&limit=5'),
          apiPath('/products?featured=1&limit=5'),
        ];
        let products = null;
        for (const u of urls) {
          products = await tryFetch(u);
          if (products) break;
        }
        setBestSellers(products?.slice(0, 5) || FALLBACK_BEST.slice(0, 5));
      } catch {
        setBestSellers(FALLBACK_BEST.slice(0, 5));
        setErrMsg('‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ä‡∏±‡πà‡∏ß‡∏Ñ‡∏£‡∏≤‡∏ß');
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

/* -------------------- ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î (‡∏Å‡∏£‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÄ‡∏ô‡∏∑‡πà‡∏≠‡∏á) -------------------- */
useEffect(() => {
  (async () => {
    const bust = Date.now();

    const toArray = (data) => {
      if (Array.isArray(data)) return data;
      if (Array.isArray(data?.data)) return data.data;
      if (Array.isArray(data?.rows)) return data.rows;
      return [];
    };

    const get = async (url) => {
      try {
        const r = await axios.get(url, {
          headers: { 'Cache-Control': 'no-cache' },
          params: { _: bust },
        });
        return toArray(r.data);
      } catch { return []; }
    };

    const normalize = (arr) => arr.map((p, i) => ({
      id: p.product_id ?? p.id ?? p._id ?? `p-${i}`,
      name: p.product_name ?? p.name_th ?? p.name ?? p.title ?? '‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤',
      price: Number(p.selling_price ?? p.price ?? p.sale_price ?? p.amount ?? p.product_price ?? 0),
      img:
        p.image_url || p.cover_url || p.main_image_url || p.image || p.thumbnail ||
        (Array.isArray(p.images) ? p.images[0] : '') ||
        process.env.PUBLIC_URL + '/logo.png',
    })).filter(x => x.id && x.name);

    try {
      setLoading(s => ({ ...s, all: true }));

      // 1) ‡∏•‡∏≠‡∏á‡∏î‡∏∂‡∏á‡∏£‡∏ß‡∏°‡∏Ñ‡∏£‡∏±‡πâ‡∏á‡πÄ‡∏î‡∏µ‡∏¢‡∏ß
      let all = normalize(
        (await get(apiPath('/products?limit=60'))) ||
        (await get(apiPath('/products/all?limit=60'))) ||
        (await get(apiPath('/products/list?limit=60')))
      );

      // 2) ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡∏ß‡πà‡∏≤‡∏á ‚Üí ‡∏î‡∏∂‡∏á‡∏à‡∏≤‡∏Å‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏•‡∏±‡∏Å‡πÇ‡∏î‡∏¢‡∏ï‡∏£‡∏á (plants/tools)
      if (!all.length) {
        const keys = (categories.length ? categories : [
          { slug: 'plants' }, { slug: 'tools' }
        ]).map(c => c.slug || c.code || c.id);

        let bag = [];
        for (const key of keys) {
          const q = encodeURIComponent(key);
          // ‡∏•‡∏≠‡∏á 2 endpoint
          const a = await get(apiPath(`/products?category=${q}&limit=30`));
          const b = a.length ? [] : await get(apiPath(`/products/by-category/${q}?limit=30`));
          bag = bag.concat(a, b);
        }
        // dedupe by id
        const seen = new Set();
        all = normalize(bag).filter(x => (x.id && !seen.has(x.id) && seen.add(x.id)));
      }

      // 3) ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡∏ß‡πà‡∏≤‡∏á‡∏≠‡∏µ‡∏Å ‚Üí ‡πÉ‡∏ä‡πâ BestSellers ‡∏°‡∏≤‡∏Å‡πà‡∏≠‡∏ô
      if (!all.length && bestSellers.length) {
        all = [...bestSellers];
      }

      // ‡πÄ‡∏£‡∏µ‡∏¢‡∏á id ‡πÉ‡∏´‡∏°‡πà‡∏•‡πà‡∏≤‡∏™‡∏∏‡∏î‡∏Å‡πà‡∏≠‡∏ô (‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏Ç)
      all.sort((a, b) => {
        const ai = Number(a.id), bi = Number(b.id);
        if (Number.isFinite(ai) && Number.isFinite(bi)) return bi - ai;
        return 0;
      });

      setAllProducts(all);
    } finally {
      setLoading(s => ({ ...s, all: false }));
    }
  })();
  // ‡πÉ‡∏´‡πâ‡∏£‡∏±‡∏ô‡πÉ‡∏´‡∏°‡πà‡πÄ‡∏°‡∏∑‡πà‡∏≠ categories ‡∏´‡∏£‡∏∑‡∏≠ bestSellers ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô
}, [categories, bestSellers]);

  return (
    <div className="home-container">
      {/* Search */}
      <div className="search-box">
        <input type="text" placeholder="‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤" />
        <button className="search-btn">üîç</button>
      </div>

      {/* Hero */}
      <section
        className="hero"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/p2.png'})`,
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <div className="hero-text">
          <h1>‚Äú<span className="highlight">‡∏£‡πâ‡∏≤‡∏ô‡∏õ‡∏£‡∏≤‡∏ä‡∏ç‡πå‡πÅ‡∏°‡πà‡πÇ‡∏à‡πâ</span>‚Äù</h1>
          <p>‡πÅ‡∏´‡∏•‡πà‡∏á‡∏ú‡∏•‡∏¥‡∏ï‡πÅ‡∏•‡∏∞‡∏à‡∏±‡∏î‡∏à‡∏≥‡∏´‡∏ô‡πà‡∏≤‡∏¢ ‡∏ï‡πâ‡∏ô‡πÑ‡∏°‡πâ ‡πÅ‡∏•‡∏∞‡∏ß‡∏±‡∏™‡∏î‡∏∏‡∏õ‡∏•‡∏π‡∏Å ‡∏°‡∏≤‡∏Å‡∏Å‡∏ß‡πà‡∏≤ 100 ‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£</p>
        </div>
      </section>

      {/* ‡∏õ‡∏∏‡πà‡∏°‡∏´‡∏°‡∏ß‡∏î 2 ‡πÉ‡∏ö */}
      <section className="categories">
        {(loading.cat ? FALLBACK_CATEGORIES : categories).map((cat, idx) => {
          const q = encodeURIComponent(cat.slug || cat.code || cat.id);
          return (
            <Link
              key={cat.id || cat.slug || `cat-${idx}`}
              to={`/products?category=${q}`}
              className="category-card"
              title={cat.name}
            >
              <img
                src={cat.image}
                alt={cat.name}
                onError={(e) => {
                  e.currentTarget.src =
                    (cat.slug || cat.code || '')
                      .toString()
                      .toLowerCase()
                      .includes('tool')
                      ? process.env.PUBLIC_URL + '/opakorn.jpg'
                      : process.env.PUBLIC_URL + '/tonmai.jpg';
                }}
              />
              <p>{cat.name}</p>
            </Link>
          );
        })}
      </section>

      {/* Best Sellers (5 ‡∏ä‡∏¥‡πâ‡∏ô) */}
      <section className="best-sellers">
        <h2>üåü ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ç‡∏≤‡∏¢‡∏î‡∏µ</h2>
        {errMsg && <div className="info-inline">{errMsg}</div>}
        <div className="product-grid">
          {(loading.best ? FALLBACK_BEST.slice(0, 5) : bestSellers).map((item, idx) => (
            <div className="product-card" key={item.id ?? `best-${idx}`}>
              <img
                src={item.img}
                alt={item.name}
                onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
              />
              <h3>{item.name}</h3>
              <p>{Number(item.price).toLocaleString()} ‡∏ö‡∏≤‡∏ó</p>
              <button className="btn-add" onClick={() => addToCart(item)}>
                + ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏•‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* All products (‡∏Å‡∏£‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÄ‡∏ô‡∏∑‡πà‡∏≠‡∏á) */}
      <section className="all-products">
        <h2>üõí ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î</h2>
        {loading.all ? (
          <div className="info-inline">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‚Ä¶</div>
        ) : (allProducts && allProducts.length) ? (
          <div className="product-grid">
            {allProducts.map((p, idx) => (
              <div className="product-card" key={p.id ?? `all-${idx}`}>
                <img
                  src={p.img}
                  alt={p.name}
                  onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
                />
                <h3>{p.name}</h3>
                <p>{Number(p.price).toLocaleString()} ‡∏ö‡∏≤‡∏ó</p>
                <button className="btn-add" onClick={() => addToCart(p)}>
                  + ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏•‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤
                </button>
              </div>
            ))}
          </div>
        ) : (
          <div className="info-inline">‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÉ‡∏ô‡∏£‡∏∞‡∏ö‡∏ö‡∏´‡∏£‡∏∑‡∏≠‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å API</div>
        )}
      </section>
    </div>
  );
}
