// FRONTEND: src/pages/SignUp.js
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

export default function SignUp() {
  const nav = useNavigate();

  // ===== ‡∏ü‡∏≠‡∏£‡πå‡∏°‡∏´‡∏•‡∏±‡∏Å =====
  const [form, setForm] = useState({
    email: '',
    password: '',
    confirm: '',
    full_name: '',
    phone: '',
    houseNo: '',
    province: '',
    amphoe: '',
    tambon: '',
    gender: '',
  });

  const [showPw, setShowPw] = useState(false);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // ===== ‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏ó‡∏±‡πâ‡∏á‡∏õ‡∏£‡∏∞‡πÄ‡∏ó‡∏® (‡πÅ‡∏ö‡∏ö‡πÄ‡∏î‡∏µ‡∏¢‡∏ß‡∏Å‡∏±‡∏ö‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô public/data) =====
  // ‡πÇ‡∏Ñ‡∏£‡∏á‡∏™‡∏£‡πâ‡∏≤‡∏á‡πÑ‡∏ü‡∏•‡πå thai-address.min.json ‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏Ñ‡∏∑‡∏≠:
  // { "<‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î>": { "<‡∏≠‡∏≥‡πÄ‡∏†‡∏≠>": { "<‡∏ï‡∏≥‡∏ö‡∏•>": "<‡∏£‡∏´‡∏±‡∏™‡πÑ‡∏õ‡∏£‡∏©‡∏ì‡∏µ‡∏¢‡πå>" } } }
  const [addrData, setAddrData] = useState(null);
  const [addrLoadErr, setAddrLoadErr] = useState('');

  useEffect(() => {
    const url = `${process.env.PUBLIC_URL}/data/thai-address.min.json`;
    fetch(url)
      .then(r => r.json())
      .then(setAddrData)
      .catch(e => {
        console.error('load thai-address failed:', e);
        setAddrLoadErr('‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
      });
  }, []);

  // ===== ‡∏≠‡∏ô‡∏∏‡∏û‡∏±‡∏ô‡∏ò‡πå‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö Select =====
  const provinces = useMemo(
    () => (addrData ? Object.keys(addrData) : []),
    [addrData]
  );
  const amphoes = useMemo(() => {
    if (!addrData || !form.province) return [];
    return Object.keys(addrData[form.province] || {});
  }, [addrData, form.province]);

  const tambons = useMemo(() => {
    if (!addrData || !form.province || !form.amphoe) return [];
    return Object.keys(addrData[form.province]?.[form.amphoe] || {});
  }, [addrData, form.province, form.amphoe]);

  // ZIP ‡∏à‡∏≤‡∏Å‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏• (‡∏≠‡πà‡∏≤‡∏ô‡∏≠‡∏±‡∏ï‡πÇ‡∏ô‡∏°‡∏±‡∏ï‡∏¥‡πÄ‡∏°‡∏∑‡πà‡∏≠‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏Ñ‡∏£‡∏ö)
  const zipcode = useMemo(() => {
    if (!addrData || !form.province || !form.amphoe || !form.tambon) return '';
    return addrData[form.province]?.[form.amphoe]?.[form.tambon] || '';
  }, [addrData, form.province, form.amphoe, form.tambon]);

  // ===== utils =====
  const isEmail = v => /\S+@\S+\.\S+/.test(String(v || '').trim());
  const splitName = (full) => {
    const parts = String(full || '').trim().split(/\s+/);
    if (parts.length <= 1) return { first_name: parts[0] || '', last_name: '' };
    return { first_name: parts[0], last_name: parts.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };

  // ===== handlers =====
  const onChange = (e) => {
    const { name, value } = e.target;
    // cascade reset
    if (name === 'province') {
      setForm(p => ({ ...p, province: value, amphoe: '', tambon: '' }));
      return;
    }
    if (name === 'amphoe') {
      setForm(p => ({ ...p, amphoe: value, tambon: '' }));
      return;
    }
    setForm(p => ({ ...p, [name]: value }));
  };

  const pwMismatch = form.password && form.confirm && form.password !== form.confirm;

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    // validate ‡πÄ‡∏ö‡∏∑‡πâ‡∏≠‡∏á‡∏ï‡πâ‡∏ô
    if (!isEmail(form.email))      return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡πÉ‡∏´‡πâ‡∏ñ‡∏π‡∏Å‡∏ï‡πâ‡∏≠‡∏á');
    if (form.password.length < 8)  return setError('‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£');
    if (pwMismatch)                return setError('‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô');
    const required = ['full_name', 'phone', 'houseNo', 'province', 'amphoe', 'tambon'];
    if (required.some(k => !String(form[k]).trim())) return setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');

    const { first_name, last_name } = splitName(form.full_name);
    const address = `${form.houseNo} ‡∏ï.${form.tambon} ‡∏≠.${form.amphoe} ‡∏à.${form.province} ${zipcode || ''}`;

    const payload = {
      first_name,
      last_name,
      email: form.email.trim(),
      username: makeUsername(form.email),
      password: form.password,
      confirm_password: form.confirm,
      phone_number: form.phone.trim(),
      address,
      // gender ‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏ö‡∏±‡∏ô‡∏ó‡∏∂‡∏Å‡∏ó‡∏µ‡πà backend ‡πÉ‡∏ô‡∏ï‡∏≠‡∏ô‡∏ô‡∏µ‡πâ
    };

    try {
      setLoading(true);
      // baseURL ‡∏ï‡∏±‡πâ‡∏á‡πÉ‡∏ô src/index.js ‡πÅ‡∏•‡πâ‡∏ß ‚Üí ‡πÉ‡∏ä‡πâ path ‡∏™‡∏±‡πâ‡∏ô‡πÑ‡∏î‡πâ
      const res = await axios.post('/auth/register', payload);

      // ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à ‚Üí ‡∏ñ‡πâ‡∏≤‡∏°‡∏µ token ‡∏Å‡πá‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô‡πÉ‡∏´‡πâ‡πÄ‡∏•‡∏¢
      let token = res?.data?.token || res?.data?.accessToken || res?.data?.jwt;
      if (!token) {
        const loginRes = await axios.post('/auth/login', {
          email: form.email,
          password: form.password,
        });
        token = loginRes?.data?.token || loginRes?.data?.accessToken || loginRes?.data?.jwt;
      }
      if (token) {
        localStorage.setItem('token', token);
        localStorage.setItem('role', res?.data?.user?.role || 'customer');
        axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      }
      nav('/', { replace: true });
    } catch (err) {
      setError(err?.response?.data?.error || '‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="signup-page">
      {/* hero + logo notch */}
      <div className="signup-hero">
        <div className="notch">
          <img src={`${process.env.PUBLIC_URL}/logo.png`} alt="PRACH MAEJO" />
        </div>
      </div>

      {/* brand */}
      <div className="brand-head">
        <div className="brand-line">
          <span className="brand">PRACH MAEJO</span>
          <span className="online">ONLINE</span>
        </div>
        <h2>‡∏™‡∏£‡πâ‡∏≤‡∏á‡∏ö‡∏±‡∏ç‡∏ä‡∏µ / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</h2>
      </div>

      {/* form */}
      <form className="signup-form" onSubmit={onSubmit} noValidate>
        {/* ‡∏≠‡∏µ‡πÄ‡∏°‡∏• */}
        <label className="field col">
          <span className="label">‡∏≠‡∏µ‡πÄ‡∏°‡∏•</span>
          <input
            type="email" name="email" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏≠‡∏µ‡πÄ‡∏°‡∏•‡∏ó‡∏µ‡πà‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô‡πÑ‡∏î‡πâ‡∏à‡∏£‡∏¥‡∏á"
            value={form.email} onChange={onChange}
          />
        </label>

        {/* ‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <div className="pw-wrap">
            <input
              type={showPw ? 'text' : 'password'}
              name="password"
              placeholder="‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ô‡πâ‡∏≠‡∏¢ 8 ‡∏ï‡∏±‡∏ß‡∏≠‡∏±‡∏Å‡∏©‡∏£"
              value={form.password}
              onChange={onChange}
              className={pwMismatch ? 'is-invalid' : ''}
            />
            <button type="button" className="pw-toggle" onClick={() => setShowPw(s => !s)}>
              {showPw ? 'üôà' : 'üëÅÔ∏è'}
            </button>
          </div>
        </label>

        {/* ‡∏ä‡∏∑‡πà‡∏≠-‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏• */}
        <label className="field col">
          <span className="label">‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•</span>
          <input
            type="text" name="full_name" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡∏ä‡∏∑‡πà‡∏≠ - ‡∏ô‡∏≤‡∏°‡∏™‡∏Å‡∏∏‡∏•"
            value={form.full_name} onChange={onChange}
          />
        </label>

        {/* ‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô */}
        <label className="field col">
          <span className="label">‡∏¢‡∏∑‡∏ô‡∏¢‡∏±‡∏ô‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô</span>
          <input
            type={showPw ? 'text' : 'password'}
            name="confirm"
            placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÄ‡∏î‡∏¥‡∏°‡∏≠‡∏µ‡∏Å‡∏Ñ‡∏£‡∏±‡πâ‡∏á"
            value={form.confirm}
            onChange={onChange}
            className={pwMismatch ? 'is-invalid' : ''}
          />
          {pwMismatch && <div className="hint-error">‡∏£‡∏´‡∏±‡∏™‡∏ú‡πà‡∏≤‡∏ô‡πÑ‡∏°‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ô</div>}
        </label>

        {/* ‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£ */}
        <label className="field col">
          <span className="label">‡πÄ‡∏ö‡∏≠‡∏£‡πå‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠</span>
          <input
            type="tel" name="phone" placeholder="‡∏Å‡∏£‡∏≠‡∏Å‡πÄ‡∏ö‡∏≠‡∏£‡πå‡πÇ‡∏ó‡∏£‡∏ó‡∏µ‡πà‡∏™‡∏≤‡∏°‡∏≤‡∏£‡∏ñ‡∏ï‡∏¥‡∏î‡∏ï‡πà‡∏≠‡πÑ‡∏î‡πâ"
            value={form.phone} onChange={onChange}
          />
        </label>

        {/* ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà (‡∏ã‡πâ‡∏≤‡∏¢) */}
        <div className="col span-2-left">
          <span className="label">‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà</span>

          {!!addrLoadErr && (
            <div className="hint-error" style={{ marginBottom: 8 }}>{addrLoadErr}</div>
          )}

          <div className="addr-grid">
            {/* ‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà/‡∏´‡∏°‡∏π‡πà/‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ */}
            <input
              type="text" name="houseNo"
              placeholder="‡∏ö‡πâ‡∏≤‡∏ô‡πÄ‡∏•‡∏Ç‡∏ó‡∏µ‡πà / ‡∏´‡∏°‡∏π‡πà / ‡∏≠‡∏≤‡∏Ñ‡∏≤‡∏£ (‡∏ñ‡πâ‡∏≤‡∏°‡∏µ)"
              value={form.houseNo} onChange={onChange}
            />
            {/* ‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î */}
            <select
              name="province"
              value={form.province}
              onChange={onChange}
              disabled={!addrData}
            >
              <option value="">{addrData ? '‚Äî ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î ‚Äî' : '‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î...'}</option>
              {provinces.map(pv => <option key={pv} value={pv}>{pv}</option>)}
            </select>

            {/* ‡∏≠‡∏≥‡πÄ‡∏†‡∏≠ */}
            <select
              name="amphoe"
              value={form.amphoe}
              onChange={onChange}
