// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import axios from 'axios';

/* ============ Axios Global Config ============ */
// ดึง BASE URL ของ API จาก .env (CRA ต้องขึ้นต้นด้วย REACT_APP_)
// ถ้าไม่ตั้งค่าไว้ จะ fallback เป็น http://localhost:3001
const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:3001/api';
const IS_DEV = process.env.NODE_ENV !== 'production';

// ตัด "/" ท้าย baseURL กัน // ซ้อน
axios.defaults.baseURL = RAW_BASE.replace(/\/+$/, '');

// ตั้ง timeout 10 วินาที ป้องกัน req ค้างนานเกินไป
axios.defaults.timeout = 10000;

// แนบ token ให้ axios ตั้งแต่หน้าโหลดครั้งแรก (กันเผื่อ request แรกวิ่งก่อน interceptor)
const bootToken = localStorage.getItem('token');
if (bootToken) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;
}

// อย่าตั้ง Content-Type แบบ global เป็น application/json
// เพราะจะกวนตอนส่ง FormData (อัปโหลดรูป) — ปล่อยให้ axios ใส่เองตามชนิดข้อมูลดีที่สุด

// ---------- Helper: สร้าง URL ไว้ log ----------
function buildUrl(cfg) {
  try {
    const b = (cfg.baseURL || '').replace(/\/+$/, '');
    const u = (cfg.url || '');
    // ถ้า u เป็น absolute (เช่น http://...) ก็แสดง u ตรง ๆ
    if (/^https?:\/\//i.test(u)) return u;
    return b + (u.startsWith('/') ? u : `/${u}`);
  } catch {
    return cfg?.url || '';
  }
}

// ---------- Request Interceptor ----------
axios.interceptors.request.use((cfg) => {
  // อ่าน token ทุกครั้ง (รองรับกรณีเพิ่ง login/logout)
  const t = localStorage.getItem('token');
  if (t) cfg.headers.Authorization = `Bearer ${t}`;

  // เก็บเวลาเริ่มไว้คำนวณ duration (ใช้เฉพาะ log)
  cfg.metadata = { start: Date.now() };

  // Dev logger (ดู method/URL/params/data แบบย่อ)
  if (IS_DEV) {
    // ข้อมูล request ขนาดใหญ่ไม่ต้อง log เต็ม ป้องกันรก console
    // eslint-disable-next-line no-console
    console.log(
      '➡️',
      (cfg.method || 'GET').toUpperCase(),
      buildUrl(cfg),
      cfg.params ? { params: cfg.params } : '',
      cfg.data && !(cfg.data instanceof FormData) ? { data: cfg.data } : ''
    );
  }
  return cfg;
});

// ---------- Response Interceptor ----------
axios.interceptors.response.use(
  (res) => {
    if (IS_DEV) {
      const ms = Date.now() - (res.config.metadata?.start || Date.now());
      // eslint-disable-next-line no-console
      console.log(
        '✅',
        res.status,
        (res.config.method || 'GET').toUpperCase(),
        buildUrl(res.config),
        `${ms}ms`
      );
    }
    return res;
  },
  (err) => {
    const { response, config } = err;
    if (IS_DEV && config) {
      const ms = Date.now() - (config.metadata?.start || Date.now());
      // eslint-disable-next-line no-console
      console.warn(
        '❌',
        response?.status || 'NETWORK',
        (config.method || 'GET').toUpperCase(),
        buildUrl(config),
        `${ms}ms`,
        err.message
      );
    }

    // ถ้า 401 → token อาจหมดอายุ/ไม่ถูกต้อง: เคลียร์ทิ้ง
    if (response?.status === 401) {
      localStorage.removeItem('token');
      // ถ้าอยากเด้งไปหน้า login อัตโนมัติ เปิดบรรทัดล่างนี้
      // window.location.assign('/login');
    }
    return Promise.reject(err);
  }
);

/* ============== React Mount ============== */
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

reportWebVitals();
