// src/components/ThaiAddressPicker.js
// หน้าที่: ให้ผู้ใช้เลือก จังหวัด -> อำเภอ -> ตำบล และเติมรหัสไปรษณีย์อัตโนมัติ
import React, { useEffect, useMemo, useState } from 'react';

export default function ThaiAddressPicker({
  value,
  onChange,
  dataUrl = '/data/thai-address.min.json', // เปลี่ยน path ได้ถ้าเก็บไฟล์ไว้ที่อื่น
  disabled = false,
}) {
  // value ควรเป็นอ็อบเจ็กต์จากฟอร์มหลัก เช่น { province, district, subdistrict, postal_code }
  const [data, setData] = useState(null); // { provinces, districts, subdistricts }
  const [loading, setLoading] = useState(true);

  // โหลดข้อมูลครั้งแรก
  useEffect(() => {
    let alive = true;
    (async () => {
      try {
        const res = await fetch(dataUrl);
        const json = await res.json();
        if (!alive) return;
        setData(json);
      } catch (e) {
        console.error('โหลดข้อมูลจังหวัดไม่สำเร็จ:', e);
      } finally {
        if (alive) setLoading(false);
      }
    })();
    return () => { alive = false; };
  }, [dataUrl]);

  // สร้างรายการตามที่เลือก
  const provinces = data?.provinces || [];
  const districtsAll = data?.districts || [];
  const subdistrictsAll = data?.subdistricts || [];

  const districts = useMemo(() => {
    if (!value?.province) return [];
    // หา province_code จากชื่อจังหวัด (หรือจะเก็บ code ไว้ใน value ก็ได้)
    // เพื่อความง่าย สมมติว่า value.province คือ "ชื่อจังหวัด"
    const prov = provinces.find(p => p.name_th === value.province);
    if (!prov) return [];
    return districtsAll.filter(d => d.province_code === prov.code);
  }, [value?.province, provinces, districtsAll]);

  const subdistricts = useMemo(() => {
    if (!value?.district) return [];
    // หา district_code จากชื่ออำเภอ
    const dist = districts.find(d => d.name_th === value.district);
    if (!dist) return [];
    return subdistrictsAll.filter(s => s.district_code === dist.code);
  }, [value?.district, districts, subdistrictsAll]);

  // helper: เปลี่ยนค่าแล้วแจ้งกลับฟอร์มหลัก
  const patch = (obj) => onChange && onChange(obj);

  const handleProvince = (e) => {
    const name = e.target.value || '';
    patch({
      province: name,
      district: '',       // reset ขั้นล่าง
      subdistrict: '',
      postal_code: '',
    });
  };
  const handleDistrict = (e) => {
    const name = e.target.value || '';
    patch({
      district: name,
      subdistrict: '',
      postal_code: '',
    });
  };
  const handleSubdistrict = (e) => {
    const name = e.target.value || '';
    // เติม zip อัตโนมัติ
    const sub = subdistricts.find(s => s.name_th === name);
    patch({
      subdistrict: name,
      postal_code: sub?.zip_code || '',
    });
  };

  // sync: ถ้าเปิดหน้าในโหมดแก้ไข (มีค่ามาอยู่แล้ว) เราไม่ต้องทำอะไรพิเศษ
  // แค่ปล่อยให้ useMemo กรองรายการตามค่า value ที่ส่งเข้ามา

  return (
    <div style={{ display: 'grid', gap: 8 }}>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: 8 }}>
        {/* จังหวัด */}
        <select value={value?.province || ''} onChange={handleProvince} disabled={disabled || loading}>
          <option value="">-- เลือกจังหวัด --</option>
          {provinces.map(p => (
            <option key={p.code} value={p.name_th}>{p.name_th}</option>
          ))}
        </select>

        {/* อำเภอ */}
        <select value={value?.district || ''} onChange={handleDistrict} disabled={disabled || !value?.province || loading}>
          <option value="">-- เลือกอำเภอ --</option>
          {districts.map(d => (
            <option key={d.code} value={d.name_th}>{d.name_th}</option>
          ))}
        </select>

        {/* ตำบล */}
        <select value={value?.subdistrict || ''} onChange={handleSubdistrict} disabled={disabled || !value?.district || loading}>
          <option value="">-- เลือกตำบล --</option>
          {subdistricts.map(s => (
            <option key={s.code} value={s.name_th}>{s.name_th}</option>
          ))}
        </select>
      </div>

      {/* รหัสไปรษณีย์ (ออโต้จากตำบล แต่ปล่อยให้แก้ไขได้เผื่อเคสพิเศษ) */}
      <input
        placeholder="รหัสไปรษณีย์"
        value={value?.postal_code || ''}
        onChange={e => patch({ postal_code: e.target.value })}
        disabled={disabled || loading}
      />
    </div>
  );
}
