// [FRONTEND] src/admin/ProductManagement.js
// ... (à¹€à¸«à¸¡à¸·à¸­à¸™à¹€à¸”à¸´à¸¡à¸ªà¹ˆà¸§à¸™ import / helpers)
import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

const asStr = (v) => (v === null || v === undefined) ? '' : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? '';

const API_BASE = (api?.defaults?.baseURL) || process.env.REACT_APP_API_BASE || 'http://localhost:3001';
const ABS_BASE = String(API_BASE).replace(/\/+$/,'');
const resolveUrl = (u) => {
  if (!u) return '';
  const s = String(u);
  if (/^(https?:|blob:)/i.test(s)) return s;
  if (s.startsWith('/uploads/')) return `${ABS_BASE}${s}`;
  return s;
};

const DEFAULT_PRODUCT_UNITS = [
  { code: 'plant', label: 'à¸•à¹‰à¸™' }, { code: 'piece', label: 'à¸Šà¸´à¹‰à¸™' }, { code: 'pot', label: 'à¸à¸£à¸°à¸–à¸²à¸‡' },
  { code: 'bag', label: 'à¸–à¸¸à¸‡' }, { code: 'pack', label: 'à¹à¸žà¹‡à¸„' }, { code: 'kg', label: 'à¸à¸´à¹‚à¸¥à¸à¸£à¸±à¸¡' },
  { code: 'g', label: 'à¸à¸£à¸±à¸¡' }, { code: 'ltr', label: 'à¸¥à¸´à¸•à¸£' }, { code: 'ml', label: 'à¸¡à¸´à¸¥à¸¥à¸´à¸¥à¸´à¸•à¸£' },
];
const DEFAULT_SIZE_UNITS = [
  { code: 'mm', label: 'à¸¡à¸¡.' }, { code: 'cm', label: 'à¸‹à¸¡.' }, { code: 'm', label: 'à¸¡.' },
  { code: 'inch', label: 'à¸™à¸´à¹‰à¸§' }, { code: 'kg', label: 'à¸à¸.' }, { code: 'ltr', label: 'à¸¥à¸´à¸•à¸£' },
];

export default function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);
  const [activeTab, setActiveTab] = useState('products');

  const [productUnits, setProductUnits] = useState(DEFAULT_PRODUCT_UNITS);
  const [sizeUnits, setSizeUnits] = useState(DEFAULT_SIZE_UNITS);
  const [newProdUnit, setNewProdUnit] = useState('');
  const [newSizeUnit, setNewSizeUnit] = useState('');

  const [form, setForm] = useState({
    product_name:'', description:'', selling_price:'', category_id:'', subcategory_id:'',
    image_url:'', unit:'', size_value:'', size_unit:'', origin:'', product_status_id:''
  });
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  const [newCat, setNewCat] = useState({ category_id:'', category_name:'' });
  const [newSub, setNewSub] = useState({ subcategory_id:'', subcategory_name:'', category_id:'' });

  const handleApiError = (err, fallback='à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”') => {
    console.error(fallback, err?.response?.data || err?.message || err);
    alert(err?.response?.data?.error || err?.message || fallback);
  };

  const fetchProducts = async () => {
    try {
      const { data } = await api.get(path('/admin/products'), { params:{ include_archived: showArchived?1:0 } });
      setProducts((Array.isArray(data?.items)?data.items:data) || []);
    } catch (e) { handleApiError(e, 'âŒ à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  };
  const fetchCategories = async () => {
    try { const { data } = await api.get(path('/categories')); setCategories(data||[]); }
    catch (e) { handleApiError(e, 'âŒ à¹‚à¸«à¸¥à¸”à¸›à¸£à¸°à¹€à¸ à¸—à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  };
  const fetchSubcategories = async () => {
    try { const { data } = await api.get(path('/subcategories')); setSubcategories(data||[]); }
    catch (e) { handleApiError(e, 'âŒ à¹‚à¸«à¸¥à¸”à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  };
  const fetchStatuses = async () => {
    try {
      let res = await api.get(path('/product-status'));
      if (!Array.isArray(res.data)) { try { res = await api.get(path('/product-statuses')); } catch {} }
      setStatuses((res.data||[]).map(x=>({ id:asStr(x?.product_status_id ?? x?.ProductStatusID ?? x?.id), name:pickStatusName(x) })));
    } catch { setStatuses([]); }
  };

  async function uploadImage(file){
    if(!file) return null;
    const sendWith = async (field)=>{
      const fd = new FormData(); fd.append(field,file);
      const res = await api.post(path('/upload'), fd, { headers:{'Content-Type':'multipart/form-data'} });
      return res?.data?.imageUrl || res?.data?.url || res?.data?.path || res?.data?.location || null;
    };
    try { return await sendWith('file'); }
    catch(e1){ try { return await sendWith('image'); } catch(e2){ try { return await sendWith('photo'); } catch{ throw e2; } } }
  }
  async function uploadSubcatImage(file){
    const fd = new FormData(); fd.append('file', file);
    const res = await api.post(path('/uploads/subcategory-image'), fd, { headers:{'Content-Type':'multipart/form-data'} });
    return res?.data?.url || null;
  }
  async function uploadCategoryImage(file){
    const fd = new FormData(); fd.append('file', file);
    const res = await api.post(path('/uploads/category-image'), fd, { headers:{'Content-Type':'multipart/form-data'} });
    return res?.data?.url || null;
  }

  useEffect(()=>{ (async()=>{ setLoading(true); await Promise.all([fetchProducts(),fetchCategories(),fetchSubcategories(),fetchStatuses()]); setLoading(false); })(); }, [showArchived]);

  const onChange = (e)=>{ const {name,value}=e.target; setForm(p=>({ ...p, [name]:value, ...(name==='category_id'?{ subcategory_id:'' }:{} ) })); };
  const onFiles = (e)=> setSelectedFiles(Array.from(e.target.files||[]));
