import React, { useEffect, useState } from "react";
import "./CartPage.css";
import { Link } from "react-router-dom";
import {
  getCart,
  updateQty,
  removeItem,
  clearCart,
  getTotal,
} from "../lib/cart";

export default function CartPage() {
  const [items, setItems] = useState(getCart());
  const [total, setTotal] = useState(getTotal());

  // sync เมื่อ cart เปลี่ยนจากหน้าอื่น (เช่น เพิ่มจากหน้า Home)
  useEffect(() => {
    const onChanged = () => {
      setItems(getCart());
      setTotal(getTotal());
    };
    window.addEventListener("cart:changed", onChanged);
    return () => window.removeEventListener("cart:changed", onChanged);
  }, []);

  const onQty = (id, q) => {
    updateQty(id, q);
    setItems(getCart());
    setTotal(getTotal());
  };

  const onRemove = (id) => {
    if (confirm("ลบสินค้านี้ออกจากตะกร้า?")) {
      removeItem(id);
      setItems(getCart());
      setTotal(getTotal());
    }
  };

  const onClear = () => {
    if (confirm("ล้างตะกร้าทั้งหมด?")) {
      clearCart();
      setItems([]);
      setTotal(0);
    }
  };

  if (!items.length) {
    return (
      <div className="cart-page">
        <h2>🛒 ตะกร้าสินค้า</h2>
        <div className="empty-cart">
          <p>ตะกร้าของคุณยังว่าง</p>
          <Link to="/plants" className="btn-primary">เลือกซื้อสินค้า</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="cart-page">
      <h2>🛒 ตะกร้าสินค้า</h2>

      <table className="cart-table">
        <thead>
          <tr>
            <th>สินค้า</th>
            <th>ราคา</th>
            <th>จำนวน</th>
            <th>รวม</th>
            <th></th>
          </tr>
        </thead>
        <tbody>
          {items.map((it) => {
            const imgSrc =
              it.img || it.image || it.thumbnail || process.env.PUBLIC_URL + "/logo.png";
            const qty = Number(it.quantity || 1);
            const price = Number(it.price || 0);
            return (
              <tr key={it.id}>
                <td className="cart-product">
                  <img src={imgSrc} alt={it.name} />
                  <div className="p-info">
                    <div className="p-name">{it.name}</div>
                    {it.variant && <div className="p-variant">{it.variant}</div>}
                  </div>
                </td>
                <td>{price.toLocaleString()} บาท</td>
                <td>
                  <div className="cart-actions">
                    <button onClick={() => onQty(it.id, Math.max(1, qty - 1))}>-</button>
                    <span>{qty}</span>
                    <button onClick={() => onQty(it.id, qty + 1)}>+</button>
                  </div>
                </td>
                <td className="cart-total">
                  {(price * qty).toLocaleString()} บาท
                </td>
                <td>
                  <button className="remove-btn" onClick={() => onRemove(it.id)}>🗑</button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      <div className="cart-footer">
        <div className="cart-left">
          <Link to="/plants" className="btn-outline">← เลือกซื้อสินค้าต่อ</Link>
          <button className="btn-ghost" onClick={onClear}>ล้างตะกร้า</button>
        </div>
        <div className="cart-right">
          <div className="summary-row">
            <span>ยอดรวมสินค้า</span>
            <span>{total.toLocaleString()} บาท</span>
          </div>
          <div className="summary-row grand">
            <span>ชำระทั้งหมด</span>
            <span>{total.toLocaleString()} บาท</span>
          </div>
          <button
            className="btn-primary"
            onClick={() => alert("ไปหน้าชำระเงิน (TODO)")}
          >
            ดำเนินการชำระเงิน →
          </button>
        </div>
      </div>
    </div>
  );
}
