      return;
    }

    try {
      setLoading(true);

      // baseURL = http://localhost:3001 (‡∏ï‡∏±‡πâ‡∏á‡πÉ‡∏ô src/index.js) ‚Üí ‡∏ï‡πâ‡∏≠‡∏á‡πÉ‡∏™‡πà /api ‡∏ó‡∏µ‡πà‡∏ô‡∏µ‡πà
