      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="login-page">
      {/* ‡∏ù‡∏±‡πà‡∏á‡πÇ‡∏õ‡∏£‡πÇ‡∏°‡∏ó */}
      <aside className="promo">
        <div className="promo-inner">
          <div className="promo-badge">GARDEN PRODUCT ONLINE</div>
          <h1>üåø ‡∏¢‡∏¥‡∏ô‡∏î‡∏µ‡∏ï‡πâ‡∏≠‡∏ô‡∏£‡∏±‡∏ö‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà ‡∏£‡πâ‡∏≤‡∏ô‡∏õ‡∏£‡∏≤‡∏ä‡∏ç‡πå‡πÅ‡∏°‡πà‡πÇ‡∏à‡πâ üåø</h1>
