// FRONTEND: src/pages/Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
// ถ้าไฟล์ Login.css อยู่ใน src/pages ให้คง './Login.css'
// ถ้าอยู่ใน src (รากของ frontend) ให้ใช้แบบนี้แทน: ../Login.css
import './Login.css'; // ← เปลี่ยนเป็น '../Login.css' หาก CSS อยู่ข้างนอกโฟลเดอร์ pages

export default function Login() {
  const navigate = useNavigate();

  const [form, setForm] = useState({ username: '', password: '', showPw: false });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

    const username = form.username.trim();
    const password = form.password.trim();
    if (!username || !password) {
      setError('กรุณากรอกข้อมูลให้ครบถ้วน');
      return;
    }

    try {
      setLoading(true);

      // baseURL = http://localhost:3001 (ตั้งใน src/index.js) → ต้องใส่ /api ที่นี่
      const res = await axios.post('/api/auth/login', {
        username,
        email: username,        // รองรับทั้ง email/username
        password,
      });

      const token =
        res.data?.token ||
        res.data?.accessToken ||
        res.data?.jwt;

      const role =
        res.data?.role ||
        res.data?.user?.role ||
        'user';

      if (!token) throw new Error('ไม่พบ token จากเซิร์ฟเวอร์');

      localStorage.setItem('token', token);
      localStorage.setItem('role', role);
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // นำทาง: admin → จัดการสินค้า, อื่น ๆ → หน้าแรก
      if (String(role).toLowerCase() === 'admin') {
        navigate('/admin/products', { replace: true });
      } else {
        navigate('/', { replace: true });
      }
    } catch (e) {
      const msg =
        e?.response?.status === 401 ? 'อีเมล/ชื่อผู้ใช้ หรือรหัสผ่านไม่ถูกต้อง'
        : e?.code === 'ERR_NETWORK'   ? 'เชื่อมต่อเซิร์ฟเวอร์ไม่ได้ (ตรวจสอบ backend พอร์ต 3001)'
        : e?.response?.data?.error    || e?.message || 'เข้าสู่ระบบล้มเหลว';
      setError(msg);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="login-page">
      {/* ฝั่งโปรโมท */}
      <aside className="promo">
        <div className="promo-inner">
          <div className="promo-badge">GARDEN PRODUCT ONLINE</div>
          <h1>🌿 ยินดีต้อนรับเข้าสู่ ร้านปราชญ์แม่โจ้ 🌿</h1>
          <p>กว่า 100 ชนิด ราคาเป็นมิตร พร้อมบริการจัดส่งรวดเร็ว</p>
        </div>
      </aside>

      {/* ฟอร์มล็อกอิน */}
      <main className="panel">
        <div className="login-card">
          <div className="brand">
            <Link to="/" className="brand-name">PRACH MAEJO</Link>
            <p className="brand-sub">กรุณาเข้าสู่ระบบ</p>
          </div>

          <form className="login-form" onSubmit={onSubmit} noValidate>
            <label className="field">
              <span className="label">อีเมล / ชื่อผู้ใช้</span>
              <div className="input-wrap">
                <input
                  type="text"
                  name="username"
                  placeholder="กรอกอีเมลหรือชื่อผู้ใช้"
                  value={form.username}
                  onChange={onChange}
                  required
                />
              </div>
            </label>

            <label className="field">
              <span className="label">รหัสผ่าน</span>
              <div className="input-wrap">
                <input
                  type={form.showPw ? 'text' : 'password'}
                  name="password"
                  placeholder="********"
                  value={form.password}
                  onChange={onChange}
                  required
                />
                <button
                  type="button"
                  className="pw-toggle"
                  onClick={() => setForm(p => ({ ...p, showPw: !p.showPw }))}>
                  {form.showPw ? '🙈' : '👁️'}
                </button>
              </div>
            </label>

            {error && <div className="form-error" role="alert">{error}</div>}

            <button className="btn-primary" type="submit" disabled={loading}>
              {loading ? 'กำลังเข้าสู่ระบบ…' : 'เข้าสู่ระบบ'}
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}
