import React, { useEffect, useMemo, useState } from "react";
import { api } from "../lib/api";
import { toast } from "react-hot-toast";
import "./UserManagement.css";

export default function UserManagement() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [q, setQ] = useState("");
  const [roleFilter, setRoleFilter] = useState("all");
  const [busyId, setBusyId] = useState(null);

  async function loadUsers() {
    setLoading(true);
    try {
      const url = `/api/admin/users?q=${encodeURIComponent(q)}&role=${roleFilter}`;
      const res = await api.get(url);
      const data = Array.isArray(res) ? res : (res?.items || res?.data?.items || []);
      setUsers(data);
    } catch (err) {
      console.error("Load users failed:", err);
      toast.error("โหลดรายชื่อผู้ใช้ไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  // ✅ เปลี่ยนบทบาท: ยืนยัน + ขอรหัสผ่านแอดมิน + แจ้งเตือน
  async function setRole(user, makeRole) {
    const userId = user.id ?? user.user_id;
    const target = user.email || user.username || `ผู้ใช้#${userId}`;
    const actionText = makeRole === "admin" ? "ตั้งเป็น admin" : "ลดสิทธิ์เป็น user";

    // ยืนยันเบื้องต้น
    if (!window.confirm(`ยืนยันการ${actionText}\n\nบัญชี: ${target}`)) return;

    // ขอรหัสผ่านแอดมินเพื่อยืนยัน (กรอกในกล่อง prompt แบบง่าย)
    const pw = window.prompt("กรอกรหัสผ่านแอดมินเพื่อยืนยัน:", "");
    if (pw === null) return; // กด Cancel
    if (!pw.trim()) {
      toast.error("กรุณากรอกรหัสผ่านยืนยัน");
      return;
    }

    setBusyId(userId);
    const tId = toast.loading(`${actionText} กำลังดำเนินการ…`);
    try {
      const res = await api.patch(`/api/admin/users/${userId}/role`, {
        make: makeRole,
        confirm_password: pw.trim(),
      });
      const updated = res?.user || res?.data?.user;

      if (updated) {
        setUsers((prev) => prev.map((u) => String(u.id ?? u.user_id) === String(userId) ? updated : u));
      } else {
        // fallback optimistic
        setUsers((prev) =>
          prev.map((u) =>
            String(u.id ?? u.user_id) === String(userId)
              ? { ...u, role: makeRole, is_admin: makeRole === "admin" }
              : u
          )
        );
      }

      toast.success(`${actionText} สำเร็จ: ${target}`, { id: tId });
    } catch (err) {
      console.error("Update role failed:", err);
      const msg =
        err?.response?.data?.message ||
        err?.response?.data?.detail ||
        err?.response?.data?.error ||
        "อัปเดตสิทธิ์ไม่สำเร็จ";
      toast.error(String(msg), { id: tId });
    } finally {
      setBusyId(null);
    }
  }

  useEffect(() => { loadUsers(); }, []);
  useEffect(() => {
    const t = setTimeout(loadUsers, 300);
    return () => clearTimeout(t);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [q, roleFilter]);

  const filtered = useMemo(() => {
    const list = users || [];
    if (!q && (roleFilter === "all" || !roleFilter)) return list;
    const ql = q.trim().toLowerCase();
    return list.filter((u) => {
      const name = (u.full_name || u.name || "").toLowerCase();
      const email = (u.email || "").toLowerCase();
      const username = (u.username || "").toLowerCase();
      const matchQ = ql ? (name.includes(ql) || email.includes(ql) || username.includes(ql)) : true;
      const role = (u.role || (u.is_admin ? "admin" : "user")) || "user";
      const matchRole = roleFilter === "all" ? true : role === roleFilter;
      return matchQ && matchRole;
    });
  }, [users, q, roleFilter]);

  return (
    <div className="user-page">
      <h2>จัดการสถานะผู้ใช้</h2>

      <div className="toolbar">
        <input
          placeholder="ค้นชื่อ/อีเมล/username…"
          value={q}
          onChange={(e) => setQ(e.target.value)}
        />
        <select value={roleFilter} onChange={(e) => setRoleFilter(e.target.value)}>
          <option value="all">ทั้งหมด</option>
          <option value="admin">admin</option>
          <option value="user">user</option>
        </select>
        <button onClick={loadUsers} disabled={loading}>
          รีเฟรช
        </button>
      </div>

      {loading ? (
        <p>กำลังโหลด...</p>
      ) : (
        <table className="user-table">
          <thead>
            <tr>
              <th>#</th>
              <th style={{ textAlign: "left" }}>ชื่อ</th>
              <th style={{ textAlign: "left" }}>อีเมล / ผู้ใช้</th>
              <th>บทบาท</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {filtered.map((u, i) => {
              const id = u.id ?? u.user_id;
              const isAdmin = !!u.is_admin || (u.role === "admin") || (u.role === "admin");
              const role = u.role || (isAdmin ? "admin" : "user");
              return (
                <tr key={id}>
                  <td>{i + 1}</td>
                  <td style={{ textAlign: "left" }}>{u.full_name || u.name || "-"}</td>
                  <td style={{ textAlign: "left" }}>{u.email || u.username || "-"}</td>
                  <td>
                    <span className={`badge ${isAdmin ? "badge-admin" : ""}`}>
                      {role}
                    </span>
                  </td>
                  <td className="actions">
                    {isAdmin ? (
                      <button
                        className="ghost"
                        disabled={busyId === id}
                        onClick={() => setRole(u, "user")}
                      >
                        {busyId === id ? "..." : "ลดสิทธิ์เป็น user"}
                      </button>
                    ) : (
                      <button
                        className="primary"
                        disabled={busyId === id}
                        onClick={() => setRole(u, "admin")}
                      >
                        {busyId === id ? "..." : "ตั้งเป็น admin"}
                      </button>
                    )}
                    <select
                      value={role}
                      disabled={busyId === id}
                      onChange={(e) => setRole(u, e.target.value)}
                    >
                      <option value="user">user</option>
                      <option value="admin">admin</option>
                    </select>
                  </td>
                </tr>
              );
            })}
            {!filtered.length && (
              <tr>
                <td colSpan={5} style={{ textAlign: "center", color: "#999" }}>
                  ไม่พบผู้ใช้ตามเงื่อนไขที่เลือก
                </td>
              </tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}
