// FRONTEND: src/pages/HomeUser.js — เวอร์ชันสมบูรณ์ (พร้อมค้นหา + responsive + ปุ่มตะกร้า)
import React, { useEffect, useMemo, useState } from "react";
import { Link } from "react-router-dom";
import "./HomeUser.css";
import { api, path, mediaSrc } from "../lib/api";
import { addItem as addToCart } from "../lib/cart";
import Footer from "../components/Footer";

/* ---------------- Helpers ---------------- */
const toArray = (d) =>
  Array.isArray(d)
    ? d
    : Array.isArray(d?.items)
    ? d.items
    : Array.isArray(d?.data?.items)
    ? d.data.items
    : Array.isArray(d?.data)
    ? d.data
    : [];

const normalizeProduct = (p, idx) => {
  const imgPath =
    p.image_url || p.cover_url || p.image || (Array.isArray(p.images) ? p.images[0] : "");
  const img = mediaSrc(imgPath);
  const price =
    Number(
      p.min_price ??
        p.selling_price ??
        p.price ??
        p.unit_price ??
        p.product_price ??
        0
    ) || 0;

  return {
    id: p.product_id ?? p.id ?? `p-${idx}`,
    name: p.product_name ?? p.name_th ?? p.name ?? p.title ?? "",
    price,
    img,
    category_id: p.category_id ?? p.cat_id ?? "",
    subcategory_id: p.subcategory_id ?? p.subcat_id ?? "",
    category_name: p.category_name ?? p.category ?? "",
    subcategory_name: p.subcategory_name ?? p.subcategory ?? "",
  };
};

/* ---------------- API Helpers ---------------- */
async function fetchWithCount(url, want) {
  const sets = [
    { limit: want },
    { take: want },
    { per_page: want },
    { pageSize: want },
    { top: want },
    {},
  ];
  for (const ps of sets) {
    try {
      const r = await api.get(path(url), { params: { ...ps, _: Date.now() } });
      const arr = toArray(r);
      if (arr.length) return arr;
    } catch {}
  }
  return [];
}

async function serverSearch(mode, value, want = 100) {
  const tryList = [];
  if (mode === "keyword") {
    tryList.push(["/products/search", { q: value, limit: want }]);
    tryList.push(["/products", { q: value, limit: want }]);
    tryList.push(["/products", { keyword: value, limit: want }]);
  } else if (mode === "category") {
    tryList.push(["/products", { category_id: value, limit: want }]);
    tryList.push(["/products/by-category", { id: value, limit: want }]);
  } else if (mode === "subcategory") {
    tryList.push(["/products", { subcategory_id: value, limit: want }]);
    tryList.push(["/products/by-subcategory", { id: value, limit: want }]);
  }

  for (const [ep, params] of tryList) {
    try {
      const r = await api.get(path(ep), { params: { ...params, _: Date.now() } });
      const arr = toArray(r);
      if (arr.length) return arr;
    } catch {}
  }
  return [];
}

/* ---------------- Component ---------------- */
export default function HomeUser() {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [searchResults, setSearchResults] = useState(null);
  const [loading, setLoading] = useState({
    cat: true,
    sub: true,
    best: true,
    all: true,
    search: false,
  });
  const [err, setErr] = useState({ search: "", all: "" });

  /* ----- Search State ----- */
  const [mode, setMode] = useState("keyword");
  const [keyword, setKeyword] = useState("");
  const [catValue, setCatValue] = useState("");
  const [subValue, setSubValue] = useState("");

  /* ---------------- Loaders ---------------- */
  useEffect(() => {
    (async () => {
      try {
        const res = await api.get(path("/categories"), { params: { _: Date.now() } });
        const raw = toArray(res).map((c, idx) => ({
          id: c.category_id ?? c.id ?? `cat-${idx}`,
          name: c.category_name ?? c.name_th ?? c.name ?? "",
          slug: String(c.slug || "").toLowerCase(),
          image: c.image_url ? mediaSrc(c.image_url) : "",
        }));
        setCategories(raw);
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
  }, []);

  useEffect(() => {
    (async () => {
      try {
        const res = await api.get(path("/subcategories"), { params: { _: Date.now() } });
        setSubcategories(
          toArray(res).map((s, i) => ({
            id: String(s.subcategory_id ?? s.id ?? `sub-${i}`),
            name: s.subcategory_name ?? s.name_th ?? s.name ?? "",
            category_id: String(s.category_id ?? s.cat_id ?? ""),
          }))
        );
      } finally {
        setLoading((s) => ({ ...s, sub: false }));
      }
    })();
  }, []);

  useEffect(() => {
    (async () => {
      try {
        const got = await fetchWithCount("/products/best-sellers", 8);
        setBestSellers(got.map((p, i) => normalizeProduct(p, i)));
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
  }, []);

  useEffect(() => {
    (async () => {
      try {
        const got = await fetchWithCount("/products", 100);
        setAllProducts(got.map((p, i) => normalizeProduct(p, i)));
      } finally {
        setLoading((s) => ({ ...s, all: false }));
      }
    })();
  }, []);

  /* ---------------- Search Logic ---------------- */
  const clientFilter = useMemo(() => {
    return (mode, value) => {
      if (!value) return [];
      const val = String(value).toLowerCase();
      if (mode === "keyword") {
        return allProducts.filter(
          (p) =>
            p.name.toLowerCase().includes(val) ||
            String(p.id).toLowerCase().includes(val)
        );
      }
      if (mode === "category") {
        return allProducts.filter(
          (p) =>
            p.category_id.toLowerCase() === val ||
            p.category_name.toLowerCase() === val
        );
      }
      if (mode === "subcategory") {
        return allProducts.filter(
          (p) =>
            p.subcategory_id.toLowerCase() === val ||
            p.subcategory_name.toLowerCase() === val
        );
      }
      return [];
    };
  }, [allProducts]);

  const onSearch = async (e) => {
    e.preventDefault();
    setSearchResults(null);
    setErr((x) => ({ ...x, search: "" }));
    setLoading((s) => ({ ...s, search: true }));

    try {
      let val = "";
      if (mode === "keyword") val = keyword.trim();
      if (mode === "category") val = catValue;
      if (mode === "subcategory") val = subValue;
      if (!val) return;

      const srv = await serverSearch(mode, val, 100);
      let list = srv.length
        ? srv.map((p, i) => normalizeProduct(p, i))
        : clientFilter(mode, val);
      setSearchResults(list);
      if (!list.length)
        setErr((x) => ({ ...x, search: "ไม่พบผลลัพธ์ที่ตรงกับเงื่อนไข" }));
    } finally {
      setLoading((s) => ({ ...s, search: false }));
    }
  };

  const listToShow = searchResults ?? allProducts;

  /* ---------------- UI ---------------- */
  return (
    <div className="home-container">
      {/* แถบค้นหา */}
      <form className="search-bar card" onSubmit={onSearch}>
        <div className="filters-row">
          <label>
            โหมดค้นหา
            <select value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="keyword">คีย์เวิร์ด</option>
              <option value="category">หมวดหมู่</option>
              <option value="subcategory">หมวดย่อย</option>
            </select>
          </label>

          {mode === "keyword" && (
            <label className="grow">
              คำค้นหา
              <input
                type="text"
                placeholder="เช่น กุหลาบ ดินปลูก ..."
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
              />
            </label>
          )}

          {mode === "category" && (
            <label className="grow">
              เลือกหมวดหมู่
              <select
                value={catValue}
                onChange={(e) => setCatValue(e.target.value)}
              >
                <option value="">— เลือกหมวดหมู่ —</option>
                {categories.map((c) => (
                  <option key={c.id} value={c.id}>
                    {c.name}
                  </option>
                ))}
              </select>
            </label>
          )}

          {mode === "subcategory" && (
            <label className="grow">
              เลือกหมวดย่อย
              <select
                value={subValue}
                onChange={(e) => setSubValue(e.target.value)}
              >
                <option value="">— เลือกหมวดย่อย —</option>
                {subcategories.map((s) => (
                  <option key={s.id} value={s.id}>
                    {s.name}
                  </option>
                ))}
              </select>
            </label>
          )}

          <button className="btn-search" disabled={loading.search}>
            <span className="btn-ico">🔍</span>
            {loading.search ? "กำลังค้นหา..." : "ค้นหา"}
          </button>
        </div>
        {!!err.search && <div className="info-inline warn">{err.search}</div>}
      </form>

      {/* หมวดหลัก */}
      <section className="type-explorer">
        <h2>🧭 สำรวจตามประเภท</h2>
        <div className="type-grid">
          {categories
            .filter((c) => ["plants", "tools"].includes(c.slug))
            .map((c) => (
              <Link key={c.id} to={`/${c.slug}`} className="type-card">
                {c.image ? (
                  <img src={c.image} alt={c.name} />
                ) : (
                  <div className="noimg">ไม่มีรูป</div>
                )}
                <h3>{c.name}</h3>
              </Link>
            ))}
        </div>
      </section>

      {/* สินค้าขายดี */}
      <section className="best-sellers">
        <h2>🌟 สินค้าขายดี</h2>
        <div className="product-grid fullwidth">
          {bestSellers.map((item) => (
            <div className="product-card" key={item.id}>
              <Link to={`/products/${item.id}`}>
                {item.img ? (
                  <img src={item.img} alt={item.name} />
                ) : (
                  <div className="noimg">ไม่มีรูป</div>
                )}
              </Link>
              <h3>
                <Link to={`/products/${item.id}`}>{item.name}</Link>
              </h3>
              <p>
                {item.price
                  ? `${item.price.toLocaleString()} บาท`
                  : "— บาท"}
              </p>
              <button className="btn-add" onClick={() => addToCart(item)}>
                + เพิ่มลงตะกร้า
              </button>
            </div>
          ))}
        </div>
      </section>

      {/* ทั้งหมด */}
      <section className="all-products">
        <h2>🛒 สินค้าทั้งหมด</h2>
        <div className="product-grid fullwidth">
          {listToShow.map((p) => (
            <div className="product-card" key={p.id}>
              <Link to={`/products/${p.id}`}>
                {p.img ? (
                  <img src={p.img} alt={p.name} />
                ) : (
                  <div className="noimg">ไม่มีรูป</div>
                )}
              </Link>
              <h3>
                <Link to={`/products/${p.id}`}>{p.name}</Link>
              </h3>
              <p>
                {p.price ? `${p.price.toLocaleString()} บาท` : "— บาท"}
              </p>
              <button className="btn-add" onClick={() => addToCart(p)}>
                + เพิ่มลงตะกร้า
              </button>
            </div>
          ))}
        </div>
      </section>

      <Footer />
    </div>
  );
}
