// src/admin/AdminLayout.js
import React from "react";
import { NavLink, Outlet, useLocation, useNavigate } from "react-router-dom";
import "./AdminLayout.css";

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  const onLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login", { replace: true });
  };

  const menus = [
    { label: "Dashboard",   icon: "üìä", path: "/admin" },
    { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤", icon: "üì¶", path: "/admin/products" },
    { label: "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á", icon: "üè¨", path: "/admin/inventory" },
    { label: "‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠",  icon: "üßæ", path: "/admin/orders" },
    { label: "‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢",  icon: "üí∞", path: "/admin/reports" },
  ];

  return (
    <div className="admin-shell">
      {/* Sidebar */}
      <aside className="admin-sidebar">
        <div className="brand">PRACH MAEJO</div>
        <nav className="menu">
          {menus.map(m => (
            <NavLink
              key={m.path}
              to={m.path}
              className={({ isActive }) =>
                "menu-item" + (isActive || location.pathname.startsWith(m.path) ? " active" : "")
              }
              end={m.path === "/admin"}
            >
              <span className="icon">{m.icon}</span>
              <span>{m.label}</span>
            </NavLink>
          ))}
        </nav>
      </aside>

      {/* Main */}
      <main className="admin-main">
        {/* Topbar ‚Äî ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏õ‡∏∏‡πà‡∏° '‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö' */}
        <header className="admin-topbar">
          <div className="left">
            <h1 className="title">ADMIN</h1>
          </div>
          <div className="right">
            <button className="btn btn-danger" onClick={onLogout}>‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö</button>
          </div>
        </header>

        <section className="admin-content">
          <Outlet />
        </section>
      </main>
    </div>
  );
}
