// src/pages/HomeUser.js
import React, { useEffect, useState, useMemo } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './HomeUser.css';

export default function HomeUser() {
  const nav = useNavigate();

  // ======================= ‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ (‡∏≠‡πà‡∏≤‡∏ô‡∏à‡∏≤‡∏Å localStorage) =======================
  const user = useMemo(() => {
    const fullname = localStorage.getItem('fullname') || '‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô';
    return {
      fullname,
      avatar: process.env.PUBLIC_URL + '/profile.jpg',
    };
  }, []);

  // ======================= ‡∏ï‡∏±‡∏ß‡∏ä‡πà‡∏ß‡∏¢ path API =======================
  const apiPath = (p) =>
    (axios.defaults.baseURL || '').replace(/\/+$/, '').endsWith('/api')
      ? p
      : '/api' + p;

  // ======================= ‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà & ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ç‡∏≤‡∏¢‡∏î‡∏µ =======================
  const [categories, setCategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [loading, setLoading] = useState({ cat: true, best: true });
  const [errMsg, setErrMsg] = useState('');

  // -------------------------- Fallback --------------------------
  const fallbackCategories = [
    {
      id: 'tools',
      slug: 'tools',
      name: '‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå‡πÄ‡∏û‡∏≤‡∏∞‡∏õ‡∏•‡∏π‡∏Å',
      image: process.env.PUBLIC_URL + '/opakorn.jpg',
    },
    {
      id: 'plants',
      slug: 'plants',
      name: '‡∏ï‡πâ‡∏ô‡πÑ‡∏°‡πâ',
      image: process.env.PUBLIC_URL + '/tonmai.jpg',
    },
  ];

  const fallbackBest = [
    { id: 1, name: '‡∏ï‡πâ‡∏ô‡∏Å‡∏•‡πâ‡∏≤‡∏•‡∏≥‡πÉ‡∏¢‡∏Ñ‡∏£‡∏¥‡∏™‡∏ï‡∏±‡∏•', price: 250, img: process.env.PUBLIC_URL + '/lamyai.jpg' },
    { id: 2, name: '‡∏ï‡πâ‡∏ô‡∏û‡∏ç‡∏≤‡∏ô‡∏≤‡∏Ñ‡∏£‡∏≤‡∏ä', price: 150, img: process.env.PUBLIC_URL + '/payanaka1.jpg' },
    { id: 3, name: '‡∏Å‡∏£‡∏∞‡∏ñ‡∏≤‡∏á‡∏°‡∏¥‡∏ô‡∏¥‡∏°‡∏≠‡∏•', price: 300, img: process.env.PUBLIC_URL + '/katar.jpeg' },
    { id: 4, name: '‡∏î‡∏¥‡∏ô‡∏™‡∏π‡∏ï‡∏£‡∏ó‡∏≤‡∏á‡∏£‡πâ‡∏≤‡∏ô', price: 99, img: process.env.PUBLIC_URL + '/din.jpg' },
  ];

  // ============================ ‡πÇ‡∏´‡∏•‡∏î‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà ============================
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, cat: true }));
        const res = await axios.get(apiPath('/categories?status=active'));
        const list = (Array.isArray(res.data?.data) ? res.data.data : res.data) || [];

        const norm = list.map((c, idx) => {
          const cid = c.category_id ?? c.id ?? c.slug ?? `cat-${idx}`;
          const rawName = (c.category_name ?? c.name ?? '').toString();
          const guessByName =
            rawName.includes('‡∏≠‡∏∏‡∏õ‡∏Å‡∏£‡∏ì‡πå') ? 'tools' :
            rawName.includes('‡∏ï‡πâ‡∏ô‡πÑ‡∏°‡πâ') ? 'plants' : undefined;

          const slug = c.slug ?? guessByName ?? String(cid).toLowerCase();

          return {
            id: String(cid),
            slug,
            name: rawName || '‡πÑ‡∏°‡πà‡∏£‡∏∞‡∏ö‡∏∏‡∏ä‡∏∑‡πà‡∏≠',
            image: c.image_url || (slug === 'tools'
              ? process.env.PUBLIC_URL + '/opakorn.jpg'
              : process.env.PUBLIC_URL + '/tonmai.jpg'),
          };
        });

        const order = ['tools', 'plants'];
        const picked = order.map((s) => norm.find((x) => x.slug === s)).filter(Boolean);
        setCategories(picked.length ? picked : fallbackCategories);
      } catch (err) {
        console.warn('load categories failed:', err.message);
        setCategories(fallbackCategories);
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
  }, []);

  // ======================= ‡πÇ‡∏´‡∏•‡∏î‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ç‡∏≤‡∏¢‡∏î‡∏µ =======================
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, best: true }));
        const res = await axios.get(apiPath('/products/best-sellers?limit=8'));
        setBestSellers(res.data?.data || fallbackBest);
      } catch (err) {
        console.warn('load best-sellers failed:', err.message);
        setBestSellers(fallbackBest);
        setErrMsg('‡πÅ‡∏™‡∏î‡∏á‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏à‡∏≤‡∏Å‡∏ï‡∏±‡∏ß‡∏≠‡∏¢‡πà‡∏≤‡∏á‡∏ä‡∏±‡πà‡∏ß‡∏Ñ‡∏£‡∏≤‡∏ß');
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
  }, []);

  // =============================== ‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤ ===============================
  const addToCart = (product) => {
    let cart = JSON.parse(localStorage.getItem('cart') || '[]');
    const idx = cart.findIndex((x) => x.id === product.id);
    if (idx > -1) cart[idx].quantity += 1;
    else cart.push({ ...product, quantity: 1 });
    localStorage.setItem('cart', JSON.stringify(cart));
    alert(`‡πÄ‡∏û‡∏¥‡πà‡∏° ${product.name} ‡∏•‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡πÅ‡∏•‡πâ‡∏ß`);
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="header">
