// frontend/src/admin/ProductManagement.js
// ✅ ฉบับสมบูรณ์ล่าสุด — เพิ่ม/แก้ตัวเลือก (SKU) ได้ก่อนบันทึก และปุ่มบันทึกอยู่ล่างสุด

import React, { useEffect, useState } from 'react';
import './ProductManagement.css';
import { api } from '../lib/api';

export default function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [form, setForm] = useState({ name: '', price: '', category_id: '', description: '' });
  const [editing, setEditing] = useState(false);
  const [productId, setProductId] = useState(null);
  const [variants, setVariants] = useState([]); // ตัวเลือกสินค้าจริง
  const [draftVariants, setDraftVariants] = useState([]); // ตัวเลือกที่ยังไม่บันทึก
  const [showForm, setShowForm] = useState(false);
  const [saving, setSaving] = useState(false);

  // โหลดรายการสินค้า
  const loadProducts = async () => {
    const res = await api.get('/admin/products');
    setProducts(res.data || []);
  };

  useEffect(() => {
    loadProducts();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((f) => ({ ...f, [name]: value }));
  };

  // เปิดหน้าแก้ไขสินค้า พร้อมโหลด SKU
  const handleEdit = async (p) => {
    setEditing(true);
    setShowForm(true);
    setForm({
      name: p.product_name,
      price: p.price,
      category_id: p.category_id,
      description: p.description,
    });
    setProductId(p.id);
    const res = await api.get(`/admin/products/${p.id}/variants`);
    setVariants(res.data || []);
    setDraftVariants([]);
  };

  const handleNew = () => {
    setEditing(false);
    setShowForm(true);
    setForm({ name: '', price: '', category_id: '', description: '' });
    setVariants([]);
    setDraftVariants([]);
    setProductId(null);
  };

  // เพิ่มตัวเลือกใหม่ (ใน state ชั่วคราว)
  const addDraftVariant = () => {
    setDraftVariants((prev) => [
      ...prev,
      { _localId: crypto.randomUUID?.() || Date.now(), sku: '', price: '', stock: 0 },
    ]);
  };

  const updateDraftVariant = (id, field, value) => {
    setDraftVariants((prev) => prev.map((v) => (v._localId === id ? { ...v, [field]: value } : v)));
  };

  const removeDraftVariant = (id) => {
    setDraftVariants((prev) => prev.filter((v) => v._localId !== id));
  };

  const updateVariant = (id, field, value) => {
    setVariants((prev) => prev.map((v) => (v.id === id ? { ...v, [field]: value } : v)));
  };

  const handleSave = async () => {
    try {
      setSaving(true);
      let pid = productId;
      if (!pid) {
        const r = await api.post('/admin/products', form);
        pid = r.data?.id;
      } else {
        await api.put(`/admin/products/${pid}`, form);
      }

      // สร้างตัวเลือกใหม่จาก draftVariants
      for (const d of draftVariants) {
        await api.post(`/admin/products/${pid}/variants`, {
          sku: d.sku,
          price: d.price,
          stock: d.stock,
        });
      }

      // อัปเดตตัวเลือกที่มีอยู่
      for (const v of variants) {
        await api.put(`/admin/variants/${v.id}`, {
          sku: v.sku,
          price: v.price,
          stock: v.stock,
        });
      }

      await loadProducts();
      setShowForm(false);
      setSaving(false);
      alert('บันทึกเรียบร้อย');
    } catch (err) {
      console.error(err);
      alert('บันทึกไม่สำเร็จ');
      setSaving(false);
    }
  };

  return (
    <div className="pm-container">
      <h1>จัดการสินค้า</h1>
      {!showForm && (
        <>
          <button className="btn btn-primary" onClick={handleNew}>+ เพิ่มสินค้าใหม่</button>
          <table className="pm-table">
            <thead>
              <tr>
                <th>ชื่อสินค้า</th>
                <th>ราคา</th>
                <th>หมวดหมู่</th>
                <th>จัดการ</th>
              </tr>
            </thead>
            <tbody>
              {products.map((p) => (
                <tr key={p.id}>
                  <td>{p.product_name}</td>
                  <td>{p.price}</td>
                  <td>{p.category_name}</td>
                  <td>
                    <button onClick={() => handleEdit(p)}>แก้ไข</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}

      {showForm && (
        <div className="pm-form">
          <h2>{editing ? 'แก้ไขสินค้า' : 'เพิ่มสินค้าใหม่'}</h2>

          <div className="field">
            <label>ชื่อสินค้า</label>
            <input name="name" value={form.name} onChange={handleChange} />
          </div>
          <div className="field">
            <label>ราคา</label>
            <input name="price" value={form.price} onChange={handleChange} />
          </div>
          <div className="field">
            <label>คำอธิบาย</label>
            <textarea name="description" value={form.description} onChange={handleChange} />
          </div>

          <h3>ตัวเลือกสินค้า (SKU)</h3>
          <button type="button" onClick={addDraftVariant} className="btn btn-outline-success">
            + เพิ่มตัวเลือกสินค้า
          </button>

          {(variants.length > 0 || draftVariants.length > 0) && (
            <table className="variant-table">
              <thead>
                <tr>
                  <th>SKU</th>
                  <th>ราคา</th>
                  <th>สต็อก</th>
                  <th>ลบ</th>
                </tr>
              </thead>
              <tbody>
                {variants.map((v) => (
                  <tr key={v.id}>
                    <td><input value={v.sku} onChange={(e) => updateVariant(v.id, 'sku', e.target.value)} /></td>
                    <td><input value={v.price} onChange={(e) => updateVariant(v.id, 'price', e.target.value)} /></td>
                    <td><input value={v.stock} onChange={(e) => updateVariant(v.id, 'stock', e.target.value)} /></td>
                    <td>-</td>
                  </tr>
                ))}
                {draftVariants.map((v) => (
                  <tr key={v._localId} className="draft">
                    <td><input value={v.sku} onChange={(e) => updateDraftVariant(v._localId, 'sku', e.target.value)} /></td>
                    <td><input value={v.price} onChange={(e) => updateDraftVariant(v._localId, 'price', e.target.value)} /></td>
                    <td><input value={v.stock} onChange={(e) => updateDraftVariant(v._localId, 'stock', e.target.value)} /></td>
                    <td><button onClick={() => removeDraftVariant(v._localId)}>ลบ</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}

          <div className="save-bar">
            <button className="btn btn-success" onClick={handleSave} disabled={saving}>
              {saving ? 'กำลังบันทึก...' : 'บันทึก'}
            </button>
            <button className="btn" onClick={() => setShowForm(false)}>ยกเลิก</button>
          </div>
        </div>
      )}
    </div>
  );
}
