// src/App.js
// หน้าที่: root ของฝั่งเว็บ จัดการเส้นทาง (routing) + การ์ดสิทธิ์พื้นฐาน (Auth/RBAC)

import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate
} from 'react-router-dom';
import axios from 'axios';

// ====== เพจ/คอมโพเนนต์หลักของแอป ======
import ProductManagement from './admin/ProductManagement'; // หน้าเดิม: จัดการสินค้า
import OrderManagement   from './admin/OrderManagement';   // หน้าเดิม: จัดการคำสั่งซื้อ
import Login             from './auth/Login';              // หน้าเข้าสู่ระบบ
import Addresses         from './account/Addresses';       // หน้า: จัดการที่อยู่ผู้ใช้
import Register          from './auth/Register';           // ✅ หน้า: สมัครสมาชิก (เพิ่มเข้ามา)
import VariantsManager   from './admin/VariantsManager';

// ✅ [เพิ่มใหม่] หน้าออปชั่น (.js)
import OptionsPage       from './admin/OptionsPage.js';

// ===========================
// สวิตช์การ์ดสิทธิ์ (เปิด-ปิดการเช็ค token ระดับเส้นทาง)
// - dev ให้ false → เข้าได้ทุกหน้า
// - production/ทดสอบจริง ตั้ง true → บังคับว่าต้องมี token ก่อนเข้าหน้าบางหน้า
// ===========================
const ENABLE_AUTH_GUARD = false;

// ===========================
// ฟังก์ชันช่วยอ่าน token จาก localStorage (เอาไว้ใช้ซ้ำหลายที่)
// ===========================
const getToken = () => localStorage.getItem('token');

// ===========================
// การ์ดสิทธิ์: ครอบเส้นทาง/หน้า ที่ต้องล็อกอินก่อนถึงจะเข้าได้
// - ถ้ายังไม่เปิด guard ก็ปล่อยผ่าน (เพื่อ dev ง่าย ๆ)
// - ถ้าเปิด guard และไม่มี token → redirect ไปหน้า /login
// ===========================
function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;              // โหมด dev: ไม่เช็ค token
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />;  // ไม่มี token → ไปล็อกอิน
  return children;                                      // มี token → เข้าได้
}

// ===========================
// Navbar: เมนูบนสุด + ปุ่ม Login/Logout
// - แสดงเมนูหลัก
// - ด้านขวาแสดง "เข้าสู่ระบบ/สมัครสมาชิก" ถ้าไม่มี token
//   หรือ "ออกจากระบบ" ถ้ามี token
// ===========================
function Navbar() {
  const nav = useNavigate();

  // สไตล์เมนู (NavLink จะส่ง isActive มาให้)
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none',
    padding: '6px 10px',
    borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  const hasToken = !!getToken();

  // ออกจากระบบ: ลบ token + ล้างหัว Authorization ของ axios + เด้งไป /login
  function logout() {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }

  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      {/* เมนูหลักฝั่งซ้าย */}
      <NavLink to="/" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      {/* (ถ้าต้องการปุ่มเมนูไปหน้าออปชั่น/หน้า SKU ที่นี่ บอกได้ เดี๋ยวผมใส่ให้) */}

      {/* ปุ่ม Login/Logout + สมัครสมาชิก ฝั่งขวา */}
      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <>
            <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
            {/* ✅ ปุ่มไปหน้า สมัครสมาชิก */}
            <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
          </>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

// ===========================
// แอปหลัก:
// - ตอน mount ครั้งแรก: ถ้ามี token ใน localStorage → ติดหัว Authorization ให้ axios
// - กำหนดเส้นทางทั้งหมดของแอป
//   * บางหน้าครอบด้วย <RequireAuth> เพื่อบังคับล็อกอิน (เมื่อเปิด guard)
// ===========================
export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
    // หมายเหตุ: baseURL แนะนำเซ็ตที่ src/index.js หรือ src/main.js (global) เช่น:
    axios.defaults.baseURL = 'http://localhost:3001';
  }, []);

  return (
    <BrowserRouter>
      <Navbar />

      <Routes>
        {/* หน้าแรก: ระบบจัดการสินค้า */}
        <Route
          path="/"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>🌿 ระบบจัดการสินค้า</h1>
              <ProductManagement />
            </div>
          }
        />

        {/* จัดการคำสั่งซื้อ (ตัวอย่างการครอบ RequireAuth) */}
        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>📦 จัดการคำสั่งซื้อ</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        {/* ที่อยู่ของฉัน (ควรล็อกอินก่อน) */}
        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>🏠 ที่อยู่ของฉัน</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        {/* หน้าเข้าสู่ระบบ */}
        <Route path="/login" element={<Login />} />

        {/* ✅ หน้า “สมัครสมาชิก” */}
        <Route path="/register" element={<Register />} />

        {/* ✅ [เพิ่มใหม่] หน้า “ออปชั่นของสินค้า” 
            ใช้ :id ให้ตรงกับ OptionsPage ที่อ่าน useParams().id */}
        <Route
          path="/admin/products/:id/options"
          element={<OptionsPage />}
        />

        {/* หน้าจัดการ SKU ที่คุณมีอยู่แล้ว (คงของเดิม) */}
        <Route
          path="/admin/products/:productId/variants"
          element={<VariantsManager />}
        />

        {/* กันหลงทาง → กลับหน้าแรก */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
