// src/pages/ProductList.js
import React, { useEffect, useMemo, useState } from 'react';
import axios from 'axios';

// ✅ ใช้ env แบบ CRA เพียว ๆ กันวอร์นิ่ง import.meta
const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:3000';

const currencyTH = new Intl.NumberFormat('th-TH', {
  style: 'currency',
  currency: 'THB',
  minimumFractionDigits: 2,
});

function ProductList() {
  const [products, setProducts] = useState([]);
  const [loading, setLoading]   = useState(true);
  const [error, setError]       = useState('');

  const axiosClient = useMemo(() => {
    const token = localStorage.getItem('token') || '';
    return axios.create({
      baseURL: API_BASE,
      headers: token ? { Authorization: `Bearer ${token}` } : {},
      timeout: 10000,
    });
  }, []);

  const fetchProducts = () => {
    setLoading(true);
    setError('');

    const controller = new AbortController();

    axiosClient
      .get('/api/products', { signal: controller.signal })
      .then(res => setProducts(res.data || []))
      .catch(err => setError(err?.response?.data?.message || err.message))
      .finally(() => setLoading(false));

    return () => controller.abort();
  };

  useEffect(() => {
    fetchProducts();
  }, []); // eslint-disable-line react-hooks/exhaustive-deps

  if (loading) return <div style={{ textAlign: 'center', padding: 24 }}>กำลังโหลดสินค้า…</div>;
  if (error)   return <div style={{ color: 'red', textAlign: 'center', padding: 24 }}>{error}</div>;

  return (
    <div style={{ maxWidth: 1200, margin: '0 auto', padding: 16 }}>
      <h1 style={{ textAlign: 'center', marginBottom: 16 }}>สินค้า</h1>
      <div style={{
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fill, minmax(220px, 1fr))',
        gap: 16
      }}>
        {products.map(p => (
          <div key={p.id} style={{ border: '1px solid #eee', borderRadius: 12, padding: 12 }}>
            <div style={{ aspectRatio: '1 / 1', overflow: 'hidden', borderRadius: 8, marginBottom: 8 }}>
              <img
                src={p.thumbnail_url || p.image_url || '/placeholder.png'}
                alt={p.name}
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
              />
            </div>
            <div style={{ fontWeight: 600, marginBottom: 4 }}>{p.name}</div>
            <div style={{ color: '#555', marginBottom: 8 }}>
              {currencyTH.format(p.price || 0)}
            </div>
            {/* ปุ่ม/ลิงก์เพิ่มตามต้องการ */}
          </div>
        ))}
      </div>
    </div>
  );
}

export default ProductList;
