// [FRONTEND] src/admin/ProductManagement.js
// จัดการสินค้า + CRUD ประเภท/หมวดย่อย + อัปโหลดรูปหลายไฟล์ + archive/unarchive + ลิงก์ไป Variants
// ปรับ: "จัดการประเภท" / "จัดการรูปภาพ" แยกคอลัมน์ + ปุ่มวางแนวตั้ง (แก้ไข/ลบ) และ (เลือกรูป/ล้างภาพ)
// คอลัมน์ "รูป" แสดงเฉพาะ thumbnail ตรงหัวข้อเสมอ
// 👇 [อัปเดต] เพิ่มป้ายสถานะใหญ่ (กำลังแสดง/ถูกซ่อน) + ปุ่ม “แสดงสินค้า/ไม่แสดงสินค้า” ที่ชัดเจน
//     และตัวกรอง 3 โหมด (ทั้งหมด/เฉพาะที่แสดง/เฉพาะที่ซ่อน)

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

/* ---------- Helpers & Config ---------- */
const asStr = (v) => (v === null || v === undefined) ? '' : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? '';

const API_BASE = (api?.defaults?.baseURL) || process.env.REACT_APP_API_BASE || 'http://localhost:3001';
const ABS_BASE = String(API_BASE).replace(/\/+$/,'');
const resolveUrl = (u) => {
  if (!u) return '';
  const s = String(u);
  if (/^(https?:|blob:)/i.test(s)) return s;
  if (s.startsWith('/uploads/')) return `${ABS_BASE}${s}`;
  return s;
};

const DEFAULT_PRODUCT_UNITS = [
  { code: 'plant', label: 'ต้น' },
  { code: 'piece', label: 'ชิ้น' },
  { code: 'pot',   label: 'กระถาง' },
  { code: 'bag',   label: 'ถุง' },
  { code: 'pack',  label: 'แพ็ค' },
  { code: 'kg',    label: 'กิโลกรัม' },
  { code: 'g',     label: 'กรัม' },
  { code: 'ltr',   label: 'ลิตร' },
  { code: 'ml',    label: 'มิลลิลิตร' },
];

const DEFAULT_SIZE_UNITS = [
  { code: 'mm',   label: 'มม.' },
  { code: 'cm',   label: 'ซม.' },
  { code: 'm',    label: 'ม.' },
  { code: 'inch', label: 'นิ้ว' },
  { code: 'kg',   label: 'กก.' },
  { code: 'ltr',  label: 'ลิตร' },
];

export default function ProductManagement() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);

  // ✅ ฟิลเตอร์ “แสดงสินค้าที่เก็บแล้ว”
  const [showArchived, setShowArchived] = useState(false);
  // ✅ ฟิลเตอร์สถานะการแสดงผล: all | shown | hidden
  const [visibilityFilter, setVisibilityFilter] = useState('all');

  const [activeTab, setActiveTab] = useState('products');

  const [productUnits, setProductUnits] = useState(DEFAULT_PRODUCT_UNITS);
  const [sizeUnits, setSizeUnits] = useState(DEFAULT_SIZE_UNITS);
  const [newProdUnit, setNewProdUnit] = useState('');
  const [newSizeUnit, setNewSizeUnit] = useState('');

  const [form, setForm] = useState({
    product_name: '',
    description: '',
    selling_price: '',
    category_id: '',
    subcategory_id: '',
    image_url: '',
    unit: '',
    size_value: '',
    size_unit: '',
    origin: '',
    product_status_id: ''
  });

  const [selectedFiles, setSelectedFiles] = useState([]);
  const [previews, setPreviews] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  const [newCat, setNewCat] = useState({ category_id: '', category_name: '' });
  const [newSub, setNewSub] = useState({ subcategory_id: '', subcategory_name: '', category_id: '' });

  /* ---------- API helpers ---------- */
  const handleApiError = (err, fallback = 'เกิดข้อผิดพลาด') => {
    console.error(fallback, err?.response?.data || err?.message || err);
    alert(err?.response?.data?.error || err?.message || fallback);
  };

  const fetchProducts = async () => {
    try {
      const { data } = await api.get(path('/admin/products'), {
        params: { include_archived: showArchived ? 1 : 0 }
      });
      const items = Array.isArray(data?.items) ? data.items : data;
      setProducts(items || []);
    } catch (err) {
      handleApiError(err, '❌ โหลดสินค้าไม่สำเร็จ');
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await api.get(path('/categories'));
      setCategories(data || []);
    } catch (err) {
      handleApiError(err, '❌ โหลดประเภทไม่สำเร็จ');
    }
  };

  

  const fetchStatuses = async () => {
    try {
      let res = await api.get(path('/product-status'));
      if (!Array.isArray(res.data)) { try { res = await api.get(path('/product-statuses')); } catch {} }
      const normalized = (res.data || []).map((x) => ({
        id: asStr(x?.product_status_id ?? x?.ProductStatusID ?? x?.id),
        name: pickStatusName(x)
      }));
      setStatuses(normalized);
    } catch {
      console.warn('⚠ ไม่มี endpoint product-status | product-statuses');
      setStatuses([]);
    }
  };

  /* ---------- Upload helpers ---------- */
  async function uploadImage(file) {
    if (!file) return null;
    const sendWith = async (field) => {
      const fd = new FormData();
      fd.append(field, file);
      const res = await api.post(path('/upload'), fd, { headers: { 'Content-Type': 'multipart/form-data' } });
      return res?.data?.imageUrl || res?.data?.url || res?.data?.path || res?.data?.location || null;
    };
    try { return await sendWith('file'); }
    catch (e1) {
      const msg = e1?.response?.data?.error || '';
      if (/no file uploaded/i.test(msg) || e1?.response?.status === 400) {
        try { return await sendWith('image'); }
        catch (e2) {
          const msg2 = e2?.response?.data?.error || '';
          if (/no file uploaded/i.test(msg2) || e2?.response?.status === 400) return await sendWith('photo');
          throw e2;
        }
      }
      throw e1;
    }
  }

  async function uploadSubcatImage(file) {
    if (!file) return null;
    const fd = new FormData();
    fd.append('file', file);
    const res = await api.post(path('/uploads/subcategory-image'), fd, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return res?.data?.url || null;
  }
  async function uploadCategoryImage(file) {
    if (!file) return null;
    const fd = new FormData();
    fd.append('file', file);
    const res = await api.post(path('/uploads/category-image'), fd, {
      headers: { 'Content-Type': 'multipart/form-data' },
    });
    return res?.data?.url || null;
  }

  /* ---------- Toggle Publish helper ---------- */
  async function togglePublish(kind, id, current) {
    // kind: 'categories' | 'subcategories' | 'products'
    try {
      await api.patch(path(`/admin/${kind}/${id}/publish`), { is_published: !current });
      if (kind === 'products') await fetchProducts();
      if (kind === 'categories') await fetchCategories();
      if (kind === 'subcategories') await fetchSubcategories();
    } catch (err) {
      handleApiError(err, '❌ อัปเดตการแสดงผลไม่สำเร็จ');
    }
  }

  /* ---------- Archive / Unarchive helpers ---------- */
  async function archiveProduct(productId) {
    if (!window.confirm('ย้ายสินค้านี้ไปถังเก็บใช่ไหม?')) return;
    try {
      await api.delete(path(`/admin/products/${productId}`));
      await fetchProducts();
    } catch (err) {
      handleApiError(err, '❌ ย้ายไปถังเก็บไม่สำเร็จ');
    }
  }

  async function unarchiveProduct(productId) {
    try {
      await api.patch(path(`/admin/products/${productId}/unarchive`));
      await fetchProducts();
    } catch (err) {
      handleApiError(err, '❌ กู้คืนสินค้าไม่สำเร็จ');
    }
  }

  /* ---------- init ---------- */
  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchCategories(), fetchSubcategories(), fetchStatuses()]);
      setLoading(false);
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [showArchived]);

  /* ---------- product form handlers ---------- */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({
      ...p,
      [name]: value,
      ...(name === 'category_id' ? { subcategory_id: '' } : {})
    }));
  };

  const onFiles = (e) => setSelectedFiles(Array.from(e.target.files || []));

  useEffect(() => {
    if (!selectedFiles?.length) { setPreviews([]); return; }
    const urls = selectedFiles.map((f) => ({
      name: f.name, url: URL.createObjectURL(f), size: f.size, type: f.type,
    }));
    setPreviews(urls);
    return () => urls.forEach(u => URL.revokeObjectURL(u.url));
  }, [selectedFiles]);

  const removeSelectedAt = (idx) => setSelectedFiles((prev) => prev.filter((_, i) => i !== idx));
  const moveSelected = (idx, dir) => {
    setSelectedFiles((prev) => {
      const arr = [...prev];
      const to = idx + dir;
      if (to < 0 || to >= arr.length) return arr;
      const t = arr[idx]; arr[idx] = arr[to]; arr[to] = t;
      return arr;
    });
  };

  const onEdit = (p) => {
    let size_value = '', size_unit = '';
    if (p.size && typeof p.size === 'string') {
      const m = p.size.trim().match(/^(\d+(?:\.\d+)?)\s*(.+)$/);
      if (m) { size_value = m[1]; size_unit = m[2]; } else { size_value = p.size; }
    }
    setForm({
      product_name:        p.product_name ?? '',
      description:         p.description ?? '',
      selling_price:       asStr(p.selling_price ?? ''),
      category_id:         asStr(p.category_id ?? ''),
      subcategory_id:      asStr(p.subcategory_id ?? ''),
      image_url:           p.image_url ?? '',
      unit:                p.unit ?? '',
      size_value,
      size_unit,
      origin:              p.origin ?? '',
      product_status_id:   asStr(p.product_status_id ?? p.ProductStatusID ?? '')
    });
    setIsEditing(true);
    setEditProductId(p.product_id);
    setActiveTab('products');
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const clearForm = () => {
    setForm({
      product_name: '', description: '', selling_price: '',
      category_id: '', subcategory_id: '', image_url: '',
      unit: '', size_value: '', size_unit: '', origin: '',
      product_status_id: ''
    });
    setSelectedFiles([]);
    setIsEditing(false);
    setEditProductId(null);
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    const trimmedPrice = String(form.selling_price ?? '').trim();
    const priceInt = Number.parseInt(trimmedPrice, 10);
    if (!Number.isInteger(priceInt) || priceInt < 0 || String(priceInt) !== trimmedPrice.replace(/^0+(?=\d)/, '')) {
      return alert('ราคาขายต้องเป็น "จำนวนเต็มไม่ติดลบ" เท่านั้น');
    }

    let sizeText = '';
    if (form.size_value) sizeText = form.size_unit ? `${form.size_value} ${form.size_unit}` : String(form.size_value);

    let uploadedUrls = [];
    if (selectedFiles.length > 0) {
      try { uploadedUrls = (await Promise.all(selectedFiles.map(f => uploadImage(f)))).filter(Boolean); }
      catch (err) { return handleApiError(err, '❌ อัปโหลดรูปไม่สำเร็จ'); }
    }
    const primaryImageUrl = uploadedUrls[0] || (form.image_url || '').trim();

    const body = {
      product_name: form.product_name,
      description: form.description,
      image_url: primaryImageUrl || '',
      selling_price: priceInt,
      category_id: form.category_id || null,
      subcategory_id: form.subcategory_id || null,
      product_status_id: form.product_status_id || null,
      ...(form.unit ? { unit: form.unit } : {}),
      ...(sizeText ? { size: sizeText } : {}),
      ...(form.origin ? { origin: form.origin } : {})
    };

    try {
      let createdId = editProductId;
      if (isEditing && editProductId) {
        await api.put(path(`/admin/products/${editProductId}`), body);
      } else {
        const res = await api.post(path('/admin/products'), body);
        createdId = res?.data?.product_id ?? res?.data?.id ?? res?.data?.ProductID ?? createdId;
      }

      if (createdId && uploadedUrls.length > 0) {
        const imagesPayload = uploadedUrls.map((url, i) => ({
          url, alt_text: previews[i]?.name || null, is_primary: i === 0, position: i + 1
        }));
        try {
          await api.post(path(`/admin/products/${createdId}/images`), { images: imagesPayload });
        } catch {
          for (const img of imagesPayload) {
            try {
              try {
                await api.post(path('/product-images'), {
                  product_id: createdId, url: img.url, alt_text: img.alt_text, is_primary: img.is_primary, position: img.position
                });
              } catch {
                await api.post(path('/admin/product-images'), {
                  product_id: createdId, url: img.url, alt_text: img.alt_text, is_primary: img.is_primary, position: img.position
                });
              }
            } catch (err) {
              console.warn('⚠ บันทึกรูปไม่สำเร็จ', err?.response?.data || err?.message || err);
            }
          }
        }
      }

      await fetchProducts();
      clearForm();
    } catch (err) {
      handleApiError(err, '❌ บันทึกสินค้าไม่สำเร็จ');
    }
  };

  /* ---------- options ---------- */
  const statusOptions = useMemo(() => ([
    <option key="" value="">— เลือกสถานะ —</option>,
    ...statuses.map(s => <option key={s.id} value={asStr(s.id)}>{s.name || '(ไม่มีชื่อ)'}</option>)
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(statuses)]);

  const categoryOptions = useMemo(() => ([
    <option key="" value="">เลือกประเภท</option>,
    ...categories.map(c => (
      <option key={c.category_id} value={asStr(c.category_id)}>{c.category_name}</option>
    ))
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(categories)]);

  const filteredSubcategories = useMemo(() => {
    if (!form.category_id) return [];
    const cid = asStr(form.category_id);
    return (subcategories || []).filter(s => asStr(s.category_id) === cid);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [form.category_id, JSON.stringify(subcategories)]);

  const subcategoryOptions = useMemo(() => ([
    <option key="" value="">{form.category_id ? 'เลือกหมวดหมู่' : 'เลือกประเภทก่อน'}</option>,
    ...filteredSubcategories.map(s => (
      <option key={s.subcategory_id} value={asStr(s.subcategory_id)}>{s.subcategory_name}</option>
    ))
  ]), [filteredSubcategories, form.category_id]);

  /* ---------- client-side filter for products ---------- */
  const viewProducts = useMemo(() => {
    if (!Array.isArray(products)) return [];
    return products.filter(p => {
      const published = (typeof p.is_published === 'boolean') ? p.is_published : true;
      if (visibilityFilter === 'shown' && !published) return false;
      if (visibilityFilter === 'hidden' && published) return false;
      return true;
    });
  }, [products, visibilityFilter]);

  if (loading) return <div style={{ padding: 12 }}>กำลังโหลดข้อมูล…</div>;

  return (
    <div className="pm-page">
      {/* Tabs */}
      <div style={{display:'flex', gap:8, margin:'12px 0 20px'}}>
        {[
          { key:'products',     label:'📦 สินค้า'       },
          { key:'categories',   label:'📂 ประเภท'       },
          { key:'subcategories',label:'🗂️ หมวดย่อย'   },
          { key:'units',        label:'📏 หน่วยสินค้า' },
          { key:'sizeunits',    label:'📐 หน่วยขนาด'   },
        ].map(t => {
          const active = activeTab === t.key;
          return (
            <button
              key={t.key}
              onClick={()=>setActiveTab(t.key)}
              style={{
                padding:'8px 12px',
                border:'1px solid ' + (active ? '#2e7d32' : '#d8e2dc'),
                borderRadius:8,
                cursor:'pointer',
                background: active ? '#2e7d32' : '#fff',
                color: active ? '#fff' : '#333',
                fontWeight:600
              }}
              title={t.label}
            >
              {t.label}
            </button>
          );
        })}
      </div>

      {/* ===== สินค้า ===== */}
      {activeTab==='products' && (
        <>
          <div className="pm-panel">
            <h2>{isEditing ? 'แก้ไขสินค้า' : 'เพิ่มสินค้าใหม่'}</h2>

            <form onSubmit={onSubmit} className="pm-form">
              {/* ฟอร์มเดิมคงไว้ทั้งหมด */}
              <input name="product_name" placeholder="ชื่อสินค้า" value={form.product_name} onChange={onChange} />

              <input
                name="selling_price"
                type="number"
                inputMode="numeric"
                min="0"
                step="1"
                placeholder="ราคาขาย (จำนวนเต็มเท่านั้น)"
                value={form.selling_price}
                onChange={onChange}
              />

              <div className="pm-input-group pm-row-3 col-span-2">
                <input
                  name="size_value"
                  type="number"
                  inputMode="decimal"
                  min="0"
                  step="1"
                  placeholder="ขนาดสินค้า (ตัวเลข)"
                  value={form.size_value}
                  onChange={onChange}
                  title="เช่น 50"
                />
                <select name="size_unit" value={form.size_unit} onChange={onChange} title="หน่วยของขนาด">
                  <option value="">— เลือกหน่วยขนาด —</option>
                  {DEFAULT_SIZE_UNITS.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
                </select>
                <select name="unit" value={form.unit} onChange={onChange} title="หน่วยสินค้า">
                  <option value="">— เลือกหน่วยสินค้า —</option>
                  {DEFAULT_PRODUCT_UNITS.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
                </select>
              </div>

              <input name="origin" placeholder="แหล่งที่มา" value={form.origin} onChange={onChange} />
              <select name="product_status_id" value={form.product_status_id} onChange={onChange}>{statusOptions}</select>
              <select name="category_id" value={form.category_id} onChange={onChange}>{categoryOptions}</select>
              <select name="subcategory_id" value={form.subcategory_id} onChange={onChange} disabled={!form.category_id}>{subcategoryOptions}</select>
              <textarea className="col-span-2" name="description" placeholder="คำอธิบายสินค้า" value={form.description} onChange={onChange} />
              <input className="col-span-2" name="image_url" placeholder="URL รูป (หากไม่อัปโหลด)" value={form.image_url} onChange={onChange} />
              <input className="col-span-2" type="file" accept="image/*" multiple onChange={onFiles} />

              {previews.length > 0 && (
                <div className="pm-files-preview col-span-2">
                  {previews.map((p, idx) => (
                    <div className="pm-file" key={p.url}>
                      <span className="pm-badge" title={idx === 0 ? 'รูปหลักเมื่อบันทึก' : `ลำดับที่ ${idx + 1}`}>
                        {idx === 0 ? '★' : idx + 1}
                      </span>
                      <img src={p.url} alt={p.name} />
                      <div className="pm-meta">
                        <div className="pm-name" title={p.name}>{p.name}</div>
                        <div className="pm-actions">
                          <button type="button" className="btn-xxs" onClick={() => moveSelected(idx, -1)} disabled={idx === 0}>↑</button>
                          <button type="button" className="btn-xxs" onClick={() => moveSelected(idx, +1)} disabled={idx === previews.length - 1}>↓</button>
                          <button type="button" className="btn-xxs danger" onClick={() => removeSelectedAt(idx)}>ลบ</button>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}

              <div className="pm-actions col-span-2">
                <button type="submit" className="btn btn-primary">{isEditing ? 'อัปเดต' : 'บันทึก'}</button>
                {isEditing && <button type="button" className="btn btn-ghost" onClick={clearForm}>ยกเลิกแก้ไข</button>}
              </div>
            </form>

            <div style={{ display:'flex', gap:8, marginTop:8 }}>
              <button type="button" className="btn btn-ghost" onClick={()=>setActiveTab('categories')}>+ เพิ่มประเภท</button>
              <button type="button" className="btn btn-ghost" onClick={()=>setActiveTab('subcategories')}>+ เพิ่มหมวดย่อย</button>
              <button type="button" className="btn btn-ghost" onClick={()=>setActiveTab('units')}>+ เพิ่มหน่วยสินค้า</button>
              <button type="button" className="btn btn-ghost" onClick={()=>setActiveTab('sizeunits')}>+ เพิ่มหน่วยขนาด</button>
            </div>
          </div>

          {/* Toolbar: ฟิลเตอร์ + แสดงเก็บแล้ว */}
          <div className="pm-toolbar">
            <div className="seg">
              <button
                type="button"
                className={`seg-btn ${visibilityFilter==='all' ? 'active': ''}`}
                onClick={()=>setVisibilityFilter('all')}
                title="ดูทั้งหมด"
              >ทั้งหมด</button>
              <button
                type="button"
                className={`seg-btn ${visibilityFilter==='shown' ? 'active': ''}`}
                onClick={()=>setVisibilityFilter('shown')}
                title="เฉพาะที่แสดง"
              >เฉพาะที่แสดง</button>
              <button
                type="button"
                className={`seg-btn ${visibilityFilter==='hidden' ? 'active': ''}`}
                onClick={()=>setVisibilityFilter('hidden')}
                title="เฉพาะที่ซ่อน"
              >เฉพาะที่ซ่อน</button>
            </div>

            <label className="pm-check">
              <input type="checkbox" checked={showArchived} onChange={e => setShowArchived(e.target.checked)} />
              <span>แสดงสินค้าที่เก็บแล้ว</span>
            </label>
          </div>

          <h2>รายการสินค้า</h2>
          <div className="pm-table-wrap">
            <table className="pm-table">
              <thead>
                <tr>
                  <th>สินค้า</th><th>ราคา</th><th>คงเหลือ</th><th>หมวดหมู่</th><th>สถานะ</th><th>จัดการ</th>
                </tr>
              </thead>
              <tbody>
                {(viewProducts || []).map(p => {
                  const published = (typeof p.is_published === 'boolean') ? p.is_published : true;
                  return (
                    <tr key={p.product_id}>
                      <td>
                        {/* ป้ายสถานะใหญ่ ชัดเจน */}
                        <span className={`pill ${published ? 'on' : 'off'}`}>
                          {published ? 'กำลังแสดง' : 'ถูกซ่อน'}
                        </span>
                        <span className="name-with-badges">
                          {p.product_name}
                          {p.is_archived && <span className="badge-archived">เก็บแล้ว</span>}
                        </span>
                      </td>
                      <td>{Number(p.selling_price ?? 0).toLocaleString()}</td>
                      <td>{p.stock ?? p.stock_quantity ?? 0}</td>
                      <td>{`${p.category_name || '-'} / ${p.subcategory_name || '-'}`}</td>
                      <td>{p.product_status_name ?? pickStatusName(p)}</td>
                      <td className="cell-actions">
                        {/* ปุ่ม “แสดงสินค้า/ไม่แสดงสินค้า” ชัดเจน */}
                        <button
                          className={`btn ${published ? 'btn-warn' : 'btn-primary'}`}
                          onClick={() => togglePublish('products', p.product_id, published)}
                          title={published ? 'ซ่อนจากหน้าร้าน' : 'แสดงในหน้าร้าน'}
                        >
                          {published ? 'ไม่แสดงสินค้า' : 'แสดงสินค้า'}
                        </button>

                        <button className="btn btn-ghost" onClick={() => onEdit(p)}>แก้ไข</button>
                        {!p.is_archived ? (
                          <button className="btn btn-warn" onClick={() => archiveProduct(p.product_id)}>ย้ายไปถังเก็บ</button>
                        ) : (
                          <button className="btn btn-primary" onClick={() => unarchiveProduct(p.product_id)}>กู้คืน</button>
                        )}
                        <Link to={`/admin/products/${p.product_id}/variants`} className="btn btn-ghost">ตัวเลือก/ขนาด</Link>
                      </td>
                    </tr>
                  );
                })}
                {(!viewProducts || viewProducts.length === 0) && (
                  <tr><td colSpan={6} style={{ color:'#777' }}>— ไม่มีข้อมูล —</td></tr>
                )}
              </tbody>
            </table>
          </div>
        </>
      )}

      {/* ===== จัดการประเภท ===== */}
      {activeTab==='categories' && (
        <div className="mt-16">
          <h3>จัดการประเภท</h3>

          <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
            <input placeholder="category_id (เว้นได้ให้ระบบ gen)"
                   value={newCat.category_id}
                   onChange={(e)=>setNewCat({ ...newCat, category_id: e.target.value })}
                   style={{ maxWidth:220 }} />
            <input placeholder="category_name"
                   value={newCat.category_name}
                   onChange={(e)=>setNewCat({ ...newCat, category_name: e.target.value })}
                   style={{ minWidth:220 }} />
            <button type="button" className="btn btn-primary"
                    onClick={async ()=>{
                      if (!newCat.category_name.trim()) return alert('ใส่ category_name ก่อน');
                      try {
                        await api.post(path('/categories'), {
                          category_id:  newCat.category_id?.trim() || undefined,
                          category_name: newCat.category_name.trim()
                        });
                        await fetchCategories();
                        setNewCat({ category_id:'', category_name:'' });
                      } catch (err) { handleApiError(err, '❌ เพิ่มประเภทไม่สำเร็จ'); }
                    }}>
              เพิ่มประเภท
            </button>
          </div>

          <div className="pm-table-wrap mt-8">
            <table className="pm-table pm-fixed">
              <colgroup>
                <col className="col-id" />
                <col className="col-name" />
                <col className="col-cat" />
                <col className="col-imgact" />
                <col className="col-thumb" />
                <col style={{ width: 140 }} />
              </colgroup>
              <thead>
                <tr>
                  <th>รหัส</th>
                  <th>ชื่อประเภท</th>
                  <th style={{textAlign:'center'}}>จัดการประเภท</th>
                  <th style={{textAlign:'center'}}>จัดการรูปภาพ</th>
                  <th style={{textAlign:'center'}}>รูป</th>
                  <th style={{textAlign:'center'}}>แสดงผล</th>
                </tr>
              </thead>
              <tbody>
                {(categories || []).map(c => {
                  const published = (typeof c.is_published === 'boolean') ? c.is_published : true;
                  return (
                  <tr key={c.category_id}>
                    <td>{c.category_id}</td>
                    <td>
                      <span className={`pill ${published ? 'on' : 'off'}`}>
                        {published ? 'กำลังแสดง' : 'ถูกซ่อน'}
                      </span>
                      <span className="name-with-badges">
                        {c.category_name}
                      </span>
                    </td>

                    {/* จัดการประเภท (แนวตั้ง) */}
                    <td className="action-stack">
                      <button className="btn btn-ghost"
                              onClick={async ()=>{
                                const name = window.prompt('ชื่อประเภทใหม่', c.category_name);
                                if (name === null) return;
                                const trimmed = name.trim();
                                if (!trimmed || trimmed === c.category_name) return;
                                try { await api.put(path(`/categories/${c.category_id}`), { category_name: trimmed }); await fetchCategories(); }
                                catch (err) { handleApiError(err, '❌ แก้ไขประเภทไม่สำเร็จ'); }
                              }}>
                        แก้ไขประเภท
                      </button>
                      <button className="btn btn-danger"
                              onClick={async ()=>{
                                if (!window.confirm(`ลบประเภท "${c.category_name}" ?`)) return;
                                try { await api.delete(path(`/categories/${c.category_id}`)); await fetchCategories(); await fetchSubcategories(); }
                                catch (err) { handleApiError(err, '❌ ลบประเภทไม่สำเร็จ'); }
                              }}>
                        ลบประเภท
                      </button>
                    </td>

                    {/* จัดการรูปภาพ (แนวตั้ง) */}
                    <td className="action-stack">
                      <label className="btn btn-ghost" style={{ cursor:'pointer', textAlign:'center' }}>
                        เลือกรูป
                        <input
                          type="file"
                          accept="image/*"
                          hidden
                          onChange={async (e)=>{
                            const f = e.target.files?.[0];
                            if (!f) return;
                            try {
                              const url = await uploadCategoryImage(f);
                              if (!url) return alert('อัปโหลดไม่สำเร็จ');
                              await api.put(path(`/categories/${c.category_id}`), { image_url: url });
                              await fetchCategories();
                            } catch (err) {
                              handleApiError(err, '❌ อัปโหลด/บันทึกรูปประเภทไม่สำเร็จ');
                            } finally {
                              e.target.value = '';
                            }
                          }}
                        />
                      </label>
                      <button className="btn btn-warn"
                              disabled={!c.image_url}
                              onClick={async ()=>{
                                if (!window.confirm(`ล้างภาพของ "${c.category_name}" ใช่ไหม?`)) return;
                                try { await api.put(path(`/categories/${c.category_id}`), { image_url: null }); await fetchCategories(); }
                                catch (err) { handleApiError(err, '❌ ล้างภาพไม่สำเร็จ'); }
                              }}>
                        ล้างภาพ
                      </button>
                    </td>

                    {/* รูป */}
                    <td className="thumb-cell">
                      {c.image_url ? (
                        <img src={resolveUrl(c.image_url)} alt={c.category_name} className="thumb-img" />
                      ) : (
                        <div className="thumb-placeholder">ไม่มีรูป</div>
                      )}
                    </td>

                    {/* แสดงผล */}
                    <td className="cell-actions" style={{ textAlign:'center' }}>
                      <button
                        className={`btn ${published ? 'btn-warn' : 'btn-primary'}`}
                        onClick={() => togglePublish('categories', c.category_id, published)}
                        title={published ? 'ซ่อน' : 'แสดง'}
                      >
                        {published ? 'ไม่แสดง' : 'แสดง'}
                      </button>
                    </td>
                  </tr>
                )})}
                {(!categories || categories.length === 0) && (
                  <tr><td colSpan={6} style={{ color:'#777' }}>— ไม่มีประเภท —</td></tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* ===== จัดการหมวดหมู่ย่อย ===== */}
      {activeTab==='subcategories' && (
        <div className="mt-16">
          <h3>จัดการหมวดหมู่ย่อย</h3>

          <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
            <input placeholder="subcategory_id (เว้นได้ให้ระบบ gen)"
                   value={newSub.subcategory_id}
                   onChange={(e)=>setNewSub({ ...newSub, subcategory_id: e.target.value })}
                   style={{ maxWidth:240 }} />
            <input placeholder="subcategory_name"
                   value={newSub.subcategory_name}
                   onChange={(e)=>setNewSub({ ...newSub, subcategory_name: e.target.value })}
                   style={{ minWidth:240 }} />
            <select value={newSub.category_id}
                    onChange={(e)=>setNewSub({ ...newSub, category_id: e.target.value })}
                    style={{ minWidth:200 }}>
              <option value="">เลือกประเภทหลัก</option>
              {(categories || []).map(c => (
                <option key={c.category_id} value={asStr(c.category_id)}>{c.category_name}</option>
              ))}
            </select>
            <button type="button" className="btn btn-primary"
                    onClick={async ()=>{
                      if (!newSub.subcategory_name.trim() || !newSub.category_id) {
                        return alert('ใส่ชื่อหมวดย่อย + เลือกประเภทหลัก');
                      }
                      try {
                        await api.post(path('/subcategories'), {
                          subcategory_id:   newSub.subcategory_id?.trim() || undefined,
                          subcategory_name: newSub.subcategory_name.trim(),
                          category_id:      newSub.category_id.trim()
                        });
                        await fetchSubcategories();
                        setNewSub({ subcategory_id:'', subcategory_name:'', category_id:'' });
                      } catch (err) { handleApiError(err, '❌ เพิ่มหมวดย่อยไม่สำเร็จ'); }
                    }}>
              เพิ่มหมวดหมู่ย่อย
            </button>
          </div>

          <div className="pm-table-wrap mt-8">
            <table className="pm-table pm-fixed">
              <colgroup>
                <col className="col-id" />
                <col className="col-name" />
                <col className="col-type" />
                <col className="col-cat" />
                <col className="col-imgact" />
                <col className="col-thumb" />
                <col style={{ width: 140 }} />
              </colgroup>
              <thead>
                <tr>
                  <th>รหัส</th>
                  <th>หมวดย่อย</th>
                  <th>ประเภท</th>
                  <th style={{textAlign:'center'}}>จัดการหมวดย่อย</th>
                  <th style={{textAlign:'center'}}>จัดการรูปภาพ</th>
                  <th style={{textAlign:'center'}}>รูป</th>
                  <th style={{textAlign:'center'}}>แสดงผล</th>
                </tr>
              </thead>
              <tbody>
                {(subcategories || []).map(s => {
                  const published = (typeof s.is_published === 'boolean') ? s.is_published : true;
                  return (
                  <tr key={s.subcategory_id}>
                    <td>{s.subcategory_id}</td>
                    <td>
                      <span className={`pill ${published ? 'on' : 'off'}`}>
                        {published ? 'กำลังแสดง' : 'ถูกซ่อน'}
                      </span>
                      <span className="name-with-badges">
                        {s.subcategory_name}
                      </span>
                    </td>
                    <td>{s.category_id}</td>

                    {/* จัดการหมวดย่อย */}
                    <td className="action-stack">
                      <button className="btn btn-ghost"
                              onClick={async ()=>{
                                const name = window.prompt('ชื่อหมวดย่อยใหม่', s.subcategory_name);
                                if (!name) return;
                                const newCat = window.prompt('ย้ายไปประเภทไหน? (พิมพ์ category_id เช่น ro1 | เว้นว่าง=ไม่ย้าย)', s.category_id);
                                const payload = { subcategory_name: name.trim() };
                                if (newCat !== null && newCat.trim() !== '' && newCat !== s.category_id) payload.category_id = newCat.trim();
                                try { await api.put(path(`/subcategories/${s.subcategory_id}`), payload); await fetchSubcategories(); }
                                catch (err) { handleApiError(err, '❌ แก้ไขหมวดย่อยไม่สำเร็จ'); }
                              }}>
                        แก้ไขหมวดย่อย
                      </button>
                      <button className="btn btn-danger"
                              onClick={async ()=>{
                                if (!window.confirm(`ลบหมวดหมู่ย่อย "${s.subcategory_name}" ?`)) return;
                                try { await api.delete(path(`/subcategories/${s.subcategory_id}`)); await fetchSubcategories(); }
                                catch (err) { handleApiError(err, '❌ ลบหมวดย่อยไม่สำเร็จ'); }
                              }}>
                        ลบหมวดย่อย
                      </button>
                    </td>

                    {/* จัดการรูปภาพ */}
                    <td className="action-stack">
                      <label className="btn btn-ghost" style={{ cursor:'pointer', textAlign:'center' }}>
                        เลือกรูป
                        <input
                          type="file"
                          accept="image/*"
                          hidden
                          onChange={async (e)=>{
                            const f = e.target.files?.[0];
                            if (!f) return;
                            try {
                              const url = await uploadSubcatImage(f);
                              if (!url) return alert('อัปโหลดไม่สำเร็จ');
                              await api.put(path(`/subcategories/${s.subcategory_id}`), { image_url: url });
                              await fetchSubcategories();
                            } catch (err) {
                              handleApiError(err, '❌ อัปโหลด/บันทึกรูปหมวดย่อยไม่สำเร็จ');
                            } finally {
                              e.target.value = '';
                            }
                          }}
                        />
                      </label>
                      <button className="btn btn-warn"
                              disabled={!s.image_url}
                              onClick={async ()=>{
                                if (!window.confirm(`ล้างภาพของ "${s.subcategory_name}" ใช่ไหม?`)) return;
                                try { await api.put(path(`/subcategories/${s.subcategory_id}`), { image_url: null }); await fetchSubcategories(); }
                                catch (err) { handleApiError(err, '❌ ล้างภาพไม่สำเร็จ'); }
                              }}>
                        ล้างภาพ
                      </button>
                    </td>

                    {/* รูป */}
                    <td className="thumb-cell">
                      {s.image_url ? (
                        <img src={resolveUrl(s.image_url)} alt={s.subcategory_name} className="thumb-img" />
                      ) : (
                        <div className="thumb-placeholder">ไม่มีรูป</div>
                      )}
                    </td>

                    {/* แสดงผล */}
                    <td className="cell-actions" style={{ textAlign:'center' }}>
                      <button
                        className={`btn ${published ? 'btn-warn' : 'btn-primary'}`}
                        onClick={() => togglePublish('subcategories', s.subcategory_id, published)}
                        title={published ? 'ซ่อน' : 'แสดง'}
                      >
                        {published ? 'ไม่แสดง' : 'แสดง'}
                      </button>
                    </td>
                  </tr>
                )})}
                {(!subcategories || subcategories.length === 0) && (
                  <tr><td colSpan={7} style={{ color:'#777' }}>— ไม่มีหมวดย่อย —</td></tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* ===== หน่วยสินค้า ===== */}
      {activeTab==='units' && (
        <div className="mt-16">
          <h3>จัดการหน่วยสินค้า</h3>
          <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
            <input placeholder="เช่น ชิ้น, กระถาง, แพ็ค"
                   value={newProdUnit}
                   onChange={(e)=>setNewProdUnit(e.target.value)}
                   style={{ minWidth:260 }} />
            <button type="button" className="btn btn-primary"
                    onClick={()=>{
                      const name = newProdUnit.trim();
                      if (!name) return;
                      if (productUnits.some(u=>u.label===name)) return alert(`มี "${name}" อยู่แล้ว`);
                      setProductUnits([...productUnits, { code:name.toLowerCase().replace(/\s+/g,'_'), label:name }]);
                      setNewProdUnit('');
                    }}>
              เพิ่มหน่วยสินค้า
            </button>
          </div>
          <div className="pm-table-wrap mt-8">
            <table className="pm-table">
              <tbody>
                {productUnits.map(u=>(
                  <tr key={u.code}>
                    <td style={{ width:200 }}>{u.code}</td>
                    <td>{u.label}</td>
                    <td className="cell-actions" style={{ width:220 }}>
                      <button className="btn btn-ghost"
                              onClick={()=>{
                                const name = window.prompt('ชื่อหน่วยใหม่', u.label);
                                if (!name || name.trim()===u.label) return;
                                if (productUnits.some(x=>x.label===name.trim())) return alert(`มี "${name.trim()}" อยู่แล้ว`);
                                const next = productUnits.map(x=> x.code===u.code ? { code:name.trim().toLowerCase().replace(/\s+/g,'_'), label:name.trim() } : x);
                                setProductUnits(next);
                                setForm(p=>({ ...p, unit: p.unit===u.label ? name.trim() : p.unit }));
                              }}>
                        แก้ไข
                      </button>
                      <button className="btn btn-danger"
                              onClick={()=>{
                                if (!window.confirm(`ลบ "${u.label}" ?`)) return;
                                const next = productUnits.filter(x=>x.code!==u.code);
                                setProductUnits(next);
                                setForm(p=>({ ...p, unit: p.unit===u.label ? '' : p.unit }));
                              }}>
                        ลบ
                      </button>
                    </td>
                  </tr>
                ))}
                {productUnits.length===0 && <tr><td colSpan={3} style={{color:'#777'}}>— ไม่มีหน่วย —</td></tr>}
              </tbody>
            </table>
          </div>
        </div>
      )}

      {/* ===== หน่วยขนาด ===== */}
      {activeTab==='sizeunits' && (
        <div className="mt-16">
          <h3>จัดการหน่วยขนาด</h3>
          <div className="pm-actions mt-8" style={{ flexWrap:'wrap' }}>
            <input placeholder="เช่น ซม., มม., นิ้ว"
                   value={newSizeUnit}
                   onChange={(e)=>setNewSizeUnit(e.target.value)}
                   style={{ minWidth:260 }} />
            <button type="button" className="btn btn-primary"
                    onClick={()=>{
                      const name = newSizeUnit.trim();
                      if (!name) return;
                      if (sizeUnits.some(u=>u.label===name)) return alert(`มี "${name}" อยู่แล้ว`);
                      setSizeUnits([...sizeUnits, { code:name.toLowerCase().replace(/\s+/g,'_'), label:name }]);
                      setNewSizeUnit('');
                    }}>
              เพิ่มหน่วยขนาด
            </button>
          </div>
          <div className="pm-table-wrap mt-8">
            <table className="pm-table">
              <tbody>
                {sizeUnits.map(u=>(
                  <tr key={u.code}>
                    <td style={{ width:200 }}>{u.code}</td>
                    <td>{u.label}</td>
                    <td className="cell-actions" style={{ width:220 }}>
                      <button className="btn btn-ghost"
                              onClick={()=>{
                                const name = window.prompt('ชื่อหน่วยใหม่', u.label);
                                if (!name || name.trim()===u.label) return;
                                if (sizeUnits.some(x=>x.label===name.trim())) return alert(`มี "${name.trim()}" อยู่แล้ว`);
                                const next = sizeUnits.map(x=> x.code===u.code ? { code:name.trim().toLowerCase().replace(/\s+/g,'_'), label:name.trim() } : x);
                                setSizeUnits(next);
                                setForm(p=>({ ...p, size_unit: p.size_unit===u.label ? name.trim() : p.size_unit }));
                              }}>
                        แก้ไข
                      </button>
                      <button className="btn btn-danger"
                              onClick={()=>{
                                if (!window.confirm(`ลบ "${u.label}" ?`)) return;
                                const next = sizeUnits.filter(x=>x.code!==u.code);
                                setSizeUnits(next);
                                setForm(p=>({ ...p, size_unit: p.size_unit===u.label ? '' : p.size_unit }));
                              }}>
                        ลบ
                      </button>
                    </td>
                  </tr>
                ))}
                {sizeUnits.length===0 && <tr><td colSpan={3} style={{color:'#777'}}>— ไม่มีหน่วย —</td></tr>}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}
