// src/pages/HomeUser.js
// mediaSrc à¸à¸±à¸™ 404, à¸£à¸²à¸„à¸² fallback, à¹à¸–à¸šà¸„à¹‰à¸™à¸«à¸² 3 à¹‚à¸«à¸¡à¸”,
// à¸à¸¥à¹ˆà¸­à¸‡à¸›à¸£à¸°à¹€à¸ à¸— (Type Explorer) + Badge "à¹ƒà¸«à¸¡à¹ˆ N", à¸à¸²à¸£à¹Œà¸”à¸«à¸¡à¸§à¸”à¸¥à¸´à¸‡à¸à¹Œ /plants /tools, à¹à¸¥à¸°à¸Ÿà¸¸à¸•à¹€à¸•à¸­à¸£à¹Œ

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './HomeUser.css';
import { api, path, mediaSrc } from '../lib/api';
import { addItem as addToCart } from '../lib/cart';
import Footer from '../components/Footer';

/* ---------- helpers ---------- */
const toArray = (d) =>
  Array.isArray(d) ? d
  : Array.isArray(d?.items) ? d.items
  : Array.isArray(d?.data?.items) ? d.data.items
  : Array.isArray(d?.data) ? d.data
  : [];

const normalizeProduct = (p, idx) => {
  const imgPath =
    p.image_url || p.cover_url || p.image || (Array.isArray(p.images) ? p.images[0] : '');
  const img = mediaSrc(imgPath);

  const priceRaw =
    p.min_price ??
    p.selling_price ??
    p.price ??
    p.unit_price ??
    p.product_price ??
    0;
  const price = Number(priceRaw) || 0;

  const cid = p.category_id ?? p.categoryId ?? p.cat_id ?? p.category ?? p.category_code ?? null;
  const sid = p.subcategory_id ?? p.subCategoryId ?? p.subcat_id ?? p.subcategory ?? null;
  const cName = p.category_name ?? p.categoryName ?? p.category ?? '';
  const sName = p.subcategory_name ?? p.subCategoryName ?? p.subcategory ?? '';

  return {
    id: p.product_id ?? p.id ?? `p-${idx}`,
    name: p.product_name ?? p.name_th ?? p.name ?? p.title ?? '',
    price,
    img,
    _cid: cid ? String(cid) : '',
    _sid: sid ? String(sid) : '',
    _cName: cName ? String(cName) : '',
    _sName: sName ? String(sName) : '',
  };
};

async function fetchWithCount(url, want) {
  const paramSets = [
    { limit: want },
    { take: want },
    { per_page: want },
    { pageSize: want },
    { top: want },
    {},
  ];
  for (const ps of paramSets) {
    try {
      const r = await api.get(path(url), { params: { ...ps, _: Date.now() } });
      const arr = toArray(r);
      if (arr.length) return arr;
    } catch {}
  }
  return [];
}

// à¸¢à¸´à¸‡à¸„à¹‰à¸™à¸«à¸² server-side (à¸–à¹‰à¸²à¸›à¸¥à¸²à¸¢à¸—à¸²à¸‡à¸£à¸­à¸‡à¸£à¸±à¸š)
async function serverSearch(mode, value, want = 100) {
  const tryList = [];
  if (mode === 'keyword') {
    tryList.push(['/products/search', { q: value, limit: want }]);
    tryList.push(['/products', { q: value, limit: want }]);
    tryList.push(['/products', { keyword: value, limit: want }]);
    tryList.push(['/products', { search: value, limit: want }]);
  } else if (mode === 'category') {
    tryList.push(['/products', { category_id: value, limit: want }]);
    tryList.push(['/products', { cat_id: value, limit: want }]);
    tryList.push(['/products', { category: value, limit: want }]);
    tryList.push(['/products/by-category', { id: value, limit: want }]);
  } else if (mode === 'subcategory') {
    tryList.push(['/products', { subcategory_id: value, limit: want }]);
    tryList.push(['/products', { subcat_id: value, limit: want }]);
    tryList.push(['/products/by-subcategory', { id: value, limit: want }]);
  }

  for (const [ep, params] of tryList) {
    try {
      const r = await api.get(path(ep), { params: { ...params, _: Date.now() } });
      const arr = toArray(r);
      if (arr.length) return arr;
    } catch {}
  }
  return [];
}

export default function HomeUser() {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [loading, setLoading] = useState({ cat: true, sub: true, best: true, all: true, search: false });
  const [err, setErr] = useState({ cat: '', sub: '', best: '', all: '', search: '' });

  // ---------- state à¹à¸–à¸šà¸„à¹‰à¸™à¸«à¸² ----------
  const [mode, setMode] = useState('keyword'); // 'keyword' | 'category' | 'subcategory'
  const [keyword, setKeyword] = useState('');
  const [catValue, setCatValue] = useState('');
  const [subValue, setSubValue] = useState('');
  const [searchResults, setSearchResults] = useState(null); // null = à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸„à¹‰à¸™à¸«à¸²

  // ---------- Type Explorer: badge "à¹ƒà¸«à¸¡à¹ˆ N" ----------
  const [newCounts, setNewCounts] = useState({}); // { [categoryId]: number }

  // à¹‚à¸«à¸¥à¸”à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆ
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, cat: true }));
        let res = null;
        try {
          res = await api.get(path('/categories'), {
            params: { status: 'active', _: Date.now() },
          });
        } catch {
          try {
            res = await api.get(path('/categories'), {
              params: { published: 1, _: Date.now() },
            });
          } catch {
            res = await api.get(path('/categories'), { params: { _: Date.now() } });
          }
        }
        const raw = toArray(res)
          .map((c, idx) => {
            const cid = c.category_id ?? c.id ?? c.code ?? c.slug ?? `cat-${idx}`;
            const name = c.category_name ?? c.name_th ?? c.name ?? '';
            const forcedSlug =
              String(cid) === 'ro1' ? 'plants'
                : String(cid) === 'ro2' ? 'tools'
                : null;
            const slug = forcedSlug ?? (c.slug ? String(c.slug).toLowerCase() : String(cid).toLowerCase());
            const img = c.image_url || c.image || c.cover || c.thumbnail || '';
            return { id: String(cid), slug, name, image: img ? mediaSrc(img) : '' };
          })
          .filter((x) => x.id && x.name);

        // à¹€à¸£à¸µà¸¢à¸‡ Plants â†’ Tools à¸à¹ˆà¸­à¸™
        const order = ['plants', 'tools'];
        raw.sort((a, b) => order.indexOf(a.slug) - order.indexOf(b.slug));
        setCategories(raw);
      } catch {
        setCategories([]);
        setErr((e) => ({ ...e, cat: 'à¹‚à¸«à¸¥à¸”à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ' }));
      } finally {
        setLoading((s) => ({ ...s, cat: false }));
      }
    })();
  }, []);

  // à¹‚à¸«à¸¥à¸”à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, sub: true }));
        let res = null;
        try {
          res = await api.get(path('/subcategories'), { params: { _: Date.now() } });
        } catch {}
        const list = toArray(res).map((s, i) => ({
          id: String(s.subcategory_id ?? s.id ?? s.code ?? `sub-${i}`),
          name: s.subcategory_name ?? s.name_th ?? s.name ?? '',
          category_id: String(s.category_id ?? s.cat_id ?? s.category ?? ''),
        })).filter(x => x.id && x.name);
        setSubcategories(list);
      } catch {
        setSubcategories([]);
      } finally {
        setLoading((s) => ({ ...s, sub: false }));
      }
    })();
  }, []);

  // à¸„à¸³à¸™à¸§à¸“ badge "à¹ƒà¸«à¸¡à¹ˆ N" à¹€à¸—à¸µà¸¢à¸šà¸à¸±à¸šà¸—à¸µà¹ˆà¹€à¸„à¸¢à¹€à¸«à¹‡à¸™à¹„à¸§à¹‰
  useEffect(() => {
    if (!subcategories.length) return;
    const grouped = subcategories.reduce((acc, s) => {
      (acc[s.category_id] ||= []).push(s);
      return acc;
    }, {});
    const counts = {};
    Object.entries(grouped).forEach(([cid, subs]) => {
      const key = `seenSubcats.${cid}`;
      const seen = JSON.parse(localStorage.getItem(key) || '[]');
      const currentIds = subs.map(s => s.id);
      const unseen = currentIds.filter(id => !seen.includes(id));
      counts[cid] = unseen.length;
    });
    setNewCounts(counts);
  }, [subcategories]);

  // à¸šà¸±à¸™à¸—à¸¶à¸à¸§à¹ˆà¸² â€œà¹€à¸«à¹‡à¸™à¹à¸¥à¹‰à¸§â€ (à¹ƒà¸«à¹‰ badge à¸«à¸²à¸¢à¹ƒà¸™à¸à¸²à¸£à¸£à¸µà¹€à¸Ÿà¸£à¸Šà¸„à¸£à¸±à¹‰à¸‡à¸–à¸±à¸”à¹„à¸›)
  useEffect(() => {
    if (!subcategories.length) return;
    const grouped = subcategories.reduce((acc, s) => {
      (acc[s.category_id] ||= []).push(s.id);
      return acc;
    }, {});
    Object.entries(grouped).forEach(([cid, ids]) => {
      localStorage.setItem(`seenSubcats.${cid}`, JSON.stringify(ids));
    });
  }, [subcategories]);

  // à¸ªà¸´à¸™à¸„à¹‰à¸²à¸‚à¸²à¸¢à¸”à¸µ
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, best: true }));
        const endpoints = [
          '/products/best-sellers',
          '/products?sort=popular',
          '/products?featured=1',
          '/products',
        ];
        let bag = [];
        for (const ep of endpoints) {
          const got = await fetchWithCount(ep, 12);
          if (got.length) bag = bag.concat(got);
          if (bag.length >= 12) break;
        }
        const list = bag.map((p, i) => normalizeProduct(p, i)).filter((x) => x.id && x.name);
        setBestSellers(list.slice(0, 5));
        if (!list.length)
          setErr((e) => ({ ...e, best: 'à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸¡à¸µà¸£à¸²à¸¢à¸à¸²à¸£à¸‚à¸²à¸¢à¸”à¸µà¸«à¸£à¸·à¸­ API à¹„à¸¡à¹ˆà¸ªà¹ˆà¸‡à¸‚à¹‰à¸­à¸¡à¸¹à¸¥' }));
      } catch {
        setBestSellers([]);
        setErr((e) => ({ ...e, best: 'à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¸‚à¸²à¸¢à¸”à¸µà¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ' }));
      } finally {
        setLoading((s) => ({ ...s, best: false }));
      }
    })();
  }, []);

  // à¸ªà¸´à¸™à¸„à¹‰à¸²à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸” (à¹ƒà¸Šà¹‰à¹€à¸›à¹‡à¸™à¹à¸«à¸¥à¹ˆà¸‡à¸ªà¸³à¸«à¸£à¸±à¸š client filter)
  useEffect(() => {
    (async () => {
      try {
        setLoading((s) => ({ ...s, all: true }));
        const got = await fetchWithCount('/products', 100);
        const seen = new Set();
        const list = got
          .map((p, i) => normalizeProduct(p, i))
          .filter((x) => x.id && x.name && !seen.has(x.id) && seen.add(x.id));
        list.sort((a, b) => {
          const ai = Number(a.id), bi = Number(b.id);
          if (Number.isFinite(ai) && Number.isFinite(bi)) return bi - ai;
          return 0;
        });
        setAllProducts(list);
        if (!list.length)
          setErr((e) => ({ ...e, all: 'à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸¡à¸µà¸ªà¸´à¸™à¸„à¹‰à¸²à¹ƒà¸™à¸£à¸°à¸šà¸šà¸«à¸£à¸·à¸­ API à¹„à¸¡à¹ˆà¸ªà¹ˆà¸‡à¸‚à¹‰à¸­à¸¡à¸¹à¸¥' }));
      } catch {
        setAllProducts([]);
        setErr((e) => ({ ...e, all: 'à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ' }));
      } finally {
        setLoading((s) => ({ ...s, all: false }));
      }
    })();
  }, []);

  // à¸à¸£à¸­à¸‡à¸à¸±à¹ˆà¸‡ client (à¸ªà¸³à¸£à¸­à¸‡ à¸–à¹‰à¸² server-search à¹€à¸‡à¸µà¸¢à¸š)
  const clientFilter = useMemo(() => {
    return (mode, value) => {
      if (!value) return [];
      const val = String(value).toLowerCase();
      if (mode === 'keyword') {
        return allProducts.filter(p =>
          p.name.toLowerCase().includes(val) ||
          String(p.id).toLowerCase().includes(val)
        );
      }
      if (mode === 'category') {
        return allProducts.filter(p =>
          p._cid.toLowerCase() === val ||
          p._cName.toLowerCase() === val
        );
      }
      if (mode === 'subcategory') {
        return allProducts.filter(p =>
          p._sid.toLowerCase() === val ||
          p._sName.toLowerCase() === val
        );
      }
      return [];
    };
  }, [allProducts]);

  const onSearch = async (e) => {
    e?.preventDefault?.();
    setSearchResults(null);
    setErr((x) => ({ ...x, search: '' }));
    setLoading((s) => ({ ...s, search: true }));

    try {
      let val = '';
      if (mode === 'keyword') val = keyword.trim();
      if (mode === 'category') val = catValue;
      if (mode === 'subcategory') val = subValue;
      if (!val) {
        setSearchResults([]);
        return;
      }

      const srv = await serverSearch(mode, val, 100);
      let list = srv.length ? srv.map((p, i) => normalizeProduct(p, i)).filter(x => x.id && x.name) : [];
      if (!list.length) list = clientFilter(mode, val);

      setSearchResults(list);
      if (!list.length) setErr((x) => ({ ...x, search: 'à¹„à¸¡à¹ˆà¸žà¸šà¸œà¸¥à¸¥à¸±à¸žà¸˜à¹Œà¸—à¸µà¹ˆà¸•à¸£à¸‡à¸à¸±à¸šà¹€à¸‡à¸·à¹ˆà¸­à¸™à¹„à¸‚' }));
    } catch {
      setErr((x) => ({ ...x, search: 'à¸„à¹‰à¸™à¸«à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ' }));
      setSearchResults([]);
    } finally {
      setLoading((s) => ({ ...s, search: false }));
    }
  };

  /* ---------- à¸›à¸¥à¸²à¸¢à¸—à¸²à¸‡à¸à¸²à¸£à¹Œà¸”à¸«à¸¡à¸§à¸”à¸«à¸¥à¸±à¸ ---------- */
  const categoryLink = (c) => {
    const s = String(c.slug || '').toLowerCase();
    if (s === 'plants') return '/plants';
    if (s === 'tools') return '/tools';
    return `/category/${encodeURIComponent(c.id)}`;
  };

  // à¸à¸¥à¸¸à¹ˆà¸¡à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢à¸•à¸²à¸¡à¸«à¸¡à¸§à¸”à¹ƒà¸«à¸à¹ˆ à¸ªà¸³à¸«à¸£à¸±à¸šà¸Šà¸´à¸›/à¸™à¸±à¸šà¸ˆà¸³à¸™à¸§à¸™
  const subsByCat = useMemo(() => {
    return subcategories.reduce((acc, s) => {
      (acc[s.category_id] ||= []).push(s);
      return acc;
    }, {});
  }, [subcategories]);

  const listToShow = searchResults ? searchResults : allProducts;

  return (
    <div className="home-container">
      {/* à¹à¸–à¸šà¸„à¹‰à¸™à¸«à¸² */}
      <form className="search-bar card" onSubmit={onSearch}>
        <div className="filters-row">
          <label>
            à¹‚à¸«à¸¡à¸”à¸„à¹‰à¸™à¸«à¸²
            <select value={mode} onChange={(e) => { setMode(e.target.value); setErr((x)=>({...x,search:''})); }}>
              <option value="keyword">à¸„à¸µà¸¢à¹Œà¹€à¸§à¸´à¸£à¹Œà¸”</option>
              <option value="category">à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆ</option>
              <option value="subcategory">à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢</option>
            </select>
          </label>

          {mode === 'keyword' && (
            <label className="grow">
              à¸„à¸³à¸„à¹‰à¸™à¸«à¸²
              <input
                type="text"
                placeholder="à¹€à¸Šà¹ˆà¸™ à¸à¸¸à¸«à¸¥à¸²à¸š à¸à¸£à¸°à¸šà¸­à¸‡à¹€à¸žà¸Šà¸£ à¸”à¸´à¸™à¸›à¸¥à¸¹à¸â€¦"
                value={keyword}
                onChange={(e) => setKeyword(e.target.value)}
              />
            </label>
          )}

          {mode === 'category' && (
            <label className="grow">
              à¹€à¸¥à¸·à¸­à¸à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆ
              <select value={catValue} onChange={(e) => setCatValue(e.target.value)}>
                <option value="">â€” à¹€à¸¥à¸·à¸­à¸à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆ â€”</option>
                {categories.map(c => (
                  <option key={c.id} value={c.id}>{c.name}</option>
                ))}
              </select>
            </label>
          )}

          {mode === 'subcategory' && (
            <label className="grow">
              à¹€à¸¥à¸·à¸­à¸à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢
              <select value={subValue} onChange={(e) => setSubValue(e.target.value)}>
                <option value="">â€” à¹€à¸¥à¸·à¸­à¸à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢ â€”</option>
                {subcategories.map(s => (
                  <option key={s.id} value={s.id}>{s.name}</option>
                ))}
              </select>
            </label>
          )}

          <button className="btn-search" type="submit" disabled={loading.search}>
            <span className="btn-ico">ðŸ”Ž</span>
            {loading.search ? 'à¸à¸³à¸¥à¸±à¸‡à¸„à¹‰à¸™à¸«à¸²â€¦' : 'à¸„à¹‰à¸™à¸«à¸²'}
          </button>
        </div>
        {!!err.search && <div className="info-inline warn">{err.search}</div>}
      </form>

      {/* à¸à¸¥à¹ˆà¸­à¸‡à¸›à¸£à¸°à¹€à¸ à¸— (Type Explorer) */}
      <section className="type-explorer">
        <h2>ðŸ§­ à¸ªà¸³à¸£à¸§à¸ˆà¸•à¸²à¸¡à¸›à¸£à¸°à¹€à¸ à¸—</h2>
        <div className="type-grid">
          {categories
            .filter(c => ['plants', 'tools'].includes(String(c.slug).toLowerCase()))
            .map(c => {
              const subs = subsByCat[c.id] || [];
              const chips = subs.slice(0, 6);
              const nNew = newCounts[c.id] || 0;
              return (
                <Link key={c.id} className="type-card" to={categoryLink(c)}>
                  {nNew > 0 && <span className="type-badge">à¹ƒà¸«à¸¡à¹ˆ {nNew}</span>}
                  {c.image ? <img src={c.image} alt={c.name} /> : <div className="noimg">â€”</div>}
                  <h3>{c.name}</h3>
                  <div className="chips">
                    {chips.length ? chips.map(s => (
                      <span
                        key={s.id}
                        className="chip"
                        onClick={(e) => {
                          e.preventDefault(); // à¹„à¸¡à¹ˆà¹ƒà¸«à¹‰à¹€à¸›à¸¥à¸µà¹ˆà¸¢à¸™à¸«à¸™à¹‰à¸²
                          setMode('subcategory');
                          setSubValue(s.id);
                          onSearch();
                        }}
                        role="button"
                        tabIndex={0}
                      >
                        {s.name}
                      </span>
                    )) : <span className="chip muted">à¸¢à¸±à¸‡à¹„à¸¡à¹ˆà¸¡à¸µà¸›à¸£à¸°à¹€à¸ à¸—</span>}
                  </div>
                  <div className="type-meta">{subs.length} à¸›à¸£à¸°à¹€à¸ à¸—à¸¢à¹ˆà¸­à¸¢</div>
                </Link>
              );
            })}
        </div>
      </section>

      {/* à¸ªà¸´à¸™à¸„à¹‰à¸²à¸‚à¸²à¸¢à¸”à¸µ */}
      <section className="best-sellers">
        <h2>ðŸŒŸ à¸ªà¸´à¸™à¸„à¹‰à¸²à¸‚à¸²à¸¢à¸”à¸µ</h2>
        {loading.best ? (
          <div className="info-inline">à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”â€¦</div>
        ) : bestSellers.length ? (
          <div className="product-grid">
            {bestSellers.map((item) => (
              <div className="product-card" key={item.id}>
                {item.img ? (
                  <img src={item.img} alt={item.name} />
                ) : (
                  <div style={{ height: 180, display: 'grid', placeItems: 'center', background: '#eef5ef' }}>
                    à¹„à¸¡à¹ˆà¸¡à¸µà¸£à¸¹à¸›
                  </div>
                )}
                <h3>{item.name}</h3>
                <p>{item.price ? `${item.price.toLocaleString()} à¸šà¸²à¸—` : 'â€” à¸šà¸²à¸—'}</p>
                <button className="btn-add" onClick={() => addToCart(item)}>+ à¹€à¸žà¸´à¹ˆà¸¡à¸¥à¸‡à¸•à¸°à¸à¸£à¹‰à¸²</button>
              </div>
            ))}
          </div>
        ) : (
          <div className="info-inline">{err.best || 'à¹„à¸¡à¹ˆà¸¡à¸µà¸‚à¹‰à¸­à¸¡à¸¹à¸¥à¸‚à¸²à¸¢à¸”à¸µ'}</div>
        )}
      </section>

      {/* à¸œà¸¥à¸„à¹‰à¸™à¸«à¸² / à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸” */}
      <section className="all-products">
        <h2>{searchResults ? 'ðŸ”Ž à¸œà¸¥à¸à¸²à¸£à¸„à¹‰à¸™à¸«à¸²' : 'ðŸ›’ à¸ªà¸´à¸™à¸„à¹‰à¸²à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”'}</h2>
        {(loading.all && !searchResults) || loading.search ? (
          <div className="info-inline">à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”â€¦</div>
        ) : listToShow.length ? (
          <div className="product-grid">
            {listToShow.map((p) => (
              <div className="product-card" key={p.id}>
                {p.img ? (
                  <img src={p.img} alt={p.name} />
                ) : (
                  <div style={{ height: 180, display: 'grid', placeItems: 'center', background: '#eef5ef' }}>
                    à¹„à¸¡à¹ˆà¸¡à¸µà¸£à¸¹à¸›
                  </div>
                )}
                <h3>{p.name}</h3>
                <p>{p.price ? `${p.price.toLocaleString()} à¸šà¸²à¸—` : 'â€” à¸šà¸²à¸—'}</p>
                <button className="btn-add" onClick={() => addToCart(p)}>+ à¹€à¸žà¸´à¹ˆà¸¡à¸¥à¸‡à¸•à¸°à¸à¸£à¹‰à¸²</button>
              </div>
            ))}
          </div>
        ) : (
          <div className="info-inline">{(searchResults && err.search) || err.all || 'à¹„à¸¡à¹ˆà¸žà¸šà¸ªà¸´à¸™à¸„à¹‰à¸²'}</div>
        )}
      </section>

      {/* à¸Ÿà¸¸à¸•à¹€à¸•à¸­à¸£à¹Œ */}
      <Footer />
    </div>
  );
}
