import React, { useMemo, useState } from "react";

/**
 * Variants Manager – Shopee‑like Demo Preview (แก้ตามที่ผู้ใช้ขอ)
 * -------------------------------------------------------------
 * • แสดงหัวข้อ "รายละเอียดที่ 1 / รายละเอียดที่ 2 ..."
 * • ปุ่ม "+ เพิ่มรายละเอียด" สำหรับเพิ่ม option ไม่จำกัด
 * • ตาราง Variants แสดงคอลัมน์แยกตามจำนวนรายละเอียดจริง
 */

const mockProduct = { id: 30, name: "กุหลาบแดง ไซซ์จัมโบ้ (Red Rose Jumbo)" };

const initialOptions = [
  { option_id: 1, values: [
    { value_id: 11, value_name: "แดง" },
    { value_id: 12, value_name: "ชมพู" },
  ]},
  { option_id: 2, values: [
    { value_id: 21, value_name: "500 กรัม" },
    { value_id: 22, value_name: "1 กิโล" },
  ]},
];

const mockVariants = [
  {
    variant_id: 1001,
    sku: "P30-RED-500",
    price: 149,
    stock_qty: 12,
    is_active: true,
    image_url: "https://images.unsplash.com/photo-1501004318641-b39e6451bec6?w=256&q=80&auto=format",
    combos: [
      { option_id: 1, value_id: 11 },
      { option_id: 2, value_id: 21 },
    ],
  },
];

function OptionCard({ opt, idx }) {
  return (
    <div className="rounded-xl border p-4 bg-gray-50">
      <div className="font-semibold mb-1">รายละเอียดที่ {idx + 1}</div>
      <label className="text-xs text-gray-500">ค่า</label>
      <input className="mt-1 w-full rounded-lg border px-3 py-2 text-sm" placeholder="พิมพ์ค่า เช่น 'แดง', '500 กรัม'" />
      <div className="mt-2 flex flex-wrap gap-2">
        {(opt.values || []).map(v => (
          <span key={v.value_id} className="inline-flex items-center gap-1 rounded-full bg-gray-100 px-3 py-1 text-xs text-gray-700">{v.value_name}</span>
        ))}
      </div>
      <p className="text-xs text-gray-500 mt-2">ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติ</p>
    </div>
  );
}

function VariantsTable({ options, rows }) {
  const headers = useMemo(() => [
    ...options.map((_, i) => `รายละเอียดที่ ${i + 1}`),
    "ราคา", "คลัง", "เลข SKU", "รูป", "สถานะ", "จัดการ"
  ], [options]);

  const optIndex = useMemo(() => {
    const m = new Map();
    options.forEach((o, i) => m.set(o.option_id, i));
    return m;
  }, [options]);

  const valName = useMemo(() => {
    const m = new Map();
    options.forEach(o => (o.values || []).forEach(v => m.set(v.value_id, v.value_name)));
    return m;
  }, [options]);

  const getCols = (row) => {
    const list = row.combo || row.combos || [];
    const arr = new Array(options.length).fill("—");
    list.forEach(({ option_id, value_id }) => {
      const idx = optIndex.get(option_id);
      if (idx != null) arr[idx] = valName.get(value_id) ?? String(value_id);
    });
    return arr;
  };

  return (
    <div className="overflow-x-auto">
      <table className="w-full border-collapse">
        <thead>
          <tr className="bg-gray-50 text-sm">
            <th className="text-left p-2 w-10">#</th>
            {headers.map((h, i) => (
              <th key={i} className={`p-2 text-left ${h === "ราคา" || h === "คลัง" ? "text-right" : ""}`}>{h}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => {
            const cols = getCols(r);
            return (
              <tr key={r.variant_id} className="border-t text-sm">
                <td className="p-2">{i + 1}</td>
                {cols.map((c, j) => <td key={j} className="p-2">{c}</td>)}
                <td className="p-2 text-right">
                  <input type="number" defaultValue={r.price ?? r.final_price ?? ""} className="w-28 rounded border px-2 py-1 text-right" />
                </td>
                <td className="p-2 text-right">
                  <input type="number" defaultValue={r.stock_qty ?? r.stock ?? 0} className="w-24 rounded border px-2 py-1 text-right" />
                </td>
                <td className="p-2">
                  <input defaultValue={r.sku} className="w-44 rounded border px-2 py-1" />
                </td>
                <td className="p-2">
                  <img src={r.image_url} alt="" className="size-12 rounded-lg object-cover border" />
                </td>
                <td className="p-2">
                  <label className="inline-flex items-center gap-2 text-xs">
                    <input type="checkbox" defaultChecked={!!r.is_active} /> แสดง
                  </label>
                </td>
                <td className="p-2">
                  <div className="flex gap-2">
                    <button className="px-3 py-1 rounded-lg bg-emerald-600 text-white text-xs">บันทึก</button>
                    <button className="px-3 py-1 rounded-lg bg-red-500 text-white text-xs">ลบ</button>
                  </div>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default function DemoVariantsPage() {
  const [options, setOptions] = useState(initialOptions);

  const addOption = () => {
    setOptions([...options, { option_id: Date.now(), values: [] }]);
  };

  return (
    <div className="min-h-dvh bg-gray-100 p-3 md:p-6">
      <div className="mx-auto max-w-6xl space-y-4">
        {/* Header */}
        <div className="flex items-center gap-3">
          <h2 className="text-xl md:text-2xl font-bold">
            ตัวเลือก/Variants — {mockProduct.name} (#{mockProduct.id})
          </h2>
        </div>

        <div className="space-y-4">
          <div className="grid md:grid-cols-2 gap-4">
            {options.map((opt, i) => (
              <OptionCard key={opt.option_id} opt={opt} idx={i} />
            ))}
            <button
              onClick={addOption}
              className="px-4 py-2 rounded-xl bg-indigo-600 text-white text-sm"
            >
              + เพิ่มรายละเอียด
            </button>
          </div>

          <VariantsTable options={options} rows={mockVariants} />
        </div>
      </div>
    </div>
  );
}
