// FRONTEND: src/pages/HomeUser.js ‚Äî Full Enhanced (Live Suggest + Live Results + Auto Search + Variant picker)
import React, { useEffect, useMemo, useRef, useState } from "react";
import { Link } from "react-router-dom";
import "./HomeUser.css";
import { api, path, mediaSrc } from "../lib/api";
import { addItem as addToCart } from "../lib/cart";

/* ---------------- Helpers ---------------- */
const toArray = (d) =>
  Array.isArray(d)
    ? d
    : Array.isArray(d?.items)
    ? d.items
    : Array.isArray(d?.data?.items)
    ? d.data.items
    : Array.isArray(d?.data)
    ? d.data
    : [];

const normalizeProduct = (p, idx) => {
  const imgPath = p.image_url || p.cover_url || p.image || (Array.isArray(p.images) ? p.images[0] : "");
  const img = mediaSrc(imgPath);
  const price =
    Number(p.min_price ?? p.selling_price ?? p.price ?? p.unit_price ?? p.product_price ?? 0) || 0;

  const cid = p.category_id ?? p.categoryId ?? p.cat_id ?? p.category ?? "";
  const sid = p.subcategory_id ?? p.subCategoryId ?? p.subcat_id ?? p.subcategory ?? "";
  const cName = p.category_name ?? p.categoryName ?? p.category ?? "";
  const sName = p.subcategory_name ?? p.subCategoryName ?? p.subcategory ?? "";

  return {
    id: p.product_id ?? p.id ?? `p-${idx}`,
    name: p.product_name ?? p.title ?? p.name_th ?? p.name ?? "",
    price,
    img,
    category_id: String(cid || ""),
    subcategory_id: String(sid || ""),
    category_name: String(cName || ""),
    subcategory_name: String(sName || ""),
  };
};

/* ---------- debounce ---------- */
const useDebounce = (value, delay = 250) => {
  const [v, setV] = useState(value);
  useEffect(() => {
    const t = setTimeout(() => setV(value), delay);
    return () => clearTimeout(t);
  }, [value, delay]);
  return v;
};

/* ---------- fetch helper ---------- */
async function fetchWithCount(url, want = 20) {
  const sets = [{ limit: want }, { take: want }, { per_page: want }, {}];
  for (const ps of sets) {
    try {
      const r = await api.get(path(url), { params: { ...ps, _: Date.now() } });
      const arr = toArray(r);
      if (arr.length) return arr;
    } catch {}
  }
  return [];
}

/* ---------- Variant fetch ---------- */
const variantCache = new Map();
async function loadVariantBrief(productId) {
  if (!productId) return { hasVariants: false, variantCount: 0, options: [], variants: [] };
  const pid = String(productId);
  if (variantCache.has(pid)) return variantCache.get(pid);

  let variants = [];
  try {
    const { data } = await api.get(path(`/api/products/${pid}/variants`), { params: { _: Date.now() } });
    const arr = Array.isArray(data?.rows) ? data.rows : Array.isArray(data) ? data : [];
    variants = arr.map((v, i) => ({
      id: v.variant_id ?? v.id ?? `v-${pid}-${i}`,
      sku: v.sku ?? "",
      price: Number(v.price_override ?? v.price ?? 0) || 0,
      stock: Number(v.stock ?? v.stock_qty ?? v.qty ?? v.onhand ?? 0) || 0,
      label: v.variant_label ?? [v.option1_value, v.option2_value, v.option3_value].filter(Boolean).join(" / "),
    }));
  } catch {}
  const brief = { hasVariants: variants.length > 0, variantCount: variants.length, variants };
  variantCache.set(pid, brief);
  return brief;
}

/* ---------- Product Card ---------- */
const ProductCard = ({ p }) => {
  const [brief, setBrief] = useState(null);
  const [open, setOpen] = useState(false);

  const load = async () => {
    if (!brief) setBrief(await loadVariantBrief(p.id));
    setOpen((v) => !v);
  };

  const addVariant = (v) => {
    addToCart({
      ...p,
      variant_id: v.id,
      sku: v.sku,
      price: v.price,
      name: `${p.name} ‚Äî ${v.label || v.sku}`,
    });
  };

  return (
    <div className="product-card">
      <Link to={`/products/${p.id}`} className="thumb-wrap">
        {p.img ? <img src={p.img} alt={p.name} /> : <div className="noimg">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏£‡∏π‡∏õ</div>}
      </Link>
      <h3><Link to={`/products/${p.id}`}>{p.name}</Link></h3>
      <p>{p.price ? `‡∏ø${p.price.toLocaleString()}` : "‚Äî ‡∏ö‡∏≤‡∏ó"}</p>
      <div className="var-actions">
        <button className="btn-add" onClick={() => addToCart(p)}>+ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏•‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤</button>
        <button className="btn-choose" onClick={load}>‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å</button>
      </div>
      {open && brief?.variants?.length > 0 && (
        <div className="var-panel">
          {brief.variants.map((v) => (
            <div key={v.id} className="var-item">
              <div>
                <div>{v.label || v.sku}</div>
                <div className="l2">‡∏ø{v.price.toLocaleString()} ‚Ä¢ ‡∏Ñ‡∏á‡πÄ‡∏´‡∏•‡∏∑‡∏≠ {v.stock}</div>
              </div>
              <button onClick={() => addVariant(v)}>‡πÄ‡∏û‡∏¥‡πà‡∏°</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

/* ---------- Page ---------- */
export default function HomeUser() {
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [bestSellers, setBestSellers] = useState([]);
  const [allProducts, setAllProducts] = useState([]);
  const [searchResults, setSearchResults] = useState(null);
  const [liveResults, setLiveResults] = useState([]);
  const [mode, setMode] = useState("keyword");
  const [keyword, setKeyword] = useState("");
  const [catValue, setCatValue] = useState("");
  const [subValue, setSubValue] = useState("");
  const [loading, setLoading] = useState(false);
  const dq = useDebounce(keyword, 300);

  /* ---------- Load data ---------- */
  useEffect(() => {
    (async () => {
      const cats = await fetchWithCount("/categories");
      setCategories(toArray(cats).map((c, i) => ({
        id: c.category_id ?? c.id ?? `c-${i}`,
        name: c.category_name ?? c.name,
      })));
      const subs = await fetchWithCount("/subcategories");
      setSubcategories(toArray(subs).map((s, i) => ({
        id: s.subcategory_id ?? s.id ?? `s-${i}`,
        name: s.subcategory_name ?? s.name,
        category_id: s.category_id ?? "",
      })));
      const best = await fetchWithCount("/products?sort=popular", 8);
      setBestSellers(best.map((p, i) => normalizeProduct(p, i)));
      const all = await fetchWithCount("/products", 100);
      setAllProducts(all.map((p, i) => normalizeProduct(p, i)));
    })();
  }, []);

  /* ---------- Filter ---------- */
  const clientFilter = useMemo(() => {
    return (mode, value) => {
      if (!value) return [];
      const v = String(value).toLowerCase();
      if (mode === "keyword")
        return allProducts.filter((p) => p.name.toLowerCase().includes(v));
      if (mode === "category")
        return allProducts.filter(
          (p) => p.category_id === v || p.category_name.toLowerCase().includes(v)
        );
      if (mode === "subcategory")
        return allProducts.filter(
          (p) => p.subcategory_id === v || p.subcategory_name.toLowerCase().includes(v)
        );
      return [];
    };
  }, [allProducts]);

  /* ---------- Live Results ---------- */
  useEffect(() => {
    if (mode !== "keyword") { setLiveResults([]); return; }
    const q = dq.trim().toLowerCase();
    if (q.length < 2) return setLiveResults([]);
    const local = clientFilter("keyword", q).slice(0, 8);
    setLiveResults(local);
  }, [dq, mode, allProducts, clientFilter]);

  /* ---------- Search ---------- */
  const onSearch = (e) => {
    e?.preventDefault?.();
    setLoading(true);
    let val = "";
    if (mode === "keyword") val = keyword.trim();
    if (mode === "category") val = catValue;
    if (mode === "subcategory") val = subValue;
    if (!val) return setLoading(false);
    const res = clientFilter(mode, val);
    setSearchResults(res);
    setLoading(false);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  // Auto search
  useEffect(() => { if (mode === "category" && catValue) onSearch(); }, [catValue]);
  useEffect(() => { if (mode === "subcategory" && subValue) onSearch(); }, [subValue]);

  const listToShow = searchResults ?? (liveResults.length ? liveResults : allProducts);

  return (
    <div className="home-container">
      {/* Search bar */}
      <form className="search-bar card" onSubmit={onSearch}>
        <div className="filters-row">
          <label>
            ‡πÇ‡∏´‡∏°‡∏î‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
            <select value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="keyword">‡∏Ñ‡∏µ‡∏¢‡πå‡πÄ‡∏ß‡∏¥‡∏£‡πå‡∏î</option>
              <option value="category">‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà</option>
              <option value="subcategory">‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢</option>
            </select>
          </label>

          {mode === "keyword" && (
            <input
              type="text"
              placeholder="‡∏û‡∏¥‡∏°‡∏û‡πå‡∏ä‡∏∑‡πà‡∏≠‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤..."
              value={keyword}
              onChange={(e) => setKeyword(e.target.value)}
            />
          )}

          {mode === "category" && (
            <select value={catValue} onChange={(e) => setCatValue(e.target.value)}>
              <option value="">-- ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏´‡∏°‡∏ß‡∏î‡∏´‡∏°‡∏π‡πà --</option>
              {categories.map((c) => (
                <option key={c.id} value={c.id}>{c.name}</option>
              ))}
            </select>
          )}

          {mode === "subcategory" && (
            <select value={subValue} onChange={(e) => setSubValue(e.target.value)}>
              <option value="">-- ‡πÄ‡∏•‡∏∑‡∏≠‡∏Å‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢ --</option>
              {subcategories.map((s) => (
                <option key={s.id} value={s.id}>{s.name}</option>
              ))}
            </select>
          )}

          <button className="btn-search" type="submit" disabled={loading}>
            üîé ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
          </button>
        </div>
      </form>

      {/* üîç Live Results */}
      {mode === "keyword" && liveResults.length > 0 && !searchResults && (
        <section className="live-results">
          <h2>‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå‡∏ó‡∏µ‡πà‡∏ï‡∏£‡∏á‡∏Å‡∏±‡∏ö ‚Äú{keyword}‚Äù</h2>
          <div className="product-grid fullwidth">
            {liveResults.map((p) => <ProductCard key={p.id} p={p} />)}
          </div>
        </section>
      )}

      {/* üåø Type Explorer */}
      <section className="type-explorer">
        <div className="type-grid">
          {categories.map((c) => {
            const subs = subcategories.filter((s) => s.category_id === c.id);
            const chips = subs.slice(0, 6);
            return (
              <Link key={c.id} className="type-card" to="#">
                <h3>{c.name}</h3>
                <div className="chips">
                  {chips.map((s) => (
                    <span
                      key={s.id}
                      className="chip"
                      onClick={(e) => {
                        e.preventDefault();
                        setMode("subcategory");
                        setSubValue(s.id);
                      }}
                    >
                      {s.name}
                    </span>
                  ))}
                </div>
                <div className="type-meta">{subs.length} ‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó‡∏¢‡πà‡∏≠‡∏¢</div>
              </Link>
            );
          })}
        </div>
      </section>

      {/* üåü Best Sellers */}
      {!searchResults && (
        <section className="best-sellers">
          <h2>üåü ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ç‡∏≤‡∏¢‡∏î‡∏µ</h2>
          <div className="product-grid fullwidth">
            {bestSellers.map((p) => <ProductCard key={p.id} p={p} />)}
          </div>
        </section>
      )}

      {/* üõí All or Search */}
      <section className="all-products">
        <h2>{searchResults ? "üîé ‡∏ú‡∏•‡∏Å‡∏≤‡∏£‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤" : "üõí ‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏±‡πâ‡∏á‡∏´‡∏°‡∏î"}</h2>
        {loading ? (
          <div className="info-inline">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡πÇ‡∏´‡∏•‡∏î‚Ä¶</div>
        ) : (
          <div className="product-grid fullwidth">
            {listToShow.length
              ? listToShow.map((p) => <ProductCard key={p.id} p={p} />)
              : <div className="info-inline">‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</div>}
          </div>
        )}
      </section>
    </div>
  );
}
