// frontend/src/admin/Dashboard.js
import React, { useEffect, useState } from 'react';
import {
  dashSummary, dashSalesByMonth, dashOrdersByStatus,
  dashCustomersByProvince, dashProductCountByCategory,
  dashRecentOrders, dashRecentProducts, dashRecentAddresses
} from '../lib/api';
import './Dashboard.css';

export default function Dashboard() {
  const [summary, setSummary] = useState({ products:0, users:0, orders:0, addresses:0, total_sales:0 });
  const [recentOrders, setRecentOrders] = useState([]);
  const [recentProducts, setRecentProducts] = useState([]);
  const [recentAddresses, setRecentAddresses] = useState([]);
  const [salesByMonth, setSalesByMonth] = useState([]);
  const [ordersByStatus, setOrdersByStatus] = useState([]);
  const [customersByProvince, setCustomersByProvince] = useState([]);
  const [productCountByCat, setProductCountByCat] = useState([]);
  const [loading, setLoading] = useState(true);
  const [errMsg, setErrMsg] = useState('');

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        const [sum, sales, byStatus, byProv, byCat, rOrders, rProds, rAddrs] = await Promise.all([
          dashSummary({ _ : Date.now() }),                 // object
          dashSalesByMonth({ _ : Date.now() }),            // array
          dashOrdersByStatus({ _ : Date.now() }),          // array
          dashCustomersByProvince({ _ : Date.now() }),     // array
          dashProductCountByCategory({ _ : Date.now() }),  // array
          dashRecentOrders(),                               // array
          dashRecentProducts(),                             // array
          dashRecentAddresses(),                            // array
        ]);

        setSummary({
          products: sum.products ?? 0,
          users: sum.users ?? 0,
          orders: sum.orders ?? 0,
          addresses: sum.addresses ?? 0,
          total_sales: sum.total_sales ?? 0,
        });
        setSalesByMonth(sales);
        setOrdersByStatus(byStatus);
        setCustomersByProvince(byProv);
        setProductCountByCat(byCat);
        setRecentOrders(rOrders);
        setRecentProducts(rProds);
        setRecentAddresses(rAddrs);
      } catch (e) {
        console.error(e);
        setErrMsg('เกิดข้อผิดพลาดในการโหลดข้อมูล');
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  return (
    <div className="dashboard">
      <h1>Dashboard</h1>

      {errMsg && <div className="error">{errMsg}</div>}
      {loading && <div className="loading">กำลังโหลด...</div>}

      {/* Summary Cards */}
      <div className="cards">
        <Card title="สินค้า" value={summary.products} />
        <Card title="ผู้ใช้งาน" value={summary.users} />
        <Card title="คำสั่งซื้อ" value={summary.orders} />
        <Card title="ที่อยู่จัดส่ง" value={summary.addresses} />
        <Card title="ยอดขายรวม (บาท)" value={Number(summary.total_sales).toLocaleString()} />
      </div>

      {/* ตาราง Recent */}
      <div className="grid">
        <TableBox title="คำสั่งซื้อล่าสุด" headers={['ID','User','วันที่สั่ง','ยอดรวม','สถานะ']}
          rows={recentOrders.map(r => [
            r.order_id, r.user_id, r.ordered_at ?? '-', Number(r.total_amount||0).toLocaleString(), r.status ?? '-'
          ])}
        />
        <TableBox title="สินค้าล่าสุด" headers={['ID','ชื่อสินค้า','เผยแพร่','สถานะเก็บ','สร้างเมื่อ']}
          rows={recentProducts.map(r => [
            r.product_id, r.product_name ?? '-', r.is_published ? 'เผยแพร่':'ซ่อน', r.is_archived ? 'เก็บแล้ว':'ใช้งาน', r.created_at ?? '-'
          ])}
        />
        <TableBox title="ที่อยู่ล่าสุด" headers={['ID','User','จังหวัด','สร้างเมื่อ']}
          rows={recentAddresses.map(r => [
            r.address_id, r.user_id ?? '-', r.province ?? '-', r.created_at ?? '-'
          ])}
        />
      </div>

      {/* หมายเหตุ: ส่วนกราฟเดิมของคุณ ให้ใช้ salesByMonth / ordersByStatus / customersByProvince / productCountByCat
          ซึ่งเป็น Array พร้อม map ได้ทันที */}
    </div>
  );
}

/* ---------- UI helpers ---------- */
function Card({ title, value }) {
  return (
    <div className="card">
      <div className="card-title">{title}</div>
      <div className="card-value">{value}</div>
    </div>
  );
}
function TableBox({ title, headers = [], rows = [] }) {
  return (
    <div className="table-box">
      <div className="table-title">{title}</div>
      <div className="table-wrapper">
        <table>
          <thead>
            <tr>{headers.map((h,i)=><th key={i}>{h}</th>)}</tr>
          </thead>
          <tbody>
            {rows.length === 0 ? (
              <tr><td colSpan={headers.length} style={{textAlign:'center',color:'#6b7280'}}>ไม่มีข้อมูล</td></tr>
            ) : (
              rows.map((r,i)=>(
                <tr key={i}>{r.map((c,j)=><td key={j}>{c}</td>)}</tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
