// src/admin/VariantsManager.js
// ======================================================================
// Variants Manager (ใช้ api+path เหมือน ProductManagement)
// - ดึงชื่อ/รหัสสินค้าแบบทนทาน
// - อัปโหลดรูป/วิดีโอ รองรับหลาย endpoint + คืน URL ได้หลายฟิลด์
// - แสดงพรีวิว + ชิปชื่อไฟล์ + ลบได้
// ======================================================================

import React, { useEffect, useMemo, useRef, useState, useCallback } from "react";
import { useParams, Link } from "react-router-dom";
import { api, path } from "../lib/api";

const MAX_VARIANT_IMAGES = 9;
const MAX_VARIANT_VIDEOS = 3;
const API_BASE = process.env.REACT_APP_API_BASE || "";

/* ---------- utils ---------- */
const upperAZ09 = (s) => (s || "").toUpperCase().replace(/[^A-Z0-9]/g, "");
const rand2 = () => String(Math.floor(Math.random() * 99) + 1).padStart(2, "0");
const firstNonEmpty = (...arr) => arr.find((v) => typeof v === "string" && v.trim())?.trim() || "";
const basename = (u = "") => {
  try { return new URL(u, window.location.origin).pathname.split("/").pop() || u; }
  catch { return u.split("?")[0].split("/").pop() || u; }
};
const normalizeUrl = (u) => {
  if (!u) return null;
  const s = String(u);
  if (/^(https?:|blob:|data:)/.test(s)) return s;
  return `${API_BASE}${s}`;
};
const shortFromName = (name, fallback) => {
  const cleaned = upperAZ09(name);
  return cleaned ? cleaned.slice(0, 3) : (fallback || "PRD");
};
const shortFromOptionText = (text) => {
  if (!text) return "";
  let t = String(text).trim();
  const num = t.match(/(\d+(?:[.,]\d+)?)/)?.[1] || "";
  const unit = t.replace(num, "").trim().toLowerCase();
  const n = num.replace(",", ".").replace(/\.0+$/,"");
  if (num) {
    if (/ก|กรัม|g\b/.test(unit)) return `${n}G`;
    if (/กก|กิโล|kg\b/.test(unit)) return `${n}KG`;
    if (/ลิตร|liter|litre|l\b/.test(unit)) return `${n}L`;
    if (/มล|ml\b/.test(unit)) return `${n}ML`;
    if (/ซม|cm\b/.test(unit)) return `${n}CM`;
    if (/มม|mm\b/.test(unit)) return `${n}MM`;
    if (/นิ้ว|inch|in\b/.test(unit)) return `${n}IN`;
  }
  return upperAZ09(t).slice(0, 6);
};
const guessField = (obj, includes, alt = []) => {
  if (!obj || typeof obj !== "object") return "";
  for (const k of alt) { const v = obj?.[k]; if (typeof v === "string" && v.trim()) return v.trim(); }
  for (const k of Object.keys(obj)) {
    const low = k.toLowerCase();
    if (includes.some(w => low.includes(w))) {
      const v = obj[k]; if (typeof v === "string" && v.trim()) return v.trim();
    }
  }
  return "";
};

/* ---------- Upload helpers (ลองหลาย endpoint + หลายชื่อฟิลด์) ---------- */
async function uploadOne(file) {
  if (!file) return null;
  const isVideo = file.type?.startsWith("video/");
  const candidates = [
    ["/upload", isVideo ? "file" : "file"],
    ["/uploads", isVideo ? "file" : "file"],
    ["/admin/uploads", isVideo ? "file" : "file"],
    ...(isVideo ? [["/upload/video","file"], ["/uploads/videos","file"], ["/admin/uploads/videos","file"]] : []),
  ];

  let lastErr;
  for (const [ep, field] of candidates) {
    try {
      const fd = new FormData();
      fd.append(field, file);
      const { data } = await api.post(path(ep), fd, { headers: { "Content-Type": "multipart/form-data" } });
      const raw =
        (typeof data === "string" ? data : null) ??
        data?.url ?? data?.imageUrl ?? data?.Location ?? data?.location ?? data?.path ??
        data?.data?.url ?? data?.file?.url ?? null;
      const url = normalizeUrl(raw);
      if (url) return url;
    } catch (e) { lastErr = e; }
  }
  if (isVideo) throw lastErr || new Error("เซิร์ฟเวอร์ไม่รองรับวิดีโอ");
  throw lastErr || new Error("อัปโหลดไม่สำเร็จ");
}
async function uploadMany(files) {
  const out = [];
  for (const f of files) {
    const u = await uploadOne(f);
    if (u) out.push(u);
  }
  return out;
}

/* =================================================================== */

export default function VariantsManager() {
  const { productId: pid, id } = useParams();
  const productId = Number(pid || id);

  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]);
  const [variants, setVariants] = useState([]);

  const [draft, setDraft] = useState({
    sku: "", price: "", stock_qty: "",
    images: [], videos: [],
    values: {}, textInputs: {},
  });

  const [skuPrefix, setSkuPrefix] = useState(`P${productId}`);
  const [genPrice, setGenPrice] = useState(0);
  const [genStock, setGenStock] = useState(0);

  const addImageRef = useRef(null);
  const addVideoRef = useRef(null);

  const boom = useCallback((e, fb="เกิดข้อผิดพลาด") => {
    setError(e?.response?.data?.message || e?.response?.data?.error || e?.message || fb);
    console.error(fb, e);
  }, []);

  /* ---------- load product/options/variants ---------- */
  const loadProduct = useCallback(async () => {
    // 1) ลอง /admin/products/:id
    const tryOne = async (ep) => {
      try { const r = await api.get(path(ep)); return r?.data || null; } catch { return null; }
    };
    let raw = await tryOne(`/admin/products/${productId}`);
    if (!raw) raw = await tryOne(`/api/products/${productId}`);
    // 2) fallback: ดึงลิสต์แล้ว find
    if (!raw) {
      const list = await tryOne(`/admin/products?include_archived=1`);
      const arr = Array.isArray(list?.items) ? list.items : (Array.isArray(list) ? list : []);
      raw = arr.find((x) => Number(x.product_id) === productId) || null;
    }
    return raw;
  }, [productId]);

  const load = useCallback(async () => {
    setLoading(true); setError(""); setMsg("");
    try {
      const p = await loadProduct();
      setProduct(p || null);

      const [optRes, varRes] = await Promise.all([
        api.get(path(`/api/variants/products/${productId}/options`)).catch(() => ({ data: [] })),
        api.get(path(`/api/variants/product/${productId}?active=1`)).catch(() => ({ data: [] })),
      ]);
      const opts = Array.isArray(optRes.data) ? optRes.data : (optRes.data?.items || []);
      const vars = Array.isArray(varRes.data) ? varRes.data : (varRes.data?.items || []);
      setOptions(opts || []);
      setVariants(vars || []);
    } catch (e) {
      boom(e, "โหลดข้อมูลไม่สำเร็จ");
    } finally { setLoading(false); }
  }, [productId, loadProduct, boom]);

  useEffect(() => { load(); }, [load]);

  /* ---------- maps ---------- */
  const optIndexById = useMemo(() => {
    const m = new Map(); options.forEach((o, i) => m.set(o.option_id, i)); return m;
  }, [options]);
  const valNameById = useMemo(() => {
    const m = new Map(); options.forEach((o) => (o.values || []).forEach((v) => m.set(v.value_id, v.value_name))); return m;
  }, [options]);

  const comboText = useCallback((row) => {
    const list = row.combo || row.combos || [];
    if (!list.length) return "—";
    return list.map((c) => {
      const idx = optIndexById.get(c.option_id) ?? 0;
      return `รายละเอียดที่ ${idx + 1}: ${valNameById.get(c.value_id) ?? c.value_id}`;
    }).join(" ・ ");
  }, [optIndexById, valNameById]);

  /* ---------- SKU ---------- */
  const buildSkuSuggestion = useCallback(() => {
    const pidPart = String(productId);
    const prodCodeRaw =
      product?.code || product?.product_code || product?.sku_prefix ||
      product?.product_name || product?.name || "";
    const prodCode = shortFromName(prodCodeRaw, `PD${pidPart}`).padEnd(3, "X");

    const firstOptId = Object.keys(draft.textInputs || {})[0];
    const typed = firstOptId ? draft.textInputs[firstOptId] : "";
    let valueShort = shortFromOptionText(typed);
    if (!valueShort) {
      const chosen = Object.entries(draft.values || [])[0];
      if (chosen) {
        const [, valId] = chosen;
        const valName = valNameById.get(Number(valId));
        valueShort = shortFromOptionText(valName);
      }
    }
    const tail = rand2();
    return ["P"+pidPart, prodCode, valueShort || null, tail].filter(Boolean).join("-");
  }, [product, draft.textInputs, draft.values, valNameById, productId]);

  /* ---------- create / update / delete ---------- */
  const chooseOrCreateValue = useCallback(async (option, typed) => {
    const text = (typed || "").trim();
    const oid = option.option_id;
    setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [oid]: text } }));
    if (!text) { setDraft((s) => ({ ...s, values: { ...s.values, [oid]: "" } })); return; }

    const exists = (option.values || []).find((v) => (v.value_name || "").toLowerCase() === text.toLowerCase());
    if (exists) { setDraft((s) => ({ ...s, values: { ...s.values, [oid]: exists.value_id } })); return; }

    try {
      await api.post(path(`/api/variants/options/${oid}/values`), { value_name: text });
      await load();
      const entry = [...valNameById.entries()].find(([, n]) => (n || "").toLowerCase() === text.toLowerCase());
      if (entry) {
        const value_id = entry[0];
        setDraft((s) => ({ ...s, values: { ...s.values, [oid]: value_id }, textInputs: { ...s.textInputs, [oid]: text } }));
      }
    } catch (e) { boom(e, "เพิ่มค่าใหม่ไม่สำเร็จ"); }
  }, [boom, load, valNameById]);

  const createVariant = useCallback(async () => {
    setBusy(true); setError(""); setMsg("");
    try {
      const option_values = Object.entries(draft.values)
        .map(([oid, vid]) => ({ option_id: Number(oid), value_id: Number(vid) }))
        .filter((v) => v.value_id);

      const finalSku = (draft.sku || "").trim() || buildSkuSuggestion();

      await api.post(path(`/api/variants/products/${productId}/variants`), {
        sku: finalSku,
        price: draft.price === "" ? 0 : Math.max(0, Number(draft.price || 0)),
        stock_qty: Math.max(0, Number(draft.stock_qty || 0)),
        image_url: draft.images[0] || null,
        images: draft.images,
        videos: draft.videos,
        is_active: true,
        option_values,
      });

      setDraft({ sku: "", price: "", stock_qty: "", images: [], videos: [], values: {}, textInputs: {} });
      await load(); setMsg("บันทึก Variant สำเร็จ");
    } catch (e) { boom(e, "บันทึก Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }, [draft, productId, load, boom, buildSkuSuggestion]);

  const updateVariant = useCallback(async (variant_id, patch) => {
    setBusy(true); setError(""); setMsg("");
    try {
      const body = {};
      if ("price" in patch) body.price = patch.price === "" ? null : Math.max(0, Number(patch.price || 0));
      if ("stock_qty" in patch) body.stock_qty = Math.max(0, Number(patch.stock_qty || 0));
      if ("sku" in patch) body.sku = patch.sku?.trim() || buildSkuSuggestion();
      if ("is_active" in patch) body.is_active = !!patch.is_active;
      if ("images" in patch) { body.image_url = patch.images?.[0] || null; body.images = patch.images; }
      if ("videos" in patch) { body.videos = patch.videos; }

      await api.put(path(`/api/variants/${variant_id}`), body);
      await load(); setMsg("อัปเดต Variant สำเร็จ");
    } catch (e) { boom(e, "อัปเดต Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }, [boom, load, buildSkuSuggestion]);

  const deleteVariant = useCallback(async (variant_id) => {
    if (!window.confirm("ลบ Variant นี้?")) return;
    setBusy(true);
    try { await api.delete(path(`/api/variants/${variant_id}`)); await load(); setMsg("ลบ Variant สำเร็จ"); }
    catch (e) { boom(e, "ลบ Variant ไม่สำเร็จ"); }
    finally { setBusy(false); }
  }, [boom, load]);

  const generateCombos = useCallback(async () => {
    setBusy(true); setError(""); setMsg("");
    try {
      await api.post(path(`/api/variants/products/${productId}/variants/generate`), {
        sku_prefix: (skuPrefix || "").trim() || `P${productId}`,
        price: Math.max(0, Number(genPrice || 0)),
        stock_qty: Math.max(0, Number(genStock || 0)),
      });
      await load(); setMsg("สร้างคอมบิเนชันสำเร็จ");
    } catch (e) { boom(e, "สร้างคอมบิเนชันไม่สำเร็จ"); }
    finally { setBusy(false); }
  }, [productId, skuPrefix, genPrice, genStock, boom, load]);

  /* ---------- render ---------- */
  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  const productName = firstNonEmpty(
    guessField(product, ["name","title"], ["product_name","name","product_title","title","name_th","title_th","product_name_th"])
  );
  const productCode = firstNonEmpty(
    guessField(product, ["code","sku","barcode"], ["code","product_code","sku_prefix","sku","barcode"])
  );

  return (
    <div style={{ padding: 16, display: "grid", gap: 16 }}>
      <div style={{ display: "flex", alignItems: "center", gap: 10, flexWrap: "wrap" }}>
        <h2 style={{ margin: 0 }}>
          ตัวเลือก/Variants —{" "}
          <span style={{ color: "#065f46", fontWeight: 700 }}>{productName || `#${productId}`}</span>
          {productCode && <span style={{ marginLeft: 8, color: "#6b7280", fontSize: 14 }}>({productCode})</span>}
        </h2>
        <Link to="/admin" style={{ marginLeft: "auto" }}>← กลับแอดมิน</Link>
      </div>

      {(error || msg) && (
        <div style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
          {error && <div style={{ color: "#fff", background: "#d33", padding: "8px 12px", borderRadius: 8 }}>{error}</div>}
          {msg && <div style={{ color: "#fff", background: "green", padding: "8px 12px", borderRadius: 8 }}>{msg}</div>}
        </div>
      )}

      {/* add variant */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>เพิ่มแบบ/ตัวเลือกสินค้า (Variant)</h3>

        <div style={{ display: "grid", gap: 10, maxWidth: 860 }}>
          {/* SKU */}
          <div style={{ display: "grid", gap: 6 }}>
            <label style={{ color: "#6b7280", fontSize: 13 }}>SKU (เช่น P30-PCH-500G)</label>
            <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
              <input
                placeholder="SKU (ปล่อยว่างให้ระบบเติมอัตโนมัติได้)"
                value={draft.sku}
                onChange={(e) => setDraft((s) => ({ ...s, sku: e.target.value }))}
                disabled={busy}
                style={{ flex: "1 1 280px" }}
              />
              <button type="button" onClick={() => setDraft((s) => ({ ...s, sku: buildSkuSuggestion() }))} disabled={busy}>
                เติม SKU อัตโนมัติ
              </button>
            </div>
            <small style={{ color: "#6b6b6b" }}>ระบบจะสร้าง: P + รหัสสินค้า + ค่าตัวเลือก + เลขสุ่ม เช่น P{productId}-PCH-500G-07</small>
          </div>

          {/* media picker */}
          <div style={{ display: "grid", gap: 8 }}>
            <label style={{ fontWeight: 600 }}>สื่อของแบบ/ตัวเลือก</label>
            <div style={{ display: "flex", gap: 12, flexWrap: "wrap" }}>
              <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
                <button type="button" onClick={() => addImageRef.current?.click()} disabled={busy}>เลือกรูป…</button>
                <small style={{ color: "#6b6b6b" }}>(สูงสุด {MAX_VARIANT_IMAGES} รูป)</small>
                <input type="file" accept="image/*" ref={addImageRef} multiple style={{ display: "none" }}
                  onChange={async (e) => {
                    const files = Array.from(e.target.files || []);
                    if (!files.length) return;
                    setBusy(true);
                    try {
                      const remain = Math.max(0, MAX_VARIANT_IMAGES - draft.images.length);
                      const urls = await uploadMany(files.slice(0, remain));
                      setDraft((s) => ({ ...s, images: [...s.images, ...urls] }));
                      setMsg("อัปโหลดรูปสำเร็จ");
                    } catch (err) { setError(err?.response?.data?.message || err?.message || "อัปโหลดรูปไม่สำเร็จ"); }
                    finally { setBusy(false); e.target.value = ""; }
                  }} />
              </div>

              <div style={{ display: "flex", gap: 8, alignItems: "center" }}>
                <button type="button" onClick={() => addVideoRef.current?.click()} disabled={busy}>เลือกวิดีโอ…</button>
                <small style={{ color: "#6b6b6b" }}>(สูงสุด {MAX_VARIANT_VIDEOS} ไฟล์)</small>
                <input type="file" accept="video/*" ref={addVideoRef} multiple style={{ display: "none" }}
                  onChange={async (e) => {
                    const files = Array.from(e.target.files || []);
                    if (!files.length) return;
                    setBusy(true);
                    try {
                      const remain = Math.max(0, MAX_VARIANT_VIDEOS - draft.videos.length);
                      const urls = await uploadMany(files.slice(0, remain));
                      setDraft((s) => ({ ...s, videos: [...s.videos, ...urls] }));
                      setMsg("อัปโหลดวิดีโอสำเร็จ");
                    } catch (err) {
                      setError(err?.response?.data?.message || err?.message || "อัปโหลดวิดีโอไม่สำเร็จ (ตรวจสอบว่าเซิร์ฟเวอร์รองรับ)");
                    } finally { setBusy(false); e.target.value = ""; }
                  }} />
              </div>
            </div>

            {/* chips */}
            {(draft.images.length > 0 || draft.videos.length > 0) && (
              <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
                {draft.images.map((u, i) => (
                  <span key={"img"+i} style={{ display:"inline-flex", alignItems:"center", gap:6, padding:"4px 8px", border:"1px solid #e5e7eb", borderRadius:999 }}>
                    🖼️ {basename(u)} <button type="button" onClick={() => setDraft(s => ({ ...s, images: s.images.filter((_, idx) => idx !== i) }))}>✕</button>
                  </span>
                ))}
                {draft.videos.map((u, i) => (
                  <span key={"vid"+i} style={{ display:"inline-flex", alignItems:"center", gap:6, padding:"4px 8px", border:"1px solid #e5e7eb", borderRadius:999 }}>
                    🎞️ {basename(u)} <button type="button" onClick={() => setDraft(s => ({ ...s, videos: s.videos.filter((_, idx) => idx !== i) }))}>✕</button>
                  </span>
                ))}
              </div>
            )}

            {/* previews */}
            {draft.images.length > 0 && (
              <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(96px, 1fr))", gap: 8 }}>
                {draft.images.map((u, i) => (
                  <div key={u+i} style={{ position: "relative" }}>
                    <img src={u} alt="" style={{ width:"100%", aspectRatio:"1/1", objectFit:"cover", borderRadius:8, border:"1px solid #eee" }}
                      onError={(e)=> (e.currentTarget.style.display = "none")} />
                    <button type="button" onClick={() => setDraft(s => ({ ...s, images: s.images.filter((_, idx) => idx !== i) }))} style={{ position:"absolute", top:4, right:4 }}>✕</button>
                    {i===0 && <div style={{ position:"absolute", left:6, bottom:6, background:"#0008", color:"#fff", fontSize:12, padding:"2px 6px", borderRadius:6 }}>รูปหลัก</div>}
                  </div>
                ))}
              </div>
            )}
            {draft.videos.length > 0 && (
              <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(160px, 1fr))", gap: 8 }}>
                {draft.videos.map((u, i) => (
                  <div key={u+i} style={{ position: "relative" }}>
                    <video src={u} style={{ width:"100%", aspectRatio:"16/9", borderRadius:8, border:"1px solid #eee" }} controls />
                    <button type="button" onClick={() => setDraft(s => ({ ...s, videos: s.videos.filter((_, idx) => idx !== i) }))} style={{ position:"absolute", top:4, right:4 }}>✕</button>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>

        {/* options */}
        <div style={{ marginTop: 12, display: "grid", gap: 12 }}>
          {options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display: "grid", gap: 6 }}>
              <label style={{ fontWeight: 600 }}>{`รายละเอียดที่ ${idx + 1}`}</label>
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่า (เช่น แบบซอง / 500 กรัม / สีแดง) หรือเลือกจากลิสต์ แล้วกด Enter/คลิกออก"
                value={draft.textInputs[opt.option_id] ?? ""}
                onChange={(e) => setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [opt.option_id]: e.target.value } }))}
                onBlur={(e) => chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={(e) => { if (e.key === "Enter") { e.preventDefault(); chooseOrCreateValue(opt, e.currentTarget.value); } }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map((v) => (<option key={v.value_id} value={v.value_name} />))}
              </datalist>
              <small style={{ color: "#666" }}>ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติ</small>
            </div>
          ))}
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 12, flexWrap: "wrap" }}>
          <input type="number" min={0} placeholder="ราคาเฉพาะแบบ/ตัวเลือก"
            value={draft.price} onChange={(e) => setDraft((s) => ({ ...s, price: e.target.value }))} disabled={busy} style={{ maxWidth: 220 }} />
          <input type="number" min={0} placeholder="สต็อก"
            value={draft.stock_qty} onChange={(e) => setDraft((s) => ({ ...s, stock_qty: e.target.value }))} disabled={busy} style={{ maxWidth: 140 }} />
          <button onClick={createVariant} disabled={busy} style={{ fontWeight: 700 }}>บันทึกแบบ/ตัวเลือกนี้</button>
        </div>
      </section>

      {/* generate all combos */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>สร้างแบบ/ตัวเลือกทั้งหมด (ทุกคอมบิเนชันที่มี)</h3>
        <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
          <label>SKU prefix</label>
          <input value={skuPrefix} onChange={(e) => setSkuPrefix(e.target.value)} disabled={busy} />
          <label>ราคาเริ่มต้น</label>
          <input type="number" min={0} value={genPrice} onChange={(e) => setGenPrice(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <label>สต็อกเริ่มต้น</label>
          <input type="number" min={0} value={genStock} onChange={(e) => setGenStock(e.target.value)} disabled={busy} style={{ maxWidth: 160 }} />
          <button onClick={generateCombos} disabled={busy}>สร้างคอมบิเนชัน</button>
        </div>
        <div style={{ color: "#6b6b6b", marginTop: 6, fontSize: 14 }}>
          ระบบจะข้ามรายการที่ซ้ำ (อย่างน้อยควร unique ที่ SKU หรือ (product_id, sku))
        </div>
      </section>

      {/* table */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>รายการแบบ/ตัวเลือก (Variants)</h3>
        <VariantsTable rows={variants} comboText={comboText} onSave={updateVariant} onDelete={deleteVariant} busy={busy} />
      </section>
    </div>
  );
}

/* ---------- table ---------- */
function VariantsTable({ rows, comboText, onSave, onDelete, busy }) {
  return (
    <table width="100%" cellPadding="8" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ background: "#fafafa" }}>
          <th align="left">#</th>
          <th align="left">สื่อ</th>
          <th align="left">SKU</th>
          <th align="left">คอมโบ</th>
          <th align="right">ราคา</th>
          <th align="right">สต็อก</th>
          <th>สถานะ</th>
          <th>จัดการ</th>
        </tr>
      </thead>
      <tbody>
        {(!rows || rows.length === 0) && <tr><td colSpan={8} align="center"><em>ยังไม่มี Variant</em></td></tr>}
        {(rows || []).map((r, i) => (
          <VariantRow key={r.variant_id ?? `${r.sku}-${i}`} idx={i + 1} row={r} comboText={comboText} onSave={onSave} onDelete={onDelete} busy={busy} />
        ))}
      </tbody>
    </table>
  );
}

function VariantRow({ idx, row, comboText, onSave, onDelete, busy }) {
  const [sku, setSku] = useState(row.sku || "");
  const [price, setPrice] = useState(row.final_price ?? row.price ?? "");
  const [stock, setStock] = useState(row.stock ?? row.stock_qty ?? 0);
  const [active, setActive] = useState(row.is_active ?? true);

  const initImages = row.images || row.image_urls || (row.image_url ? [row.image_url] : []);
  const initVideos = row.videos || row.video_urls || [];
  const [images, setImages] = useState(Array.isArray(initImages) ? initImages : []);
  const [videos, setVideos] = useState(Array.isArray(initVideos) ? initVideos : []);

  const imgRef = useRef(null);
  const vidRef = useRef(null);

  return (
    <tr style={{ borderTop: "1px solid #eee" }}>
      <td>{idx}</td>
      <td>
        <div style={{ display: "grid", gap: 6 }}>
          <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
            {images.length === 0 && <div style={{ width: 48, height: 48, border: "1px dashed #ccc", borderRadius: 6 }} />}
            {images.map((u, j) => (
              <div key={u + j} style={{ position: "relative" }}>
                <img src={u} alt="thumb" style={{ width: 48, height: 48, objectFit: "cover", borderRadius: 6, border: "1px solid #eee" }}
                     onError={(e) => (e.currentTarget.style.display = "none")} />
                <button type="button" onClick={() => setImages(prev => prev.filter((_, idx) => idx !== j))}
                        style={{ position: "absolute", top: 2, right: 2, fontSize: 12, lineHeight: 1 }}>✕</button>
                {j === 0 && <div style={{ position: "absolute", left: 4, bottom: 4, background: "#0008", color: "#fff", fontSize: 10, padding: "1px 4px", borderRadius: 4 }}>รูปหลัก</div>}
              </div>
            ))}
          </div>

          {videos.length > 0 && (
            <div style={{ display: "flex", gap: 6, flexWrap: "wrap" }}>
              {videos.map((u, i) => (
                <div key={u + i} style={{ position: "relative" }}>
                  <video src={u} style={{ width: 80, height: 48, borderRadius: 6, border: "1px solid #eee" }} muted controls />
                  <button type="button" onClick={() => setVideos(prev => prev.filter((_, idx) => idx !== i))}
                          style={{ position: "absolute", top: 2, right: 2, fontSize: 12, lineHeight: 1 }}>✕</button>
                </div>
              ))}
            </div>
          )}

          <div style={{ display: "flex", gap: 8, flexWrap: "wrap" }}>
            <input type="file" accept="image/*" ref={imgRef} multiple style={{ display: "none" }}
              onChange={async (e) => {
                const files = Array.from(e.target.files || []); if (!files.length) return;
                try {
                  const remain = Math.max(0, MAX_VARIANT_IMAGES - images.length);
                  const urls = await uploadMany(files.slice(0, remain));
                  setImages((prev) => [...prev, ...urls]);
                } finally { e.target.value = ""; }
              }} />
            <button type="button" onClick={() => imgRef.current?.click()} disabled={busy}>อัปโหลดรูปเพิ่ม…</button>

            <input type="file" accept="video/*" ref={vidRef} multiple style={{ display: "none" }}
              onChange={async (e) => {
                const files = Array.from(e.target.files || []); if (!files.length) return;
                try {
                  const remain = Math.max(0, MAX_VARIANT_VIDEOS - videos.length);
                  const urls = await uploadMany(files.slice(0, remain));
                  setVideos((prev) => [...prev, ...urls]);
                } finally { e.target.value = ""; }
              }} />
            <button type="button" onClick={() => vidRef.current?.click()} disabled={busy}>อัปโหลดวิดีโอเพิ่ม…</button>
          </div>
        </div>
      </td>

      <td><input value={sku} onChange={(e) => setSku(e.target.value)} style={{ width: 180 }} /></td>
      <td>{comboText(row)}</td>
      <td align="right"><input type="number" min={0} value={price} onChange={(e) => setPrice(e.target.value)} style={{ width: 120, textAlign: "right" }} /></td>
      <td align="right"><input type="number" min={0} value={stock} onChange={(e) => setStock(e.target.value)} style={{ width: 100, textAlign: "right" }} /></td>
      <td>
        <label style={{ fontSize: 13 }}>
          <input type="checkbox" checked={!!active} onChange={(e) => setActive(e.target.checked)} />{" "}แสดง
        </label>
      </td>
      <td>
        <button onClick={() => onSave(row.variant_id, { sku, price, stock_qty: stock, is_active: active, images, videos })}
                disabled={busy} style={{ fontWeight: 700 }}>บันทึกแถวนี้</button>
        <button onClick={() => onDelete(row.variant_id)} disabled={busy} style={{ marginLeft: 8 }}>ลบ</button>
      </td>
    </tr>
  );
}
