// [FRONTEND] src/App.js
import React, { useEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate,
  useNavigate, useLocation
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import VariantsManager   from './admin/VariantsManager';
import AdminLayout       from './admin/AdminLayout';
import Dashboard         from './admin/Dashboard';
import ProductEditPage   from './admin/ProductEditPage';

import Addresses     from './account/Addresses';
import SignUp        from './pages/SignUp';
import Login         from './pages/Login';
import ResetPassword from './pages/ResetPassword';
import HomeUser      from './pages/HomeUser';

import ProductList   from './pages/ProductList';
import Plants        from './pages/Plants';
import Tools         from './pages/Tools';
import ProfileModal  from './pages/ProfileModal';

// const InventoryPage = ...

/* ================== ตั้งค่า API BASE ทั้งแอป ================== */
const API_BASE = import.meta?.env?.VITE_API_BASE || 'http://localhost:3001';
axios.defaults.baseURL = API_BASE;
/* ============================================================ */

/* ====================== Helpers ====================== */
const getToken = () => localStorage.getItem('token') || '';
const getRole  = () => (localStorage.getItem('role') || '').toLowerCase();

const isAdminRole     = (r) => ['admin', 'staff', 'superadmin'].includes(String(r||'').toLowerCase());
const isCustomerRole  = (r) => ['customer', 'user', 'member', 'client', 'buyer', ''].includes(String(r||'').toLowerCase());

const bootToken = getToken();
if (bootToken) axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;

/* ==================== ErrorBoundary =================== */
class ErrorBoundary extends React.Component {
  constructor(p){ super(p); this.state = { err: null }; }
  static getDerivedStateFromError(error){ return { err: error }; }
  componentDidCatch(error, info){ console.error('UI error:', error, info); }
  render(){
    if (this.state.err){
      return (
        <div style={{ padding:16 }}>
          <h2>เกิดข้อผิดพลาดในหน้า</h2>
          <pre style={{ background:'#111', color:'#0f0', padding:12, borderRadius:8, overflow:'auto' }}>
{String(this.state.err?.message || this.state.err)}
          </pre>
        </div>
      );
    }
    return this.props.children;
  }
}

/* ====================== Guards ======================== */
function RequireAuth({ children }) {
  const loc = useLocation();
  if (!getToken()) {
    const redirect = encodeURIComponent(loc.pathname + loc.search);
    return <Navigate to={`/login?redirect=${redirect}&msg=login_required`} replace />;
  }
  return children;
}

function RequireAdmin({ children }) {
  const loc = useLocation();
  if (!getToken()) {
    const redirect = encodeURIComponent(loc.pathname + loc.search);
    return <Navigate to={`/login?redirect=${redirect}&msg=login_required`} replace />;
  }
  if (!isAdminRole(getRole())) return <Navigate to="/" replace />;
  return children;
}

/* ============== Header แบบแถบขาวเส้นเดียว ============== */
function Navbar() {
  const nav = useNavigate();
  const loc = useLocation();

  // ซ่อน Header บนหน้า admin
  if (loc.pathname.startsWith('/admin')) return null;

  const hasToken = !!getToken();
  const role = getRole();
  const isHome = loc.pathname === '/' || loc.pathname === '/home' || loc.pathname === '/home-user';
  const displayName = localStorage.getItem('display_name') || 'ผู้ใช้งาน';

  async function logout(){
    // ถ้ามี API logout ให้เรียกได้ เช่น await axios.post('/api/auth/logout')
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }

  // ปุ่มไอคอนพื้นฐาน
  const IconBtn = ({ onClick, title, children, to }) => {
    const common = {
      title,
      style: {
        display:'grid', placeItems:'center',
        width:36, height:36, borderRadius:9999,
        border:'1px solid #e5e7eb', background:'#fff',
        cursor:'pointer'
      }
    };
    if (to) return <button {...common} onClick={() => nav(to)}>{children}</button>;
    return <button {...common} onClick={onClick}>{children}</button>;
  };

  // Avatar (เปลี่ยนเป็น <img src="..."> ได้ภายหลัง)
  const Avatar = (
    <span
      aria-label="บัญชีผู้ใช้" title="บัญชีผู้ใช้"
      style={{
        width:36, height:36, display:'grid', placeItems:'center',
        borderRadius:9999, background:'#f3f4f6', border:'1px solid #e5e7eb'
      }}
    >
      <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
        <path d="M12 12c2.761 0 5-2.239 5-5S14.761 2 12 2 7 4.239 7 7s2.239 5 5 5Zm0 2c-4.418 0-8 2.239-8 5v3h16v-3c0-2.761-3.582-5-8-5Z" fill="#374151"/>
      </svg>
    </span>
  );

  // เมนูผู้ใช้ (dropdown)
  const [open, setOpen] = React.useState(false);
  const menuRef = React.useRef(null);
  React.useEffect(() => {
    const onKey = (e) => e.key === 'Escape' && setOpen(false);
    const onClick = (e) => { if (menuRef.current && !menuRef.current.contains(e.target)) setOpen(false); };
    document.addEventListener('keydown', onKey);
    document.addEventListener('mousedown', onClick);
    return () => { document.removeEventListener('keydown', onKey); document.removeEventListener('mousedown', onClick); };
  }, []);

  return (
    <header
      style={{
        position:'sticky', top:0, zIndex:40,
        background:'#fff', borderBottom:'1px solid #eee'
      }}
    >
      <div
        style={{
          maxWidth:'1200px', margin:'0 auto',
          display:'grid', gridTemplateColumns:'1fr auto 1fr',
          alignItems:'center', gap:12, padding:'12px 16px'
        }}
      >
        {/* ซ้าย: โลโก้/ชื่อร้าน */}
        <div style={{ display:'flex', alignItems:'center', gap:10 }}>
          <button onClick={()=>nav('/')} title="หน้าแรก"
            style={{ background:'transparent', border:0, cursor:'pointer', fontWeight:800, fontSize:18 }}>
            PRACH MAEJO
          </button>
        </div>

        {/* กลาง: สโลแกน/ข้อความสั้น */}
        <div style={{ textAlign:'center', fontSize:12, color:'#6b7280', fontWeight:600 }}>
          GARDEN PRODUCT ONLINE
        </div>

        {/* ขวา: กลุ่มปุ่ม + Greeting เฉพาะหน้าแรก */}
        <div style={{ display:'flex', alignItems:'center', gap:10, justifyContent:'flex-end' }}>
          {/* หน้าแรก */}
          <IconBtn title="หน้าแรก" to="/">
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
              <path d="M3 10.5 12 4l9 6.5V20a1 1 0 0 1-1 1h-5v-6H9v6H4a1 1 0 0 1-1-1v-9.5Z" stroke="#374151" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </IconBtn>

          {/* ประวัติคำสั่งซื้อ (ปรับ path ตามของจริงได้) */}
          {hasToken && (
            <IconBtn title="ประวัติคำสั่งซื้อ" to="/orders">
              <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
                <path d="M7 7h10M7 12h10M7 17h6" stroke="#374151" strokeWidth="1.8" strokeLinecap="round"/>
              </svg>
            </IconBtn>
          )}

          {/* ตะกร้าสินค้า (ปรับ path ตามของจริงได้) */}
          <IconBtn title="ตะกร้าสินค้า" to="/cart">
            <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
              <path d="M6 6h15l-1.5 9h-12L5 3H2" stroke="#374151" strokeWidth="1.8" strokeLinecap="round" strokeLinejoin="round"/>
              <circle cx="9" cy="21" r="1" fill="#374151"/><circle cx="18" cy="21" r="1" fill="#374151"/>
            </svg>
          </IconBtn>

          {/* Greeting เฉพาะหน้าแรก */}
          {hasToken && isHome && (
            <div style={{ display:'flex', alignItems:'center', gap:8, marginLeft:6, paddingLeft:8, borderLeft:'1px solid #eee' }}>
              {Avatar}
              <div style={{ lineHeight:1 }}>
                <div style={{ fontSize:12, color:'#6b7280' }}>สวัสดี</div>
                <div style={{ fontSize:13, fontWeight:600 }}>{displayName}</div>
              </div>
            </div>
          )}

          {/* เมนูผู้ใช้ (หน้าอื่นๆ โชว์ชื่อสั้น + รูป) */}
          <div ref={menuRef} style={{ position:'relative' }}>
            <button
              onClick={()=>setOpen(v=>!v)}
              style={{ display:'flex', alignItems:'center', gap:8, border:0, background:'transparent', paddingLeft:isHome?8:0 }}
              aria-haspopup="menu" aria-expanded={open}
              title="บัญชีผู้ใช้"
            >
              {Avatar}
              {hasToken && !isHome && (
                <span style={{ fontSize:13, fontWeight:600 }}>{displayName}</span>
              )}
            </button>

            {open && (
              <div role="menu"
                style={{
                  position:'absolute', right:0, top:'calc(100% + 8px)',
                  width:220, background:'#fff', border:'1px solid #e5e7eb',
                  borderRadius:10, boxShadow:'0 12px 24px rgba(0,0,0,.08)', zIndex:50, overflow:'hidden'
                }}
              >
                {hasToken ? (
                  <>
                    <button onClick={()=>{ setOpen(false); nav('/profile'); }}
                      style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14 }}>
                      โปรไฟล์ของฉัน
                    </button>
                    <button onClick={()=>{ setOpen(false); nav('/account/addresses'); }}
                      style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14 }}>
                      ที่อยู่ของฉัน
                    </button>
                    {isAdminRole(role) && (
                      <button onClick={()=>{ setOpen(false); nav('/admin'); }}
                        style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14 }}>
                        ไปหน้าแอดมิน
                      </button>
                    )}
                    <hr style={{ margin:0, borderTop:'1px solid #e5e7eb' }} />
                    <button onClick={logout}
                      style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14, color:'#b91c1c' }}>
                      ออกจากระบบ
                    </button>
                  </>
                ) : (
                  <>
                    <button onClick={()=>{ setOpen(false); nav('/login'); }}
                      style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14 }}>
                      เข้าสู่ระบบ
                    </button>
                    <button onClick={()=>{ setOpen(false); nav('/register'); }}
                      style={{ display:'block', width:'100%', textAlign:'left', padding:'10px 12px', background:'#fff', border:0, fontSize:14 }}>
                      สมัครสมาชิก
                    </button>
                  </>
                )}
              </div>
            )}
          </div>
        </div>
      </div>
    </header>
  );
}

/* ======================== App ========================= */
export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <ErrorBoundary>
        <Navbar />
        <Routes>
          {/* ✅ หน้าแรก */}
          <Route path="/" element={<HomeUser />} />
          <Route path="/home-user" element={<Navigate to="/" replace />} />

          {/* ✅ Landing ต้นไม้/อุปกรณ์ + รายการสินค้า */}
          <Route path="/plants" element={<Plants />} />
          <Route path="/tools" element={<Tools />} />
          <Route path="/products" element={<ProductList />} />

          {/* ✅ โปรไฟล์แบบป๊อปอัพ (ต้องล็อกอิน) */}
          <Route
            path="/profile"
            element={
              <RequireAuth>
                <ProfileModal />
              </RequireAuth>
            }
          />

          {/* ต้องล็อกอิน */}
          <Route path="/account/addresses" element={
            <RequireAuth><Addresses /></RequireAuth>
          } />

          {/* แอดมิน */}
          <Route path="/admin" element={
            <RequireAdmin><AdminLayout /></RequireAdmin>
          }>
            <Route index element={<Dashboard />} />
            <Route path="products" element={<ProductManagement />} />
            <Route path="products/:id" element={<ProductEditPage />} />
            {/* <Route path="inventory" element={<InventoryPage />} /> */}
            <Route path="orders"   element={<OrderManagement />} />
            <Route path="products/:productId/variants" element={<VariantsManager />} />
          </Route>

          {/* Auth */}
          <Route path="/login"          element={<Login />} />
          <Route path="/register"       element={<SignUp />} />
          <Route path="/reset-password" element={<ResetPassword />} />

          {/* 404 → หน้าแรก */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </ErrorBoundary>
    </BrowserRouter>
  );
}
