// src/admin/VariantsManager.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, Link } from 'react-router-dom';

export default function VariantsManager() {
  const { productId } = useParams();
  const [loading, setLoading] = useState(true);
  const [data, setData] = useState({ product: null, options: [], skus: [] });
  const [newOptionName, setNewOptionName] = useState('');
  const [skuDraft, setSkuDraft] = useState({ sku: '', price: '', stock_qty: '', values: {} });
  const [msg, setMsg] = useState('');

  async function load() {
    setLoading(true);
    const res = await axios.get(`/api/admin/products/${productId}/variants`);
    setData(res.data);
    setLoading(false);
  }
  useEffect(() => { load(); }, [productId]);

  async function addOption() {
    if (!newOptionName.trim()) return;
    await axios.post(`/api/admin/products/${productId}/options`, { option_name: newOptionName.trim() });
    setNewOptionName(''); load();
  }
  async function addValue(option_id, value_name) {
    if (!value_name.trim()) return;
    await axios.post(`/api/admin/options/${option_id}/values`, { value_name: value_name.trim() });
    load();
  }
  async function deleteOption(option_id) {
    if (!window.confirm('ลบออปชั่นนี้ทั้งหมด?')) return;
    await axios.delete(`/api/admin/options/${option_id}`); load();
  }
  async function deleteValue(value_id) {
    try { await axios.delete(`/api/admin/values/${value_id}`); load(); }
    catch { alert('ลบไม่ได้: ค่านี้ถูกใช้ในบาง SKU'); }
  }
  async function createSku() {
    const values = Object.entries(skuDraft.values)
      .map(([option_id, value_id]) => ({ option_id: Number(option_id), value_id: Number(value_id) }))
      .filter(v => v.value_id);
    if (!values.length) return setMsg('กรุณาเลือกค่าอย่างน้อย 1 รายการ');
    const body = {
      sku: skuDraft.sku || null,
      price: Number(skuDraft.price || 0),
      stock_qty: Number(skuDraft.stock_qty || 0),
      values
    };
    const res = await axios.post(`/api/admin/products/${productId}/skus`, body);
    setMsg(`สร้าง/อัปเดต SKU สำเร็จ: ${res.data?.sku?.sku || '(auto)'}`);
    setSkuDraft({ sku: '', price: '', stock_qty: '', values: {} });
    load();
  }
  async function updateSku(sku_id, row) { await axios.put(`/api/admin/skus/${sku_id}`, row); load(); }
  async function deleteSku(sku_id) {
    if (!window.confirm('ลบ SKU นี้?')) return;
    await axios.delete(`/api/admin/skus/${sku_id}`); load();
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  return (
    <div style={{ padding: 16, display: 'grid', gap: 16 }}>
      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
        <h2 style={{ margin:0 }}>จัดการตัวเลือก — {data.product?.product_name} (#{data.product?.product_id})</h2>
        <Link to="/" style={{ marginLeft:'auto' }}>← กลับหน้าแรก</Link>
      </div>

      {/* OPTIONS */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>ออปชั่น</h3>
        <div style={{ display:'flex', gap:8, marginBottom:8 }}>
          <input placeholder="ชื่อออปชั่น เช่น ขนาด / เสริมราก"
                 value={newOptionName} onChange={e=>setNewOptionName(e.target.value)}
                 style={{ flex:1, padding:8 }} />
          <button onClick={addOption}>+ เพิ่มออปชั่น</button>
        </div>

        {data.options.map(opt => (
          <div key={opt.option_id} style={{ borderTop:'1px dashed #ddd', paddingTop:10, marginTop:10 }}>
            <div style={{ display:'flex', alignItems:'center', gap:8 }}>
              <strong>{opt.option_name}</strong>
              <button onClick={()=>deleteOption(opt.option_id)} style={{ marginLeft:'auto' }}>ลบออปชั่น</button>
            </div>

            <div style={{ display:'flex', gap:8, flexWrap:'wrap', marginTop:8 }}>
              {(opt.values || []).map(v => (
                <span key={v.value_id} style={{ background:'#f5f5f5', padding:'6px 10px', borderRadius:8 }}>
                  {v.value_name}
                  <button onClick={()=>deleteValue(v.value_id)} style={{ marginLeft:8 }}>x</button>
                </span>
              ))}
            </div>

            <AddValueInline onAdd={(name)=>addValue(opt.option_id, name)} />
          </div>
        ))}
      </section>

      {/* CREATE SKU */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>สร้าง SKU</h3>
        <div style={{ display:'grid', gap:10 }}>
          <input placeholder="SKU code (ไม่บังคับ)" value={skuDraft.sku}
                 onChange={e=>setSkuDraft(s=>({ ...s, sku: e.target.value }))} />
          {data.options.map(opt => (
            <div key={opt.option_id} style={{ display:'flex', gap:10 }}>
              <label style={{ width:120 }}>{opt.option_name}</label>
              <select
                value={skuDraft.values[opt.option_id] || ''}
                onChange={e=>setSkuDraft(s=>({
                  ...s, values: { ...s.values, [opt.option_id]: e.target.value }
                }))}
              >
                <option value="">-- เลือกค่า --</option>
                {(opt.values || []).map(v => (
                  <option key={v.value_id} value={v.value_id}>{v.value_name}</option>
                ))}
              </select>
            </div>
          ))}
          <div style={{ display:'flex', gap:8 }}>
            <input type="number" placeholder="ราคา" value={skuDraft.price}
                   onChange={e=>setSkuDraft(s=>({ ...s, price: e.target.value }))} />
            <input type="number" placeholder="สต็อก" value={skuDraft.stock_qty}
                   onChange={e=>setSkuDraft(s=>({ ...s, stock_qty: e.target.value }))} />
            <button onClick={createSku}>+ สร้าง/อัปเดต SKU</button>
          </div>
          {msg && <div style={{ color:'green' }}>{msg}</div>}
        </div>
      </section>

      {/* SKUs LIST */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>SKU ทั้งหมด</h3>
        <table width="100%" cellPadding="8" style={{ borderCollapse:'collapse' }}>
          <thead>
            <tr style={{ background:'#fafafa' }}>
              <th align="left">SKU</th>
              <th align="right">ราคา</th>
              <th align="right">สต็อก</th>
              <th>สถานะ</th>
              <th>คำสั่ง</th>
            </tr>
          </thead>
          <tbody>
            {data.skus.map(s => (
              <tr key={s.sku_id} style={{ borderTop:'1px solid #eee' }}>
                <td>{s.sku || <em>(auto)</em>}</td>
                <td align="right"><InlineNumber value={s.price} onSave={v=>updateSku(s.sku_id,{price:Number(v)})}/></td>
                <td align="right"><InlineNumber value={s.stock_qty} onSave={v=>updateSku(s.sku_id,{stock_qty:Number(v)})}/></td>
                <td>
                  <select value={s.is_active ? '1':'0'} onChange={e=>updateSku(s.sku_id,{is_active:e.target.value==='1'})}>
                    <option value="1">เปิดขาย</option><option value="0">ปิด</option>
                  </select>
                </td>
                <td><button onClick={()=>deleteSku(s.sku_id)}>ลบ</button></td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

function AddValueInline({ onAdd }) {
  const [val, setVal] = useState('');
  return (
    <div style={{ display:'flex', gap:8, marginTop:8 }}>
      <input placeholder="เพิ่มค่าใหม่ เช่น 400ซม." value={val} onChange={e=>setVal(e.target.value)} />
      <button onClick={()=>{ onAdd(val); setVal(''); }}>+ เพิ่มค่า</button>
    </div>
  );
}

function InlineNumber({ value, onSave }) {
  const [v, setV] = useState(value);
  return (
    <span>
      <input type="number" value={v} onChange={e=>setV(e.target.value)} style={{ width:90 }} />
      <button onClick={()=>onSave(v)} style={{ marginLeft:6 }}>บันทึก</button>
    </span>
  );
}
