// src/App.js
// หน้าที่: root ของฝั่งเว็บ จัดการเส้นทาง + การ์ดสิทธิ์พื้นฐาน (Auth)

import React from 'react';
import { BrowserRouter, Routes, Route, NavLink, Navigate, useNavigate } from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement'; // จัดการสินค้า
import OrderManagement from './admin/OrderManagement';     // จัดการคำสั่งซื้อ (แอดมิน)
import Login from './auth/Login';                           // เข้าสู่ระบบ

// ⭐ ใหม่: หน้าใช้งานผู้ใช้ทั่วไป
import Addresses from './account/Addresses';               // จัดการที่อยู่ของฉัน
import CheckoutPage from './pages/CheckoutPage';           // ชำระเงิน

/* สวิตช์การ์ดสิทธิ์:
   - dev ให้ false → เข้าได้ทุกหน้า
   - หลังทดสอบล็อกอินผ่าน ค่อยตั้ง true เพื่อบังคับต้องมี token */
const ENABLE_AUTH_GUARD = false;

/* utility อ่าน token จาก localStorage */
const getToken = () => localStorage.getItem('token');

/* คอมโพเนนต์การ์ดสิทธิ์: ครอบหน้า/เส้นทางที่ต้องล็อกอิน */
function RequireAuth({ children }) {
  if (!ENABLE_AUTH_GUARD) return children;                 // โหมด dev: ปล่อยผ่าน
  const token = getToken();
  if (!token) return <Navigate to="/login" replace />;     // ไม่มี token → ไปล็อกอิน
  return children;
}

/* แถบนำทางด้านบน + ปุ่ม Login/Logout */
function Navbar() {
  const nav = useNavigate();
  const hasToken = !!getToken();

  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none',
    padding: '6px 10px',
    borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  function logout() {
    localStorage.removeItem('token');
    // ไม่จำเป็นต้องลบ default header เพราะเราใช้ interceptor ใน index.js อยู่แล้ว
    delete axios.defaults.headers.common['Authorization'];
    nav('/login', { replace: true });
  }

  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      <NavLink to="/" style={linkStyle} end>สินค้า</NavLink>
      <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      <NavLink to="/checkout" style={linkStyle}>ชำระเงิน</NavLink>
      <NavLink to="/admin/orders" style={linkStyle}>คำสั่งซื้อ (แอดมิน)</NavLink>

      <div style={{ marginLeft: 'auto', display: 'flex', gap: 8 }}>
        {!hasToken ? (
          <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
        ) : (
          <button onClick={logout} style={{ padding: '6px 10px', borderRadius: 8 }}>
            ออกจากระบบ
          </button>
        )}
      </div>
    </nav>
  );
}

/* แอปหลัก: กำหนดเส้นทางทั้งหมด */
export default function App() {
  // หมายเหตุ: ไม่ต้อง set Authorization ใน useEffect ตรงนี้แล้ว
  // เพราะเราแนบ token ด้วย axios interceptor ใน src/index.js อยู่แล้ว

  return (
    <BrowserRouter>
      <Navbar />

      <Routes>
        {/* หน้าแรก: ระบบจัดการสินค้า */}
        <Route
          path="/"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>🌿 ระบบจัดการสินค้า</h1>
              <ProductManagement />
            </div>
          }
        />

        {/* ✅ ใหม่: จัดการที่อยู่ของฉัน (ผู้ใช้ทั่วไป) */}
        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>🏠 ที่อยู่ของฉัน</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        {/* ✅ ใหม่: หน้าชำระเงิน (โหลดที่อยู่ default อัตโนมัติ) */}
        <Route
          path="/checkout"
          element={
            <RequireAuth>
              <CheckoutPage />
            </RequireAuth>
          }
        />

        {/* จัดการคำสั่งซื้อ (แอดมิน) */}
        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>📦 จัดการคำสั่งซื้อ</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        {/* หน้าเข้าสู่ระบบ */}
        <Route path="/login" element={<Login />} />

        {/* กันหลงทาง → กลับหน้าแรก */}
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </BrowserRouter>
  );
}
