// src/admin/VariantsManager.js
// ======================================================================
// หน้าจัดการตัวเลือก (Options/Values) และ Variants ของสินค้า
// ใช้กับ backend เส้นทางเหล่านี้:
// - GET  /api/variants/products/:product_id/options
// - POST /api/variants/products/:product_id/options            (เพิ่ม option)
// - PUT  /api/variants/options/:option_id                      (แก้ชื่อ option)
// - DELETE /api/variants/options/:option_id                    (ลบ option)
// - POST /api/variants/options/:option_id/values               (เพิ่มค่า)
// - DELETE /api/variants/values/:value_id                      (ลบค่า)
// - GET  /api/variants/product/:product_id?active=1            (อ่าน variants)
// - POST /api/variants/products/:product_id/variants           (สร้าง variant)
// - PUT  /api/variants/:variant_id                             (อัปเดต variant)
// - DELETE /api/variants/:variant_id                           (ลบ variant)
// - POST /api/variants/products/:product_id/variants/generate  (สร้างคอมโบทั้งหมด)
// ======================================================================

import React, { useEffect, useMemo, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";

// ใส่ JWT แอดมินจาก localStorage
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

export default function VariantsManager() {
  const params = useParams();
  const productId = Number(params.productId || params.id);

  // สถานะหลักของหน้า
  const [loading, setLoading] = useState(true);
  const [busy, setBusy] = useState(false);
  const [error, setError] = useState("");
  const [msg, setMsg] = useState("");

  // ข้อมูลสินค้า + โครง option/value + variants
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState([]); // [{option_id, option_name, values:[{value_id,value_name}]}]
  const [variants, setVariants] = useState([]); // [{variant_id, sku, final_price/price, stock/stock_qty, combo}]

  // ฟอร์มสร้าง variant
  const [draft, setDraft] = useState({
    sku: "",
    price: "", // ส่งคีย์ "price" (backend จะ map ให้เข้ากับคอลัมน์จริง)
    stock_qty: "", // ส่งคีย์ "stock_qty"
    values: {}, // mapping { [option_id]: value_id }
    textInputs: {}, // เก็บข้อความที่พิมพ์สำหรับแต่ละ option
  });

  // ฟอร์ม generate combos
  const [skuPrefix, setSkuPrefix] = useState(`P${productId}`);
  const [genPrice, setGenPrice] = useState(0);
  const [genStock, setGenStock] = useState(0);

  // ตัวเลือก UI
  const [showOptions, setShowOptions] = useState(false);
  const [useGeneric, setUseGeneric] = useState(true); // ✅ ค่าเริ่มต้น: ใช้ป้ายทั่วไป “รายละเอียดอื่นๆ N”

  const boom = (e, fallback = "เกิดข้อผิดพลาด") => {
    setError(
      e?.response?.data?.message ||
        e?.response?.data?.error ||
        e?.message ||
        fallback
    );
    console.error(fallback, e);
  };

  // ---------- โหลดข้อมูลหลัก ----------
  async function load() {
    setLoading(true);
    setError("");
    setMsg("");
    try {
      // ชื่อสินค้า (public products)
      const p = await axios
        .get(`/api/products/${productId}`)
        .catch(() => ({ data: null }));
      setProduct(p?.data || null);

      // options + variants
      const [{ data: opts }, { data: vars }] = await Promise.all([
        axios.get(`/api/variants/products/${productId}/options`),
        axios.get(`/api/variants/product/${productId}?active=1`),
      ]);
      setOptions(opts || []);
      setVariants(vars || []);
    } catch (e) {
      boom(e, "โหลดข้อมูลไม่สำเร็จ");
    } finally {
      setLoading(false);
    }
  }

  // โหลดครั้งแรกและเมื่อ productId เปลี่ยน
  useEffect(() => {
    load();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [productId]);

  // ---------- helpers เพื่อแสดงคอมโบ/ป้าย ----------
  const optNameById = useMemo(() => {
    const m = new Map();
    options.forEach((o) => m.set(o.option_id, o.option_name));
    return m;
  }, [options]);

  // map: option_id -> index (ไว้คำนวน "รายละเอียดอื่นๆ N")
  const optIndexById = useMemo(() => {
    const m = new Map();
    options.forEach((o, i) => m.set(o.option_id, i));
    return m;
  }, [options]);

  const valNameById = useMemo(() => {
    const m = new Map();
    options.forEach((o) =>
      (o.values || []).forEach((v) => m.set(v.value_id, v.value_name))
    );
    return m;
  }, [options]);

  // ป้ายที่ใช้ในฟอร์มอินพุต (เข้าใจง่าย): "รายละเอียดอื่นๆ N" + (ชื่อจริงถ้ามี)
  const formLabel = (opt, idx) => {
    const generic = `รายละเอียดอื่นๆ ${idx + 1}`;
    if (useGeneric) {
      return opt.option_name ? `${generic}  (${opt.option_name})` : generic;
    }
    return opt.option_name || generic;
  };

  // แสดงคอมโบในตาราง: ใช้ป้ายทั่วไป + ชื่อจริง (อ่านง่าย)
  const comboText = (row) => {
    const list = row.combo || row.combos || [];
    if (!list.length) return "—";
    return list
      .map((c) => {
        const idx = optIndexById.get(c.option_id) ?? 0;
        const generic = `รายละเอียดอื่นๆ ${idx + 1}`;
        const real = optNameById.get(c.option_id);
        const label = useGeneric ? (real ? `${generic} (${real})` : generic) : (real || generic);
        return `${label}: ${valNameById.get(c.value_id) ?? c.value_id}`;
      })
      .join(" ・ ");
  };

  // ---------- actions: OPTIONS ----------
  async function addOption(name) {
    setBusy(true);
    try {
      await axios.post(
        `/api/variants/products/${productId}/options`,
        { option_name: (name || "").trim() || undefined },
        { headers: authHeader() }
      );
      await load();
      setMsg("เพิ่มออปชั่นสำเร็จ");
    } catch (e) {
      boom(e, "เพิ่มออปชั่นไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function renameOption(option_id, name) {
    setBusy(true);
    try {
      await axios.put(
        `/api/variants/options/${option_id}`,
        { option_name: name },
        { headers: authHeader() }
      );
      await load();
      setMsg("เปลี่ยนชื่อออปชั่นสำเร็จ");
    } catch (e) {
      boom(e, "เปลี่ยนชื่อออปชั่นไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteOption(option_id) {
    if (!window.confirm("ลบออปชั่นนี้ทั้งหมด?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/options/${option_id}`, {
        headers: authHeader(),
      });
      await load();
      setMsg("ลบออปชั่นสำเร็จ");
    } catch (e) {
      boom(e, "ลบออปชั่นไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  // ---------- actions: VALUES ----------
  async function addValue(option_id, value_name) {
    setBusy(true);
    try {
      await axios.post(
        `/api/variants/options/${option_id}/values`,
        { value_name },
        { headers: authHeader() }
      );
      await load();
      setMsg(`เพิ่มค่า "${value_name}" สำเร็จ`);
    } catch (e) {
      boom(e, "เพิ่มค่าไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteValue(value_id) {
    if (!window.confirm("ลบค่านี้?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/values/${value_id}`, {
        headers: authHeader(),
      });
      await load();
      setMsg("ลบค่าสำเร็จ");
    } catch (e) {
      boom(e, "ลบค่าไม่สำเร็จ (อาจถูกใช้อยู่ใน variant)");
    } finally {
      setBusy(false);
    }
  }

  // UX: พิมพ์ค่า → ถ้ามีตรงชื่อเดิม เลือกให้เลย, ไม่มีก็สร้างใหม่แล้ว bind เข้าฟอร์ม
  async function chooseOrCreateValue(option, typed) {
    const text = (typed || "").trim();
    const oid = option.option_id;
    setDraft((s) => ({ ...s, textInputs: { ...s.textInputs, [oid]: text } }));

    if (!text) {
      setDraft((s) => ({ ...s, values: { ...s.values, [oid]: "" } }));
      return;
    }

    const exists = (option.values || []).find(
      (v) => (v.value_name || "").toLowerCase() === text.toLowerCase()
    );
    if (exists) {
      setDraft((s) => ({ ...s, values: { ...s.values, [oid]: exists.value_id } }));
      return;
    }

    // ไม่เจอ → สร้างใหม่แล้วรีโหลด options
    await addValue(oid, text);
    // หลังโหลดเสร็จ map ชื่อ -> id อีกที
    const found = [...valNameById.entries()].find(
      ([, name]) => (name || "").toLowerCase() === text.toLowerCase()
    );
    if (found) {
      const value_id = found[0];
      setDraft((s) => ({
        ...s,
        values: { ...s.values, [oid]: value_id },
        textInputs: { ...s.textInputs, [oid]: text },
      }));
    }
  }

  // ---------- actions: VARIANTS ----------
  async function createVariant() {
    setBusy(true);
    setError("");
    setMsg("");
    try {
      const option_values = Object.entries(draft.values)
        .map(([oid, vid]) => ({
          option_id: Number(oid),
          value_id: Number(vid),
        }))
        .filter((v) => v.value_id); // ตัดค่าที่ว่างออก

      await axios.post(
        `/api/variants/products/${productId}/variants`,
        {
          sku: (draft.sku || "").trim() || null,
          price: draft.price === "" ? 0 : Number(draft.price || 0), // ส่งคีย์ price
          stock_qty: Number(draft.stock_qty || 0), // ส่งคีย์ stock_qty
          is_active: true,
          option_values,
        },
        { headers: authHeader() }
      );

      setDraft({
        sku: "",
        price: "",
        stock_qty: "",
        values: {},
        textInputs: {},
      });
      await load();
      setMsg("บันทึก Variant สำเร็จ");
    } catch (e) {
      boom(e, "บันทึก Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function updateVariant(variant_id, patch) {
    setBusy(true);
    setError("");
    setMsg("");
    try {
      const body = {};
      if ("price" in patch)
        body.price = patch.price === "" ? null : Number(patch.price);
      if ("stock_qty" in patch) body.stock_qty = Number(patch.stock_qty || 0);
      if ("sku" in patch) body.sku = patch.sku;
      if ("is_active" in patch) body.is_active = !!patch.is_active;

      await axios.put(`/api/variants/${variant_id}`, body, {
        headers: authHeader(),
      });
      await load();
      setMsg("อัปเดต Variant สำเร็จ");
    } catch (e) {
      boom(e, "อัปเดต Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  async function deleteVariant(variant_id) {
    if (!window.confirm("ลบ Variant นี้?")) return;
    setBusy(true);
    try {
      await axios.delete(`/api/variants/${variant_id}`, {
        headers: authHeader(),
      });
      await load();
      setMsg("ลบ Variant สำเร็จ");
    } catch (e) {
      boom(e, "ลบ Variant ไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  // สร้างคอมโบทั้งหมดตามค่าที่มี
  async function generateCombos() {
    setBusy(true);
    setError("");
    setMsg("");
    try {
      await axios.post(
        `/api/variants/products/${productId}/variants/generate`,
        {
          sku_prefix: (skuPrefix || "").trim() || `P${productId}`,
          price: Number(genPrice || 0),
          stock_qty: Number(genStock || 0),
        },
        { headers: authHeader() }
      );
      await load();
      setMsg("สร้างคอมบิเนชันสำเร็จ");
    } catch (e) {
      boom(e, "สร้างคอมบิเนชันไม่สำเร็จ");
    } finally {
      setBusy(false);
    }
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  return (
    <div style={{ padding: 16, display: "grid", gap: 16 }}>
      {/* Header + toggle ต่าง ๆ */}
      <div style={{ display: "flex", alignItems: "center", gap: 10 }}>
        <h2 style={{ margin: 0 }}>
          ตัวเลือก/Variants —{" "}
          {product?.product_name
            ? `${product.product_name} (#${productId})`
            : `สินค้า #${productId}`}
        </h2>

        {/* ปุ่มเล็ก: โหมดป้ายทั่วไป */}
        <label
          title="เปิด = ใช้ป้าย 'รายละเอียดอื่นๆ 1/2/3' (ช่วยลดความสับสน); ปิด = โชว์ชื่อจริงของออปชั่น เช่น สี/ขนาด"
          style={{ marginLeft: 12, fontSize: 12, opacity: 0.9, display: "flex", alignItems: "center", gap: 6 }}
        >
          <input
            type="checkbox"
            checked={useGeneric}
            onChange={(e) => setUseGeneric(e.target.checked)}
            style={{ transform: "scale(0.9)" }}
          />
          ป้ายทั่วไป "รายละเอียดอื่นๆ"
        </label>

        <button style={{ marginLeft: 12 }} onClick={() => setShowOptions((s) => !s)}>
          {showOptions ? "ซ่อนรายการออปชั่น" : "แสดงรายการออปชั่น"}
        </button>

        {options.length === 0 && (
          <button onClick={() => addOption("")} disabled={busy} style={{ marginLeft: 12 }}>
            + สร้างออปชั่นแรก
          </button>
        )}

        <Link to="/admin" style={{ marginLeft: "auto" }}>
          ← กลับแอดมิน
        </Link>
      </div>

      {/* สถานะข้อความ */}
      {(error || msg) && (
        <div style={{ display: "flex", gap: 12 }}>
          {error && (
            <div style={{ color: "#fff", background: "#d33", padding: "8px 12px", borderRadius: 8 }}>
              {error}
            </div>
          )}
          {msg && (
            <div style={{ color: "#fff", background: "green", padding: "8px 12px", borderRadius: 8 }}>
              {msg}
            </div>
          )}
        </div>
      )}

      {/* ฟอร์มเพิ่ม Variant เดียว */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>เพิ่มแบบ/ตัวเลือกสินค้า (Variant)</h3>

        <div style={{ display: "grid", gap: 10, maxWidth: 560 }}>
          <input
            placeholder="SKU (ถ้ามี)"
            value={draft.sku}
            onChange={(e) => setDraft((s) => ({ ...s, sku: e.target.value }))}
            disabled={busy}
          />
        </div>

        {/* อินพุตต่อ option: พิมพ์ค่าใหม่ได้ / เลือกจากลิสต์ได้ */}
        <div style={{ marginTop: 12, display: "grid", gap: 12 }}>
          {options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display: "grid", gap: 6 }}>
              <label style={{ fontWeight: 600 }}>
                {formLabel(opt, idx)}
              </label>
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่า (เช่น 'แดง', '150 ซม') หรือเลือกจากลิสต์ แล้วกด Enter/คลิกออก"
                value={draft.textInputs[opt.option_id] ?? ""}
                onChange={(e) =>
                  setDraft((s) => ({
                    ...s,
                    textInputs: { ...s.textInputs, [opt.option_id]: e.target.value },
                  }))
                }
                onBlur={(e) => chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    chooseOrCreateValue(opt, e.currentTarget.value);
                  }
                }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map((v) => (
                  <option key={v.value_id} value={v.value_name} />
                ))}
              </datalist>
              <small style={{ color: "#666" }}>
                ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติ
              </small>
            </div>
          ))}
        </div>

        <div style={{ display: "flex", gap: 8, marginTop: 12 }}>
          <input
            type="number"
            placeholder="ราคาเฉพาะแบบ/ตัวเลือก"
            value={draft.price}
            onChange={(e) => setDraft((s) => ({ ...s, price: e.target.value }))}
            disabled={busy}
            style={{ maxWidth: 220 }}
          />
          <input
            type="number"
            placeholder="สต็อก"
            value={draft.stock_qty}
            onChange={(e) => setDraft((s) => ({ ...s, stock_qty: e.target.value }))}
            disabled={busy}
            style={{ maxWidth: 140 }}
          />
          <button onClick={createVariant} disabled={busy}>
            บันทึกแบบ/ตัวเลือก
          </button>
        </div>
      </section>

      {/* Generate variants จากทุกคอมบิเนชัน (สะดวกมากตอนเริ่มต้น) */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>สร้างแบบ/ตัวเลือกทั้งหมด (ทุกคอมบิเนชันที่มี)</h3>
        <div style={{ display: "flex", gap: 8, alignItems: "center", flexWrap: "wrap" }}>
          <label>SKU prefix</label>
          <input value={skuPrefix} onChange={(e) => setSkuPrefix(e.target.value)} disabled={busy} />
          <label>ราคาเริ่มต้น</label>
          <input
            type="number"
            value={genPrice}
            onChange={(e) => setGenPrice(e.target.value)}
            disabled={busy}
            style={{ maxWidth: 160 }}
          />
          <label>สต็อกเริ่มต้น</label>
          <input
            type="number"
            value={genStock}
            onChange={(e) => setGenStock(e.target.value)}
            disabled={busy}
            style={{ maxWidth: 160 }}
          />
          <button onClick={generateCombos} disabled={busy}>
            สร้างคอมบิเนชัน
          </button>
        </div>
        <div style={{ color: "#6b6b6b", marginTop: 6, fontSize: 14 }}>
          ระบบจะข้ามรายการที่ซ้ำ (ควรมี unique อย่างน้อยที่ sku หรือ (product_id, sku) ใน DB)
        </div>
      </section>

      {/* รายการออปชั่น: เพิ่ม/เปลี่ยนชื่อ/ลบ + เพิ่ม/ลบค่า */}
      {showOptions && (
        <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
          <h3>รายการออปชั่น</h3>
          <div style={{ display: "flex", gap: 8, marginBottom: 12 }}>
            <input
              placeholder="ชื่อออปชั่น (ว่างได้)"
              id="new-opt"
              disabled={busy}
              onKeyDown={(e) => {
                if (e.key === "Enter") addOption(e.currentTarget.value);
              }}
            />
            <button
              onClick={() => {
                const el = document.getElementById("new-opt");
                const v = (el?.value || "").trim();
                addOption(v);
                if (el) el.value = "";
              }}
              disabled={busy}
            >
              เพิ่มออปชั่น
            </button>
          </div>

          <div style={{ display: "grid", gap: 14 }}>
            {options.map((opt, idx) => (
              <div key={opt.option_id} style={{ borderTop: "1px dashed #ddd", paddingTop: 10 }}>
                <div style={{ display: "flex", alignItems: "center", gap: 8 }}>
                  <strong>{formLabel(opt, idx)}</strong>

                  <button
                    onClick={() => {
                      const name = prompt("ชื่อออปชั่นใหม่", opt.option_name || "");
                      if (name !== null) renameOption(opt.option_id, name.trim());
                    }}
                    disabled={busy}
                  >
                    เปลี่ยนชื่อ
                  </button>

                  <button
                    onClick={() => deleteOption(opt.option_id)}
                    disabled={busy}
                    style={{ marginLeft: "auto" }}
                  >
                    ลบออปชั่น
                  </button>
                </div>

                {/* ค่าที่อยู่ใต้ option นี้ */}
                <div style={{ display: "flex", gap: 8, flexWrap: "wrap", marginTop: 8 }}>
                  {(opt.values || []).length ? (
                    opt.values.map((v) => (
                      <span
                        key={v.value_id}
                        style={{ background: "#f5f5f5", padding: "6px 10px", borderRadius: 8 }}
                      >
                        {v.value_name}
                        <button
                          onClick={() => deleteValue(v.value_id)}
                          disabled={busy}
                          style={{ marginLeft: 8 }}
                        >
                          x
                        </button>
                      </span>
                    ))
                  ) : (
                    <span style={{ color: "#777" }}>— ยังไม่มีค่า —</span>
                  )}
                </div>

                {/* เพิ่มค่าใหม่แบบเร็ว */}
                <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
                  <input placeholder="เพิ่มค่าใหม่ เช่น แดง / S" id={`add-${opt.option_id}`} disabled={busy} />
                  <button
                    onClick={() => {
                      const el = document.getElementById(`add-${opt.option_id}`);
                      if (el && el.value.trim()) {
                        addValue(opt.option_id, el.value.trim());
                        el.value = "";
                      }
                    }}
                    disabled={busy}
                  >
                    + เพิ่มค่า
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* ตาราง Variants */}
      <section style={{ border: "1px solid #eee", borderRadius: 12, padding: 12 }}>
        <h3>รายการแบบ/ตัวเลือก (Variants)</h3>
        <VariantsTable
          rows={variants}
          comboText={comboText}
          onSave={(id, patch) => updateVariant(id, patch)}
          onDelete={(id) => deleteVariant(id)}
          busy={busy}
        />
      </section>
    </div>
  );
}

/* ตาราง variants แยกคอมโพเนนต์เพื่อใช้ useState ต่อแถวได้ถูกต้อง */
function VariantsTable({ rows, comboText, onSave, onDelete, busy }) {
  return (
    <table width="100%" cellPadding="8" style={{ borderCollapse: "collapse" }}>
      <thead>
        <tr style={{ background: "#fafafa" }}>
          <th align="left">#</th>
          <th align="left">SKU</th>
          <th align="left">คอมโบ</th>
          <th align="right">ราคา</th>
          <th align="right">สต็อก</th>
          <th>สถานะ</th>
          <th>จัดการ</th>
        </tr>
      </thead>
      <tbody>
        {rows.length === 0 && (
          <tr>
            <td colSpan={7} align="center">
              <em>ยังไม่มี Variant</em>
            </td>
          </tr>
        )}
        {rows.map((r, i) => (
          <VariantRow
            key={r.variant_id}
            idx={i + 1}
            row={r}
            comboText={comboText}
            onSave={onSave}
            onDelete={onDelete}
            busy={busy}
          />
        ))}
      </tbody>
    </table>
  );
}

function VariantRow({ idx, row, comboText, onSave, onDelete, busy }) {
  const [sku, setSku] = useState(row.sku || "");
  const [price, setPrice] = useState(row.final_price ?? row.price ?? "");
  const [stock, setStock] = useState(row.stock ?? row.stock_qty ?? 0);
  const [active, setActive] = useState(row.is_active ?? true);

  return (
    <tr style={{ borderTop: "1px solid #eee" }}>
      <td>{idx}</td>
      <td>
        <input value={sku} onChange={(e) => setSku(e.target.value)} style={{ width: 180 }} />
      </td>
      <td>{comboText(row)}</td>
      <td align="right">
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          style={{ width: 120, textAlign: "right" }}
        />
      </td>
      <td align="right">
        <input
          type="number"
          value={stock}
          onChange={(e) => setStock(e.target.value)}
          style={{ width: 100, textAlign: "right" }}
        />
      </td>
      <td>
        <label style={{ fontSize: 13 }}>
          <input type="checkbox" checked={!!active} onChange={(e) => setActive(e.target.checked)} /> แสดง
        </label>
      </td>
      <td>
        <button
          onClick={() => onSave(row.variant_id, { sku, price, stock_qty: stock, is_active: active })}
          disabled={busy}
        >
          บันทึก
        </button>
        <button onClick={() => onDelete(row.variant_id)} disabled={busy} style={{ marginLeft: 8 }}>
          ลบ
        </button>
      </td>
    </tr>
  );
}
