// [FRONTEND] src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();

  // role = 'user' | 'admin' (‡∏≠‡πà‡∏≤‡∏ô‡∏à‡∏≤‡∏Å query)
  const [role, setRole] = useState('user');
  const [form, setForm] = useState({ username: '', password: '', showPw: false });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // ‚úÖ CRA env: ‡πÉ‡∏ä‡πâ REACT_APP_API_BASE; ‡∏ñ‡πâ‡∏≤‡πÑ‡∏°‡πà‡∏ï‡∏±‡πâ‡∏á‡∏à‡∏∞ fallback ‡πÄ‡∏õ‡πá‡∏ô http://localhost:3001
  const API_BASE = (process.env.REACT_APP_API_BASE || 'http://localhost:3001').replace(/\/+$/, '');

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const r = params.get('role');
    if (r) setRole(r);
  }, [location.search]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!form.username.trim() || !form.password.trim()) {
      setError('‡∏Å‡∏£‡∏∏‡∏ì‡∏≤‡∏Å‡∏£‡∏≠‡∏Å‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡πÉ‡∏´‡πâ‡∏Ñ‡∏£‡∏ö‡∏ñ‡πâ‡∏ß‡∏ô');
      return;
    }

    try {
      setLoading(true);

      // üîê ‡∏¢‡∏¥‡∏á‡πÑ‡∏õ‡∏ó‡∏µ‡πà‡πÅ‡∏ö‡πá‡∏Å‡πÄ‡∏≠‡∏ô‡∏î‡πå‡∏à‡∏£‡∏¥‡∏á (JWT) ‚Äî ‡πÄ‡∏£‡∏≤‡∏ï‡πà‡∏≠ /api ‡∏ä‡∏±‡∏î‡πÄ‡∏à‡∏ô
      const payload = {
        username: form.username.trim(),
        email: form.username.trim(),   // ‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡πÅ‡∏ö‡πá‡∏Å‡πÄ‡∏≠‡∏ô‡∏î‡πå‡∏Ñ‡πâ‡∏ô‡πÑ‡∏î‡πâ‡∏ó‡∏±‡πâ‡∏á username/email
        password: form.password.trim(),
      };

      const res = await fetch(`${API_BASE}/api/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'omit',
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        let msg = '‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß';
        try { msg = (await res.json())?.error || msg; } catch {}
        throw new Error(msg);
      }

      const data = await res.json();

      // ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö‡∏´‡∏•‡∏≤‡∏¢‡∏Ñ‡∏µ‡∏¢‡πå‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡∏ù‡∏±‡πà‡∏á‡πÄ‡∏ã‡∏¥‡∏£‡πå‡∏ü‡πÄ‡∏ß‡∏≠‡∏£‡πå‡∏ï‡∏±‡πâ‡∏á‡∏ä‡∏∑‡πà‡∏≠‡πÑ‡∏°‡πà‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡∏Å‡∏±‡∏ô
      const token = data?.token || data?.accessToken || data?.jwt;
      const serverRole = data?.role || data?.user?.role || role;

      if (!token) throw new Error('‡πÑ‡∏°‡πà‡∏û‡∏ö token ‡∏à‡∏≤‡∏Å‡πÄ‡∏ã‡∏¥‡∏£‡πå‡∏ü‡πÄ‡∏ß‡∏≠‡∏£‡πå');

      // üíæ ‡πÄ‡∏Å‡πá‡∏ö token/role
      localStorage.setItem('token', token);
      localStorage.setItem('role', serverRole);

      // üëâ ‡πÅ‡∏ô‡∏ö token ‡πÉ‡∏´‡πâ axios ‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡∏£‡∏µ‡πÄ‡∏Ñ‡∏ß‡∏™‡∏ï‡πå‡∏ñ‡∏±‡∏î‡πÑ‡∏õ
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // ‡∏ô‡∏≥‡∏ó‡∏≤‡∏á‡∏ï‡∏≤‡∏°‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå
      if (serverRole === 'admin') navigate('/admin/products', { replace: true });
      else navigate('/home-user', { replace: true });

    } catch (err) {
      setError(err.message || '‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö‡∏•‡πâ‡∏°‡πÄ‡∏´‡∏•‡∏ß');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      {/* ‡∏ù‡∏±‡πà‡∏á‡πÇ‡∏õ‡∏£‡πÇ‡∏°‡∏ó */}
