// [FRONTEND] src/pages/Login.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

export default function Login() {
  const navigate = useNavigate();
  const location = useLocation();

  // role = 'user' | 'admin' (อ่านจาก query)
  const [role, setRole] = useState('user');
  const [form, setForm] = useState({ username: '', password: '', showPw: false });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // ✅ CRA env: ใช้ REACT_APP_API_BASE; ถ้าไม่ตั้งจะ fallback เป็น http://localhost:3001
  const API_BASE = (process.env.REACT_APP_API_BASE || 'http://localhost:3001').replace(/\/+$/, '');

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const r = params.get('role');
    if (r) setRole(r);
  }, [location.search]);

  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({ ...p, [name]: value }));
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!form.username.trim() || !form.password.trim()) {
      setError('กรุณากรอกข้อมูลให้ครบถ้วน');
      return;
    }

    try {
      setLoading(true);

      // 🔐 ยิงไปที่แบ็กเอนด์จริง (JWT) — เราต่อ /api ชัดเจน
      const payload = {
        username: form.username.trim(),
        email: form.username.trim(),   // เผื่อแบ็กเอนด์ค้นได้ทั้ง username/email
        password: form.password.trim(),
      };

      const res = await fetch(`${API_BASE}/api/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'omit',
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        let msg = 'เข้าสู่ระบบล้มเหลว';
        try { msg = (await res.json())?.error || msg; } catch {}
        throw new Error(msg);
      }

      const data = await res.json();

      // รองรับหลายคีย์เผื่อฝั่งเซิร์ฟเวอร์ตั้งชื่อไม่เหมือนกัน
      const token = data?.token || data?.accessToken || data?.jwt;
      const serverRole = data?.role || data?.user?.role || role;

      if (!token) throw new Error('ไม่พบ token จากเซิร์ฟเวอร์');

      // 💾 เก็บ token/role
      localStorage.setItem('token', token);
      localStorage.setItem('role', serverRole);

      // 👉 แนบ token ให้ axios เผื่อรีเควสต์ถัดไป
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;

      // นำทางตามสิทธิ์
      if (serverRole === 'admin') navigate('/admin/products', { replace: true });
      else navigate('/home-user', { replace: true });

    } catch (err) {
      setError(err.message || 'เข้าสู่ระบบล้มเหลว');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-page">
      {/* ฝั่งโปรโมท */}
      <aside className="promo">
        <div className="promo-inner">
          <div className="promo-badge">
            {role === 'admin' ? 'ADMIN LOGIN' : 'GARDEN PRODUCT ONLINE'}
          </div>
          <h1>
            {role === 'admin'
              ? '🔐 แอดมินปราชญ์แม่โจ้'
              : '🌿 ยินดีต้อนรับเข้าสู่ ร้านปราชญ์แม่โจ้ 🌿'}
          </h1>
          <p>
            {role === 'admin'
              ? 'กรุณาระบุรหัสผ่านให้ถูกต้อง'
              : 'กว่า 100 ชนิด ราคาเป็นมิตร พร้อมบริการจัดส่งรวดเร็ว'}
          </p>
        </div>
      </aside>

      {/* ฝั่งฟอร์มล็อกอิน */}
      <main className="panel">
        <div className="login-card">
          <div className="brand">
            <Link to="/" className="brand-name">PRACH MAEJO</Link>
            <p className="brand-sub">
              {role === 'admin' ? 'เข้าสู่ระบบแอดมิน' : 'กรุณาเข้าสู่ระบบ'}
            </p>
          </div>

          <form className="login-form" onSubmit={onSubmit} noValidate>
            <label className="field">
              <span className="label">
                {role === 'admin' ? 'ชื่อผู้ใช้' : 'อีเมล / เบอร์โทรศัพท์'}
              </span>
              <div className="input-wrap">
                <input
                  type="text"
                  name="username"
                  placeholder={role === 'admin' ? 'กรอกชื่อผู้ใช้' : 'กรอกอีเมลหรือเบอร์ที่ลงทะเบียนไว้'}
                  value={form.username}
                  onChange={onChange}
                  required
                />
              </div>
            </label>

            <label className="field">
              <span className="label">รหัสผ่าน</span>
              <div className="input-wrap">
                <input
                  type={form.showPw ? 'text' : 'password'}
                  name="password"
                  placeholder="********"
                  value={form.password}
                  onChange={onChange}
                  required
                />
                <button
                  type="button"
                  className="pw-toggle"
                  onClick={() => setForm(p => ({ ...p, showPw: !p.showPw }))}>
                  {form.showPw ? '🙈' : '👁️'}
                </button>
              </div>
            </label>

            {error && <div className="form-error" role="alert">{error}</div>}

            <button className="btn-primary" type="submit" disabled={loading}>
              {loading ? 'กำลังเข้าสู่ระบบ…' : 'เข้าสู่ระบบ'}
            </button>
          </form>
        </div>
      </main>
    </div>
  );
}
