// src/components/HeaderClassic.js
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { getCount, CART_EVENT } from "../lib/cart";
import ProfileModal from "../pages/ProfileModal";

export default function HeaderClassic() {
  // ‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡∏Å‡∏≤‡∏£‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô + ‡∏ä‡∏∑‡πà‡∏≠
  const [isAuth, setIsAuth] = useState(!!localStorage.getItem("token"));
  const [fullname, setFullname] = useState(localStorage.getItem("fullname") || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô");
  const avatar = process.env.PUBLIC_URL + "/profile.jpg";

  // ‡πÅ‡∏™‡∏î‡∏á‡∏à‡∏≥‡∏ô‡∏ß‡∏ô‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡πÉ‡∏ô‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤ (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≠‡∏ô‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô)
  const [cartCount, setCartCount] = useState(getCount());
  useEffect(() => {
    const onChanged = () => setCartCount(getCount());
    window.addEventListener(CART_EVENT, onChanged);
    return () => window.removeEventListener(CART_EVENT, onChanged);
  }, []);

  // ‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏™‡∏ñ‡∏≤‡∏ô‡∏∞‡πÄ‡∏°‡∏∑‡πà‡∏≠ localStorage ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô (‡πÄ‡∏ä‡πà‡∏ô ‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô/‡∏•‡πá‡∏≠‡∏Å‡πÄ‡∏≠‡∏≤‡∏ï‡πå‡∏à‡∏≤‡∏Å‡πÅ‡∏ó‡πá‡∏ö‡∏≠‡∏∑‡πà‡∏ô ‡∏´‡∏£‡∏∑‡∏≠‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏ä‡∏∑‡πà‡∏≠)
  useEffect(() => {
    const onStorage = () => {
      setIsAuth(!!localStorage.getItem("token"));
      setFullname(localStorage.getItem("fullname") || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô");
    };
    window.addEventListener("storage", onStorage);
    // ‡∏£‡∏≠‡∏á‡∏£‡∏±‡∏ö custom event ‡∏ó‡∏µ‡πà‡πÄ‡∏£‡∏≤‡∏à‡∏∞‡∏¢‡∏¥‡∏á‡πÄ‡∏≠‡∏á‡πÄ‡∏ß‡∏•‡∏≤ auth ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô
    window.addEventListener("auth:changed", onStorage);
    window.addEventListener("profile:changed", onStorage);
    return () => {
      window.removeEventListener("storage", onStorage);
      window.removeEventListener("auth:changed", onStorage);
      window.removeEventListener("profile:changed", onStorage);
    };
  }, []);

  const [showProfile, setShowProfile] = useState(false);
  const [initialTab, setInitialTab] = useState("profile");

  function openProfile(tab = "profile") {
    setInitialTab(tab);
    setShowProfile(true);
  }

  function logout() {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    // ‡πÅ‡∏à‡πâ‡∏á component ‡∏≠‡∏∑‡πà‡∏ô ‡πÜ ‡∏ß‡πà‡∏≤ auth ‡πÄ‡∏õ‡∏•‡∏µ‡πà‡∏¢‡∏ô‡πÅ‡∏•‡πâ‡∏ß
    window.dispatchEvent(new Event("auth:changed"));
    window.location.href = "/login";
  }

  return (
    <>
      <header
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between",
          padding: "10px 16px",
          borderBottom: "1px solid #e5e7eb",
          background: "#fff",
          position: "sticky",
          top: 0,
          zIndex: 50,
        }}
      >
        {/* ‡πÇ‡∏•‡πÇ‡∏Å‡πâ */}
        <Link
          to="/"
          style={{
            fontWeight: 700,
            fontSize: 20,
            color: "#16a34a",
            textDecoration: "none",
          }}
        >
          PRACH MAEJO
        </Link>

        <div style={{ display: "flex", alignItems: "center", gap: 16 }}>
          {/* ‡∏ñ‡πâ‡∏≤‡∏¢‡∏±‡∏á‡πÑ‡∏°‡πà‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô ‚Üí ‡πÅ‡∏™‡∏î‡∏á‡∏õ‡∏∏‡πà‡∏°‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö/‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å */}
          {!isAuth ? (
            <>
              <Link to="/login" className="link-login">‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö</Link>
              <Link to="/signup" className="link-signup">‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å</Link>
            </>
          ) : (
            <>
              {/* ‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô‡πÅ‡∏•‡πâ‡∏ß‡πÄ‡∏ó‡πà‡∏≤‡∏ô‡∏±‡πâ‡∏ô ‚Üí ‡πÅ‡∏™‡∏î‡∏á‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤/‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠/‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå/‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö */}
              <div style={{ position: "relative" }}>
                <Link to="/cart" title="‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤" aria-label="‡∏ï‡∏∞‡∏Å‡∏£‡πâ‡∏≤‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤">
                  üõí
                </Link>
                {cartCount > 0 && (
                  <span
                    style={{
                      position: "absolute",
                      top: -6,
                      right: -10,
                      background: "#ef4444",
                      color: "#fff",
                      borderRadius: 999,
                      fontSize: 12,
                      minWidth: 18,
                      height: 18,
                      display: "grid",
                      placeItems: "center",
                      padding: "0 4px",
                    }}
                  >
                    {cartCount}
                  </span>
                )}
              </div>

              <button
                onClick={() => openProfile("orders")}
                style={{
                  border: "none",
                  background: "transparent",
                  cursor: "pointer",
                  fontSize: 18,
                }}
                title="‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠"
                aria-label="‡∏õ‡∏£‡∏∞‡∏ß‡∏±‡∏ï‡∏¥‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠"
              >
                üìë
              </button>

              <button
                onClick={() => openProfile("profile")}
                style={{
                  display: "flex",
                  alignItems: "center",
                  gap: 8,
                  border: "none",
                  background: "transparent",
                  cursor: "pointer",
                }}
                title="‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå"
                aria-label="‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå"
              >
                <img
                  src={avatar}
                  alt="avatar"
                  onError={(e) =>
                    (e.currentTarget.src = process.env.PUBLIC_URL + "/logo.png")
                  }
                  style={{
                    width: 32,
                    height: 32,
                    borderRadius: 999,
                    objectFit: "cover",
                  }}
                />
                <span>{fullname}</span>
              </button>

              <button
                onClick={logout}
                style={{ border: "none", background: "transparent", cursor: "pointer" }}
                title="‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö"
                aria-label="‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö"
              >
                üö™
              </button>
            </>
          )}
        </div>
      </header>

      {/* Modal ‡πÇ‡∏õ‡∏£‡πÑ‡∏ü‡∏•‡πå (‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡∏ï‡∏≠‡∏ô‡∏•‡πá‡∏≠‡∏Å‡∏≠‡∏¥‡∏ô) */}
      {isAuth && showProfile && (
        <ProfileModal
          initialTab={initialTab}
          onClose={() => {
            setShowProfile(false);
            // ‡∏´‡∏•‡∏±‡∏á‡∏õ‡∏¥‡∏î modal ‡∏•‡∏≠‡∏á‡∏î‡∏∂‡∏á‡∏ä‡∏∑‡πà‡∏≠‡πÉ‡∏´‡∏°‡πà‡∏à‡∏≤‡∏Å localStorage ‡πÄ‡∏ú‡∏∑‡πà‡∏≠‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡πÄ‡∏û‡∏¥‡πà‡∏á‡∏≠‡∏±‡∏õ‡πÄ‡∏î‡∏ï‡∏ä‡∏∑‡πà‡∏≠
            setFullname(localStorage.getItem("fullname") || "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ‡∏á‡∏≤‡∏ô");
            window.dispatchEvent(new Event("profile:changed"));
          }}
        />
      )}
    </>
  );
}
