// src/components/VariantPicker.jsx
import React, { useEffect, useRef, useState } from "react";
import { searchItems, ensureVariant } from "../lib/api";

export default function VariantPicker({
  onChange,                          // (variant) => void
  placeholder = "พิมพ์ชื่อสินค้า หรือ SKU เพื่อค้นหา",
  autoFocus = false,
  mode = "in",                       // "in" = ไม่กรองสต๊อก, "out" = ต้องมีสต๊อก > 0
}) {
  const [q, setQ] = useState("");
  const [open, setOpen] = useState(false);
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(false);
  const [creating, setCreating] = useState(false); // ระหว่างสร้าง SKU อัตโนมัติ
  const [highlight, setHighlight] = useState(-1);
  const boxRef = useRef(null);
  const inputRef = useRef(null);
  const timer = useRef(null);

  // โฟกัสอัตโนมัติ
  useEffect(() => {
    if (autoFocus && inputRef.current) inputRef.current.focus();
  }, [autoFocus]);

  // ปิด dropdown เมื่อคลิกนอกกล่อง
  useEffect(() => {
    const onDoc = (e) => {
      if (!boxRef.current?.contains(e.target)) setOpen(false);
    };
    document.addEventListener("mousedown", onDoc);
    return () => document.removeEventListener("mousedown", onDoc);
  }, []);

  // debounce 300ms แล้วค่อยยิงค้นหาใหม่ -> /api/inventory/search/items
  useEffect(() => {
    clearTimeout(timer.current);
    if (!q.trim()) { setItems([]); setOpen(false); return; }

    timer.current = setTimeout(async () => {
      setLoading(true);
      try {
        const rows = await searchItems(q.trim(), { mode, limit: 20 });
        // กันเคส backend เก่า: ถ้า mode="out" ให้กรองสต๊อก>0 เผื่อไว้
        const list = mode === "out"
          ? (rows || []).filter((r) => Number(r.stock_qty ?? r.stock ?? 0) > 0)
          : (rows || []);
        setItems(Array.isArray(list) ? list : []);
        setOpen(true);
        setHighlight(-1);
      } catch (e) {
        console.error("VariantPicker search error:", e);
        setItems([]);
        setOpen(true);
      } finally {
        setLoading(false);
      }
    }, 300);

    return () => clearTimeout(timer.current);
  }, [q, mode]);

  const materializeVariant = (raw) => ({
    kind: "variant",
    variant_id: raw.variant_id,
    sku: raw.sku,
    product_id: raw.product_id,
    product_name: raw.product_name,
    stock_qty: Number(raw.stock_qty ?? raw.stock ?? 0),
    unit_name: raw.unit_name ?? null,
  });

  const choose = async (it) => {
    try {
      if (it?.kind === "product" || (!it?.variant_id && it?.product_id)) {
        // ยังไม่มี SKU -> สร้างอัตโนมัติ
        setCreating(true);
        const v = await ensureVariant(it.product_id);
        const picked = materializeVariant({
          ...v,
          product_name: it.product_name || v.product_name,
        });
        onChange && onChange(picked);
        setQ(`${picked.product_name} (${picked.sku})`);
        setOpen(false);
        return;
      }

      // มี SKU อยู่แล้ว -> ส่งออกทันที
      const picked = materializeVariant(it);
      onChange && onChange(picked);
      setQ(`${picked.product_name} (${picked.sku})`);
      setOpen(false);
    } catch (err) {
      console.error("ensureVariant failed:", err);
      alert("สร้าง SKU อัตโนมัติไม่สำเร็จ กรุณาลองใหม่");
    } finally {
      setCreating(false);
    }
  };

  const onKeyDown = async (e) => {
    if (!open) return;
    if (e.key === "ArrowDown") {
      e.preventDefault();
      setHighlight((h) => Math.min(items.length - 1, h + 1));
    } else if (e.key === "ArrowUp") {
      e.preventDefault();
      setHighlight((h) => Math.max(0, h - 1));
    } else if (e.key === "Enter") {
      if (highlight >= 0 && items[highlight]) {
        e.preventDefault();
        await choose(items[highlight]);
      }
    } else if (e.key === "Escape") {
      setOpen(false);
    }
  };

  return (
    <div className="relative" ref={boxRef}>
      <label className="block text-sm">ค้นหาสินค้า / SKU</label>
      <div className="relative">
        <input
          ref={inputRef}
          className="border rounded px-3 py-2 w-full"
          value={q}
          onChange={(e) => setQ(e.target.value)}
          onFocus={() => { if (items.length > 0) setOpen(true); }}
          onKeyDown={onKeyDown}
          placeholder={placeholder}
          autoFocus={autoFocus}
        />
        {(loading || creating) && (
          <div className="absolute right-2 top-1/2 -translate-y-1/2 text-xs text-gray-500">
            {creating ? "กำลังสร้าง SKU…" : "กำลังค้นหา…"}
          </div>
        )}
      </div>

      {open && (
        <div className="absolute z-50 mt-1 bg-white border rounded shadow w-full max-h-80 overflow-auto">
          {!loading && items.length === 0 && (
            <div className="p-2 text-sm text-gray-500">ไม่พบรายการ</div>
          )}
          {items.map((it, idx) => {
            const stock = Number(it.stock_qty ?? it.stock ?? 0);
            const isVariant = it.kind === "variant";
            const key = `${it.kind}:${it.variant_id || it.product_id}`;
            return (
              <button
                key={key}
                className={`w-full text-left p-2 hover:bg-gray-100 ${idx === highlight ? "bg-gray-100" : ""}`}
                onMouseEnter={() => setHighlight(idx)}
                onMouseDown={(e) => { e.preventDefault(); choose(it); }}
                type="button"
              >
                <div className="text-sm font-medium">
                  {it.product_name}{" "}
                  {isVariant && it.sku ? <span className="text-gray-600">({it.sku})</span> : null}
                </div>
                <div className="text-xs text-gray-600">
                  {isVariant
                    ? <>คงคลัง: {stock.toLocaleString("th-TH")}</>
                    : <>ยังไม่มี SKU (จะสร้างอัตโนมัติ)</>}
                </div>
              </button>
            );
          })}
        </div>
      )}
    </div>
  );
}
