// src/admin/VariantsManager.js
// ======================================================================
// VariantsManager — โหลด/แก้ไข Variants ให้แสดง "ตัวเลือก" ตามที่บันทึกใน DB จริง
// - รองรับรูปแบบข้อมูลหลายแบบ: details[], options[], option1_value.., และ option_text ("สี:ชมพู | ขนาด:81")
// - ดึงทั้งหมด, ใส่ตารางแก้ไขได้ (ราคา, SKU, รูป), ชื่อคอลัมน์ตั้งจากข้อมูลจริง
// - ถ้าไม่มี variants เดิม → โหมด chips เพื่อสร้างคอมโบใหม่
// - เซฟแบบ batch: upsert-batch → PUT/POST variants → generate
// ======================================================================

import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useParams, Link } from "react-router-dom";
import { api, path, mediaSrc } from "../lib/api";
import "./VariantsManager.css";

/* ---------- Helpers ---------- */
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

const pickStr = (...xs) => xs.find((v) => typeof v === "string" && v.trim())?.trim() || "";
const rand2 = () => String(Math.floor(Math.random() * 99) + 1).padStart(2, "0");

/* ---------- Upload ---------- */
const extractUrl = (data) => {
  if (!data) return null;
  if (typeof data === "string") return data;
  const direct =
    data.url || data.path || data.imageUrl || data.Location || data.location ||
    data.file?.url || data.file?.path || data.data?.url || data.data?.path || data.data?.imageUrl;
  if (direct) return direct;
  try {
    const flat = JSON.stringify(data);
    const m = flat.match(/"([^"]+\.(?:png|jpe?g|webp|gif))"/i);
    return m ? m[1] : null;
  } catch { return null; }
};

const tryUpload = async (file) => {
  const fd = new FormData();
  fd.append("file", file);
  const res = await api.post(path("/upload"), fd, {
    headers: { ...authHeader(), "Content-Type": "multipart/form-data" },
  });
  const url = extractUrl(res?.data);
  if (!url) throw new Error("UPLOAD_OK_BUT_NO_URL");
  return url;
};

/* ---------- Normalize product ---------- */
const normalizeProduct = (raw) => {
  if (!raw) return null;
  const d = raw.data || raw.item || raw.product || raw.result || raw;
  return d || null;
};

/* ---------- Parse helpers for options ---------- */
function parseFromOptionText(optionText) {
  // ตัวอย่าง: "สี:ชมพู | ขนาด:81 | วัสดุ:"
  const res = { names: [], values: [] };
  const s = String(optionText || "").trim();
  if (!s) return res;
  const parts = s.split("|").map((x) => x.trim()).filter(Boolean);
  for (const p of parts) {
    const m = p.split(":");
    if (m.length >= 2) {
      const name = String(m[0] || "").trim();
      const value = String(m.slice(1).join(":") || "").trim(); // กันเคสมี ":" ในค่า
      res.names.push(name || "");
      res.values.push(value || "");
    } else {
      // ไม่มี ":" → ดันเป็น value อย่างเดียว
      res.names.push("");
      res.values.push(p);
    }
  }
  // เติมให้ครบ 3
  while (res.names.length < 3) res.names.push("");
  while (res.values.length < 3) res.values.push("");
  return res;
}

function firstNonEmpty(...xs) {
  for (const x of xs) {
    if (x !== undefined && x !== null) {
      const s = String(x).trim();
      if (s) return s;
    }
  }
  return "";
}

/* ---------- Variant normalize (โฟกัสเรื่องตัวเลือก) ---------- */
const normVariant = (v) => {
  if (!v) return null;

  // รูปและราคา/sku
  const sku = firstNonEmpty(v.sku, v.SKU, v.code);
  const price = v.price ?? v.unit_price ?? v.sale_price ?? "";
  const image = firstNonEmpty(v.image_url, v.image, v.imageUrl, v.variant_image);

  // 1) รูปแบบ details[] หรือ options[]: [{name,value},...]
  const details = Array.isArray(v.details) ? v.details
                 : Array.isArray(v.options) ? v.options
                 : null;

  let names = ["ตัวเลือก 1", "ตัวเลือก 2", "ตัวเลือก 3"];
  let values = ["", "", ""];

  if (details && details.length) {
    for (let i = 0; i < 3; i++) {
      if (!details[i]) break;
      names[i]  = firstNonEmpty(details[i].name, details[i].label, names[i]);
      values[i] = firstNonEmpty(details[i].value, details[i].val, "");
    }
  } else {
    // 2) รูปแบบ field เดี่ยว ๆ
    names[0] = firstNonEmpty(v.opt1_name, v.option1_name, v.name1, v.option1, names[0]);
    names[1] = firstNonEmpty(v.opt2_name, v.option2_name, v.name2, v.option2, names[1]);
    names[2] = firstNonEmpty(v.opt3_name, v.option3_name, v.name3, v.option3, "");

    values[0] = firstNonEmpty(v.option1_value, v.opt1, v.value1, v.color, "");
    values[1] = firstNonEmpty(v.option2_value, v.opt2, v.value2, v.size, "");
    values[2] = firstNonEmpty(v.option3_value, v.opt3, v.value3, v.material, "");

    // 3) ถ้ายังว่าง → แยกจาก option_text
    if (!values[0] && !values[1] && !values[2]) {
      const parsed = parseFromOptionText(firstNonEmpty(v.option_text, v.options_text));
      names = parsed.names.map((x, i) => firstNonEmpty(names[i], x));
      values = parsed.values.map((x) => firstNonEmpty(x));
    }
  }

  return {
    variant_id: v.variant_id ?? v.id ?? v.product_variant_id ?? null,
    sku,
    price: typeof price === "number" ? String(price) : String(price || ""),
    image: image || null,
    opt1_name: names[0] || "ตัวเลือก 1",
    opt2_name: names[1] || "ตัวเลือก 2",
    opt3_name: names[2] || "",
    opt1: values[0] || "",
    opt2: values[1] || "",
    opt3: values[2] || "",
  };
};

/* ====================================================================== */
export default function VariantsManager() {
  const { id, productId: pid } = useParams();
  const productId = Number(id || pid);

  // product for header + SKU
  const [product, setProduct] = useState(null);

  // option names
  const [opt1Name, setOpt1Name] = useState("สี");
  const [opt2Name, setOpt2Name] = useState("ขนาด");
  const [opt3Name, setOpt3Name] = useState("");

  // combos (แถวที่แก้ไข)
  const [rows, setRows] = useState([]);

  // โหมดไม่มี variants → สร้างใหม่ด้วย chips
  const [opt1Values, setOpt1Values] = useState([]);
  const [opt2Values, setOpt2Values] = useState([]);
  const [opt3Values, setOpt3Values] = useState([]);

  const [saving, setSaving] = useState(false);
  const [msg, setMsg] = useState("");

  /* ---------- Load product ---------- */
  const loadProduct = useCallback(async () => {
    if (!productId) return;
    let p = null;
    try {
      const r = await api.get(path(`/admin/products/${productId}`));
      p = normalizeProduct(r?.data);
    } catch {}
    if (!p) {
      try {
        const r = await api.get(path(`/products/${productId}`));
        p = normalizeProduct(r?.data);
      } catch {}
    }
    if (!p) {
      try {
        const r = await api.get(path(`/admin/products`), { params: { include_archived: 1 } });
        const arr = Array.isArray(r?.data?.items) ? r.data.items : (Array.isArray(r?.data) ? r.data : []);
        p = (arr || []).find((x) => Number(x.product_id) === productId) || null;
      } catch {}
    }
    setProduct(p || null);
  }, [productId]);

  /* ---------- Load variants เดิม ---------- */
  const loadVariants = useCallback(async () => {
    if (!productId) return;
    let list = [];

    const endpoints = [
      path(`/admin/products/${productId}/variants`),
      path(`/products/${productId}/variants`),
      path(`/api/variants/by-product/${productId}`),
      path(`/api/variants?product_id=${productId}`),
    ];

    for (const url of endpoints) {
      try {
        const r = await api.get(url, { headers: authHeader() });
        const arr = r?.data?.items || r?.data?.rows || r?.data || [];
        if (Array.isArray(arr) && arr.length) { list = arr; break; }
      } catch { /* try next */ }
    }

    if (list.length) {
      const mapped = list.map((v) => {
        const n = normVariant(v);
        return {
          __existing: true,
          variant_id: n.variant_id,
          opt1: n.opt1, opt2: n.opt2, opt3: n.opt3,
          price: n.price, sku: n.sku, image: n.image,
          opt1_name: n.opt1_name, opt2_name: n.opt2_name, opt3_name: n.opt3_name,
        };
      });
      setRows(mapped);
      // ตั้งชื่อหัวคอลัมน์จากข้อมูลจริง
      const first = mapped[0];
      if (first) {
        if (first.opt1_name) setOpt1Name(first.opt1_name);
        if (first.opt2_name) setOpt2Name(first.opt2_name);
        if (first.opt3_name) setOpt3Name(first.opt3_name);
      }
    } else {
      setRows([]); // ไม่มี → chips mode
    }
  }, [productId]);

  useEffect(() => { loadProduct(); loadVariants(); }, [loadProduct, loadVariants]);

  const productName = useMemo(
    () => pickStr(product?.product_name, product?.name, product?.title),
    [product]
  );
  const productCode = useMemo(
    () => pickStr(product?.product_code, product?.code, `P${productId}`),
    [product, productId]
  );

  /* ---------- สร้างคอมโบใหม่ เมื่อไม่มี rows เดิม ---------- */
  useEffect(() => {
    if (rows.length) return; // อย่าสร้างทับของเดิม
    const v1 = opt1Values.length ? opt1Values : [null];
    const v2 = opt2Values.length ? opt2Values : [null];
    const v3 = opt3Values.length ? opt3Values : [null];
    const combos = [];
    for (const a of v1) for (const b of v2) for (const c of v3) {
      combos.push({ opt1: a, opt2: b, opt3: c, price: "", sku: "", image: null });
    }
    setRows(combos);
  }, [opt1Values, opt2Values, opt3Values]); // eslint-disable-line react-hooks/exhaustive-deps

  /* ---------- chip helpers ---------- */
  const addTag = (list, setList, txt) => {
    const v = String(txt || "").trim();
    if (!v) return;
    if (!list.some((x) => x === v)) setList([...list, v]);
  };
  const removeTag = (list, setList, idx) => setList(list.filter((_, i) => i !== idx));

  /* ---------- per-row upload ---------- */
  const onUploadRowImage = async (file, idx) => {
    if (!file) return;
    const tempUrl = URL.createObjectURL(file);
    setRows((prev) => {
      const a = [...prev];
      a[idx] = { ...a[idx], image: tempUrl, __temp: true };
      return a;
    });
    try {
      const serverUrl = await tryUpload(file);
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: serverUrl, __temp: false };
        return a;
      });
    } catch (e) {
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: null, __temp: false };
        return a;
      });
      alert(e?.message || "Upload failed");
    } finally {
      setTimeout(() => URL.revokeObjectURL(tempUrl), 2500);
    }
  };

  /* ---------- Auto SKU ---------- */
  const autoSku = () => {
    const base = productCode || `P${productId}`;
    setRows((prev) =>
      prev.map((r) => {
        const parts = [base, r.opt1, r.opt2, r.opt3].filter(Boolean);
        return { ...r, sku: parts.join("-") + "-" + rand2() };
      })
    );
  };

  /* ---------- Save batch ---------- */
  const saveAll = async () => {
    setSaving(true);
    try {
      // keep only rows that have something or are existing
      const effective = rows.filter((r) => r.__existing || r.opt1 || r.opt2 || r.opt3);

      const payloadRows = effective.map((r) => ({
        variant_id: r.variant_id || null,
        details: [
          r.opt1 ? { name: opt1Name, value: r.opt1 } : null,
          r.opt2 ? { name: opt2Name, value: r.opt2 } : null,
          r.opt3 && (opt3Name || r.opt3) ? { name: opt3Name || "ตัวเลือก 3", value: r.opt3 } : null,
        ].filter(Boolean),
        sku: r.sku || null,
        price: r.price !== "" ? Number(r.price) : null,
        image_url: r.image || null,
      }));

      if (!payloadRows.length) {
        alert("ยังไม่มีแถวที่มีรายละเอียดสำหรับบันทึก");
        setSaving(false);
        return;
      }

      let done = false;

      // 0) upsert-batch
      try {
        await api.post(
          path(`/api/variants/upsert-batch`),
          { product_id: productId, rows: payloadRows },
          { headers: authHeader() }
        );
        done = true;
      } catch {}

      if (!done) {
        // 1) PUT /admin/products/:id/variants
        try {
          await api.put(
            path(`/admin/products/${productId}/variants`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
          done = true;
        } catch {}
      }

      if (!done) {
        // 2) POST /admin/products/:id/variants/save
        try {
          await api.post(
            path(`/admin/products/${productId}/variants/save`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
          done = true;
        } catch {}
      }

      if (!done) {
        // 3) generate
        await api.post(
          path(`/admin/products/${productId}/variants/generate`),
          { rows: payloadRows },
          { headers: authHeader() }
        );
      }

      setMsg("✅ บันทึกแล้ว");
      setTimeout(() => setMsg(""), 1800);
      await loadVariants(); // reload
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.message || "❌ Save failed");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="vm-page">
      {/* Header */}
      <div className="vm-header">
        <div className="vm-title">
          <div className="vm-product-name">{productName || `#${productId}`}</div>
          <div className="vm-product-code">{productCode}</div>
        </div>
        <Link to="/admin/products" className="vm-back">← กลับไปหน้าสินค้า</Link>
      </div>

      {msg && <div className="vm-msg">{msg}</div>}

      {/* Options (ใช้เป็นหัวตาราง + chips โหมดไม่มี rows) */}
      <div className="vm-options">
        {/* option 1 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 1</label>
          <input value={opt1Name} onChange={(e) => setOpt1Name(e.target.value)} />
          <div className="vm-chips">
            {rows.length === 0 && opt1Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt1Values, setOpt1Values, i)}>×</button>
              </span>
            ))}
            {rows.length === 0 && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt1Values, setOpt1Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>

        {/* option 2 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 2 (ถ้ามี)</label>
          <input value={opt2Name} onChange={(e) => setOpt2Name(e.target.value)} />
          <div className="vm-chips">
            {rows.length === 0 && opt2Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt2Values, setOpt2Values, i)}>×</button>
              </span>
            ))}
            {rows.length === 0 && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt2Values, setOpt2Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>

        {/* option 3 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 3 (ถ้ามี)</label>
          <input
            value={opt3Name}
            onChange={(e) => setOpt3Name(e.target.value)}
            placeholder="เช่น วัสดุ / รุ่น"
          />
          <div className="vm-chips">
            {rows.length === 0 && opt3Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt3Values, setOpt3Values, i)}>×</button>
              </span>
            ))}
            {rows.length === 0 && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt3Values, setOpt3Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>
      </div>

      {/* Table */}
      <div className="vm-table-wrap">
        <table className="vm-table">
          <thead>
            <tr>
              <th>รูปภาพ</th>
              <th>{opt1Name || "ตัวเลือก 1"}</th>
              <th>{opt2Name || "ตัวเลือก 2"}</th>
              <th>{opt3Name || "ตัวเลือก 3"}</th>
              <th>ราคา</th>
              <th>SKU</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r, i) => (
              <tr key={i}>
                <td>
                  {r.image ? (
                    <img src={mediaSrc(r.image)} alt="" className="vm-thumb" />
                  ) : (
                    <label className="vm-upload">
                      +
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => onUploadRowImage(e.target.files?.[0], i)}
                      />
                    </label>
                  )}
                </td>
                <td>{r.opt1 || "-"}</td>
                <td>{r.opt2 || "-"}</td>
                <td>{r.opt3 || "-"}</td>
                <td>
                  <input
                    type="number"
                    min="0"
                    placeholder="ไม่ระบุ"
                    value={r.price}
                    onChange={(e) => {
                      const v = e.target.value;
                      setRows((prev) => {
                        const a = [...prev];
                        a[i] = { ...a[i], price: v };
                        return a;
                      });
                    }}
                  />
                </td>
                <td>
                  <input
                    placeholder="SKU"
                    value={r.sku}
                    onChange={(e) => {
                      const v = e.target.value;
                      setRows((prev) => {
                        const a = [...prev];
                        a[i] = { ...a[i], sku: v };
                        return a;
                      });
                    }}
                  />
                </td>
              </tr>
            ))}
            {rows.length === 0 && (
              <tr>
                <td colSpan={6} style={{ textAlign: "center", padding: "16px" }}>
                  ยังไม่มีตัวเลือกของสินค้านี้ — เพิ่มค่าในช่องด้านบนเพื่อสร้างคอมโบ แล้วกดบันทึก
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Actions */}
      <div className="vm-actions">
        <button className="btn" onClick={autoSku} disabled={saving}>
          เติม SKU อัตโนมัติ
        </button>
        <button className="btn-primary" onClick={saveAll} disabled={saving}>
          {saving ? "กำลังบันทึก…" : "บันทึกทั้งหมด"}
        </button>
      </div>

      {/* Notes */}
      <div className="vm-hint">
        <p>• ถ้ามี SKU เดิม ระบบจะโหลดมาให้แก้ไขได้ทันที</p>
        <p>• ถ้าไม่มี SKU เดิม ให้เพิ่มค่าตัวเลือกด้านบน แล้วค่อยกดบันทึก</p>
        <p>• สต็อกจะอิงจากคลังจริง (ไม่ต้องกรอกที่นี่)</p>
      </div>
    </div>
  );
}
