// frontend/src/admin/AdminDashboard.jsx
import React, { useEffect, useState, useMemo } from 'react';
import { api } from '../lib/api';
import {
  Chart as ChartJS, CategoryScale, LinearScale, BarElement, ArcElement, Tooltip, Legend
} from 'chart.js';
import { Bar, Pie } from 'react-chartjs-2';
ChartJS.register(CategoryScale, LinearScale, BarElement, ArcElement, Tooltip, Legend);

function currency(n) {
  const x = Number(n || 0);
  return x.toLocaleString('th-TH', { minimumFractionDigits: 0, maximumFractionDigits: 0 });
}

export default function AdminDashboard() {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const res = await api.get('/dashboard/overview');
        if (mounted) setData(res.data);
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, []);

  const revBar = useMemo(() => {
    const rows = data?.charts?.revenue_by_month || [];
    return {
      labels: rows.map(r => r.month),
      datasets: [{ label: 'ยอดขาย (บาท)', data: rows.map(r => r.total) }]
    };
  }, [data]);

  const statusPie = useMemo(() => {
    const rows = data?.charts?.order_status_distribution || [];
    return {
      labels: rows.map(r => r.status),
      datasets: [{ label: 'ออเดอร์', data: rows.map(r => r.count) }]
    };
  }, [data]);

  const provincesBar = useMemo(() => {
    const rows = (data?.charts?.top_provinces || []).slice(0, 10);
    return {
      labels: rows.map(r => r.province),
      datasets: [{ label: 'ผู้ใช้ (คน)', data: rows.map(r => r.users) }]
    };
  }, [data]);

  const catByProductsBar = useMemo(() => {
    const rows = data?.charts?.top_categories_by_products || [];
    return {
      labels: rows.map(r => r.category),
      datasets: [{ label: 'จำนวนสินค้า', data: rows.map(r => r.products) }]
    };
  }, [data]);

  const catBySalesBar = useMemo(() => {
    const rows = data?.charts?.top_categories_by_sales || [];
    return {
      labels: rows.map(r => r.category),
      datasets: [{ label: 'มูลค่าขาย', data: rows.map(r => r.sales) }]
    };
  }, [data]);

  const bestBar = useMemo(() => {
    const rows = data?.charts?.best_sellers || [];
    return {
      labels: rows.map(r => r.product_name),
      datasets: [{ label: 'ชิ้นที่ขายได้', data: rows.map(r => r.qty) }]
    };
  }, [data]);

  if (loading) return <div className="p-6">กำลังโหลดแดชบอร์ด…</div>;
  if (!data) return <div className="p-6">ไม่พบข้อมูลแดชบอร์ด</div>;

  const k = data.kpis || {};
  const recent = data.tables?.recent_orders || [];

  return (
    <div className="p-6 space-y-8">
      {/* KPI Cards */}
      <div className="grid grid-cols-2 md:grid-cols-5 gap-4">
        <Kpi title="สินค้า" value={k.products} />
        <Kpi title="ผู้ใช้งาน" value={k.users} />
        <Kpi title="คำสั่งซื้อ" value={k.orders} />
        <Kpi title="ที่อยู่ผู้ใช้" value={k.addresses} />
        <Kpi title="ยอดขายรวม (บาท)" value={currency(k.total_sales)} />
      </div>

      {/* Charts Row 1 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card title="ยอดขายรายเดือน (12 เดือน)">
          <Bar data={revBar} />
        </Card>
        <Card title="สถานะคำสั่งซื้อ">
          <Pie data={statusPie} />
        </Card>
      </div>

      {/* Charts Row 2 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card title="จังหวัดที่ลูกค้าอยู่มากที่สุด (Top 10)">
          <Bar data={provincesBar} options={{ indexAxis: 'y' }} />
        </Card>
        <Card title="หมวดสินค้าตามจำนวนสินค้า (Top 10)">
          <Bar data={catByProductsBar} />
        </Card>
      </div>

      {/* Charts Row 3 */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <Card title="หมวดสินค้าตามยอดขาย (Top 10)">
          <Bar data={catBySalesBar} />
        </Card>
        <Card title="สินค้าขายดี (Top 10)">
          <Bar data={bestBar} options={{ indexAxis: 'y' }} />
        </Card>
      </div>

      {/* Recent Orders */}
      <Card title="คำสั่งซื้อล่าสุด">
        <div className="overflow-x-auto">
          <table className="min-w-full text-sm">
            <thead>
              <tr className="text-left border-b">
                <th className="py-2 pr-4">ID</th>
                <th className="py-2 pr-4">User</th>
                <th className="py-2 pr-4">วันที่สั่ง</th>
                <th className="py-2 pr-4">ยอดรวม</th>
                <th className="py-2 pr-4">สถานะ</th>
              </tr>
            </thead>
            <tbody>
              {recent.map(r => (
                <tr key={r.order_id} className="border-b last:border-0">
                  <td className="py-2 pr-4">{r.order_id}</td>
                  <td className="py-2 pr-4">{r.user_email}</td>
                  <td className="py-2 pr-4">{new Date(r.order_date).toLocaleString('th-TH')}</td>
                  <td className="py-2 pr-4">{currency(r.total_amount)}</td>
                  <td className="py-2 pr-4">{r.status}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Card>
    </div>
  );
}

function Kpi({ title, value }) {
  return (
    <div className="p-4 rounded-2xl shadow border bg-white">
      <div className="text-slate-500 text-sm">{title}</div>
      <div className="text-2xl font-semibold">{value}</div>
    </div>
  );
}

function Card({ title, children }) {
  return (
    <div className="p-4 rounded-2xl shadow border bg-white">
      <div className="mb-3 font-semibold">{title}</div>
      {children}
    </div>
  );
}
