// FRONTEND: src/pages/HomeUser.js ‚Äî Search ‡∏Å‡∏∞‡∏ó‡∏±‡∏î‡∏£‡∏±‡∏î ‡∏Å‡∏∂‡πà‡∏á‡∏Å‡∏•‡∏≤‡∏á + Live suggestions + Grid results
import React, { useEffect, useRef, useState } from "react";
import { Link } from "react-router-dom";
import { api, path, mediaSrc } from "../lib/api";
import "./HomeUser.css";

/* ---------- debounce hook ---------- */
const useDebounce = (value, delay = 250) => {
  const [v, setV] = useState(value);
  useEffect(() => { const t = setTimeout(() => setV(value), delay); return () => clearTimeout(t); }, [value, delay]);
  return v;
};

/* ---------- product card ---------- */
const ProductCard = ({ p }) => {
  const img = p.image_url || p.cover_url || p.thumbnail_url;
  const price = p.price_override ?? p.price ?? 0;
  const stock = p.stock ?? p.stock_qty ?? p.live_stock ?? null;
  return (
    <div className="hu-card">
      <Link to={`/product/${p.product_id || p.id}`} className="hu-card-link">
        <div className="hu-card-thumb">
          {img ? <img src={mediaSrc(img)} alt={p.product_name} /> : <div className="hu-noimg">‡πÑ‡∏°‡πà‡∏°‡∏µ‡∏£‡∏π‡∏õ</div>}
        </div>
        <div className="hu-card-body">
          <div className="hu-card-title" title={p.product_name}>{p.product_name}</div>
          <div className="hu-card-meta">
            <span className="price">‡∏ø{Number(price || 0).toLocaleString()}</span>
            {Number.isFinite(Number(stock)) && <span className="stock">‡∏™‡∏ï‡πá‡∏≠‡∏Å {Number(stock)}</span>}
          </div>
        </div>
      </Link>
    </div>
  );
};

export default function HomeUser() {
  // ‚ö†Ô∏è ‡∏•‡∏ö Footer ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏´‡∏ô‡πâ‡∏≤‡∏ô‡∏µ‡πâ‡πÅ‡∏•‡πâ‡∏ß ‡πÄ‡∏û‡∏∑‡πà‡∏≠‡πÑ‡∏°‡πà‡πÉ‡∏´‡πâ‡∏ã‡πâ‡∏≠‡∏ô‡∏™‡∏≠‡∏á‡∏ä‡∏±‡πâ‡∏ô
  const [mode, setMode] = useState("keyword"); // "keyword" | "sku"
  const [q, setQ] = useState("");
  const dq = useDebounce(q, 250);

  const [suggest, setSuggest] = useState([]);
  const [openSug, setOpenSug] = useState(false);
  const sugWrap = useRef(null);

  const [loading, setLoading] = useState(false);
  const [results, setResults] = useState([]);
  const [fetched, setFetched] = useState(false);

  // ‡∏õ‡∏¥‡∏î dropdown ‡πÄ‡∏°‡∏∑‡πà‡∏≠‡∏Ñ‡∏•‡∏¥‡∏Å‡∏ô‡∏≠‡∏Å
  useEffect(() => {
    const onClick = (e) => { if (sugWrap.current && !sugWrap.current.contains(e.target)) setOpenSug(false); };
    document.addEventListener("click", onClick);
    return () => document.removeEventListener("click", onClick);
  }, []);

  // Live suggestions
  useEffect(() => {
    const run = async () => {
      if (!dq || dq.trim().length < 2) { setSuggest([]); return; }
      try {
        const params = { limit: 8, search: dq.trim() };
        if (mode === "sku") params.sku = dq.trim();
        setLoading(true);
        const { data } = await api.get(path("/api/products"), { params });
        const rows = Array.isArray(data?.rows) ? data.rows : Array.isArray(data) ? data : [];
        setSuggest(rows.slice(0, 8));
        setOpenSug(true);
      } catch { setSuggest([]); }
      finally { setLoading(false); }
    };
    run();
  }, [dq, mode]);

  const doSearch = async (term) => {
    const s = (term ?? q).trim();
    if (!s) { setResults([]); setFetched(true); setOpenSug(false); return; }
    try {
      setLoading(true);
      const params = { search: s, limit: 48 };
      if (mode === "sku") params.sku = s;
      const { data } = await api.get(path("/api/products"), { params });
      const rows = Array.isArray(data?.rows) ? data.rows : Array.isArray(data) ? data : [];
      setResults(rows);
      setFetched(true);
    } catch { setResults([]); setFetched(true); }
    finally { setLoading(false); setOpenSug(false); }
  };

  const onPick = (p) => {
    setQ(p.product_name || p.name || "");
    doSearch(p.product_name || p.name || "");
  };

  return (
    <div className="hu">
      {/* ===== Search ===== */}
      <section className="hu-search">
        <div className="hu-search-inner">
          <div className="hu-field">
            <label className="hu-label">‡πÇ‡∏´‡∏°‡∏î‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤</label>
            <select className="hu-select" value={mode} onChange={(e) => setMode(e.target.value)}>
              <option value="keyword">‡∏Ñ‡∏µ‡∏¢‡πå‡πÄ‡∏ß‡∏¥‡∏£‡πå‡∏î</option>
              <option value="sku">SKU</option>
            </select>
          </div>

          <div className="hu-field hu-field-grow" ref={sugWrap}>
            <label className="hu-label">‡∏Ñ‡∏≥‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤</label>
            <input
              className="hu-input"
              placeholder="‡πÄ‡∏ä‡πà‡∏ô ‡∏Å‡∏∏‡∏´‡∏•‡∏≤‡∏ö ‡∏Å‡∏£‡∏∞‡∏ñ‡∏≤‡∏á‡πÄ‡∏û‡∏ä‡∏£ ‡∏î‡∏¥‡∏ô‡∏õ‡∏•‡∏π‡∏Å..."
              value={q}
              onChange={(e) => setQ(e.target.value)}
              onFocus={() => suggest.length && setOpenSug(true)}
            />
            {openSug && suggest.length > 0 && (
              <div className="hu-suggest">
                {suggest.map((p) => (
                  <button
                    key={p.product_id || p.id}
                    type="button"
                    className="hu-suggest-item"
                    onClick={() => onPick(p)}
                  >
                    <span className="name">{p.product_name || p.name}</span>
                    <span className="price">‡∏ø{Number(p.price_override ?? p.price ?? 0).toLocaleString()}</span>
                  </button>
                ))}
              </div>
            )}
          </div>

          <div className="hu-actions">
            <button className="hu-btn" type="button" onClick={() => doSearch()} disabled={loading}>
              üîé ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤
            </button>
          </div>
        </div>
      </section>

      {/* ===== Results ===== */}
      <section className="hu-results">
        {loading && <div className="hu-info">‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‚Ä¶</div>}
        {!loading && fetched && results.length === 0 && <div className="hu-info">‡πÑ‡∏°‡πà‡∏û‡∏ö‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</div>}
        {!loading && results.length > 0 && (
          <div className="hu-grid">
            {results.map((p) => (<ProductCard key={p.product_id || p.id} p={p} />))}
          </div>
        )}
      </section>
    </div>
  );
}
