// src/admin/VariantsManager.js
// ======================================================================
// VariantsManager — โหลด SKU เดิมขึ้นมาแก้ไขได้ + สร้างคอมโบใหม่ได้
// - โหลดชื่อสินค้า
// - โหลดรายการ variants เดิม (รูป/ราคา/SKU/ตัวเลือก 1-3)
// - อนุญาตแก้ไขทีละแถว แล้ว "บันทึกทั้งหมด"
// - ถ้ายังไม่มี variants → ผู้ใช้ใส่ค่า chips เพื่อ generate ชุดคอมโบใหม่
// - รองรับ backend หลายแบบ (PUT /variants, POST /variants/save, POST /variants/generate)
// ======================================================================

import React, { useEffect, useMemo, useState, useCallback } from "react";
import { useParams, Link } from "react-router-dom";
import { api, path, mediaSrc } from "../lib/api";
import "./VariantsManager.css";

/* ---------- Helpers ---------- */
const authHeader = () => {
  const t = localStorage.getItem("token");
  return t ? { Authorization: `Bearer ${t}` } : {};
};

const pickStr = (...xs) => xs.find((v) => typeof v === "string" && v.trim())?.trim() || "";
const rand2 = () => String(Math.floor(Math.random() * 99) + 1).padStart(2, "0");

/* ---------- Upload ---------- */
const extractUrl = (data) => {
  if (!data) return null;
  if (typeof data === "string") return data;
  const direct =
    data.url || data.path || data.imageUrl || data.Location || data.location ||
    data.file?.url || data.file?.path || data.data?.url || data.data?.path || data.data?.imageUrl;
  if (direct) return direct;
  try {
    const flat = JSON.stringify(data);
    const m = flat.match(/"([^"]+\.(?:png|jpe?g|webp|gif))"/i);
    return m ? m[1] : null;
  } catch { return null; }
};

const tryUpload = async (file) => {
  const fd = new FormData();
  fd.append("file", file);
  const res = await api.post(path("/upload"), fd, {
    headers: { ...authHeader(), "Content-Type": "multipart/form-data" },
  });
  const url = extractUrl(res?.data);
  if (!url) throw new Error("UPLOAD_OK_BUT_NO_URL");
  return url;
};

/* ---------- Normalize product ---------- */
const normalizeProduct = (raw) => {
  if (!raw) return null;
  const d = raw.data || raw.item || raw.product || raw.result || raw;
  return d || null;
};

/* ---------- Variant helpers ---------- */
function normalizeVariant(v) {
  if (!v) return null;
  // รองรับหลาย schema ของฟิลด์
  const out = {
    variant_id: v.variant_id ?? v.id ?? v.product_variant_id ?? null,
    sku: pickStr(v.sku, v.SKU, v.code),
    price: v.price ?? v.unit_price ?? v.sale_price ?? "",
    image: v.image_url ?? v.image ?? v.imageUrl ?? null,
    // value ของตัวเลือก
    opt1: pickStr(v.opt1, v.option1_value, v.value1, v.color),
    opt2: pickStr(v.opt2, v.option2_value, v.value2, v.size),
    opt3: pickStr(v.opt3, v.option3_value, v.value3, v.material),
    // ชื่อของตัวเลือก
    opt1_name: pickStr(v.opt1_name, v.option1_name, v.name1, v.option1, "ตัวเลือก 1"),
    opt2_name: pickStr(v.opt2_name, v.option2_name, v.name2, v.option2, "ตัวเลือก 2"),
    opt3_name: pickStr(v.opt3_name, v.option3_name, v.name3, v.option3, ""),
  };
  // number to string for <input type=number>
  if (typeof out.price === "number") out.price = String(out.price);
  return out;
}

function toRowFromVariant(v) {
  const n = normalizeVariant(v);
  if (!n) return null;
  return {
    __existing: true,
    variant_id: n.variant_id,
    opt1: n.opt1 || "",
    opt2: n.opt2 || "",
    opt3: n.opt3 || "",
    price: n.price ?? "",
    sku: n.sku || "",
    image: n.image || null,
    opt1_name: n.opt1_name,
    opt2_name: n.opt2_name,
    opt3_name: n.opt3_name,
  };
}

/* ====================================================================== */
export default function VariantsManager() {
  const { id, productId: pid } = useParams();
  const productId = Number(id || pid);

  // product for header + SKU
  const [product, setProduct] = useState(null);

  // option names
  const [opt1Name, setOpt1Name] = useState("สี");
  const [opt2Name, setOpt2Name] = useState("ขนาด");
  const [opt3Name, setOpt3Name] = useState("");

  // option values (สำหรับโหมด generate ใหม่)
  const [opt1Values, setOpt1Values] = useState([]);
  const [opt2Values, setOpt2Values] = useState([]);
  const [opt3Values, setOpt3Values] = useState([]);

  // rows = รายการคอมโบที่กำลังแก้ไข (อาจเป็นของเดิม หรือที่ generate)
  const [rows, setRows] = useState([]);

  const [saving, setSaving] = useState(false);
  const [msg, setMsg] = useState("");

  /* ---------- Load product (ชื่อ/โค้ด) ---------- */
  const loadProduct = useCallback(async () => {
    let p = null;
    try {
      const r = await api.get(path(`/admin/products/${productId}`));
      p = normalizeProduct(r?.data);
    } catch {}
    if (!p) {
      try {
        const r = await api.get(path(`/products/${productId}`));
        p = normalizeProduct(r?.data);
      } catch {}
    }
    if (!p) {
      try {
        const r = await api.get(path(`/admin/products`), { params: { include_archived: 1 } });
        const arr = Array.isArray(r?.data?.items) ? r.data.items : (Array.isArray(r?.data) ? r.data : []);
        p = (arr || []).find((x) => Number(x.product_id) === productId) || null;
      } catch {}
    }
    setProduct(p || null);
  }, [productId]);

  /* ---------- Load variants เดิม ---------- */
  const loadVariants = useCallback(async () => {
    let arr = [];
    // 1) admin variants
    try {
      const r = await api.get(path(`/admin/products/${productId}/variants`), { headers: authHeader() });
      arr = Array.isArray(r?.data?.items) ? r.data.items : (Array.isArray(r?.data) ? r.data : []);
    } catch {}
    // 2) public fallback
    if (!arr?.length) {
      try {
        const r = await api.get(path(`/products/${productId}/variants`));
        arr = Array.isArray(r?.data?.items) ? r.data.items : (Array.isArray(r?.data) ? r.data : []);
      } catch {}
    }
    if (arr?.length) {
      const mapped = arr.map(toRowFromVariant).filter(Boolean);
      setRows(mapped);

      // เดาชื่อ option จากแถวแรก ถ้ามี
      const first = mapped[0];
      if (first) {
        if (first.opt1_name) setOpt1Name(first.opt1_name);
        if (first.opt2_name) setOpt2Name(first.opt2_name);
        if (first.opt3_name) setOpt3Name(first.opt3_name);
      }
    } else {
      // ถ้าไม่มี variant เดิม → เคลียร์ rows และรอให้ผู้ใช้สร้างคอมโบใหม่
      setRows([]);
    }
  }, [productId]);

  useEffect(() => { loadProduct(); loadVariants(); }, [loadProduct, loadVariants]);

  const productName = useMemo(
    () => pickStr(product?.product_name, product?.name, product?.title),
    [product]
  );
  const productCode = useMemo(
    () => pickStr(product?.product_code, product?.code, `P${productId}`),
    [product, productId]
  );

  /* ---------- Rebuild combos when values change (เฉพาะโหมดสร้างใหม่) ---------- */
  useEffect(() => {
    // เฉพาะตอน "ยังไม่มีแถวเดิม" เท่านั้นถึงจะ build จาก chips
    if (rows.length) return;
    const v1 = opt1Values.length ? opt1Values : [null];
    const v2 = opt2Values.length ? opt2Values : [null];
    const v3 = opt3Values.length ? opt3Values : [null];
    const combos = [];
    for (const a of v1) for (const b of v2) for (const c of v3) {
      combos.push({ opt1: a, opt2: b, opt3: c, price: "", sku: "", image: null });
    }
    setRows(combos);
  }, [opt1Values, opt2Values, opt3Values]); // eslint-disable-line react-hooks/exhaustive-deps

  /* ---------- chip helpers ---------- */
  const addTag = (list, setList, txt) => {
    const v = String(txt || "").trim();
    if (!v) return;
    if (!list.some((x) => x === v)) setList([...list, v]);
  };
  const removeTag = (list, setList, idx) => setList(list.filter((_, i) => i !== idx));

  /* ---------- per-row upload ---------- */
  const onUploadRowImage = async (file, idx) => {
    if (!file) return;

    const tempUrl = URL.createObjectURL(file);
    setRows((prev) => {
      const a = [...prev];
      a[idx] = { ...a[idx], image: tempUrl, __temp: true };
      return a;
    });

    try {
      const serverUrl = await tryUpload(file);
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: serverUrl, __temp: false };
        return a;
      });
    } catch (e) {
      setRows((prev) => {
        const a = [...prev];
        a[idx] = { ...a[idx], image: null, __temp: false };
        return a;
      });
      alert(e?.message || "Upload failed");
    } finally {
      setTimeout(() => URL.revokeObjectURL(tempUrl), 2500);
    }
  };

  /* ---------- Auto SKU (ไม่แปลงภาษา) ---------- */
  const autoSku = () => {
    const base = productCode || `P${productId}`;
    setRows((prev) =>
      prev.map((r) => {
        const parts = [base, r.opt1, r.opt2, r.opt3].filter(Boolean);
        return { ...r, sku: parts.join("-") + "-" + rand2() };
      })
    );
  };

  /* ---------- Save batch (ลองหลาย endpoint เพื่อให้เข้ากับหลังบ้าน) ---------- */
  const saveAll = async () => {
    setSaving(true);
    try {
      // กรองเฉพาะแถวที่มีรายละเอียดอย่างน้อย 1 ช่อง
      const effective = rows.filter((r) => r.opt1 || r.opt2 || r.opt3 || r.__existing);

      const payloadRows = effective.map((r) => ({
        variant_id: r.variant_id || null,
        details: [
          r.opt1 ? { name: opt1Name, value: r.opt1 } : null,
          r.opt2 ? { name: opt2Name, value: r.opt2 } : null,
          r.opt3 && (opt3Name || r.opt3) ? { name: opt3Name || "ตัวเลือก 3", value: r.opt3 } : null,
        ].filter(Boolean),
        sku: r.sku || null,
        price: r.price !== "" ? Number(r.price) : null,
        image_url: r.image || null,
      }));

      if (!payloadRows.length) {
        alert("ยังไม่มีแถวที่มีรายละเอียดสำหรับบันทึก");
        setSaving(false);
        return;
      }

      // 1) PUT /variants (อัปเดตแบบ batch)
      try {
        await api.put(
          path(`/admin/products/${productId}/variants`),
          { rows: payloadRows },
          { headers: authHeader() }
        );
      } catch (e1) {
        // 2) POST /variants/save (upsert)
        try {
          await api.post(
            path(`/admin/products/${productId}/variants/save`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
        } catch (e2) {
          // 3) POST /variants/generate (สร้างใหม่ทั้งหมด)
          await api.post(
            path(`/admin/products/${productId}/variants/generate`),
            { rows: payloadRows },
            { headers: authHeader() }
          );
        }
      }

      setMsg("✅ Saved");
      setTimeout(() => setMsg(""), 1800);
      // reload เพื่อให้แน่ใจว่า state ตรงกับ DB
      await loadVariants();
    } catch (e) {
      console.error(e);
      alert(e?.response?.data?.message || "❌ Save failed");
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="vm-page">
      {/* Header */}
      <div className="vm-header">
        <div className="vm-title">
          <div className="vm-product-name">{productName || `#${productId}`}</div>
          <div className="vm-product-code">{productCode}</div>
        </div>
        <Link to="/admin" className="vm-back">← กลับ</Link>
      </div>

      {msg && <div className="vm-msg">{msg}</div>}

      {/* Options (แสดงเสมอ — ใช้ตั้งชื่อหัวคอลัมน์ และใช้ generate เมื่อยังไม่มีแถวเดิม) */}
      <div className="vm-options">
        {/* option 1 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 1</label>
          <input value={opt1Name} onChange={(e) => setOpt1Name(e.target.value)} />
          <div className="vm-chips">
            {opt1Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt1Values, setOpt1Values, i)}>×</button>
              </span>
            ))}
            {!rows.length && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt1Values, setOpt1Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>

        {/* option 2 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 2 (ถ้ามี)</label>
          <input value={opt2Name} onChange={(e) => setOpt2Name(e.target.value)} />
          <div className="vm-chips">
            {opt2Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt2Values, setOpt2Values, i)}>×</button>
              </span>
            ))}
            {!rows.length && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt2Values, setOpt2Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>

        {/* option 3 */}
        <div className="vm-opt">
          <label>ตัวเลือกที่ 3 (ถ้ามี)</label>
          <input
            value={opt3Name}
            onChange={(e) => setOpt3Name(e.target.value)}
            placeholder="เช่น วัสดุ / รุ่น"
          />
          <div className="vm-chips">
            {opt3Values.map((v, i) => (
              <span key={i} className="vm-chip">
                {v}
                <button onClick={() => removeTag(opt3Values, setOpt3Values, i)}>×</button>
              </span>
            ))}
            {!rows.length && (
              <input
                placeholder="พิมพ์แล้ว Enter"
                onKeyDown={(e) => {
                  if (e.key === "Enter") {
                    e.preventDefault();
                    addTag(opt3Values, setOpt3Values, e.currentTarget.value);
                    e.currentTarget.value = "";
                  }
                }}
              />
            )}
          </div>
        </div>
      </div>

      {/* Table */}
      <div className="vm-table-wrap">
        <table className="vm-table">
          <thead>
            <tr>
              <th>รูปภาพ</th>
              <th>{opt1Name || "ตัวเลือก 1"}</th>
              <th>{opt2Name || "ตัวเลือก 2"}</th>
              <th>{opt3Name || "ตัวเลือก 3"}</th>
              <th>ราคา</th>
              <th>SKU</th>
            </tr>
          </thead>
          <tbody>
            {rows.map((r, i) => (
              <tr key={i}>
                <td>
                  {r.image ? (
                    <img src={mediaSrc(r.image)} alt="" className="vm-thumb" />
                  ) : (
                    <label className="vm-upload">
                      +
                      <input
                        type="file"
                        accept="image/*"
                        onChange={(e) => onUploadRowImage(e.target.files?.[0], i)}
                      />
                    </label>
                  )}
                </td>
                <td>{r.opt1 || "-"}</td>
                <td>{r.opt2 || "-"}</td>
                <td>{r.opt3 || "-"}</td>
                <td>
                  <input
                    type="number"
                    min="0"
                    placeholder="ไม่ระบุ"
                    value={r.price}
                    onChange={(e) => {
                      const v = e.target.value;
                      setRows((prev) => {
                        const a = [...prev];
                        a[i] = { ...a[i], price: v };
                        return a;
                      });
                    }}
                  />
                </td>
                <td>
                  <input
                    placeholder="SKU"
                    value={r.sku}
                    onChange={(e) => {
                      const v = e.target.value;
                      setRows((prev) => {
                        const a = [...prev];
                        a[i] = { ...a[i], sku: v };
                        return a;
                      });
                    }}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Actions */}
      <div className="vm-actions">
        <button className="btn" onClick={autoSku} disabled={saving}>
          เติม SKU อัตโนมัติ
        </button>
        <button className="btn-primary" onClick={saveAll} disabled={saving}>
          {saving ? "กำลังบันทึก…" : "บันทึกทั้งหมด"}
        </button>
      </div>

      {/* Notes */}
      <div className="vm-hint">
        <p>• ถ้ามี SKU เดิม ระบบจะโหลดมาให้แก้ไขได้ทันที</p>
        <p>• ถ้าไม่มี SKU เดิม ให้เพิ่มค่าตัวเลือกด้านบน แล้วค่อยกดบันทึก</p>
        <p>• สต็อกจะอิงจากคลังจริง (ไม่ต้องกรอกที่นี่)</p>
      </div>
    </div>
  );
}
