// src/account/Addresses.js
// หน้าที่: จัดการที่อยู่ผู้ใช้ (CRUD + ตั้งค่า default)
// ใช้ ThaiAddressPicker ให้ผู้ใช้ "เลือกจังหวัด→อำเภอ→ตำบล" และเติมรหัสไปรษณีย์อัตโนมัติ

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ThaiAddressPicker from '../components/ThaiAddressPicker'; // ตัวเลือกจังหวัด/อำเภอ/ตำบล

export default function Addresses() {
  /* -------------------- State หลัก -------------------- */
  const [rows, setRows] = useState([]);           // ลิสต์ที่อยู่ทั้งหมดของฉัน
  const [loading, setLoading] = useState(false);  // สถานะโหลด (โชว์ “กำลังโหลด...”)
  const [editingId, setEditingId] = useState(null); // ถ้ามี = โหมดแก้ไข; ถ้า null = โหมดเพิ่มใหม่

  // สเตตของฟอร์ม (ผูกกับ input ทีละช่อง)
  const [form, setForm] = useState({
    label: '',
    recipient_name: '',
    phone: '',
    line1: '',
    line2: '',
    subdistrict: '',
    district: '',
    province: '',
    postal_code: '',
    country: 'TH',
    is_default: false,
  });

  /* -------------------- ดึงรายการที่อยู่จาก API -------------------- */
  async function fetchList() {
    setLoading(true);
    try {
      // backend จะ ORDER BY is_default DESC ให้ default อยู่บนสุดอยู่แล้ว
      const res = await axios.get('/api/addresses');
      setRows(res.data || []);
    } catch (e) {
      alert(e?.response?.data?.error || 'ดึงรายการไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }
  useEffect(() => { fetchList(); }, []); // โหลดครั้งแรกเมื่อเปิดหน้า

  /* -------------------- ล้างฟอร์ม/ออกจากโหมดแก้ไข -------------------- */
  function resetForm() {
    setEditingId(null);
    setForm({
      label: '',
      recipient_name: '',
      phone: '',
      line1: '',
      line2: '',
      subdistrict: '',
      district: '',
      province: '',
      postal_code: '',
      country: 'TH',
      is_default: false,
    });
  }

  /* -------------------- บันทึกฟอร์ม (เพิ่ม/แก้ไข) -------------------- */
  async function handleSubmit(e) {
    e.preventDefault();
    try {
      if (!form.line1) return alert('กรอกที่อยู่บรรทัด 1');
      if (editingId) {
        // โหมดแก้ไข: PUT /api/addresses/:id
        await axios.put(`/api/addresses/${editingId}`, form);
      } else {
        // โหมดเพิ่มใหม่: POST /api/addresses
        await axios.post('/api/addresses', form);
      }
      resetForm();
      fetchList(); // รีโหลดตาราง
    } catch (e) {
      alert(e?.response?.data?.error || 'บันทึกไม่สำเร็จ');
    }
  }

  /* -------------------- เข้าโหมดแก้ไข (กรอกค่าลงฟอร์ม) -------------------- */
  function handleEdit(a) {
    setEditingId(a.address_id);
    setForm({
      label: a.label || '',
      recipient_name: a.recipient_name || '',
      phone: a.phone || '',
      line1: a.line1 || '',
      line2: a.line2 || '',
      subdistrict: a.subdistrict || '',
      district: a.district || '',
      province: a.province || '',
      postal_code: a.postal_code || '',
      country: a.country || 'TH',
      is_default: !!a.is_default,
    });
  }

  /* -------------------- ลบที่อยู่ -------------------- */
  async function handleDelete(a) {
    if (!window.confirm('ลบที่อยู่นี้ใช่ไหม?')) return;
    try {
      await axios.delete(`/api/addresses/${a.address_id}`);
      fetchList();
    } catch (e) {
      alert(e?.response?.data?.error || 'ลบไม่สำเร็จ');
    }
  }

  /* -------------------- ตั้งเป็นค่าเริ่มต้น -------------------- */
  async function setDefault(a) {
    try {
      await axios.patch(`/api/addresses/${a.address_id}/default`);
      fetchList();
    } catch (e) {
      alert(e?.response?.data?.error || 'ตั้งค่าเริ่มต้นไม่สำเร็จ');
    }
  }

  /* ==================== UI ==================== */
  return (
    <div style={{ maxWidth: 880, margin: '0 auto', padding: 16 }}>
      <h2>ที่อยู่ของฉัน</h2>

      {/* ฟอร์มเพิ่ม/แก้ไข */}
      <form onSubmit={handleSubmit} style={{ display: 'grid', gap: 8, marginBottom: 24 }}>
        {/* แถวที่ 1: label + ชื่อผู้รับ */}
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input
            placeholder="ชื่อเรียก (บ้าน/คอนโด)"
            value={form.label}
            onChange={e => setForm(f => ({ ...f, label: e.target.value }))}
          />
          <input
            placeholder="ชื่อผู้รับ *"
            value={form.recipient_name}
            onChange={e => setForm(f => ({ ...f, recipient_name: e.target.value }))}
          />
        </div>

        {/* แถวที่ 2: เบอร์โทร + ประเทศ */}
        <div style={{ display: 'grid', gap: 8, gridTemplateColumns: '1fr 1fr' }}>
          <input
            placeholder="เบอร์โทร"
            value={form.phone}
            onChange={e => setForm(f => ({ ...f, phone: e.target.value }))}
          />
          <input
            placeholder="ประเทศ"
            value={form.country}
            onChange={e => setForm(f => ({ ...f, country: e.target.value }))}
          />
        </div>

        {/* แถวที่ 3-4: ที่อยู่บรรทัด 1/2 */}
        <input
          placeholder="ที่อยู่ (บรรทัด 1) *"
          value={form.line1}
          required
          onChange={e => setForm(f => ({ ...f, line1: e.target.value }))}
        />
        <input
          placeholder="ที่อยู่ (บรรทัด 2)"
          value={form.line2}
          onChange={e => setForm(f => ({ ...f, line2: e.target.value }))}
        />

        {/* ✅ ตัวเลือกจังหวัด→อำเภอ→ตำบล + auto ใส่รหัสไปรษณีย์ */}
        <ThaiAddressPicker
          value={form}                                   // รับค่าปัจจุบันจากฟอร์ม
          onChange={patch => setForm(f => ({ ...f, ...patch }))} // หลอมค่าที่เปลี่ยนกลับเข้าฟอร์ม
          // dataUrl="/data/thai-address.min.json"       // ถ้าเก็บไฟล์ไว้ path อื่น สามารถระบุได้
        />

        {/* ตั้งเป็นค่าเริ่มต้น (is_default) */}
        <label style={{ display: 'flex', alignItems: 'center', gap: 8 }}>
          <input
            type="checkbox"
            checked={form.is_default}
            onChange={e => setForm(f => ({ ...f, is_default: e.target.checked }))}
          />
          ตั้งเป็นค่าเริ่มต้น
        </label>

        {/* ปุ่มบันทึก/ยกเลิกแก้ไข */}
        <div style={{ display: 'flex', gap: 8 }}>
          <button type="submit" disabled={loading}>
            {editingId ? 'อัปเดตที่อยู่' : 'เพิ่มที่อยู่'}
          </button>
          {editingId && (
            <button type="button" onClick={resetForm}>
              ยกเลิกแก้ไข
            </button>
          )}
        </div>
      </form>

      {/* ตารางรายการที่อยู่ (อ่านอย่างเดียว + ปุ่มจัดการ) */}
      {loading ? (
        <p>กำลังโหลด...</p>
      ) : rows.length === 0 ? (
        <p>ยังไม่มีที่อยู่</p>
      ) : (
        <table width="100%" border="1" cellPadding="8" style={{ borderCollapse: 'collapse' }}>
          <thead>
            <tr>
              <th>ค่าเริ่มต้น</th>
              <th>ชื่อ/โทร</th>
              <th>ที่อยู่</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {rows.map(a => (
              <tr key={a.address_id}>
                <td style={{ textAlign: 'center' }}>{a.is_default ? '✅' : ''}</td>
                <td>
                  <div><b>{a.recipient_name || '-'}</b></div>
                  <div>{a.phone || '-'}</div>
                  <div style={{ fontSize: 12, color: '#666' }}>{a.label || ''}</div>
                </td>
                <td>
                  <div>{a.line1}{a.line2 ? `, ${a.line2}` : ''}</div>
                  <div>{[a.subdistrict, a.district, a.province].filter(Boolean).join(', ')}</div>
                  <div>{a.postal_code} {a.country}</div>
                </td>
                <td>
                  <div style={{ display: 'flex', gap: 8, flexWrap: 'wrap' }}>
                    {!a.is_default && (
                      <button onClick={() => setDefault(a)}>ตั้งเป็นค่าเริ่มต้น</button>
                    )}
                    <button onClick={() => handleEdit(a)}>แก้ไข</button>
                    <button onClick={() => handleDelete(a)}>ลบ</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
