// src/admin/VariantsManager.js
// หน้าที่: จัดการ Variant ของสินค้ารายตัว (คอมโบออปชั่น + ราคา/สต็อก)
// ใช้ร่วมกับ backend routes ใต้ /api/admin/products/:productId/variants และ /api/admin/variants/:variantId

import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { useParams, Link, useNavigate } from 'react-router-dom';

export default function VariantsManager() {
  const { productId } = useParams();
  const pid = Number(productId || 0);
  const nav = useNavigate();

  // ---------- STATE ----------
  const [loading, setLoading]   = useState(true);
  const [busy, setBusy]         = useState(false);
  const [error, setError]       = useState('');
  const [msg, setMsg]           = useState('');

  const [product, setProduct]   = useState(null);
  const [options, setOptions]   = useState([]);     // [{option_id, option_name, values:[{value_id,value_name}]}]
  const [variants, setVariants] = useState([]);     // [{variant_id, sku, price_override, stock, combos:[...]}]

  // ฟอร์มสร้าง/อัปเดต variant
  const [form, setForm] = useState({
    sku: '',
    price_override: '',
    stock: '',
    chosen: {}, // mapping { [option_id]: value_id }
  });

  const boom = (e, fallback = 'เกิดข้อผิดพลาด') => {
    const m = e?.response?.data?.error || e?.message || fallback;
    setError(m);
    console.error(fallback, e);
  };

  // ---------- LOAD META ----------
  const fetchMeta = useCallback(async () => {
    if (!pid) return;
    setLoading(true); setError(''); setMsg('');
    try {
      const { data } = await axios.get(`/api/admin/products/${pid}/variants`);
      setProduct(data.product || null);
      setOptions(data.options || []);
      setVariants(data.variants || []);

      // ตั้งค่า chosen เริ่มต้นจากค่าแรกของแต่ละ option
      const init = {};
      (data.options || []).forEach(o => {
        if (o.values?.length) init[o.option_id] = o.values[0].value_id;
      });
      setForm(f => ({ ...f, chosen: init }));
    } catch (e) {
      boom(e, 'โหลดข้อมูลไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }, [pid]);

  useEffect(() => { fetchMeta(); }, [fetchMeta]);

  // ---------- HELPERS ----------
  const setField = (name, value) => setForm(prev => ({ ...prev, [name]: value }));
  const setChosen = (option_id, value_id) =>
    setForm(prev => ({ ...prev, chosen: { ...prev.chosen, [option_id]: Number(value_id) }}));

  const chosenToValues = () =>
    Object.entries(form.chosen)
      .map(([option_id, value_id]) => ({ option_id: Number(option_id), value_id: Number(value_id) }))
      .filter(v => v.option_id && v.value_id);

  const combosText = (combos) =>
    Array.isArray(combos) && combos.length
      ? combos.map(c => `${c.option_name}: ${c.value_name}`).join(' • ')
      : '-';

  // ---------- ACTIONS: VARIANT ----------
  async function saveVariant(e) {
    e?.preventDefault();
    setError(''); setMsg('');

    const values = chosenToValues();
    if (!values.length) return setError('กรุณาเลือกค่าออปชั่นอย่างน้อย 1 รายการ');

    const body = {
      sku: form.sku?.trim() || undefined, // optional
      price_override: form.price_override === '' ? null : Number(form.price_override),
      stock: Number(form.stock || 0),
      values
    };

    setBusy(true);
    try {
      await axios.post(`/api/admin/products/${pid}/variants`, body);
      setMsg('บันทึก Variant สำเร็จ');
      setForm(f => ({ ...f, sku: '', price_override: '', stock: '' }));
      await fetchMeta();
    } catch (e) {
      boom(e, 'บันทึก Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  async function updateVariant(variantId, patch) {
    setBusy(true); setError(''); setMsg('');
    try {
      await axios.put(`/api/admin/variants/${variantId}`, patch); // { sku?, price_override?, stock? }
      await fetchMeta();
      setMsg('อัปเดต Variant สำเร็จ');
    } catch (e) {
      boom(e, 'อัปเดต Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  async function removeVariant(variantId) {
    if (!window.confirm('ลบ Variant นี้ใช่ไหม?')) return;
    setBusy(true); setError(''); setMsg('');
    try {
      await axios.delete(`/api/admin/variants/${variantId}`);
      await fetchMeta();
      setMsg('ลบ Variant สำเร็จ');
    } catch (e) {
      boom(e, 'ลบ Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  // ---------- RENDER ----------
  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  return (
    <div style={{ padding: 16, maxWidth: 1000, margin: '0 auto', display: 'grid', gap: 16 }}>
      {/* Header */}
      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
        <h2 style={{ margin:0 }}>
          ตัวเลือก/ขนาด (Variants) — {product ? `${product.product_name} (#${product.product_id})` : '—'}
        </h2>
        <div style={{ marginLeft:'auto', display:'flex', gap:8 }}>
          <button onClick={() => nav(`/admin/products/${pid}/options`)}>ไปหน้าออปชั่น</button>
          <button onClick={fetchMeta}>รีเฟรช</button>
          <Link to="/">← กลับหน้าแรก</Link>
        </div>
      </div>

      {(error || msg) && (
        <div style={{ display:'flex', gap: 12 }}>
          {error && <div style={{ color:'#fff', background:'#d33', padding:'8px 12px', borderRadius:8 }}>{error}</div>}
          {msg &&   <div style={{ color:'#fff', background:'green', padding:'8px 12px', borderRadius:8 }}>{msg}</div>}
        </div>
      )}

      {/* Create / Update (by same SKU) */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3 style={{ marginTop:0 }}>เพิ่ม/อัปเดต Variant</h3>
        <form onSubmit={saveVariant} style={{ display:'grid', gap:10 }}>
          {options.map(opt => (
            <div key={opt.option_id} style={{ display:'flex', gap:10, alignItems:'center' }}>
              <label style={{ width:140 }}>{opt.option_name}</label>
              <select
                value={form.chosen[opt.option_id] ?? ''}
                onChange={(e) => setChosen(opt.option_id, e.target.value)}
                disabled={busy}
              >
                {opt.values?.length ? opt.values.map(v => (
                  <option key={v.value_id} value={v.value_id}>{v.value_name}</option>
                )) : <option value="">— ไม่มีค่า —</option>}
              </select>
            </div>
          ))}

          <div style={{ display:'flex', gap:10, flexWrap:'wrap' }}>
            <label>SKU (ถ้ามี)
              <input
                value={form.sku}
                onChange={(e)=>setField('sku', e.target.value)}
                placeholder="เช่น P23-GAMAKA-150"
                style={{ marginLeft:8 }}
                disabled={busy}
              />
            </label>
            <label>ราคาเฉพาะ Variant
              <input
                type="number" step="0.01"
                value={form.price_override}
                onChange={(e)=>setField('price_override', e.target.value)}
                placeholder="เว้นว่าง = ใช้ราคาหลัก"
                style={{ marginLeft:8 }}
                disabled={busy}
              />
            </label>
            <label>สต็อก
              <input
                type="number"
                value={form.stock}
                onChange={(e)=>setField('stock', e.target.value)}
                placeholder="0"
                style={{ marginLeft:8 }}
                disabled={busy}
              />
            </label>
          </div>

          <div>
            <button type="submit" disabled={busy}>บันทึก Variant</button>
          </div>
        </form>
      </section>

      {/* Variants list */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3 style={{ marginTop:0 }}>รายการ Variants</h3>
        {variants.length === 0 ? (
          <div style={{ color:'#666' }}>ยังไม่มี Variant</div>
        ) : (
          <table width="100%" cellPadding="8" style={{ borderCollapse:'collapse' }}>
            <thead>
              <tr style={{ background:'#fafafa' }}>
                <th align="left">#</th>
                <th align="left">SKU</th>
                <th align="left">คอมโบ</th>
                <th align="right">ราคาเฉพาะ</th>
                <th align="right">สต็อก</th>
                <th>จัดการ</th>
              </tr>
            </thead>
            <tbody>
              {variants.map(v => (
                <tr key={v.variant_id} style={{ borderTop:'1px solid #eee' }}>
                  <td>{v.variant_id}</td>
                  <td>
                    <InlineText
                      value={v.sku || ''}
                      placeholder="(ไม่มี)"
                      onSave={(val)=>updateVariant(v.variant_id, { sku: val || null })}
                      disabled={busy}
                    />
                  </td>
                  <td>{combosText(v.combos)}</td>
                  <td align="right">
                    <InlineNumber
                      value={v.price_override ?? ''}
                      onSave={(num)=>updateVariant(v.variant_id, { price_override: (num === '' ? null : Number(num)) })}
                      disabled={busy}
                    />
                  </td>
                  <td align="right">
                    <InlineNumber
                      value={v.stock ?? 0}
                      onSave={(num)=>updateVariant(v.variant_id, { stock: Number(num || 0) })}
                      disabled={busy}
                    />
                  </td>
                  <td>
                    <button onClick={()=>removeVariant(v.variant_id)} disabled={busy}>ลบ</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </section>
    </div>
  );
}

/* ---------- Components ---------- */
function InlineNumber({ value, onSave, disabled }) {
  const [v, setV] = useState(value === null ? '' : value);
  return (
    <span>
      <input
        type="number"
        step="0.01"
        value={v}
        onChange={(e)=>setV(e.target.value)}
        style={{ width: 110 }}
        disabled={disabled}
      />
      <button onClick={()=>onSave(v)} style={{ marginLeft:6 }} disabled={disabled}>บันทึก</button>
    </span>
  );
}

function InlineText({ value, onSave, placeholder, disabled }) {
  const [v, setV] = useState(value || '');
  return (
    <span>
      <input
        value={v}
        onChange={(e)=>setV(e.target.value)}
        placeholder={placeholder}
        disabled={disabled}
      />
      <button onClick={()=>onSave(v)} style={{ marginLeft:6 }} disabled={disabled}>บันทึก</button>
    </span>
  );
}
