// src/admin/AdminLayout.js
import React from "react";
import { NavLink, Outlet, useNavigate, useLocation } from "react-router-dom";
import "./AdminLayout.css";

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  // ‡∏î‡∏∂‡∏á productId ‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô‡∏à‡∏≤‡∏Å‡∏û‡∏≤‡∏ò ‡∏ñ‡πâ‡∏≤‡∏≠‡∏¢‡∏π‡πà‡πÉ‡∏ô‡∏´‡∏ô‡πâ‡∏≤ /admin/products/:id ‡∏´‡∏£‡∏∑‡∏≠ path ‡∏•‡∏π‡∏Å
  const match = location.pathname.match(/\/admin\/products\/(\d+)/);
  const currentProductId = match ? match[1] : null;

  const menus = [
    { label: "Dashboard",     icon: "üìä", path: "/admin" },
    { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤",   icon: "üì¶", path: "/admin/products" },

    // ‚úÖ ‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏°‡∏ô‡∏π‡πÉ‡∏´‡∏°‡πà
    { label: "‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó",         icon: "üìÇ", path: "/admin/categories" },
    { label: "‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢",       icon: "üóÇÔ∏è", path: "/admin/subcategories" },
    { label: "‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤",     icon: "üìè", path: "/admin/units" },
    { label: "‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏Ç‡∏ô‡∏≤‡∏î",      icon: "üìê", path: "/admin/sizes" },

    { label: "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á",   icon: "üè¨", path: "/admin/inventory" },
    { label: "‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠",      icon: "üßæ", path: "/admin/orders" },
    { label: "‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢",     icon: "üí∞", path: "/admin/reports" },
  ];

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login?role=admin");
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg flex flex-col">
        <div className="p-4 text-2xl font-bold border-b">üåø ADMIN</div>
        <nav className="flex-1 p-4 flex flex-col gap-2">
          {menus.map((m, i) => (
            <NavLink
              key={i}
              to={m.path}
              end={m.path === "/admin"}
              className={({ isActive }) =>
                `block px-3 py-2 rounded-lg ${
                  isActive ? "bg-green-500 text-white" : "hover:bg-green-100"
                }`
              }
            >
              <span className="mr-2">{m.icon}</span>
              {m.label}
            </NavLink>
          ))}

          {/* üü¢ ‡∏•‡∏¥‡∏á‡∏Å‡πå‡πÑ‡∏î‡∏ô‡∏≤‡∏°‡∏¥‡∏Å‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Variants ‡∏Ç‡∏≠‡∏á "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ó‡∏µ‡πà‡∏Å‡∏≥‡∏•‡∏±‡∏á‡∏î‡∏π‡∏≠‡∏¢‡∏π‡πà" */}
          {currentProductId && (
            <NavLink
              to={`/admin/products/${currentProductId}/variants`}
              className={({ isActive }) =>
                `block px-3 py-2 rounded-lg mt-2 ${
                  isActive ? "bg-green-600 text-white" : "bg-green-50 hover:bg-green-100"
                }`
              }
              title={`‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/Variants ‡∏Ç‡∏≠‡∏á‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ #${currentProductId}`}
            >
              <span className="mr-2">üéõÔ∏è</span>
              ‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/Variants (‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏µ‡πâ)
            </NavLink>
          )}
        </nav>
        <button
          className="m-4 px-4 py-2 rounded-lg bg-red-500 text-white hover:bg-red-600"
          onClick={logout}
        >
          üö™ ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6 overflow-y-auto">
        <Outlet />
      </main>
    </div>
  );
}
