// src/admin/AdminUnits.js
// ✅ หน่วยสินค้า (product_units) — โหลดจากฐานข้อมูลเท่านั้น
// ❌ ไม่มี UI is_published (แต่สร้างใหม่ฝั่ง backend ตั้งค่าเองได้)
// Endpoints ใช้จริง:
//   GET    /api/categories?published=1
//   GET    /api/units               (list)
//   POST   /api/admin/units         (create)
//   PUT    /api/admin/units/:code   (update)
//   DELETE /api/admin/units/:code   (delete)

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

const asStr = (v) => (v === null || v === undefined) ? '' : String(v).trim();

/* helpers: ดึง category_ids/ข้อความ สำหรับแสดงและฟอร์ม */
function extractCategoryIds(u) {
  if (Array.isArray(u?.category_ids) && u.category_ids.length) return u.category_ids.map(String);
  if (Array.isArray(u?.categories) && u.categories.length) return u.categories.map((c) => String(c.category_id));
  return u?.category_id ? [String(u.category_id)] : [];
}
function categoryText(u) {
  const ids = extractCategoryIds(u);
  return ids.length ? ids.join(', ') : '-';
}

export default function AdminUnits() {
  const [units, setUnits] = useState([]);
  const [categories, setCategories] = useState([]); // [{category_id, category_name}]
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [error, setError] = useState('');
  const [q, setQ] = useState('');

  // ฟอร์มด้านบน (create/edit)
  const emptyForm = { code: '', unit_name: '', description: '', category_ids: [] };
  const [form, setForm] = useState(emptyForm);
  const [editingCode, setEditingCode] = useState(''); // code เดิมตอนแก้ไข

  const handleErr = (err, msg = 'เกิดข้อผิดพลาด') => {
    console.error(msg, err?.response?.data || err?.message || err);
    setError(err?.response?.data?.error || err?.message || msg);
    alert(err?.response?.data?.error || err?.message || msg);
  };

  /* ---------- fetch DB ---------- */
  const fetchCategories = async () => {
    const { data } = await api.get(path('/categories'), { params: { published: 1 } });
    const arr = Array.isArray(data) ? data : (data?.items || []);
    const norm = arr.map((c) => ({
      category_id: asStr(c.category_id ?? c.id ?? c.code ?? ''),
      category_name: asStr(c.category_name ?? c.name ?? ''),
    })).filter((c) => c.category_id && c.category_name);
    setCategories(norm);
  };

  const fetchUnits = async () => {
    const { data } = await api.get(path('/units'));
    const arr = Array.isArray(data) ? data : (data?.items || []);
    const norm = arr.map((u) => ({
      code: asStr(u.code ?? u.unit_code ?? u.id ?? ''),
      unit_name: asStr(u.unit_name ?? u.label ?? u.name ?? ''),
      description: asStr(u.description ?? ''),
      // รองรับทั้งแบบเดิมและ many-to-many
      category_id: asStr(u.category_id ?? ''),            // legacy
      category_ids: extractCategoryIds(u),                // array
      categories: Array.isArray(u.categories) ? u.categories : [],
      updated_at: u.updated_at,
      created_at: u.created_at,
    })).filter((u) => u.code && u.unit_name);
    setUnits(norm);
  };

  useEffect(() => {
    (async () => {
      try {
        setLoading(true);
        setError('');
        await Promise.all([fetchCategories(), fetchUnits()]);
      } catch (err) {
        handleErr(err, 'โหลดข้อมูลจากฐานข้อมูลไม่สำเร็จ');
      } finally {
        setLoading(false);
      }
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  /* ---------- filter ---------- */
  const list = useMemo(() => {
    const qq = q.trim().toLowerCase();
    if (!qq) return units;
    return units.filter((u) => {
      const code = String(u.code || '').toLowerCase();
      const name = String(u.unit_name || '').toLowerCase();
      return code.includes(qq) || name.includes(qq);
    });
  }, [units, q]);

  /* ---------- validate ---------- */
  const validate = (payload) => {
    if (!asStr(payload.code)) return 'กรอกรหัสหน่วย (code)';
    if (!/^[a-z0-9_]+$/i.test(payload.code)) return 'code ต้องเป็นตัวอักษร/ตัวเลข/ขีดล่าง';
    if (!asStr(payload.unit_name)) return 'กรอกชื่อหน่วย (unit_name)';
    if (!Array.isArray(payload.category_ids) || payload.category_ids.length === 0) {
      return 'เลือกหมวดหมู่ (เลือกได้หลายค่า)';
    }
    return '';
  };

  /* ---------- create ---------- */
  const createUnit = async () => {
    const payload = {
      code: asStr(form.code).toLowerCase(),
      unit_name: asStr(form.unit_name),
      description: asStr(form.description),
      category_ids: Array.from(new Set(form.category_ids)).filter(Boolean),
      // is_published ไม่ต้องส่งใน UI (ถ้าจะให้บังคับ true ให้ทำใน backend)
    };
    const msg = validate(payload);
    if (msg) return alert(msg);
    if (units.some((u) => u.code === payload.code)) {
      return alert(`code "${payload.code}" มีอยู่แล้ว`);
    }
    try {
      setSaving(true);
      await api.post(path('/admin/units'), payload);
      await fetchUnits();
      resetForm();
    } catch (err) {
      handleErr(err, 'บันทึกหน่วยสินค้าไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  /* ---------- edit via top form ---------- */
  const startEdit = (u) => {
    setEditingCode(u.code); // จำ code เดิมไว้สำหรับ URL ตอนเซฟ
    setForm({
      code: u.code,
      unit_name: u.unit_name,
      description: u.description || '',
      category_ids: extractCategoryIds(u),
    });
    try { window.scrollTo({ top: 0, behavior: 'smooth' }); } catch {}
  };

  const cancelEdit = () => resetForm();

  const saveEdit = async () => {
    const payload = {
      // code ใหม่ถ้าจะยอมให้แก้ (backend เราอ้างอิง URL ด้วย code เดิม)
      unit_name: asStr(form.unit_name),
      description: asStr(form.description),
      category_ids: Array.from(new Set(form.category_ids)).filter(Boolean),
    };
    // ตรวจด้วย payload + code ปัจจุบัน
    const msg = validate({ ...payload, code: editingCode });
    if (msg) return alert(msg);

    try {
      setSaving(true);
      await api.put(path(`/admin/units/${encodeURIComponent(editingCode)}`), payload);
      await fetchUnits();
      resetForm();
    } catch (err) {
      handleErr(err, 'แก้ไขหน่วยไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  const resetForm = () => {
    setEditingCode('');
    setForm(emptyForm);
  };

  /* ---------- delete ---------- */
  const deleteUnit = async (u) => {
    if (!window.confirm(`ลบหน่วย "${u.unit_name}" ?`)) return;
    try {
      setSaving(true);
      await api.delete(path(`/admin/units/${encodeURIComponent(u.code)}`));
      await fetchUnits();
    } catch (err) {
      handleErr(err, 'ลบหน่วยไม่สำเร็จ');
    } finally {
      setSaving(false);
    }
  };

  /* ---------- render ---------- */
  if (loading) return <div style={{ padding: 12 }}>กำลังโหลดข้อมูลจากฐานข้อมูล…</div>;

  return (
    <div className="pm-page">
      {/* ปุ่มกลับ */}
      <div style={{display:'flex', gap:8, margin:'12px 0 20px', flexWrap:'wrap'}}>
        <Link to="/admin/products" className="btn btn-ghost">← กลับไปจัดการสินค้า</Link>
      </div>

      <h2>จัดการหน่วยสินค้า (จากฐานข้อมูล)</h2>

      {!!error && (
        <div className="pm-alert pm-alert-error" style={{ marginTop: 8 }}>
          {error}
        </div>
      )}

      {/* ฟอร์มเพิ่ม/แก้ไข */}
      <div className="pm-actions mt-8" style={{ flexWrap:'wrap', rowGap: 8 }}>
        <input
          placeholder="code (เช่น: piece, pot) [a-z0-9_]"
          value={form.code}
          onChange={(e)=>setForm((p)=>({ ...p, code: e.target.value }))}
          style={{ maxWidth:220 }}
          disabled={!!editingCode} // ขณะ edit ไม่ให้แก้ code เพื่อความง่าย
        />
        <input
          placeholder="unit_name (เช่น: ชิ้น, กระถาง)"
          value={form.unit_name}
          onChange={(e)=>setForm((p)=>({ ...p, unit_name: e.target.value }))}
          style={{ minWidth:220 }}
        />

        <div style={{ minWidth: 280 }}>
          <label style={{ display:'block', fontSize:12, color:'#666', marginBottom:4 }}>
            หมวด (เลือกได้หลายค่า)
          </label>
          <select
            multiple
            value={form.category_ids}
            onChange={(e)=>{
              const selected = Array.from(e.target.selectedOptions, op => op.value);
              setForm((p)=>({ ...p, category_ids: selected }));
            }}
            style={{ width:'100%', height:140 }}
          >
            {categories.length ? (
              categories.map(c => (
                <option key={c.category_id} value={c.category_id}>
                  {c.category_id} — {c.category_name}
                </option>
              ))
            ) : (
              // fallback: ดึงจาก units ที่มีอยู่
              Array.from(new Set(units.flatMap(u => extractCategoryIds(u)))).sort().map(cid => (
                <option key={cid} value={cid}>{cid}</option>
              ))
            )}
          </select>
          <div style={{ fontSize:12, color:'#777', marginTop:6 }}>
            กด <kbd>Ctrl</kbd>/<kbd>Cmd</kbd> ค้างเพื่อเลือกหลายค่า
          </div>
        </div>

        <input
          placeholder="description (ถ้ามี)"
          value={form.description}
          onChange={(e)=>setForm((p)=>({ ...p, description: e.target.value }))}
          style={{ minWidth:300 }}
        />

        {!editingCode ? (
          <button
            type="button"
            className="btn btn-primary"
            disabled={saving}
            onClick={createUnit}
          >
            {saving ? 'กำลังบันทึก…' : 'เพิ่มหน่วยสินค้า'}
          </button>
        ) : (
          <div style={{ display:'flex', gap:8, alignItems:'center', flexWrap:'wrap' }}>
            <button
              type="button"
              className="btn btn-primary"
              disabled={saving}
              onClick={saveEdit}
            >
              {saving ? 'กำลังบันทึก…' : 'บันทึกการแก้ไข'}
            </button>
            <button type="button" className="btn btn-ghost" onClick={cancelEdit}>
              ยกเลิก
            </button>
            <span style={{ color:'#777' }}>กำลังแก้ไข: <b>{editingCode}</b></span>
          </div>
        )}
      </div>

      {/* แถบค้นหา */}
      <div style={{ marginTop: 12 }}>
        <input
          value={q}
          onChange={(e)=>setQ(e.target.value)}
          placeholder="ค้นหา code หรือชื่อหน่วย"
          style={{ padding: '8px 12px', border: '1px solid #ddd', borderRadius: 8, minWidth: 260 }}
        />
      </div>

      {/* ตาราง */}
      <div className="pm-table-wrap mt-8">
        <table className="pm-table pm-fixed">
          <thead>
            <tr>
              <th style={{width:160}}>code</th>
              <th style={{width:200}}>unit_name</th>
              <th>description</th>
              <th style={{width:260}}>category_id (หลายค่า)</th>
              <th style={{textAlign:'center', width:200}}>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {list.map((u) => (
              <tr key={u.code}>
                <td>{u.code}</td>
                <td><span className="name-with-badges">{u.unit_name}</span></td>
                <td style={{ color: u.description ? '#222' : '#999' }}>
                  {u.description || '—'}
                </td>
                <td>
                  <code>{u.categories?.length
                    ? u.categories.map(c => c.category_id).join(', ')
                    : (u.category_ids?.length ? u.category_ids.join(', ') : (u.category_id || <span style={{ color:'#c00' }}>NULL (ควรแก้)</span>))}
                  </code>
                </td>
                <td className="cell-actions">
                  <button className="btn btn-ghost" onClick={()=>startEdit(u)}>แก้ไข</button>
                  <button className="btn btn-danger" onClick={()=>deleteUnit(u)}>ลบ</button>
                </td>
              </tr>
            ))}
            {list.length === 0 && (
              <tr><td colSpan={5} style={{ color:'#777' }}>— ไม่มีข้อมูล —</td></tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}
