// src/index.js
import React from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';
import axios from 'axios';

/* ============ Axios Global Config ============ */
// CRA ใช้ตัวแปร env ขึ้นต้นด้วย REACT_APP_
const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:3001/api';
const IS_DEV = process.env.NODE_ENV !== 'production';

// ✅ แก้จาก RAW_BASE -> API_BASE
axios.defaults.baseURL = API_BASE.replace(/\/+$/, '');

// ตั้ง timeout 10 วิ กันรีเควสต์ค้าง
axios.defaults.timeout = 10000;

// แนบ token เริ่มต้น (เผื่อรีเควสต์แรก)
const bootToken = localStorage.getItem('token');
if (bootToken) {
  axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;
}

// ---------- Helper: สร้าง URL ไว้ log ----------
function buildUrl(cfg) {
  try {
    const b = (cfg.baseURL || '').replace(/\/+$/, '');
    const u = (cfg.url || '');
    if (/^https?:\/\//i.test(u)) return u; // absolute
    return b + (u.startsWith('/') ? u : `/${u}`);
  } catch {
    return cfg?.url || '';
  }
}

// ---------- Request Interceptor ----------
axios.interceptors.request.use((cfg) => {
  const t = localStorage.getItem('token');
  if (t) cfg.headers.Authorization = `Bearer ${t}`;
  cfg.metadata = { start: Date.now() };

  if (IS_DEV) {
    // eslint-disable-next-line no-console
    console.log(
      '➡️',
      (cfg.method || 'GET').toUpperCase(),
      buildUrl(cfg),
      cfg.params ? { params: cfg.params } : '',
      cfg.data && !(cfg.data instanceof FormData) ? { data: cfg.data } : ''
    );
  }
  return cfg;
});

// ---------- Response Interceptor ----------
axios.interceptors.response.use(
  (res) => {
    if (IS_DEV) {
      const ms = Date.now() - (res.config.metadata?.start || Date.now());
      // eslint-disable-next-line no-console
      console.log(
        '✅',
        res.status,
        (res.config.method || 'GET').toUpperCase(),
        buildUrl(res.config),
        `${ms}ms`
      );
    }
    return res;
  },
  (err) => {
    const { response, config } = err;
    if (IS_DEV && config) {
      const ms = Date.now() - (config.metadata?.start || Date.now());
      // eslint-disable-next-line no-console
      console.warn(
        '❌',
        response?.status || 'NETWORK',
        (config.method || 'GET').toUpperCase(),
        buildUrl(config),
        `${ms}ms`,
        err.message
      );
    }
    if (response?.status === 401) {
      localStorage.removeItem('token');
      // window.location.assign('/login'); // ถ้าต้องการเด้งไปหน้า login
    }
    return Promise.reject(err);
  }
);

/* ============== React Mount ============== */
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

reportWebVitals();
