// src/admin/VariantsManager.js
// ======================================================================
// หน้านี้ใช้ "จัดการตัวเลือก/ขนาด (Options/Values) และ Variants" ของสินค้า
// โหมดใช้งานง่าย + ปลอดภัย + ตรงสโคป:
//  - เพิ่ม/แก้ชื่อ/ลบ "ออปชั่น" (เช่น สี, ขนาด)
//  - เพิ่ม "ค่า (value)" ได้จากการพิมพ์ (ไม่ต้องเปิด dialog) → ถ้าไม่เจอจะสร้างให้ทันที
//  - บันทึก Variant ได้แม้ values ว่าง (ใช้ราคา/สต็อกเฉพาะ variant ได้)
//  - แสดงชื่อออปชั่นแบบ generic "ข้อมูลอื่นๆ N" ได้ (toggle)
//  - ใช้ API helper รวมศูนย์ baseURL + แนบ JWT อัตโนมัติ
// ======================================================================

import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
// ⭐ ใช้ API helper รวมศูนย์ baseURL + Authorization header
import * as API from '../lib/api';

export default function VariantsManager() {
  // ⭐ รองรับทั้งเส้นทางที่ใช้ :productId หรือ :id เพื่อให้ router ยืดหยุ่น
  const params = useParams();
  const productId = params.productId || params.id;

  // ---------- state หลักของหน้า ----------
  const [loading, setLoading] = useState(true);   // โหลด meta ครั้งแรก/ระหว่างรีเฟรช
  const [busy, setBusy]       = useState(false);  // กำลังส่งคำสั่ง (เพื่อ disable ปุ่ม/อินพุต)
  const [error, setError]     = useState('');     // แสดงข้อความ error
  const [msg, setMsg]         = useState('');     // แสดงข้อความ success/info

  // meta = { product, options(with values), variants(with combos) }
  const [data, setData] = useState({ product: null, options: [], variants: [] });

  // ---------- ตัวเลือก UI ----------
  const [useGeneric, setUseGeneric]   = useState(true);   // แสดงชื่อออปชั่นแบบ "ข้อมูลอื่นๆ N"
  const [showOptions, setShowOptions] = useState(false);  // ซ่อน/แสดงพาเนล "รายการออปชั่น"

  // ---------- draft ฟอร์มเพิ่ม Variant ----------
  const [draft, setDraft] = useState({
    sku: '',
    price_override: '',
    stock: '',
    values: {}, // mapping { [option_id]: value_id }  (ฝั่ง UI ใช้ง่าย)
    texts : {}, // เก็บข้อความที่ผู้ใช้พิมพ์สำหรับแต่ละ option { [option_id]: text }
  });

  // ---------- state สำหรับ "เพิ่ม/แก้ชื่อ" ออปชั่น ----------
  const [newOptName, setNewOptName]   = useState('');   // ชื่อออปชั่นใหม่ตอนกด "เพิ่ม"
  const [editOptId, setEditOptId]     = useState(null); // option_id ที่กำลังแก้ชื่อ
  const [editOptText, setEditOptText] = useState('');   // ชื่อใหม่ของออปชั่น

  // ---------- helper แจ้ง error แบบสั้น ----------
  const boom = (e, fallback = 'เกิดข้อผิดพลาด') => {
    const m = e?.response?.data?.error || e?.message || fallback;
    setError(m);
    console.error(fallback, e);
  };

  // ---------- โหลด meta ของสินค้าทั้งชุด ----------
  async function load() {
    // รีเซ็ตข้อความสถานะเพื่อความชัดเจน
    setLoading(true); setError(''); setMsg('');
    try {
      const res = await API.getProductMeta(productId);
      const variants = res.variants ?? [];
      setData({ product: res.product, options: res.options || [], variants });
    } catch (e) {
      boom(e, 'โหลดข้อมูลไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  }

  // โหลดเมื่อเปิดหน้า/เปลี่ยน productId
  useEffect(() => { load(); }, [productId]);

  // ---------- เลือก/สร้างค่า (value) จากข้อความที่พิมพ์ ----------
  // NOTE: UX ที่ต้องการ: พิมพ์ค่า → ถ้าเจอชื่อตรงเดิม เลือกให้เลย; ถ้าไม่เจอ สร้างให้อัตโนมัติ
  async function chooseOrCreateValue(option, typedText) {
    const text = (typedText || '').trim();
    const option_id = option.option_id;

    // อัปเดต "ข้อความที่ผู้ใช้พิมพ์" ของ option นี้
    setDraft(s => ({ ...s, texts: { ...s.texts, [option_id]: text } }));

    // ถ้าเว้นว่าง → ล้างค่า value_id
    if (!text) {
      setDraft(s => ({ ...s, values: { ...s.values, [option_id]: '' } }));
      return;
    }

    // ถ้าค่าที่พิมพ์ "ตรงกับ value เดิม" (ไม่สนตัวพิมพ์) → ผูก value_id ที่มีอยู่
    const exists = (option.values || []).find(
      v => (v.value_name || '').toLowerCase() === text.toLowerCase()
    );
    if (exists) {
      setDraft(s => ({ ...s, values: { ...s.values, [option_id]: exists.value_id } }));
      return;
    }

    // ไม่พบ → "สร้างค่าใหม่" สำหรับ option นี้ แล้ว bind เข้าฟอร์ม
    try {
      setBusy(true); setError(''); setMsg('');
      const data = await API.addValue(option_id, text); // POST /admin/options/:option_id/values
      await load(); // รีโหลด meta เพื่อให้ datalist มีตัวเลือกใหม่ทันที
      setDraft(s => ({
        ...s,
        values: { ...s.values, [option_id]: data.value_id },
        texts : { ...s.texts , [option_id]: text }
      }));
      setMsg(`เพิ่มค่าใหม่: "${text}"`);
    } catch (e) {
      boom(e, 'เพิ่มค่าใหม่ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  // ---------- สร้าง Variant ใหม่ (values ว่างก็ได้) ----------
  // NOTE: ฟอร์มนี้อนุญาตให้เว้นออปชั่นได้ เผื่อสินค้าบางชนิดไม่มีตัวเลือก
  async function createVariant() {
    setError(''); setMsg('');

    // แปลง mapping -> array ของ { option_id, value_id } และตัดค่าที่ว่างออก
    const values = Object.entries(draft.values)
      .map(([oid, vid]) => ({ option_id: Number(oid), value_id: Number(vid) }))
      .filter(v => !!v.value_id);

    const body = {
      sku: draft.sku || null,
      price_override: draft.price_override === '' ? null : Number(draft.price_override || 0),
      stock: Number(draft.stock || 0),
      values
    };

    setBusy(true);
    try {
      await API.createVariant(productId, body); // POST /admin/products/:productId/variants
      setDraft({ sku: '', price_override: '', stock: '', values: {}, texts: {} });
      await load();
      setMsg('บันทึก Variant สำเร็จ');
    } catch (e) {
      // กรณีคอมโบซ้ำ backend ตอบ 409 → boom จะดึงข้อความจาก backend มาแสดงให้
      boom(e, 'บันทึก Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  // ---------- อัปเดตฟิลด์ของ Variant (ราคา/สต็อก/sku) ----------
  async function updateVariant(variant_id, patch) {
    setBusy(true); setError(''); setMsg('');
    try {
      await API.updateVariant(variant_id, patch); // PUT /admin/variants/:variantId
      await load();
      setMsg('อัปเดต Variant สำเร็จ');
    } catch (e) {
      boom(e, 'อัปเดต Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  // ---------- ลบ Variant (รวม mapping ด้วย) ----------
  async function deleteVariant(variant_id) {
    if (!window.confirm('ลบ Variant นี้?')) return;
    setBusy(true); setError(''); setMsg('');
    try {
      await API.deleteVariant(variant_id); // DELETE /admin/variants/:variantId
      await load();
      setMsg('ลบ Variant สำเร็จ');
    } catch (e) {
      boom(e, 'ลบ Variant ไม่สำเร็จ');
    } finally {
      setBusy(false);
    }
  }

  if (loading) return <div style={{ padding: 16 }}>กำลังโหลด...</div>;

  // ---------- helper แสดงชื่อออปชั่น (generic หรือชื่อจริง) ----------
  const displayName = (opt, idx) =>
    useGeneric ? `ข้อมูลอื่นๆ ${idx + 1}` : (opt.option_name || `ข้อมูลอื่นๆ ${idx + 1}`);

  return (
    <div style={{ padding: 16, display: 'grid', gap: 16 }}>
      {/* Header + toggle UI */}
      <div style={{ display:'flex', alignItems:'center', gap:10 }}>
        <h2 style={{ margin:0 }}>
          ตัวเลือก/ขนาด — {data.product?.product_name} (#{data.product?.product_id})
        </h2>

        {/* toggle แสดงชื่อออปชั่นแบบ generic */}
        <label style={{marginLeft:12, fontSize:13}}>
          <input
            type="checkbox"
            checked={useGeneric}
            onChange={e=>setUseGeneric(e.target.checked)}
            style={{marginRight:6}}
          />
          แสดงชื่อทั่วไป (ข้อมูลอื่นๆ 1/2/3)
        </label>

        {/* toggle ซ่อน/แสดงพาเนลรายการออปชั่น */}
        <button style={{marginLeft:12}} onClick={()=>setShowOptions(s=>!s)}>
          {showOptions ? 'ซ่อนรายการออปชั่น' : 'แสดงรายการออปชั่น'}
        </button>

        {/* กรณีสินค้ายังไม่มีออปชั่นเลย → ปุ่มสร้างอย่างไว */}
        {data.options.length === 0 && (
          <button
            onClick={async ()=>{
              try {
                setBusy(true); setError(''); setMsg('');
                await API.addOption(productId, ''); // ว่าง = backend ตั้งชื่อ "ข้อมูลอื่นๆ 1"
                await load();
                setMsg('สร้างออปชั่นเริ่มต้นสำเร็จ');
              } catch(e) {
                boom(e, 'สร้างออปชั่นเริ่มต้นไม่สำเร็จ');
              } finally {
                setBusy(false);
              }
            }}
            disabled={busy}
            style={{marginLeft:12}}
          >
            + สร้างออปชั่นแรก
          </button>
        )}

        {/* ลิงก์กลับหน้าแรก/หน้ารายการสินค้า */}
        <Link to="/" style={{ marginLeft:'auto' }}>← กลับหน้าแรก</Link>
      </div>

      {/* แถบสถานะ error/success */}
      {(error || msg) && (
        <div style={{ display:'flex', gap: 12 }}>
          {error && <div style={{ color:'#fff', background:'#d33', padding:'8px 12px', borderRadius:8 }}>{error}</div>}
          {msg   && <div style={{ color:'#fff', background:'green', padding:'8px 12px', borderRadius:8 }}>{msg}</div>}
        </div>
      )}

      {/* ฟอร์มเพิ่ม/อัปเดต Variant */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>เพิ่ม/อัปเดต Variant</h3>

        {/* SKU */}
        <div style={{display:'grid', gap:10, maxWidth:560}}>
          <input
            placeholder="SKU (ถ้ามี)"
            value={draft.sku}
            onChange={e=>setDraft(s=>({ ...s, sku: e.target.value }))}
            disabled={busy}
          />
        </div>

        {/* เลือก/พิมพ์ค่า values ของแต่ละ option (ดึงจาก data.options) */}
        <div style={{ marginTop:12, display:'grid', gap:12 }}>
          {data.options.map((opt, idx) => (
            <div key={opt.option_id} style={{ display:'grid', gap:6 }}>
              <label style={{fontWeight:600}}>
                {displayName(opt, idx)}
                {useGeneric && opt.option_name ? <span style={{color:'#999'}}> ({opt.option_name})</span> : null}
              </label>

              {/* พิมพ์ค่าได้ + ใช้ datalist แนะนำค่าที่มีอยู่ */}
              <input
                list={`opt-${opt.option_id}-list`}
                placeholder="พิมพ์ค่าใหม่ หรือเลือกจากลิสต์ แล้วกด Enter"
                value={draft.texts[opt.option_id] ?? ''}
                onChange={e=>setDraft(s=>({ ...s, texts: { ...s.texts, [opt.option_id]: e.target.value } }))}
                onBlur={e=>chooseOrCreateValue(opt, e.target.value)}
                onKeyDown={e=>{
                  if (e.key === 'Enter') {
                    e.preventDefault();
                    chooseOrCreateValue(opt, e.currentTarget.value);
                  }
                }}
                disabled={busy}
              />
              <datalist id={`opt-${opt.option_id}-list`}>
                {(opt.values || []).map(v => <option key={v.value_id} value={v.value_name} />)}
              </datalist>
              <small style={{ color:'#666' }}>
                ปล่อยว่างได้ • ถ้าพิมพ์ค่าใหม่ ระบบจะสร้างให้อัตโนมัติเมื่อกด Enter/คลิกออก
              </small>
            </div>
          ))}
        </div>

        {/* ราคาเฉพาะ/สต็อก + ปุ่มบันทึก */}
        <div style={{ display:'flex', gap:8, marginTop:12 }}>
          <input
            type="number" placeholder="ราคาเฉพาะ Variant (เว้นว่าง = ใช้ราคาหลัก)"
            value={draft.price_override}
            onChange={e=>setDraft(s=>({ ...s, price_override: e.target.value }))}
            disabled={busy}
            style={{ maxWidth:260 }}
          />
          <input
            type="number" placeholder="สต็อก"
            value={draft.stock}
            onChange={e=>setDraft(s=>({ ...s, stock: e.target.value }))}
            disabled={busy}
            style={{ maxWidth:140 }}
          />
          <button onClick={createVariant} disabled={busy}>บันทึก Variant</button>
        </div>
      </section>

      {/* รายการออปชั่น (จัดการค่า/แก้ชื่อ/ลบออปชั่น) */}
      {showOptions && (
        <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
          <h3>รายการออปชั่น (จัดการค่า/เปลี่ยนชื่อ/ลบออปชั่น)</h3>

          {/* แถบ “เพิ่มออปชั่น” */}
          <div style={{display:'flex', gap:8, marginBottom:12}}>
            <input
              placeholder="ชื่อออปชั่น (ว่างได้ → ระบบตั้งชื่อให้)"
              value={newOptName}
              onChange={e=>setNewOptName(e.target.value)}
              disabled={busy}
              style={{maxWidth:360}}
            />
            <button
              disabled={busy}
              onClick={async ()=>{
                try {
                  setBusy(true); setError(''); setMsg('');
                  await API.addOption(productId, newOptName.trim() || undefined);
                  setNewOptName('');
                  await load();
                  setMsg('เพิ่มออปชั่นสำเร็จ');
                } catch (e) {
                  boom(e,'เพิ่มออปชั่นไม่สำเร็จ');
                } finally {
                  setBusy(false);
                }
              }}
            >
              เพิ่มออปชั่น
            </button>
          </div>

          <div style={{ display:'grid', gap:14 }}>
            {data.options.map((opt, idx) => (
              <div key={opt.option_id} style={{ borderTop:'1px dashed #ddd', paddingTop:10 }}>
                {/* หัวบรรทัด: แสดง/แก้ชื่อ/ลบออปชั่น */}
                <div style={{display:'flex', alignItems:'center', gap:8}}>
                  {/* โหมดแก้ชื่อ */}
                  {editOptId === opt.option_id ? (
                    <>
                      <input
                        value={editOptText}
                        onChange={e=>setEditOptText(e.target.value)}
                        disabled={busy}
                        style={{maxWidth:260}}
                      />
                      <button
                        disabled={busy || !editOptText.trim()}
                        onClick={async ()=>{
                          try {
                            setBusy(true); setError(''); setMsg('');
                            await API.renameOption(productId, opt.option_id, editOptText.trim());
                            setEditOptId(null); setEditOptText('');
                            await load();
                            setMsg('เปลี่ยนชื่อออปชั่นสำเร็จ');
                          } catch (e) {
                            boom(e, 'เปลี่ยนชื่อออปชั่นไม่สำเร็จ');
                          } finally {
                            setBusy(false);
                          }
                        }}
                      >
                        บันทึกชื่อ
                      </button>
                      <button onClick={()=>{ setEditOptId(null); setEditOptText(''); }} disabled={busy}>
                        ยกเลิก
                      </button>
                    </>
                  ) : (
                    <>
                      <strong>{displayName(opt, idx)}</strong>
                      {useGeneric && opt.option_name ? <span style={{color:'#999'}}>({opt.option_name})</span> : null}

                      <button
                        onClick={()=>{ setEditOptId(opt.option_id); setEditOptText(opt.option_name || ''); }}
                        disabled={busy}
                      >
                        เปลี่ยนชื่อ
                      </button>

                      <button
                        onClick={async ()=>{
                          if (!window.confirm('ลบออปชั่นนี้ทั้งหมด? (รวมค่าทั้งหมดและ mapping variants)')) return;
                          try {
                            setBusy(true);
                            await API.deleteOption(opt.option_id);
                            await load();
                            setMsg('ลบออปชั่นสำเร็จ');
                          } catch(e) {
                            boom(e,'ลบออปชั่นไม่สำเร็จ');
                          } finally {
                            setBusy(false);
                          }
                        }}
                        disabled={busy}
                        style={{marginLeft:'auto'}}
                      >
                        ลบออปชั่น
                      </button>
                    </>
                  )}
                </div>

                {/* รายการค่า (values) ของออปชั่นนี้ + ปุ่มลบค่า */}
                <div style={{ display:'flex', gap:8, flexWrap:'wrap', marginTop:8 }}>
                  {(opt.values || []).length
                    ? opt.values.map(v => (
                        <span key={v.value_id} style={{ background:'#f5f5f5', padding:'6px 10px', borderRadius:8 }}>
                          {v.value_name}
                          <button
                            onClick={async ()=>{
                              try {
                                setBusy(true);
                                await API.deleteValue(v.value_id);  // ถ้า value ถูกใช้ใน Variant → backend จะตอบ 409
                                await load();
                              } catch(e) {
                                boom(e,'ลบค่าไม่สำเร็จ (อาจถูกใช้ใน Variant)');
                              } finally {
                                setBusy(false);
                              }
                            }}
                            disabled={busy}
                            style={{ marginLeft:8 }}
                          >
                            x
                          </button>
                        </span>
                      ))
                    : <span style={{color:'#777'}}>— ยังไม่มีค่า —</span>}
                </div>
              </div>
            ))}
          </div>
        </section>
      )}

      {/* ตารางรายการ Variants */}
      <section style={{ border:'1px solid #eee', borderRadius:12, padding:12 }}>
        <h3>รายการ Variants</h3>
        <table width="100%" cellPadding="8" style={{ borderCollapse:'collapse' }}>
          <thead>
            <tr style={{ background:'#fafafa' }}>
              <th align="left">#</th>
              <th align="left">SKU</th>
              <th align="left">คอมโบ</th>
              <th align="right">ราคาเฉพาะ</th>
              <th align="right">สต็อก</th>
              <th>จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {data.variants.map((s, idx) => (
              <tr key={s.variant_id} style={{ borderTop:'1px solid #eee' }}>
                <td>{idx + 1}</td>
                <td>{s.sku || <em>(auto)</em>}</td>
                <td>
                  {(s.combos || []).length
                    ? s.combos.map((c) => {
                        const iOpt = data.options.findIndex(o => o.option_id === c.option_id);
                        return `${displayName({option_name:c.option_name}, iOpt)}: ${c.value_name}`;
                      }).join(' ・ ')
                    : <em>-</em>}
                </td>
                <td align="right">
                  <InlineNumber
                    value={s.price_override ?? ''}
                    placeholder="-"
                    onSave={v => updateVariant(s.variant_id, { price_override: v === '' ? '' : Number(v) })}
                    disabled={busy}
                  />
                </td>
                <td align="right">
                  <InlineNumber
                    value={s.stock ?? 0}
                    onSave={v => updateVariant(s.variant_id, { stock: Number(v || 0) })}
                    disabled={busy}
                  />
                </td>
                <td>
                  <button onClick={()=>deleteVariant(s.variant_id)} disabled={busy}>ลบ</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </section>
    </div>
  );
}

// ---------- อินพุตตัวเลขแบบบันทึก inline (ลด friction เวลาแก้ราคา/สต็อก) ----------
function InlineNumber({ value, onSave, disabled, placeholder }) {
  const [v, setV] = useState(value ?? '');
  useEffect(() => setV(value ?? ''), [value]);
  return (
    <span>
      <input
        type="number"
        value={v}
        placeholder={placeholder}
        onChange={e=>setV(e.target.value)}
        style={{ width:110 }}
        disabled={disabled}
      />
      <button onClick={()=>onSave(v)} style={{ marginLeft:6 }} disabled={disabled}>บันทึก</button>
    </span>
  );
}
