// src/components/ThaiAddressPicker.js
// หน้าที่: ให้ผู้ใช้เลือก จังหวัด → อำเภอ → ตำบล แบบโหลดทีละชั้น
// และเติมรหัสไปรษณีย์อัตโนมัติจากข้อมูลตำบล

import React, { useEffect, useRef, useState } from 'react';

export default function ThaiAddressPicker({
  value,                 // อ็อบเจ็กต์ฟอร์มหลัก { province, district, subdistrict, postal_code }
  onChange,              // ฟังก์ชันรับ patch แล้วรวมเข้าฟอร์มหลัก
  basePath = '/data',    // ที่อยู่ไฟล์ใน public/ (เช่น /data/provinces.json)
  disabled = false,
}) {
  /* ---------- สเตตของรายการแต่ละชั้น ---------- */
  const [provinces, setProvinces] = useState([]);
  const [districts, setDistricts] = useState([]);
  const [subdistricts, setSubdistricts] = useState([]);

  /* ---------- สถานะโหลด เพื่อ disable เฉพาะชั้น ---------- */
  const [loadingProv, setLoadingProv] = useState(true);
  const [loadingDist, setLoadingDist] = useState(false);
  const [loadingSub,  setLoadingSub ] = useState(false);

  /* ---------- แคชในหน่วยความจำ กันโหลดซ้ำเมื่อสลับไปมา ---------- */
  const cacheRef = useRef({ districts: {}, subdistricts: {} });

  /* ---------- helper: ส่ง patch กลับไปอัปเดตฟอร์มหลัก ---------- */
  const patch = (obj) => onChange && onChange(obj);

  /* ========== 1) โหลด "จังหวัด" ตอน mount ========== */
  useEffect(() => {
    let alive = true;
    (async () => {
      setLoadingProv(true);
      try {
        const res  = await fetch(`${basePath}/provinces.json`, { cache: 'force-cache' });
        const data = await res.json();
        if (!alive) return;
        // คาดว่า data = [{ code: '10', name_th: 'กรุงเทพมหานคร' }, ...]
        setProvinces(Array.isArray(data) ? data : []);
      } catch (e) {
        console.error('[ThaiAddressPicker] load provinces error:', e);
        if (alive) setProvinces([]);
      } finally {
        if (alive) setLoadingProv(false);
      }
    })();
    return () => { alive = false; };
  }, [basePath]);

  // หา code ของจังหวัด/อำเภอจากชื่อ (อ้างกับรายการ "ปัจจุบัน")
  const getProvCode = (name) => provinces.find(p => p.name_th === name)?.code;
  const getDistCode = (name)   => districts.find(d => d.name_th === name)?.code;

  /* ========== 2) เมื่อ "จังหวัด" เปลี่ยน → โหลด "อำเภอ" ของจังหวัดนั้น ========== */
  useEffect(() => {
    const provCode = getProvCode(value?.province);
    // ถ้ายังไม่เลือกจังหวัด → ล้างชั้นล่าง ๆ
    if (!provCode) {
      setDistricts([]);
      setSubdistricts([]);
      return;
    }

    // ใช้แคชถ้ามีแล้ว
    if (cacheRef.current.districts[provCode]) {
      setDistricts(cacheRef.current.districts[provCode]);
      return;
    }

    let alive = true;
    (async () => {
      setLoadingDist(true);
      try {
        const res  = await fetch(`${basePath}/districts/${provCode}.json`, { cache: 'force-cache' });
        const data = await res.json();
        if (!alive) return;
        // คาดว่า data = [{ code: '1001', name_th: 'พระนคร' }, ...]
        cacheRef.current.districts[provCode] = Array.isArray(data) ? data : [];
        setDistricts(cacheRef.current.districts[provCode]);
      } catch (e) {
        console.error('[ThaiAddressPicker] load districts error:', e);
        if (alive) setDistricts([]);
      } finally {
        if (alive) setLoadingDist(false);
      }
    })();
    return () => { alive = false; };
  }, [value?.province, basePath]); // trigger เมื่อเลือกจังหวัดใหม่

  /* ========== 3) เมื่อ "อำเภอ" เปลี่ยน → โหลด "ตำบล" ของอำเภอนั้น ========== */
  useEffect(() => {
    const distCode = getDistCode(value?.district);
    if (!distCode) {
      setSubdistricts([]);
      return;
    }

    if (cacheRef.current.subdistricts[distCode]) {
      setSubdistricts(cacheRef.current.subdistricts[distCode]);
      return;
    }

    let alive = true;
    (async () => {
      setLoadingSub(true);
      try {
        const res  = await fetch(`${basePath}/subdistricts/${distCode}.json`, { cache: 'force-cache' });
        const data = await res.json();
        if (!alive) return;
        // คาดว่า data = [{ code:'100101', name_th:'พระบรมมหาราชวัง', zip_code:'10200' }, ...]
        cacheRef.current.subdistricts[distCode] = Array.isArray(data) ? data : [];
        setSubdistricts(cacheRef.current.subdistricts[distCode]);
      } catch (e) {
        console.error('[ThaiAddressPicker] load subdistricts error:', e);
        if (alive) setSubdistricts([]);
      } finally {
        if (alive) setLoadingSub(false);
      }
    })();
    return () => { alive = false; };
  }, [value?.district, basePath]); // trigger เมื่อเลือกอำเภอใหม่

  /* ========== onChange ของแต่ละ select ========== */
  const handleProvince = (e) => {
    const name = e.target.value || '';
    // เปลี่ยนจังหวัด → เคลียร์อำเภอ/ตำบล/ไปรษณีย์
    patch({ province: name, district: '', subdistrict: '', postal_code: '' });
  };

  const handleDistrict = (e) => {
    const name = e.target.value || '';
    // เปลี่ยนอำเภอ → เคลียร์ตำบล/ไปรษณีย์
    patch({ district: name, subdistrict: '', postal_code: '' });
  };

  const handleSubdistrict = (e) => {
    const name = e.target.value || '';
    const sub = subdistricts.find(s => s.name_th === name);
    // เลือกตำบล → เติม zip อัตโนมัติ
    patch({ subdistrict: name, postal_code: sub?.zip_code || '' });
  };

  /* ---------- คำนวณ disabled ของแต่ละชั้น ---------- */
  const disProv = disabled || loadingProv;
  const disDist = disabled || !value?.province || loadingDist;
  const disSub  = disabled || !value?.district || loadingSub;

  /* ========== UI ========== */
  return (
    <div style={{ display: 'grid', gap: 8 }}>
      <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: 8 }}>
        {/* จังหวัด */}
        <select value={value?.province || ''} onChange={handleProvince} disabled={disProv}>
          <option value="">{loadingProv ? 'กำลังโหลดจังหวัด...' : '-- เลือกจังหวัด --'}</option>
          {provinces.map(p => <option key={p.code} value={p.name_th}>{p.name_th}</option>)}
        </select>

        {/* อำเภอ */}
        <select value={value?.district || ''} onChange={handleDistrict} disabled={disDist}>
          <option value="">{loadingDist ? 'กำลังโหลดอำเภอ...' : '-- เลือกอำเภอ --'}</option>
          {districts.map(d => <option key={d.code} value={d.name_th}>{d.name_th}</option>)}
        </select>

        {/* ตำบล */}
        <select value={value?.subdistrict || ''} onChange={handleSubdistrict} disabled={disSub}>
          <option value="">{loadingSub ? 'กำลังโหลดตำบล...' : '-- เลือกตำบล --'}</option>
          {subdistricts.map(s => <option key={s.code} value={s.name_th}>{s.name_th}</option>)}
        </select>
      </div>

      {/* รหัสไปรษณีย์: เติมจากตำบล แต่เปิดให้แก้ไขได้ */}
      <input
        placeholder="รหัสไปรษณีย์"
        value={value?.postal_code || ''}
        onChange={e => patch({ postal_code: e.target.value })}
        disabled={disabled}
      />
    </div>
  );
}
