import React from 'react';
import { Link } from 'react-router-dom';
import './HomeUser.css';

export default function HomeUser() {
  const user = {
    fullname: 'ศุภชัย ครับผม',
    avatar: process.env.PUBLIC_URL + '/profile.jpg', // ✅ แก้ให้ชี้จาก public
  };

  // ✅ สินค้าขายดี
  const bestSellers = [
  { id: 1, name: 'ต้นกล้าลำใยคริสตัล', price: 250, img: process.env.PUBLIC_URL + '/lamyai.jpg' },
  { id: 2, name: 'ต้นพญานาคราช', price: 150, img: process.env.PUBLIC_URL + '/payanaka1.jpg' },
  { id: 3, name: 'กระถางมินิมอล ', price: 300, img: process.env.PUBLIC_URL + '/katar.jpeg' },
  { id: 4, name: 'ดินสูตรทางร้าน', price: 99, img: process.env.PUBLIC_URL + '/din.jpg' }
];


  const addToCart = (product) => {
    let cart = JSON.parse(localStorage.getItem('cart')) || [];
    const existing = cart.find((item) => item.id === product.id);

    if (existing) {
      existing.quantity += 1;
    } else {
      cart.push({ ...product, quantity: 1 });
    }

    localStorage.setItem('cart', JSON.stringify(cart));
    alert(`เพิ่ม ${product.name} ลงตะกร้าแล้ว`);
  };

  return (
    <div className="home-container">
      {/* Header */}
      <header className="header">
        <div className="logo">
          <span>PRACH MAEJO</span>
        </div>

        <div className="user-icons">
          <Link to="/profile" className="avatar-link">
            <img
              src={user.avatar}
              alt="Avatar"
              className="avatar-img"
              onError={(e) => { e.currentTarget.src = process.env.PUBLIC_URL + '/logo.png'; }}
            />
          </Link>
          <span className="hello">{user.fullname}</span>

          <Link to="/cart" title="ตะกร้าสินค้า">
            <span className="icon-cart">🛒</span>
          </Link>

          <Link to="/logout" className="icon-logout" title="ออกจากระบบ">
            🚪
          </Link>
        </div>
      </header>

      {/* Middle bar */}
      <div className="middle-bar">GARDEN PRODUCT ONLINE</div>

      {/* Search */}
      <div className="search-box">
        <input type="text" placeholder="ค้นหาสินค้า" />
        <button className="search-btn">🔍</button>
      </div>

      {/* Hero */}
      <section
        className="hero"
        style={{
          backgroundImage: `url(${process.env.PUBLIC_URL + '/p2.png'})`,
          backgroundRepeat: 'no-repeat',
          backgroundPosition: 'center',
          backgroundSize: 'cover',
        }}
      >
        <div className="hero-text">
          <h1>“<span className="highlight">ร้านปราชญ์แม่โจ้</span>”</h1>
          <p>
            แหล่งผลิตและจัดจำหน่าย ต้นไม้ <br />
            และวัสดุปลูก มากกว่า 100 รายการ
          </p>
        </div>
      </section>

      {/* Categories */}
      <section className="categories">
        <Link to="/products?category=tools" className="category-card">
          <img src={process.env.PUBLIC_URL + '/opakorn.jpg'} alt="อุปกรณ์ปลูก" />
          <p>อุปกรณ์ปลูก</p>
        </Link>
        <Link to="/products?category=plants" className="category-card">
          <img src={process.env.PUBLIC_URL + '/tonmai.jpg'} alt="ต้นไม้" />
          <p>ต้นไม้</p>
        </Link>
      </section>

      {/* Best Sellers */}
      <section className="best-sellers">
        <h2>🌟 สินค้าขายดี</h2>
        <div className="product-grid">
          {bestSellers.map((item) => (
            <div className="product-card" key={item.id}>
              <img src={item.img} alt={item.name} />
              <h3>{item.name}</h3>
              <p>{item.price} บาท</p>
              <button
                className="btn-add"
                onClick={() => addToCart(item)}
              >
                + เพิ่มลงตะกร้า
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
