// FRONTEND: src/pages/SignUp.js
import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './SignUp.css';

export default function SignUp() {
  const nav = useNavigate();

  /* ========== ‡∏ü‡∏≠‡∏£‡πå‡∏°: ‡πÄ‡∏Å‡πá‡∏ö‡πÄ‡∏õ‡πá‡∏ô code (‡∏Å‡∏±‡∏ô render object + ‡πÄ‡∏™‡∏ñ‡∏µ‡∏¢‡∏£‡πÄ‡∏ß‡∏•‡∏≤ lookup) ========== */
  const [form, setForm] = useState({
    email: '',
    password: '',
    confirm: '',
    full_name: '',
    phone: '',
    houseNo: '',
    province_code: '',
    amphoe_code: '',
    tambon_code: '',
    gender: '',
  });
  const [showPw, setShowPw] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  /* ========== ‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà ========== */
  const [db, setDb] = useState(null);
  const [addrLoadErr, setAddrLoadErr] = useState('');

  useEffect(() => {
    (async () => {
      try {
        const url = `${process.env.PUBLIC_URL}/data/thai-address.min.json`;
        const res = await fetch(url);
        const json = await res.json();
        setDb(json);
      } catch (e) {
        console.error(e);
        setAddrLoadErr('‡πÇ‡∏´‡∏•‡∏î‡∏ê‡∏≤‡∏ô‡∏Ç‡πâ‡∏≠‡∏°‡∏π‡∏•‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡πÑ‡∏°‡πà‡∏™‡∏≥‡πÄ‡∏£‡πá‡∏à');
      }
    })();
  }, []);

  /* ===================== Normalizers ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡πÄ‡∏ß‡∏≠‡∏£‡πå‡∏ä‡∏±‡∏ô Register ‡πÄ‡∏î‡∏¥‡∏° ===================== */
  // ‡πÅ‡∏õ‡∏•‡∏á‡∏≠‡∏∞‡πÑ‡∏£‡∏Å‡πá‡πÑ‡∏î‡πâ‡πÉ‡∏´‡πâ‡∏Å‡∏•‡∏≤‡∏¢‡πÄ‡∏õ‡πá‡∏ô array
  const toArray = (v) => {
    if (!v) return [];
    if (Array.isArray(v)) return v;
    if (typeof v === 'object') return Object.values(v);
    return [];
  };

  // ‡∏´‡∏¢‡∏¥‡∏ö‡∏ä‡∏∑‡πà‡∏≠/‡πÇ‡∏Ñ‡πâ‡∏î‡∏≠‡∏≠‡∏Å‡∏°‡∏≤‡πÅ‡∏ö‡∏ö‡∏ó‡∏ô‡∏ó‡∏≤‡∏ô‡∏ï‡πà‡∏≠‡∏´‡∏•‡∏≤‡∏¢‡∏™‡∏Ñ‡∏µ‡∏°‡∏≤
  const pickName = (o) =>
    (o && (o.name_th ?? o.name ?? o.label ?? o.PROVINCE_NAME_TH ?? o.AMPHOE_NAME_TH ?? o.TAMBON_NAME_TH)) ||
    String(o);

  const pickCode = (o) =>
    (o && (o.code ?? o.id ?? o.value ?? o.province_code ?? o.amphoe_code ?? o.tambon_code ?? o.PROVINCE_CODE ?? o.AMPHOE_CODE ?? o.TAMBON_CODE)) ||
    pickName(o);

  // ‡πÅ‡∏õ‡∏•‡∏á‡∏£‡∏≤‡∏¢‡∏Å‡∏≤‡∏£‡πÉ‡∏´‡πâ‡πÄ‡∏´‡∏•‡∏∑‡∏≠‡∏£‡∏π‡∏õ‡πÄ‡∏î‡∏µ‡∏¢‡∏ß [{code, name_th, zip?}]
  const normList = (raw) =>
    toArray(raw).map((it) => {
      if (it && typeof it === 'object') {
        return {
          code: pickCode(it),
          name_th: pickName(it),
          zip: it.zip ?? it.zipcode ?? it.postcode ?? it.postal_code ?? '',
        };
      }
      const s = String(it);
      return { code: s, name_th: s, zip: '' };
    });

  /* ---------------------- ‡∏î‡∏∂‡∏á‡∏à‡∏±‡∏á‡∏´‡∏ß‡∏±‡∏î‡πÅ‡∏ö‡∏ö ‚Äú‡∏Ñ‡∏•‡∏µ‡πà group‚Äù ‡πÄ‡∏´‡∏°‡∏∑‡∏≠‡∏ô‡∏´‡∏ô‡πâ‡∏≤ Register ---------------------- */
  const extractProvinces = (dbObj) => {
    const pv = dbObj?.provinces || dbObj?.province || dbObj?.prov;
    if (!pv) return [];

    // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô array ‡∏≠‡∏¢‡∏π‡πà‡πÅ‡∏•‡πâ‡∏ß -> ‡∏õ‡∏Å‡∏ï‡∏¥
    if (Array.isArray(pv)) return normList(pv);

    // ‡∏ñ‡πâ‡∏≤‡πÄ‡∏õ‡πá‡∏ô object ‡πÄ‡∏ä‡πá‡∏Ñ‡∏ß‡πà‡∏≤ ‚Äú‡πÄ‡∏õ‡πá‡∏ô province ‡∏ï‡∏£‡∏á ‡πÜ‚Äù ‡∏´‡∏£‡∏∑‡∏≠ ‚Äú‡πÄ‡∏õ‡πá‡∏ô‡∏Å‡∏•‡∏∏‡πà‡∏°‡∏Ç‡∏≠‡∏á province‚Äù
    const vals = Object.values(pv);
    if (!vals.length) return [];

    const valueLooksProvince = (v) =>
      typeof v === 'string' || !!(v && (v.name_th || v.name || v.PROVINCE_NAME_TH || v.code || v.PROVINCE_CODE));

    // ‡∏Å‡∏£‡∏ì‡∏µ grouped-object (‡πÄ‡∏ä‡πà‡∏ô {north:{'10':{...},...}, central:{...}})
    const flattened = vals.flatMap((v) => (valueLooksProvince(v) ? [v] : Object.values(v || {})));
    return normList(flattened).sort((a, b) => a.name_th.localeCompare(b.name_th, 'th'));
  };

  // ‡∏≠‡∏≥‡πÄ‡∏†‡∏≠: ‡∏•‡∏≠‡∏á‡∏´‡∏•‡∏≤‡∏¢‡∏Ñ‡∏µ‡∏¢‡πå‡∏ó‡∏µ‡πà‡∏û‡∏ö‡∏ö‡πà‡∏≠‡∏¢
  const extractAmphoes = (dbObj, provCode) => {
    const candidates = [
      dbObj?.districts?.[provCode],
      dbObj?.amphoes?.[provCode],
      dbObj?.districts_by_province?.[provCode],
      dbObj?.amphoesByProvince?.[provCode],
    ];
    const raw = candidates.find(Boolean);
    return normList(raw).sort((a, b) => a.name_th.localeCompare(b.name_th, 'th'));
  };

  // ‡∏ï‡∏≥‡∏ö‡∏•
  const extractTambons = (dbObj, provCode, amphCode) => {
    const candidates = [
      dbObj?.subdistricts?.[provCode]?.[amphCode],
      dbObj?.tambons?.[provCode]?.[amphCode],
      dbObj?.subdistricts_by_amphoe?.[provCode]?.[amphCode],
      dbObj?.tambonsByAmphoe?.[provCode]?.[amphCode],
    ];
    const raw = candidates.find(Boolean);
    return normList(raw).sort((a, b) => a.name_th.localeCompare(b.name_th, 'th'));
  };

  /* ===================== options ‡∏Ç‡∏≠‡∏á select ===================== */
  const provinces = useMemo(() => (db ? extractProvinces(db) : []), [db]);
  const amphoes = useMemo(() => (!db || !form.province_code ? [] : extractAmphoes(db, form.province_code)), [db, form.province_code]);
  const tambons = useMemo(
    () => (!db || !form.province_code || !form.amphoe_code ? [] : extractTambons(db, form.province_code, form.amphoe_code)),
    [db, form.province_code, form.amphoe_code]
  );

  // ZIP ‡∏à‡∏≤‡∏Å‡∏ï‡∏≥‡∏ö‡∏•
  const zipcode = useMemo(() => {
    const t = tambons.find((x) => x.code === form.tambon_code);
    return t?.zip || '';
  }, [tambons, form.tambon_code]);

  /* ===================== helpers/validation ===================== */
  const isEmail = (v) => /\S+@\S+\.\S+/.test(String(v || '').trim());
  const pwMismatch = form.password && form.confirm && form.password !== form.confirm;

  const splitName = (full) => {
    const p = String(full || '').trim().split(/\s+/);
    if (p.length <= 1) return { first_name: p[0] || '', last_name: '' };
    return { first_name: p[0], last_name: p.slice(1).join(' ') };
  };
  const makeUsername = (email) => {
    const raw = String(email).split('@')[0] || '';
    const cleaned = raw.toLowerCase().replace(/[^a-z0-9_.-]/g, '');
    return cleaned || `cust${Date.now().toString().slice(-6)}`;
  };
  const findName = (list, code) => list.find((x) => x.code === code)?.name_th || '';

  /* ===================== handlers ===================== */
  const onChange = (e) => {
    const { name, value } = e.target;
    if (name === 'province_code') return setForm((p) => ({ ...p, province_code: value, amphoe_code: '', tambon_code: '' }));
    if (name === 'amphoe_code') return setForm((p) => ({ ...p, amphoe_code: value, tambon_code: '' }));
    setForm((p) => ({ ...p, [name]: value }));
  };

  async function onSubmit(e) {
    e.preventDefault();
    setError('');

