// [FRONTEND] src/App.js
import React, { useEffect, useEffect as ReactUseEffect } from 'react';
import {
  BrowserRouter, Routes, Route, NavLink, Navigate,
  useNavigate, useLocation
} from 'react-router-dom';
import axios from 'axios';

import ProductManagement from './admin/ProductManagement';
import OrderManagement   from './admin/OrderManagement';
import VariantsManager   from './admin/VariantsManager';
import AdminLayout       from './admin/AdminLayout';
import Dashboard         from './admin/Dashboard';
import ProductEditPage   from './admin/ProductEditPage';

import Addresses     from './account/Addresses';
import SignUp        from './pages/SignUp';
import Login         from './pages/Login';
import ResetPassword from './pages/ResetPassword';
import HomeUser      from './pages/HomeUser';

import ProductList   from './pages/ProductList';
import Plants        from './pages/Plants';
import Tools         from './pages/Tools';
import ProfileModal  from './pages/ProfileModal';   // 🟢 NEW

// const InventoryPage = ...

/* ================== ตั้งค่า API BASE ทั้งแอป ================== */
const API_BASE = import.meta?.env?.VITE_API_BASE || 'http://localhost:3001';
axios.defaults.baseURL = API_BASE;
/* ============================================================ */

/* ====================== Helpers ====================== */
const getToken = () => localStorage.getItem('token') || '';
const getRole  = () => (localStorage.getItem('role') || '').toLowerCase();

const isAdminRole     = (r) => ['admin', 'staff', 'superadmin'].includes(String(r||'').toLowerCase());
const isCustomerRole  = (r) => ['customer', 'user', 'member', 'client', 'buyer', ''].includes(String(r||'').toLowerCase());

const bootToken = getToken();
if (bootToken) axios.defaults.headers.common['Authorization'] = `Bearer ${bootToken}`;

/* ==================== ErrorBoundary =================== */
class ErrorBoundary extends React.Component {
  constructor(p){ super(p); this.state = { err: null }; }
  static getDerivedStateFromError(error){ return { err: error }; }
  componentDidCatch(error, info){ console.error('UI error:', error, info); }
  render(){
    if (this.state.err){
      return (
        <div style={{ padding:16 }}>
          <h2>เกิดข้อผิดพลาดในหน้า</h2>
          <pre style={{ background:'#111', color:'#0f0', padding:12, borderRadius:8, overflow:'auto' }}>
{String(this.state.err?.message || this.state.err)}
          </pre>
        </div>
      );
    }
    return this.props.children;
  }
}

/* ====================== Guards ======================== */
function RequireAuth({ children }) {
  const loc = useLocation();
  if (!getToken()) {
    const redirect = encodeURIComponent(loc.pathname + loc.search);
    return <Navigate to={`/login?redirect=${redirect}&msg=login_required`} replace />;
  }
  return children;
}

function RequireAdmin({ children }) {
  const loc = useLocation();
  if (!getToken()) {
    const redirect = encodeURIComponent(loc.pathname + loc.search);
    return <Navigate to={`/login?redirect=${redirect}&msg=login_required`} replace />;
  }
  if (!isAdminRole(getRole())) return <Navigate to="/" replace />;
  return children;
}

/* ============== UserMenu (เมนูผู้ใช้แบบฝังในไฟล์) ============== */
function UserMenu({ loggedIn, onLogout, isAdmin, showAddressesLink }) {
  const [open, setOpen] = React.useState(false);
  const ref = React.useRef(null);
  const nav = useNavigate();

  // ปิดเมนูเมื่อคลิกนอก/กด ESC
  React.useEffect(() => {
    const onKey = (e) => e.key === 'Escape' && setOpen(false);
    const onClick = (e) => { if (ref.current && !ref.current.contains(e.target)) setOpen(false); };
    document.addEventListener('keydown', onKey);
    document.addEventListener('mousedown', onClick);
    return () => {
      document.removeEventListener('keydown', onKey);
      document.removeEventListener('mousedown', onClick);
    };
  }, []);

  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  if (!loggedIn) {
    return (
      <div style={{ display:'flex', gap:8 }}>
        <NavLink to="/login" style={linkStyle}>เข้าสู่ระบบ</NavLink>
        <NavLink to="/register" style={linkStyle}>สมัครสมาชิก</NavLink>
      </div>
    );
  }

  const Avatar = (
    <span
      aria-label="บัญชีผู้ใช้"
      title="บัญชีผู้ใช้"
      style={{
        width: 36, height: 36, display:'grid', placeItems:'center',
        borderRadius:'9999px', background:'#f3f4f6', border:'1px solid #e5e7eb'
      }}
    >
      <svg width="18" height="18" viewBox="0 0 24 24" fill="none">
        <path d="M12 12c2.761 0 5-2.239 5-5S14.761 2 12 2 7 4.239 7 7s2.239 5 5 5Zm0 2c-4.418 0-8 2.239-8 5v3h16v-3c0-2.761-3.582-5-8-5Z" fill="#374151"/>
      </svg>
    </span>
  );

  const itemStyle = {
    display:'block', width:'100%', textAlign:'left', padding:'10px 12px',
    fontSize:14, background:'#fff', border:0, cursor:'pointer'
  };

  const handleLogout = async () => {
    try { await onLogout?.(); } finally { setOpen(false); nav('/login'); }
  };

  return (
    <div style={{ position:'relative' }} ref={ref}>
      <button
        onClick={() => setOpen(v => !v)}
        style={{ display:'flex', alignItems:'center', gap:8, border:0, background:'transparent' }}
        aria-haspopup="menu" aria-expanded={open}
      >
        {Avatar}
      </button>

      {open && (
        <div
          role="menu"
          style={{
            position:'absolute', right:0, top:'calc(100% + 8px)',
            width:200, background:'#fff', border:'1px solid #e5e7eb',
            borderRadius:10, boxShadow:'0 12px 24px rgba(0,0,0,.08)', zIndex:50, overflow:'hidden'
          }}
        >
          <NavLink to="/profile" role="menuitem" style={itemStyle} onClick={()=>setOpen(false)}>
            โปรไฟล์ของฉัน
          </NavLink>

          {showAddressesLink && (
            <NavLink to="/account/addresses" role="menuitem" style={itemStyle} onClick={()=>setOpen(false)}>
              ที่อยู่ของฉัน
            </NavLink>
          )}

          {isAdmin && (
            <NavLink to="/admin" role="menuitem" style={itemStyle} onClick={()=>setOpen(false)}>
              ไปหน้าแอดมิน
            </NavLink>
          )}

          <hr style={{ margin:0, borderTop:'1px solid #e5e7eb' }} />
          <button role="menuitem" onClick={handleLogout}
                  style={{ ...itemStyle, color:'#b91c1c' }}>
            ออกจากระบบ
          </button>
        </div>
      )}
    </div>
  );
}

/* ============== Navbar (โชว์บนฝั่งลูกค้า) ============== */
function Navbar() {
  const nav = useNavigate();
  const loc = useLocation();
  if (loc.pathname.startsWith('/admin')) return null;

  const hasToken = !!getToken();
  const role = getRole();
  const linkStyle = ({ isActive }) => ({
    textDecoration: 'none', padding: '6px 10px', borderRadius: 8,
    background: isActive ? '#f0f0f0' : 'transparent',
  });

  function logout(){
    // ถ้ามี API logout ให้เรียกที่นี่ได้ เช่น await axios.post('/api/auth/logout')
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    delete axios.defaults.headers.common['Authorization'];
    nav('/', { replace: true });
  }

  return (
    <nav style={{ padding: 10, display: 'flex', gap: 16, borderBottom: '1px solid #eee' }}>
      <NavLink to="/" style={linkStyle} end>หน้าแรก</NavLink>

      {hasToken && isCustomerRole(role) && (
        <NavLink to="/account/addresses" style={linkStyle}>ที่อยู่ของฉัน</NavLink>
      )}

      <div style={{ marginLeft:'auto' }}>
        {/* 🔁 แทนปุ่ม "ออกจากระบบ" ด้วยไอคอนผู้ใช้ + เมนู */}
        <UserMenu
          loggedIn={hasToken}
          onLogout={logout}
          isAdmin={isAdminRole(role)}
          showAddressesLink={hasToken && isCustomerRole(role)}
        />
      </div>
    </nav>
  );
}

/* ======================== App ========================= */
export default function App() {
  useEffect(() => {
    const t = getToken();
    if (t) axios.defaults.headers.common['Authorization'] = `Bearer ${t}`;
  }, []);

  return (
    <BrowserRouter>
      <ErrorBoundary>
        <Navbar />
        <Routes>
          {/* ✅ หน้าแรก */}
          <Route path="/" element={<HomeUser />} />
          <Route path="/home-user" element={<Navigate to="/" replace />} />

          {/* ✅ Landing ต้นไม้/อุปกรณ์ + รายการสินค้า */}
          <Route path="/plants" element={<Plants />} />
          <Route path="/tools" element={<Tools />} />
          <Route path="/products" element={<ProductList />} />

          {/* ✅ โปรไฟล์แบบป๊อปอัพ (ต้องล็อกอิน) */}
          <Route
            path="/profile"
            element={
              <RequireAuth>
                <ProfileModal />
              </RequireAuth>
            }
          />

          {/* ต้องล็อกอิน */}
          <Route path="/account/addresses" element={
            <RequireAuth><Addresses /></RequireAuth>
          } />

          {/* แอดมิน */}
          <Route path="/admin" element={
            <RequireAdmin><AdminLayout /></RequireAdmin>
          }>
            <Route index element={<Dashboard />} />
            <Route path="products" element={<ProductManagement />} />
            <Route path="products/:id" element={<ProductEditPage />} />
            {/* <Route path="inventory" element={<InventoryPage />} /> */}
            <Route path="orders"   element={<OrderManagement />} />
            <Route path="products/:productId/variants" element={<VariantsManager />} />
          </Route>

          {/* Auth */}
          <Route path="/login"          element={<Login />} />
          <Route path="/register"       element={<SignUp />} />
          <Route path="/reset-password" element={<ResetPassword />} />

          {/* 404 → หน้าแรก */}
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </ErrorBoundary>
    </BrowserRouter>
  );
}
