// src/admin/AdminLayout.js
import React, { useEffect, useRef, useState } from "react";
import { NavLink, Outlet, useNavigate, useLocation } from "react-router-dom";
import "./AdminLayout.css";
import { getNewOrdersCount } from "../lib/api"; // ‚úÖ ‡πÉ‡∏ä‡πâ helper ‡∏ó‡∏µ‡πà‡∏Ñ‡∏∏‡∏ì‡πÉ‡∏™‡πà‡πÑ‡∏ß‡πâ‡πÉ‡∏ô api.js ‡πÅ‡∏•‡πâ‡∏ß

export default function AdminLayout() {
  const navigate = useNavigate();
  const location = useLocation();

  const [newOrders, setNewOrders] = useState(0);
  const lastCheckRef = useRef(null);

  // ‡∏î‡∏∂‡∏á productId ‡∏õ‡∏±‡∏à‡∏à‡∏∏‡∏ö‡∏±‡∏ô‡∏à‡∏≤‡∏Å‡∏û‡∏≤‡∏ò
  const match = location.pathname.match(/\/admin\/products\/(\d+)/);
  const currentProductId = match ? match[1] : null;

  const menus = [
    { label: "Dashboard",     icon: "üìä", path: "/admin" },
    { label: "‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÅ‡∏•‡∏∞‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤",   icon: "üì¶", path: "/admin/products" },
    { label: "‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏õ‡∏£‡∏∞‡πÄ‡∏†‡∏ó",         icon: "üìÇ", path: "/admin/categories" },
    { label: "‡∏à‡∏´‡∏°‡∏ß‡∏î‡∏¢‡πà‡∏≠‡∏¢",       icon: "üóÇÔ∏è", path: "/admin/subcategories" },
    { label: "‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤",     icon: "üìè", path: "/admin/units" },
    { label: "‡∏´‡∏ô‡πà‡∏ß‡∏¢‡∏Ç‡∏ô‡∏≤‡∏î",      icon: "üìê", path: "/admin/sizes" },
    { label: "‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏Ñ‡∏á‡∏Ñ‡∏•‡∏±‡∏á",   icon: "üè¨", path: "/admin/inventory" },
    { label: "‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠",      icon: "üßæ", path: "/admin/orders", badge: true },
    { label: "‡∏™‡∏£‡∏∏‡∏õ‡∏¢‡∏≠‡∏î‡∏Ç‡∏≤‡∏¢",     icon: "üí∞", path: "/admin/reports" },
     { label: "‡∏ú‡∏π‡πâ‡πÉ‡∏ä‡πâ/‡∏™‡∏¥‡∏ó‡∏ò‡∏¥‡πå",    icon: "üë§", path: "/admin/user-management" },
  ];

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("role");
    navigate("/login?role=admin");
  };

  // ‚úÖ Polling ‡∏™‡∏≥‡∏´‡∏£‡∏±‡∏ö badge NEW (x)
  useEffect(() => {
    let alive = true;

    async function tick() {
      try {
        const count = await getNewOrdersCount({
          updatedSince: lastCheckRef.current || null,
        });
        if (!alive) return;
        setNewOrders(count);
        lastCheckRef.current = new Date().toISOString();
      } catch {}
    }

    tick();
    const id = setInterval(tick, 30_000);

    // ‡∏ü‡∏±‡∏á‡∏™‡∏±‡∏ç‡∏ç‡∏≤‡∏ì‡∏à‡∏≤‡∏Å OrderManagement
    function onOrdersSignal(ev) {
      if (ev?.detail === "orders:refreshed") tick();
    }
    window.addEventListener("orders:signal", onOrdersSignal);

    return () => {
      alive = false;
      clearInterval(id);
      window.removeEventListener("orders:signal", onOrdersSignal);
    };
  }, []);

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <aside className="w-64 bg-white shadow-lg flex flex-col">
        <div className="p-4 text-2xl font-bold border-b">üåø ADMIN</div>
        <nav className="flex-1 p-4 flex flex-col gap-2">
          {menus.map((m, i) => (
            <NavLink
              key={i}
              to={m.path}
              end={m.path === "/admin"}
              className={({ isActive }) =>
                `flex items-center justify-between px-3 py-2 rounded-lg ${
                  isActive ? "bg-green-500 text-white" : "hover:bg-green-100"
                }`
              }
            >
              <div>
                <span className="mr-2">{m.icon}</span>
                {m.label}
              </div>
              {/* ‚úÖ Badge ‡πÄ‡∏â‡∏û‡∏≤‡∏∞‡πÄ‡∏°‡∏ô‡∏π Orders */}
              {m.badge && newOrders > 0 && (
                <span className="badge-new">NEW ({newOrders})</span>
              )}
            </NavLink>
          ))}

          {/* üü¢ ‡∏•‡∏¥‡∏á‡∏Å‡πå‡πÑ‡∏î‡∏ô‡∏≤‡∏°‡∏¥‡∏Å‡πÑ‡∏õ‡∏´‡∏ô‡πâ‡∏≤ Variants */}
          {currentProductId && (
            <NavLink
              to={`/admin/products/${currentProductId}/variants`}
              className={({ isActive }) =>
                `block px-3 py-2 rounded-lg mt-2 ${
                  isActive ? "bg-green-600 text-white" : "bg-green-50 hover:bg-green-100"
                }`
              }
              title={`‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/Variants ‡∏Ç‡∏≠‡∏á‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤ #${currentProductId}`}
            >
              <span className="mr-2">üéõÔ∏è</span>
              ‡∏ï‡∏±‡∏ß‡πÄ‡∏•‡∏∑‡∏≠‡∏Å/Variants (‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤‡∏ô‡∏µ‡πâ)
            </NavLink>
          )}
        </nav>
        <button
          className="m-4 px-4 py-2 rounded-lg bg-red-500 text-white hover:bg-red-600"
          onClick={logout}
        >
          üö™ ‡∏≠‡∏≠‡∏Å‡∏à‡∏≤‡∏Å‡∏£‡∏∞‡∏ö‡∏ö
        </button>
      </aside>

      {/* Main Content */}
      <main className="flex-1 p-6 overflow-y-auto">
        <Outlet />
      </main>
    </div>
  );
}
