          path="/"
          element={
            <div className="App" style={{ padding: 16 }}>
              <h1>üåø ‡∏£‡∏∞‡∏ö‡∏ö‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏™‡∏¥‡∏ô‡∏Ñ‡πâ‡∏≤</h1>
              <ProductManagement />
