        <Route
          path="/admin/orders"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>üì¶ ‡∏à‡∏±‡∏î‡∏Å‡∏≤‡∏£‡∏Ñ‡∏≥‡∏™‡∏±‡πà‡∏á‡∏ã‡∏∑‡πâ‡∏≠</h1>
                <OrderManagement />
              </div>
            </RequireAuth>
          }
        />

        {/* ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏Ç‡∏≠‡∏á‡∏â‡∏±‡∏ô */}
        <Route
          path="/account/addresses"
          element={
            <RequireAuth>
              <div style={{ padding: 16 }}>
                <h1>üè† ‡∏ó‡∏µ‡πà‡∏≠‡∏¢‡∏π‡πà‡∏Ç‡∏≠‡∏á‡∏â‡∏±‡∏ô</h1>
                <Addresses />
              </div>
            </RequireAuth>
          }
        />

        {/* ‡πÄ‡∏Ç‡πâ‡∏≤‡∏™‡∏π‡πà‡∏£‡∏∞‡∏ö‡∏ö / ‡∏™‡∏°‡∏±‡∏Ñ‡∏£‡∏™‡∏°‡∏≤‡∏ä‡∏¥‡∏Å */}
