import React, { useEffect, useState } from 'react';
import { listInventory, adjustInventory, setInventory } from '../lib/api';

export default function InventoryPage() {
  const [items, setItems] = useState([]);
  const [total, setTotal] = useState(0);
  const [q, setQ] = useState('');
  const [order, setOrder] = useState('low_stock');
  const [limit] = useState(20);
  const [offset, setOffset] = useState(0);
  const [loading, setLoading] = useState(false);
  const [note, setNote] = useState('');

  const load = async () => {
    setLoading(true);
    try {
      const res = await listInventory({ search: q, limit, offset, order });
      setItems(res.items || []);
      setTotal(res.total || 0);
    } catch (e) {
      console.error(e);
      alert('โหลดข้อมูลไม่สำเร็จ');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { load(); /* eslint-disable-next-line */ }, [offset, order]);

  const canPrev = offset > 0;
  const canNext = offset + limit < total;

  const doAdjust = async (id, delta) => {
    try { await adjustInventory(id, delta, note); await load(); }
    catch (e) { console.error(e); alert('ปรับสต็อกไม่สำเร็จ'); }
  };

  const doSet = async (id, stock) => {
    const n = parseInt(prompt('กำหนดสต็อกเป็น (จำนวนเต็ม >= 0):', String(stock ?? 0)), 10);
    if (!Number.isFinite(n) || n < 0) return;
    try { await setInventory(id, n, note); await load(); }
    catch (e) { console.error(e); alert('ตั้งค่าสต็อกไม่สำเร็จ'); }
  };

  const onSearch = (e) => { e.preventDefault(); setOffset(0); load(); };

  return (
    <div className="p-4 space-y-4">
      <h1 className="text-xl font-bold">จัดการคลังสินค้า</h1>

      <form onSubmit={onSearch} className="flex gap-2">
        <input value={q} onChange={(e) => setQ(e.target.value)} placeholder="ค้นหาชื่อ/คำอธิบาย…"
               className="border px-3 py-2 rounded w-72" />
        <select value={order} onChange={(e) => setOrder(e.target.value)} className="border px-3 py-2 rounded">
          <option value="low_stock">เรียงสต็อกน้อยก่อน</option>
          <option value="newest">ใหม่ล่าสุด</option>
          <option value="name_asc">ชื่อ A→Z</option>
          <option value="name_desc">ชื่อ Z→A</option>
        </select>
        <input value={note} onChange={(e) => setNote(e.target.value)} placeholder="หมายเหตุ (เช่น รับล็อต ก.ย.)"
               className="border px-3 py-2 rounded flex-1" />
        <button className="px-4 py-2 bg-black text-white rounded">ค้นหา</button>
      </form>

      <div className="border rounded">
        <table className="w-full text-sm">
          <thead className="bg-gray-50">
            <tr>
              <th className="text-left p-2">สินค้า</th>
              <th className="text-left p-2">หมวด</th>
              <th className="text-right p-2">ราคา</th>
              <th className="text-right p-2">สต็อก</th>
              <th className="text-right p-2">จัดการ</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={5} className="p-4 text-center">กำลังโหลด…</td></tr>
            ) : items.length === 0 ? (
              <tr><td colSpan={5} className="p-4 text-center">ไม่พบสินค้า</td></tr>
            ) : items.map((it) => (
              <tr key={it.product_id} className="border-t">
                <td className="p-2">
                  <div className="flex items-center gap-2">
                    {it.image_url ? <img src={it.image_url} alt="" className="w-10 h-10 object-cover rounded" /> : <div className="w-10 h-10 bg-gray-200 rounded" />}
                    <div>
                      <div className="font-medium">{it.product_name}</div>
                      <div className="text-xs text-gray-500">{it.product_id}</div>
                    </div>
                  </div>
                </td>
                <td className="p-2">
                  <div className="text-xs">{it.category_name || '-'}</div>
                  <div className="text-xs text-gray-500">{it.subcategory_name || ''}</div>
                </td>
                <td className="p-2 text-right">{Number(it.selling_price).toLocaleString()}</td>
                <td className="p-2 text-right font-semibold">{it.stock ?? 0}</td>
                <td className="p-2 text-right">
                  <div className="inline-flex gap-1">
                    <button onClick={() => doAdjust(it.product_id, -1)} className="px-2 py-1 border rounded">-1</button>
                    <button onClick={() => doAdjust(it.product_id, +1)} className="px-2 py-1 border rounded">+1</button>
                    <button onClick={() => doAdjust(it.product_id, -5)} className="px-2 py-1 border rounded">-5</button>
                    <button onClick={() => doAdjust(it.product_id, +5)} className="px-2 py-1 border rounded">+5</button>
                    <button onClick={() => doSet(it.product_id, it.stock)} className="ml-2 px-3 py-1 border rounded">ตั้งค่า…</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="flex justify-between items-center">
        <div className="text-sm text-gray-600">
          รวม {total} รายการ · แสดง {items.length} รายการ
        </div>
        <div className="flex gap-2">
          <button disabled={offset<=0} onClick={() => setOffset(Math.max(0, offset - limit))}
                  className={`px-3 py-1 border rounded ${offset<=0 ? 'opacity-50 cursor-not-allowed' : ''}`}>ก่อนหน้า</button>
          <button disabled={offset + limit >= total} onClick={() => setOffset(offset + limit)}
                  className={`px-3 py-1 border rounded ${(offset + limit >= total) ? 'opacity-50 cursor-not-allowed' : ''}`}>ถัดไป</button>
        </div>
      </div>
    </div>
  );
}
