// [FRONTEND] src/admin/ProductManagement.js
// à¸ˆà¸±à¸”à¸à¸²à¸£à¸ªà¸´à¸™à¸„à¹‰à¸² + CRUD à¸›à¸£à¸°à¹€à¸ à¸—/à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢ + à¸­à¸±à¸›à¹‚à¸«à¸¥à¸”à¸£à¸¹à¸›à¸«à¸¥à¸²à¸¢à¹„à¸Ÿà¸¥à¹Œ + archive/unarchive + à¸¥à¸´à¸‡à¸à¹Œà¹„à¸› Variants
// à¸›à¸£à¸±à¸šà¸›à¸£à¸¸à¸‡: à¹à¸¢à¸à¸«à¸™à¹‰à¸² "à¸ˆà¸±à¸”à¸à¸²à¸£à¸«à¸™à¹ˆà¸§à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸²/à¸«à¸™à¹ˆà¸§à¸¢à¸‚à¸™à¸²à¸”" à¹€à¸«à¸¡à¸·à¸­à¸™à¸ˆà¸±à¸”à¸à¸²à¸£à¸›à¸£à¸°à¹€à¸ à¸—

import React, { useEffect, useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import './ProductManagement.css';
import { api, path } from '../lib/api';

/* Helpers */
const asStr = (v) => (v === null || v === undefined) ? '' : String(v);
const pickStatusName = (x) => x?.status_name ?? x?.StatusName ?? x?.name ?? '';

/* à¸£à¸²à¸¢à¸à¸²à¸£à¹€à¸£à¸´à¹ˆà¸¡à¸•à¹‰à¸™ (à¸¢à¸±à¸‡à¹€à¸à¹‡à¸šà¸à¸±à¹ˆà¸‡ FE) */
const DEFAULT_PRODUCT_UNITS = [
  { code: 'piece', label: 'à¸Šà¸´à¹‰à¸™' },
  { code: 'pot',   label: 'à¸à¸£à¸°à¸–à¸²à¸‡' },
  { code: 'bag',   label: 'à¸–à¸¸à¸‡' },
  { code: 'pack',  label: 'à¹à¸žà¹‡à¸„' },
  { code: 'kg',    label: 'à¸à¸´à¹‚à¸¥à¸à¸£à¸±à¸¡' },
  { code: 'g',     label: 'à¸à¸£à¸±à¸¡' },
  { code: 'ltr',   label: 'à¸¥à¸´à¸•à¸£' },
  { code: 'ml',    label: 'à¸¡à¸´à¸¥à¸¥à¸´à¸¥à¸´à¸•à¸£' },
];

const DEFAULT_SIZE_UNITS = [
  { code: 'mm',   label: 'à¸¡à¸¡.' },
  { code: 'cm',   label: 'à¸‹à¸¡.' },
  { code: 'm',    label: 'à¸¡.' },
  { code: 'inch', label: 'à¸™à¸´à¹‰à¸§' },
  { code: 'kg',   label: 'à¸à¸.' },
  { code: 'ltr',  label: 'à¸¥à¸´à¸•à¸£' },
];

export default function ProductManagement() {
  /* ---------- state à¸«à¸¥à¸±à¸ ---------- */
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [subcategories, setSubcategories] = useState([]);
  const [statuses, setStatuses] = useState([]);
  const [loading, setLoading] = useState(false);
  const [showArchived, setShowArchived] = useState(false);

  /* à¸«à¸™à¹ˆà¸§à¸¢ (à¸ˆà¸±à¸”à¸à¸²à¸£à¹€à¸›à¹‡à¸™à¹€à¸‹à¸à¸Šà¸±à¸™à¹à¸¢à¸) */
  const [productUnits, setProductUnits] = useState(DEFAULT_PRODUCT_UNITS);
  const [sizeUnits, setSizeUnits] = useState(DEFAULT_SIZE_UNITS);
  const [newProdUnit, setNewProdUnit] = useState('');
  const [newSizeUnit, setNewSizeUnit] = useState('');

  /* ---------- state à¸Ÿà¸­à¸£à¹Œà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸² ---------- */
  const [form, setForm] = useState({
    product_name: '',
    description: '',
    selling_price: '',
    category_id: '',
    subcategory_id: '',
    image_url: '',
    unit: '',
    size_value: '',
    size_unit: '',
    origin: '',
    product_status_id: ''
  });

  const [selectedFiles, setSelectedFiles] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [editProductId, setEditProductId] = useState(null);

  /* ---------- state à¸Ÿà¸­à¸£à¹Œà¸¡à¸›à¸£à¸°à¹€à¸ à¸—/à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢ ---------- */
  const [newCat, setNewCat] = useState({ category_id: '', category_name: '' });
  const [newSub, setNewSub] = useState({ subcategory_id: '', subcategory_name: '', category_id: '' });

  /* ------------------------- API ------------------------- */
  const handleApiError = (err, fallback = 'à¹€à¸à¸´à¸”à¸‚à¹‰à¸­à¸œà¸´à¸”à¸žà¸¥à¸²à¸”') => {
    console.error(fallback, err?.response?.data || err?.message || err);
    alert(err?.response?.data?.error || err?.message || fallback);
  };

  const fetchProducts = async () => {
    try {
      const { data } = await api.get(path('/admin/products'), {
        params: { include_archived: showArchived ? 1 : 0 }
      });
      const items = Array.isArray(data?.items) ? data.items : data;
      setProducts(items || []);
    } catch (err) {
      handleApiError(err, 'âŒ à¹‚à¸«à¸¥à¸”à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  const fetchCategories = async () => {
    try {
      const { data } = await api.get(path('/categories'));
      setCategories(data || []);
    } catch (err) {
      handleApiError(err, 'âŒ à¹‚à¸«à¸¥à¸”à¸›à¸£à¸°à¹€à¸ à¸—à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  const fetchSubcategories = async () => {
    try {
      const { data } = await api.get(path('/subcategories'));
      setSubcategories(data || []);
    } catch (err) {
      handleApiError(err, 'âŒ à¹‚à¸«à¸¥à¸”à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  const fetchStatuses = async () => {
    try {
      let res = await api.get(path('/product-status'));
      if (!Array.isArray(res.data)) { try { res = await api.get(path('/product-statuses')); } catch {} }
      const normalized = (res.data || []).map((x) => ({
        id: asStr(x?.product_status_id ?? x?.ProductStatusID ?? x?.id),
        name: pickStatusName(x)
      }));
      setStatuses(normalized);
    } catch {
      console.warn('âš  à¹„à¸¡à¹ˆà¸¡à¸µ endpoint product-status | product-statuses');
      setStatuses([]);
    }
  };

  /* ------------------------- CRUD à¸›à¸£à¸°à¹€à¸ à¸—/à¸«à¸¡à¸§à¸”à¸¢à¹ˆà¸­à¸¢ ------------------------- */
  async function createCategory(payload) {
    try { await api.post(path('/categories'), payload); await fetchCategories(); }
    catch (err) { handleApiError(err, 'âŒ à¹€à¸žà¸´à¹ˆà¸¡à¸›à¸£à¸°à¹€à¸ à¸—à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }
  async function updateCategory(category_id, payload) {
    try { await api.put(path(`/categories/${category_id}`), payload); await fetchCategories(); }
    catch (err) { handleApiError(err, 'âŒ à¹à¸à¹‰à¹„à¸‚à¸›à¸£à¸°à¹€à¸ à¸—à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }
  async function deleteCategory(category_id) {
    try { await api.delete(path(`/categories/${category_id}`)); await fetchCategories(); await fetchSubcategories(); }
    catch (err) { handleApiError(err, 'âŒ à¸¥à¸šà¸›à¸£à¸°à¹€à¸ à¸—à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }
  async function createSubcategory(payload) {
    try { await api.post(path('/subcategories'), payload); await fetchSubcategories(); }
    catch (err) { handleApiError(err, 'âŒ à¹€à¸žà¸´à¹ˆà¸¡à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }
  async function updateSubcategory(subcategory_id, payload) {
    try { await api.put(path(`/subcategories/${subcategory_id}`), payload); await fetchSubcategories(); }
    catch (err) { handleApiError(err, 'âŒ à¹à¸à¹‰à¹„à¸‚à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }
  async function deleteSubcategory(subcategory_id) {
    try { await api.delete(path(`/subcategories/${subcategory_id}`)); await fetchSubcategories(); }
    catch (err) { handleApiError(err, 'âŒ à¸¥à¸šà¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆà¸¢à¹ˆà¸­à¸¢à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  }

  /* ------------------------- à¸­à¸±à¸›à¹‚à¸«à¸¥à¸”à¸£à¸¹à¸› ------------------------- */
  async function uploadImage(file) {
    if (!file) return null;

    // à¸£à¸­à¸‡à¸£à¸±à¸š response à¸¡à¸²à¸•à¸£à¸à¸²à¸™ { files: [{ url }] } à¹à¸¥à¸° fallback à¹€à¸”à¸´à¸¡
    const sendWith = async (field) => {
      const fd = new FormData();
      fd.append(field, file);
      const res = await api.post(path('/upload'), fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });
      const d = res?.data || {};
      if (Array.isArray(d.files) && d.files.length) {
        const f = d.files[0];
        return f?.url || f?.path || null;
      }
      return d.imageUrl || d.url || d.path || d.location || null;
    };

    try { 
      const u = await sendWith('file');
      if (u) return u;
    } catch {}

    try { return await sendWith('image'); } catch {}
    try { return await sendWith('photo'); } catch {}

    return null;
  }

  useEffect(() => {
    (async () => {
      setLoading(true);
      await Promise.all([fetchProducts(), fetchCategories(), fetchSubcategories(), fetchStatuses()]);
      setLoading(false);
    })();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [showArchived]);

  /* ------------------------- handlers à¸Ÿà¸­à¸£à¹Œà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸² ------------------------- */
  const onChange = (e) => {
    const { name, value } = e.target;
    setForm((p) => ({
      ...p,
      [name]: value,
      ...(name === 'category_id' ? { subcategory_id: '' } : {})
    }));
  };
  const onFiles = (e) => setSelectedFiles(Array.from(e.target.files || []));

  const onEdit = (p) => {
    // à¹à¸¢à¸ size "50 à¸‹à¸¡." â†’ 50 + à¸‹à¸¡.
    let size_value = '', size_unit = '';
    if (p.size && typeof p.size === 'string') {
      const m = p.size.trim().match(/^(\d+(?:\.\d+)?)\s*(.+)$/);
      if (m) { size_value = m[1]; size_unit = m[2]; } else { size_value = p.size; }
    }
    setForm({
      product_name:        p.product_name ?? '',
      description:         p.description ?? '',
      selling_price:       asStr(p.selling_price ?? ''),
      category_id:         asStr(p.category_id ?? ''),
      subcategory_id:      asStr(p.subcategory_id ?? ''),
      image_url:           p.image_url ?? '',
      unit:                p.unit ?? '',
      size_value,
      size_unit,
      origin:              p.origin ?? '',
      product_status_id:   asStr(p.product_status_id ?? p.ProductStatusID ?? '')
    });
    setIsEditing(true);
    setEditProductId(p.product_id);
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const clearForm = () => {
    setForm({
      product_name: '', description: '', selling_price: '',
      category_id: '', subcategory_id: '', image_url: '',
      unit: '', size_value: '', size_unit: '', origin: '',
      product_status_id: ''
    });
    setSelectedFiles([]);
    setIsEditing(false);
    setEditProductId(null);
  };

  const onSubmit = async (e) => {
    e.preventDefault();

    // âœ… à¸£à¸²à¸„à¸²à¸‚à¸²à¸¢à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡à¹„à¸¡à¹ˆà¸•à¸´à¸”à¸¥à¸š
    const trimmedPrice = String(form.selling_price ?? '').trim();
    const priceInt = Number.parseInt(trimmedPrice, 10);
    if (!Number.isInteger(priceInt) || priceInt < 0 || String(priceInt) !== trimmedPrice.replace(/^0+(?=\d)/, '')) {
      return alert('à¸£à¸²à¸„à¸²à¸‚à¸²à¸¢à¸•à¹‰à¸­à¸‡à¹€à¸›à¹‡à¸™ "à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡à¹„à¸¡à¹ˆà¸•à¸´à¸”à¸¥à¸š" à¹€à¸—à¹ˆà¸²à¸™à¸±à¹‰à¸™');
    }

    // à¸£à¸§à¸¡ size à¹€à¸›à¹‡à¸™à¸‚à¹‰à¸­à¸„à¸§à¸²à¸¡ (à¸ªà¸­à¸”à¸„à¸¥à¹‰à¸­à¸‡à¸„à¸­à¸¥à¸±à¸¡à¸™à¹Œ text à¹€à¸”à¸´à¸¡)
    let sizeText = '';
    if (form.size_value) sizeText = form.size_unit ? `${form.size_value} ${form.size_unit}` : String(form.size_value);

    // 1) à¸­à¸±à¸›à¹‚à¸«à¸¥à¸”à¸£à¸¹à¸› (à¹€à¸¥à¸·à¸­à¸à¸«à¸¥à¸²à¸¢à¹„à¸Ÿà¸¥à¹Œà¹„à¸”à¹‰)
    let uploadedUrls = [];
    if (selectedFiles.length > 0) {
      try { uploadedUrls = (await Promise.all(selectedFiles.map(f => uploadImage(f)))).filter(Boolean); }
      catch (err) { return handleApiError(err, 'âŒ à¸­à¸±à¸›à¹‚à¸«à¸¥à¸”à¸£à¸¹à¸›à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
    }
    const primaryImageUrl = uploadedUrls[0] || (form.image_url || '').trim();

    // 2) body
    const body = {
      product_name: form.product_name,
      description: form.description,
      image_url: primaryImageUrl || '',
      selling_price: priceInt,
      category_id: form.category_id || null,
      subcategory_id: form.subcategory_id || null,
      product_status_id: form.product_status_id || null,
      ...(form.unit ? { unit: form.unit } : {}),
      ...(sizeText ? { size: sizeText } : {}),
      ...(form.origin ? { origin: form.origin } : {})
    };

    try {
      let createdId = editProductId;
      if (isEditing && editProductId) {
        await api.put(path(`/admin/products/${editProductId}`), body);
      } else {
        const res = await api.post(path('/admin/products'), body);
        createdId = res?.data?.product_id ?? res?.data?.id ?? res?.data?.ProductID ?? createdId;
      }

      // 3) ðŸ†• à¸šà¸±à¸™à¸—à¸¶à¸à¸£à¸¹à¸›à¸—à¸±à¹‰à¸‡à¸«à¸¡à¸”à¸¥à¸‡ product_images (à¸•à¸±à¹‰à¸‡à¸£à¸¹à¸›à¹à¸£à¸à¹€à¸›à¹‡à¸™ primary + à¸ˆà¸±à¸”à¸¥à¸³à¸”à¸±à¸š)
      if (createdId && uploadedUrls.length > 0) {
        await api.post(path(`/products/${createdId}/images`), {
          images: uploadedUrls.map((url, idx) => ({
            url,
            is_primary: idx === 0,
            position: idx + 1,
          })),
        });
      }

      await fetchProducts();
      clearForm();
    } catch (err) {
      handleApiError(err, 'âŒ à¸šà¸±à¸™à¸—à¸¶à¸à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ');
    }
  };

  /* ------------------------- archive / unarchive ------------------------- */
  const archiveProduct = async (id) => {
    if (!window.confirm('à¸¢à¹‰à¸²à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸²à¸™à¸µà¹‰à¹„à¸›à¸–à¸±à¸‡à¹€à¸à¹‡à¸šà¹ƒà¸Šà¹ˆà¹„à¸«à¸¡?')) return;
    try { await api.delete(path(`/admin/products/${id}`)); fetchProducts(); }
    catch (err) { handleApiError(err, 'âŒ à¹€à¸à¹‡à¸šà¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  };
  const unarchiveProduct = async (id) => {
    try { await api.patch(path(`/admin/products/${id}/unarchive`)); fetchProducts(); }
    catch (err) { handleApiError(err, 'âŒ à¸à¸¹à¹‰à¸„à¸·à¸™à¸ªà¸´à¸™à¸„à¹‰à¸²à¹„à¸¡à¹ˆà¸ªà¸³à¹€à¸£à¹‡à¸ˆ'); }
  };

  /* ------------------------- options ------------------------- */
  const statusOptions = useMemo(() => ([
    <option key="" value="">â€” à¹€à¸¥à¸·à¸­à¸à¸ªà¸–à¸²à¸™à¸° â€”</option>,
    ...statuses.map(s => <option key={s.id} value={asStr(s.id)}>{s.name || '(à¹„à¸¡à¹ˆà¸¡à¸µà¸Šà¸·à¹ˆà¸­)'}</option>)
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(statuses)]);

  const categoryOptions = useMemo(() => ([
    <option key="" value="">à¹€à¸¥à¸·à¸­à¸à¸›à¸£à¸°à¹€à¸ à¸—</option>,
    ...categories.map(c => (
      <option key={c.category_id} value={asStr(c.category_id)}>{c.category_name}</option>
    ))
  // eslint-disable-next-line react-hooks/exhaustive-deps
  ]), [JSON.stringify(categories)]);

  const filteredSubcategories = useMemo(() => {
    if (!form.category_id) return [];
    const cid = asStr(form.category_id);
    return (subcategories || []).filter(s => asStr(s.category_id) === cid);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [form.category_id, JSON.stringify(subcategories)]);

  const subcategoryOptions = useMemo(() => ([
    <option key="" value="">{form.category_id ? 'à¹€à¸¥à¸·à¸­à¸à¸«à¸¡à¸§à¸”à¸«à¸¡à¸¹à¹ˆ' : 'à¹€à¸¥à¸·à¸­à¸à¸›à¸£à¸°à¹€à¸ à¸—à¸à¹ˆà¸­à¸™'}</option>,
    ...filteredSubcategories.map(s => (
      <option key={s.subcategory_id} value={asStr(s.subcategory_id)}>{s.subcategory_name}</option>
    ))
  ]), [filteredSubcategories, form.category_id]);

  /* ------------------------- UI ------------------------- */
  if (loading) return <div style={{ padding: 12 }}>à¸à¸³à¸¥à¸±à¸‡à¹‚à¸«à¸¥à¸”à¸‚à¹‰à¸­à¸¡à¸¹à¸¥â€¦</div>;

  return (
    <div className="pm-page">
      <div className="pm-panel">
        <h2>{isEditing ? 'à¹à¸à¹‰à¹„à¸‚à¸ªà¸´à¸™à¸„à¹‰à¸²' : 'à¹€à¸žà¸´à¹ˆà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸²à¹ƒà¸«à¸¡à¹ˆ'}</h2>

        {/* à¸Ÿà¸­à¸£à¹Œà¸¡à¸ªà¸´à¸™à¸„à¹‰à¸² */}
        <form onSubmit={onSubmit} className="pm-form">
          <input name="product_name" placeholder="à¸Šà¸·à¹ˆà¸­à¸ªà¸´à¸™à¸„à¹‰à¸²" value={form.product_name} onChange={onChange} />

          {/* à¸£à¸²à¸„à¸²à¸‚à¸²à¸¢: à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡ â‰¥ 0 */}
          <input
            name="selling_price"
            type="number"
            inputMode="numeric"
            min="0"
            step="1"
            placeholder="à¸£à¸²à¸„à¸²à¸‚à¸²à¸¢ (à¸ˆà¸³à¸™à¸§à¸™à¹€à¸•à¹‡à¸¡à¹€à¸—à¹ˆà¸²à¸™à¸±à¹‰à¸™)"
            value={form.selling_price}
            onChange={onChange}
          />

          {/* à¸‚à¸™à¸²à¸”(à¸•à¸±à¸§à¹€à¸¥à¸‚) + à¸«à¸™à¹ˆà¸§à¸¢à¸‚à¸™à¸²à¸” + à¸«à¸™à¹ˆà¸§à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸² (à¸šà¸£à¸£à¸—à¸±à¸”à¹€à¸”à¸µà¸¢à¸§) */}
          <div className="pm-input-group pm-row-3 col-span-2">
            <input
              name="size_value"
              type="number"
              inputMode="decimal"
              min="0"
              step="1"
              placeholder="à¸‚à¸™à¸²à¸”à¸ªà¸´à¸™à¸„à¹‰à¸² (à¸•à¸±à¸§à¹€à¸¥à¸‚)"
              value={form.size_value}
              onChange={onChange}
              title="à¹€à¸Šà¹ˆà¸™ 50"
            />
            <select name="size_unit" value={form.size_unit} onChange={onChange} title="à¸«à¸™à¹ˆà¸§à¸¢à¸‚à¸­à¸‡à¸‚à¸™à¸²à¸”">
              <option value="">â€” à¹€à¸¥à¸·à¸­à¸à¸«à¸™à¹ˆà¸§à¸¢à¸‚à¸™à¸²à¸” â€”</option>
              {sizeUnits.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
            </select>
            <select name="unit" value={form.unit} onChange={onChange} title="à¸«à¸™à¹ˆà¸§à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸²">
              <option value="">â€” à¹€à¸¥à¸·à¸­à¸à¸«à¸™à¹ˆà¸§à¸¢à¸ªà¸´à¸™à¸„à¹‰à¸² â€”</option>
              {productUnits.map(u => <option key={u.code} value={u.label}>{u.label}</option>)}
            </select>
          </div>

          <input name="origin" placeholder="à¹à¸«à¸¥à¹ˆà¸‡à¸—à¸µà¹ˆà¸¡à¸²" value={form.origin} onChange={onChange} />

          <select name="product_status_id" value={form.product_status_id} onChange={onChange}>
            {statusOptions}
          </select>

          <select name="category_id" value={form.category_id} onChange={onChange}>
            {categoryOptions}
          </select>

          <select
            name="subcategory_id"
            value={form.subcategory_id}
            onChange={onChange}
            disabled={!form.category_id}
          >
            {subcategoryOptions}
          </select>

